insert into feedme.recipes VALUES ('CourgetteLinguine', 'Courgette Linguine', 'A beautiful Summery Courgette Linguine with fresh mint and lemon', 'italian');
insert into feedme.recipe_ingredients values('CourgetteLinguine','Courgette','courgettes, cut into ribbons with a peeler', 2,'unit');
insert into feedme.recipe_ingredients values('CourgetteLinguine','Onion','onions, thinly sliced',0.5,'unit');
insert into feedme.recipe_ingredients values('CourgetteLinguine','Lemon','lemon',1,'unit');
insert into feedme.recipe_ingredients values('CourgetteLinguine','Chilli Flakes','chilli flakes',2,'tspn');
insert into feedme.recipe_ingredients values('CourgetteLinguine','Fresh Mint','fresh mint, cut coarsely',1,'packet');
insert into feedme.recipe_ingredients values('CourgetteLinguine','Linguine','linguine',300,'gram');
insert into feedme.recipe_ingredients values('CourgetteLinguine','Parmesan','grated parmesan cheese',50,'gram');
insert into feedme.recipe_ingredients values('CourgetteLinguine','Olive Oil','olive oil',1,'tblspn');
insert into feedme.recipe_steps values('CourgetteLinguine', 1, 0, 'Start boiling linguine in lightly salted water');
insert into feedme.recipe_steps values('CourgetteLinguine', 2, 1, 'Prepare your ingredients. Thinly slice onions, create courgette ribbons using a peeler, and grate zest of lemon and parmesan');
insert into feedme.recipe_steps values('CourgetteLinguine', 3, 6, 'Under a low heat, fry courgette and onions with a spoon of olive oil');
insert into feedme.recipe_steps values('CourgetteLinguine', 4, 10, 'Add lemon zest, juice of half a lemon, and chilli flakes to the frying pan');
insert into feedme.recipe_steps values('CourgetteLinguine', 5, 12, 'Add linguine and some of the boiling water to the pan. Add olive oil and parmesan, and continue to boil in the pan for two minutes');
insert into feedme.recipe_steps values('CourgetteLinguine', 6, 14, 'Serve on dishes, and garnish with fresh chopped mint');
insert into feedme.recipes VALUES ('ChickenCourgettePie', 'Chicken and Courgette Pie', 'A hearty mediterranean alternative to the classic British chicken and leek pie', 'british');
insert into feedme.recipe_ingredients values('ChickenCourgettePie','Courgette','courgettes, diced', 1,'unit');
insert into feedme.recipe_ingredients values('ChickenCourgettePie','Aubergine','aubergine, diced', 1,'unit');
insert into feedme.recipe_ingredients values('ChickenCourgettePie','RedOnion','red onions, thickly sliced',2,'unit');
insert into feedme.recipe_ingredients values('ChickenCourgettePie','Tomato','tin of peeled, plum tomatoes',1,'unit');
insert into feedme.recipe_ingredients values('ChickenCourgettePie','Chicken','chicken thighs, sliced',2,'unit');
insert into feedme.recipe_ingredients values('ChickenCourgettePie','Oregano','dried oregano',1,'tspn');
insert into feedme.recipe_ingredients values('ChickenCourgettePie','BayLeaf','bay leaf',1,'unit');
insert into feedme.recipe_ingredients values('ChickenCourgettePie','PuffPastry','packets of puff-pastry',1,'unit');
insert into feedme.recipe_ingredients values('ChickenCourgettePie','Olive Oil','olive oil',1,'tblspn');
insert into feedme.recipe_steps values('ChickenCourgettePie', 1, 0, 'Turn the oven to 220C');
insert into feedme.recipe_steps values('ChickenCourgettePie', 2, 1, 'Chop all your ingredients coarsely, and the chicken thighs too');
insert into feedme.recipe_steps values('ChickenCourgettePie', 3, 6, 'Fry the chicken in one pan under a medium heat. In another pan, fry your vegetables under a low heat');
insert into feedme.recipe_steps values('ChickenCourgettePie', 4, 8, 'In a baking dish, lay out your puff pastry, keeping some for a lid. Place in the oven to blind bake');
insert into feedme.recipe_steps values('ChickenCourgettePie', 5, 11, 'Add chicken to the pan with vegetables, add a tin of chopped tomatoes, some oregano, and let reduce');
insert into feedme.recipe_steps values('ChickenCourgettePie', 6, 12, 'Remove your pastry from the oven, and turn the heat down to 160C');
insert into feedme.recipe_steps values('ChickenCourgettePie', 7, 16, 'Add your chicken and pastry mix to the dish, place remainign pastry on as a lid, and place in the oven and wait');
insert into feedme.recipe_steps values('ChickenCourgettePie', 8, 60, 'Remove your pie from the oven and enjoy');
insert into feedme.recipes VALUES ('CourgetteLasagne', 'Creamy Courgette Lasagne', 'A creamy quick dish for a last minute dinner party and impress veggie friends', 'italian');
insert into feedme.recipe_ingredients values('CourgetteLasagne','Courgette','courgettes, coarsely grated', 700,'gram');
insert into feedme.recipe_ingredients values('CourgetteLasagne','Onion','onions, finely chopped',1,'unit');
insert into feedme.recipe_ingredients values('CourgetteLasagne','Garlic Cloved','garlic cloves, crushed',2,'unit');
insert into feedme.recipe_ingredients values('CourgetteLasagne','Ricotta','tub of ricotta',1,'unit');
insert into feedme.recipe_ingredients values('CourgetteLasagne','Cheddar','cheddar grated',50,'gram');
insert into feedme.recipe_ingredients values('CourgetteLasagne','Lasagne Sheets','lasagne sheets',9,'unit');
insert into feedme.recipe_ingredients values('CourgetteLasagne','Tomato','tomato sauce',350,'gram');
insert into feedme.recipe_ingredients values('CourgetteLasagne','Sunflower Oil','olive oil',1,'tblspn');
insert into feedme.recipe_steps values('CourgetteLasagne', 1, 0, 'Heat oven to 220C/fan 200C/gas 7. Put a pan of water on to boil, then cook the lasagne sheets for about 5 mins until softened, but not cooked through. Rinse in cold water, then drizzle with a little oil to stop them sticking together.');
insert into feedme.recipe_steps values('CourgetteLasagne', 2, 5, 'Meanwhile, heat the oil in a large frying pan, then fry the onion. After 3 mins, add the courgettes and garlic and continue to fry until the courgette has softened and turned bright green. Stir in 2/3 of both the ricotta and the cheddar, then season to taste. Heat the tomato sauce in the microwave for 2 mins on High until hot.');
insert into feedme.recipe_steps values('CourgetteLasagne', 3, 12, 'In a large baking dish, layer up the lasagne, starting with half the courgette mix, then pasta, then tomato sauce. Repeat, top with blobs of the remaining ricotta, then scatter with the rest of the cheddar. Bake on the top shelf for about 10 mins until the pasta is tender and the cheese is golden.');
insert into feedme.recipes VALUES ('CourgetteBaconPasta', 'Creamy courgette & bacon pasta', 'A quick and creamy carbonara-style tagliatelle that showcases delicious courgettes contrasted with cream and pancetta', 'italian');
insert into feedme.recipe_ingredients values('CourgetteBaconPasta','Courgette','courgettes, coarsely grated', 4,'unit');
insert into feedme.recipe_ingredients values('CourgetteBaconPasta','Bacon','diced pancetta or bacon lardons',150,'gram');
insert into feedme.recipe_ingredients values('CourgetteBaconPasta','Garlic Cloved','garlic cloves, crushed',2,'unit');
insert into feedme.recipe_ingredients values('CourgetteBaconPasta','Creme Fraich','tub of creme fraiche',200,'gram');
insert into feedme.recipe_ingredients values('CourgetteBaconPasta','Cheddar','cheddar grated',50,'gram');
insert into feedme.recipe_ingredients values('CourgetteBaconPasta','Tagliatelle','lasagne sheets',300,'gram');
insert into feedme.recipe_ingredients values('CourgetteBaconPasta','Tomato','tomato sauce',350,'gram');
insert into feedme.recipe_ingredients values('CourgetteBaconPasta','Olive Oil','olive oil',1,'tblspn');
insert into feedme.recipe_steps values('CourgetteBaconPasta', 1, 0, 'Heat the olive oil in a large frying pan and sizzle the pancetta or bacon for about 5 mins until starting to crisp. Turn up the heat and add the grated courgette to the pan. Cook for 5 mins or until soft and starting to brown then add the garlic and cook for a minute longer. Season and set aside.');
insert into feedme.recipe_steps values('CourgetteBaconPasta', 2, 5, 'Cook the tagliatelle according to the pack instructions and scoop out a cupful of cooking water. Drain the tagliatelle and tip into the frying pan with the bacon and courgette. Over a low heat toss everything together with the cr√®me fraiche and half the Parmesan adding a splash of pasta water too if you need to loosen the sauce. Season to taste and serve twirled into bowls with the remaining Parmesan scattered over.');
insert into feedme.recipes VALUES ('EggsOnToast', 'Egg with Toast', 'A class British breakfast for a', 'british');
insert into feedme.recipe_ingredients values('EggsOnToast','Bread','bread, one slice', 1,'unit');
insert into feedme.recipe_ingredients values('EggsOnToast','Egg','an egg',1,'unit');
insert into feedme.recipe_steps values('EggsOnToast', 1, 0, 'Put the egg and some salted water in a pot and heat. Leave boil for three minutes');
insert into feedme.recipe_steps values('EggsOnToast', 2, 4, 'With one minute to go before your egg is ready, put the bread in the toaster.');
insert into feedme.recipe_steps values('EggsOnToast', 3, 5, 'Enjoy!');
