insert into feedme.recipe_ingredients values('CourgetteLinguine','Onion','onions, thinly sliced',0.5,'unit');
insert into feedme.recipe_ingredients values('CourgetteLinguine','Lemon','lemon',1,'unit');
insert into feedme.recipe_ingredients values('CourgetteLinguine','Chilli Flakes','chilli flakes',2,'tspn');
insert into feedme.recipe_ingredients values('CourgetteLinguine','Fresh Mint','fresh mint, cut coarsely',1,'packet');
insert into feedme.recipe_ingredients values('CourgetteLinguine','Linguine','linguine',300,'gram');
insert into feedme.recipe_ingredients values('CourgetteLinguine','Parmesan','grated parmesan cheese',50,'gram');
insert into feedme.recipe_ingredients values('CourgetteLinguine','Olive Oil','olive oil',1,'tblspn');
insert into feedme.recipe_steps values('CourgetteLinguine', 1, 0, 'Start boiling linguine in lightly salted water');
insert into feedme.recipe_steps values('CourgetteLinguine', 2, 1, 'Prepare your ingredients. Thinly slice onions, create courgette ribbons using a peeler, and grate zest of lemon and parmesan');
insert into feedme.recipe_steps values('CourgetteLinguine', 3, 6, 'Under a low heat, fry courgette and onions with a spoon of olive oil');
insert into feedme.recipe_steps values('CourgetteLinguine', 4, 10, 'Add lemon zest, juice of half a lemon, and chilli flakes to the frying pan');
insert into feedme.recipe_steps values('CourgetteLinguine', 5, 12, 'Add linguine and some of the boiling water to the pan. Add olive oil and parmesan, and continue to boil in the pan for two minutes');
insert into feedme.recipe_steps values('CourgetteLinguine', 6, 14, 'Serve on dishes, and garnish with fresh chopped mint');
insert into feedme.recipes VALUES ('ChickenCourgettePie', 'Chicken and Courgette Pie', 'A hearty mediterranean alternative to the classic British chicken and leek pie', 'british');
insert into feedme.recipe_ingredients values('ChickenCourgettePie','Courgette','courgettes, diced', 1,'unit');
insert into feedme.recipe_ingredients values('ChickenCourgettePie','Aubergine','aubergine, diced', 1,'unit');
insert into feedme.recipe_ingredients values('ChickenCourgettePie','RedOnion','red onions, thickly sliced',2,'unit');
insert into feedme.recipe_ingredients values('ChickenCourgettePie','Tomato','tin of peeled, plum tomatoes',1,'unit');
insert into feedme.recipe_ingredients values('ChickenCourgettePie','Chicken','chicken thighs, sliced',2,'unit');
insert into feedme.recipe_ingredients values('ChickenCourgettePie','Oregano','dried oregano',1,'tspn');
insert into feedme.recipe_ingredients values('ChickenCourgettePie','BayLeaf','bay leaf',1,'unit');
insert into feedme.recipe_ingredients values('ChickenCourgettePie','PuffPastry','packets of puff-pastry',1,'unit');
insert into feedme.recipe_ingredients values('ChickenCourgettePie','Olive Oil','olive oil',1,'tblspn');
insert into feedme.recipe_steps values('ChickenCourgettePie', 1, 0, 'Turn the oven to 220C');
insert into feedme.recipe_steps values('ChickenCourgettePie', 2, 1, 'Chop all your ingredients coarsely, and the chicken thighs too');
insert into feedme.recipe_steps values('ChickenCourgettePie', 3, 6, 'Fry the chicken in one pan under a medium heat. In another pan, fry your vegetables under a low heat');
insert into feedme.recipe_steps values('ChickenCourgettePie', 4, 8, 'In a baking dish, lay out your puff pastry, keeping some for a lid. Place in the oven to blind bake');
insert into feedme.recipe_steps values('ChickenCourgettePie', 5, 11, 'Add chicken to the pan with vegetables, add a tin of chopped tomatoes, some oregano, and let reduce');
insert into feedme.recipe_steps values('ChickenCourgettePie', 6, 12, 'Remove your pastry from the oven, and turn the heat down to 160C');
insert into feedme.recipe_steps values('ChickenCourgettePie', 7, 16, 'Add your chicken and pastry mix to the dish, place remainign pastry on as a lid, and place in the oven and wait');
insert into feedme.recipe_steps values('ChickenCourgettePie', 8, 60, 'Remove your pie from the oven and enjoy');
insert into feedme.recipes VALUES ('CourgetteLasagne', 'Creamy Courgette Lasagne', 'A creamy quick dish for a last minute dinner party and impress veggie friends', 'italian');
insert into feedme.recipe_ingredients values('CourgetteLasagne','Courgette','courgettes, coarsely grated', 700,'gram');
insert into feedme.recipe_ingredients values('CourgetteLasagne','Onion','onions, finely chopped',1,'unit');
insert into feedme.recipe_ingredients values('CourgetteLasagne','Garlic Cloved','garlic cloves, crushed',2,'unit');
insert into feedme.recipe_ingredients values('CourgetteLasagne','Ricotta','tub of ricotta',1,'unit');
insert into feedme.recipe_ingredients values('CourgetteLasagne','Cheddar','cheddar grated',50,'gram');
insert into feedme.recipe_ingredients values('CourgetteLasagne','Lasagne Sheets','lasagne sheets',9,'unit');
insert into feedme.recipe_ingredients values('CourgetteLasagne','Tomato','tomato sauce',350,'gram');
insert into feedme.recipe_ingredients values('CourgetteLasagne','Sunflower Oil','olive oil',1,'tblspn');
insert into feedme.recipe_steps values('CourgetteLasagne', 1, 0, 'Heat oven to 220C/fan 200C/gas 7. Put a pan of water on to boil, then cook the lasagne sheets for about 5 mins until softened, but not cooked through. Rinse in cold water, then drizzle with a little oil to stop them sticking together.');
insert into feedme.recipe_steps values('CourgetteLasagne', 2, 5, 'Meanwhile, heat the oil in a large frying pan, then fry the onion. After 3 mins, add the courgettes and garlic and continue to fry until the courgette has softened and turned bright green. Stir in 2/3 of both the ricotta and the cheddar, then season to taste. Heat the tomato sauce in the microwave for 2 mins on High until hot.');
insert into feedme.recipe_steps values('CourgetteLasagne', 3, 12, 'In a large baking dish, layer up the lasagne, starting with half the courgette mix, then pasta, then tomato sauce. Repeat, top with blobs of the remaining ricotta, then scatter with the rest of the cheddar. Bake on the top shelf for about 10 mins until the pasta is tender and the cheese is golden.');
insert into feedme.recipes VALUES ('CourgetteBaconPasta', 'Creamy courgette & bacon pasta', 'A quick and creamy carbonara-style tagliatelle that showcases delicious courgettes contrasted with cream and pancetta', 'italian');
insert into feedme.recipe_ingredients values('CourgetteBaconPasta','Courgette','courgettes, coarsely grated', 4,'unit');
insert into feedme.recipe_ingredients values('CourgetteBaconPasta','Bacon','diced pancetta or bacon lardons',150,'gram');
insert into feedme.recipe_ingredients values('CourgetteBaconPasta','Garlic Cloved','garlic cloves, crushed',2,'unit');
insert into feedme.recipe_ingredients values('CourgetteBaconPasta','Creme Fraich','tub of creme fraiche',200,'gram');
insert into feedme.recipe_ingredients values('CourgetteBaconPasta','Cheddar','cheddar grated',50,'gram');
insert into feedme.recipe_ingredients values('CourgetteBaconPasta','Tagliatelle','lasagne sheets',300,'gram');
insert into feedme.recipe_ingredients values('CourgetteBaconPasta','Tomato','tomato sauce',350,'gram');
insert into feedme.recipe_ingredients values('CourgetteBaconPasta','Olive Oil','olive oil',1,'tblspn');
insert into feedme.recipe_steps values('CourgetteBaconPasta', 1, 0, 'Heat the olive oil in a large frying pan and sizzle the pancetta or bacon for about 5 mins until starting to crisp. Turn up the heat and add the grated courgette to the pan. Cook for 5 mins or until soft and starting to brown then add the garlic and cook for a minute longer. Season and set aside.');
insert into feedme.recipe_steps values('CourgetteBaconPasta', 2, 5, 'Cook the tagliatelle according to the pack instructions and scoop out a cupful of cooking water. Drain the tagliatelle and tip into the frying pan with the bacon and courgette. Over a low heat toss everything together with the crème fraiche and half the Parmesan adding a splash of pasta water too if you need to loosen the sauce. Season to taste and serve twirled into bowls with the remaining Parmesan scattered over.');
insert into feedme.recipes VALUES ('EggsOnToast', 'Egg with Toast', 'A class British breakfast for a', 'british');
insert into feedme.recipe_ingredients values('EggsOnToast','Bread','bread, one slice', 1,'unit');
insert into feedme.recipe_ingredients values('EggsOnToast','Egg','an egg',1,'unit');
insert into feedme.recipe_steps values('EggsOnToast', 1, 0, 'Put the egg and some salted water in a pot and heat. Leave boil for three minutes');
insert into feedme.recipe_steps values('EggsOnToast', 2, 4, 'With one minute to go before your egg is ready, put the bread in the toaster.');
insert into feedme.recipe_steps values('EggsOnToast', 3, 5, 'Enjoy!');
insert into feedme.recipes VALUES ('easy-chicken-tagine', 'Easy chicken tagine', 'Simple one-pots are perfect for the busy cook. This chicken tagine is great eaten with cous cous for something a bit more filling', 'moroccan');
insert into feedme.recipe_ingredients values('easy-chicken-tagine','olive oil','2 tbsp olive oil',2.0,'tbsp');
insert into feedme.recipe_ingredients values('easy-chicken-tagine','chicken','8 skinless boneless chicken thighs, halved if large',8.0,'unit');
insert into feedme.recipe_ingredients values('easy-chicken-tagine','onion','1 onion, chopped',1.0,'unit');
insert into feedme.recipe_ingredients values('easy-chicken-tagine','ginger','2 tsp grated fresh root ginger',2.0,'tsp');
insert into feedme.recipe_ingredients values('easy-chicken-tagine','saffron','pinch saffron or tumeric',NULL,'null');
insert into feedme.recipe_ingredients values('easy-chicken-tagine','honey','1 tbsp honey',1.0,'tbsp');
insert into feedme.recipe_ingredients values('easy-chicken-tagine','carrot','400g carrot, cut into sticks',400.0,'gram');
insert into feedme.recipe_ingredients values('easy-chicken-tagine','parsley','small bunch parsley, roughly chopped',NULL,'null');
insert into feedme.recipe_ingredients values('easy-chicken-tagine','lemon','lemon wedges, to serve',NULL,'null');
insert into feedme.recipe_steps values('easy-chicken-tagine',0,0, 'Heat the oil in a large, wide pan with a lid, add the chicken, then fry quickly until lightly coloured. Add the onion and ginger, then fry for a further 2 mins.');
insert into feedme.recipe_steps values('easy-chicken-tagine',1,1, 'Add 150ml water, the saffron, honey and carrots, season, then stir well. Bring to the boil, cover tightly, then simmer for 30 mins until the chicken is tender. Uncover and increase the heat for about 5 mins to reduce the sauce a little. Sprinkle with parsley and serve with lemon wedges for squeezing over.');
insert into feedme.recipes VALUES ('salt-and-pepper-squid', 'Salt & pepper squid', 'Crisp, spicy coating and hot, tender squid make an irresistible combination', 'vietnamese');
insert into feedme.recipe_ingredients values('salt-and-pepper-squid','cornflour','85g cornflour',85.0,'gram');
insert into feedme.recipe_ingredients values('salt-and-pepper-squid','flour','85g plain flour',85.0,'gram');
insert into feedme.recipe_ingredients values('salt-and-pepper-squid','pepper','2 tsp black pepper, cracked',2.0,'tsp');
insert into feedme.recipe_ingredients values('salt-and-pepper-squid','peppercorn','2 tbsp ground Szechuan peppercorns, roughly crushed',2.0,'tbsp');
insert into feedme.recipe_ingredients values('salt-and-pepper-squid','vegetable oil','sunflower or vegetable oil, for frying',NULL,'null');
insert into feedme.recipe_ingredients values('salt-and-pepper-squid','squid','400g squid, cleaned and cut into strips (see tips, below)',400.0,'gram');
insert into feedme.recipe_ingredients values('salt-and-pepper-squid','chilli','finely sliced spring onionand green chilli, to serve',NULL,'null');
insert into feedme.recipe_steps values('salt-and-pepper-squid',0,0, 'To make the dipping sauce, mix all the ingredients in a small bowl until the sugar has dissolved, then set aside. Mix the cornflour and plain flour with both peppers and 2 tsp sea salt in a large bowl, then set aside. Line a tray with kitchen paper and make sure you have more salt to sprinkle with.');
insert into feedme.recipe_steps values('salt-and-pepper-squid',1,1, 'Heat about 7cm of oil to 180C in a deep fryer, wok or deep pan. If you don’t have a thermometer, you can test it with a cube of bread – it should brown in 20 secs. Coat the squid well with the flour mix and fry in batches for about 2 mins each or until crisp. Use a slotted spoon to lift out the squid, then drain on the kitchen paper and sprinkle with a little more salt. Serve the squid scattered with the spring onion and chilli, with the dipping sauce on the side.');
insert into feedme.recipes VALUES ('spanish-rice-and-prawn-onepot', 'Spanish rice & prawn one-pot', 'A fast and easy meal filled with Mediterranean holiday flavours', 'spanish');
insert into feedme.recipe_ingredients values('spanish-rice-and-prawn-onepot','onion','1 onion, sliced',1.0,'unit');
insert into feedme.recipe_ingredients values('spanish-rice-and-prawn-onepot','pepper','1 red and 1 green pepper, deseeded and sliced',1.0,'unit');
insert into feedme.recipe_ingredients values('spanish-rice-and-prawn-onepot','chorizo','50g chorizo, sliced',50.0,'gram');
insert into feedme.recipe_ingredients values('spanish-rice-and-prawn-onepot','garlic','2 garlic cloves, crushed',2.0,'unit');
insert into feedme.recipe_ingredients values('spanish-rice-and-prawn-onepot','olive oil','1 tbsp olive oil',1.0,'tbsp');
insert into feedme.recipe_ingredients values('spanish-rice-and-prawn-onepot','rice','250g easy cook basmati rice(we used Tilda)',250.0,'gram');
insert into feedme.recipe_ingredients values('spanish-rice-and-prawn-onepot','tomato','400g can chopped tomato',400.0,'gram');
insert into feedme.recipe_ingredients values('spanish-rice-and-prawn-onepot','prawn','200g raw, peeled prawns, defrosted if frozen',200.0,'gram');
insert into feedme.recipe_steps values('spanish-rice-and-prawn-onepot',0,0, 'Boil the kettle. In a non-stick frying or shallow pan with a lid, fry the onion, peppers, chorizo and garlic in the oil over a high heat for 3 mins. Stir in the rice and chopped tomatoes with 500ml boiling water, cover, then cook over a high heat for 12 mins.');
insert into feedme.recipe_steps values('spanish-rice-and-prawn-onepot',1,1, 'Uncover, then stir – the rice should be almost tender. Stir in the prawns, with a splash more water if the rice is looking dry, then cook for another min until the prawns are just pink and rice tender.');
insert into feedme.recipes VALUES ('moroccan-meatball-tagine-with-lemon-and-olives', 'Moroccan meatball tagine with lemon & olives', 'A wonderfully aromatic North African lamb casserole with a citrus tang - great for dinner parties', 'moroccan');
insert into feedme.recipe_ingredients values('moroccan-meatball-tagine-with-lemon-and-olives','onion','3 onions, peeled',3.0,'unit');
insert into feedme.recipe_ingredients values('moroccan-meatball-tagine-with-lemon-and-olives','lamb','500g minced lamb',500.0,'gram');
insert into feedme.recipe_ingredients values('moroccan-meatball-tagine-with-lemon-and-olives','lemon','zest and juice 1 unwaxed lemon, quartered',NULL,'null');
insert into feedme.recipe_ingredients values('moroccan-meatball-tagine-with-lemon-and-olives','cumin','1 tsp ground cumin',1.0,'tsp');
insert into feedme.recipe_ingredients values('moroccan-meatball-tagine-with-lemon-and-olives','cinnamon','1 tsp ground cinnamon',1.0,'tsp');
insert into feedme.recipe_ingredients values('moroccan-meatball-tagine-with-lemon-and-olives','pepper','pinch cayenne pepper',NULL,'null');
insert into feedme.recipe_ingredients values('moroccan-meatball-tagine-with-lemon-and-olives','parsley','small bunch flat-leaf parsley, chopped',NULL,'null');
insert into feedme.recipe_ingredients values('moroccan-meatball-tagine-with-lemon-and-olives','olive oil','2 tbsp olive oil',2.0,'tbsp');
insert into feedme.recipe_ingredients values('moroccan-meatball-tagine-with-lemon-and-olives','ginger','thumb-sized piece ginger, peeled and grated',NULL,'null');
insert into feedme.recipe_ingredients values('moroccan-meatball-tagine-with-lemon-and-olives','chilli','1 red chilli, deseeded and finely chopped',1.0,'unit');
insert into feedme.recipe_ingredients values('moroccan-meatball-tagine-with-lemon-and-olives','saffron','pinch saffronstrands',NULL,'null');
insert into feedme.recipe_ingredients values('moroccan-meatball-tagine-with-lemon-and-olives','stock','250ml lambstock',250.0,'ml');
insert into feedme.recipe_ingredients values('moroccan-meatball-tagine-with-lemon-and-olives','tomato','1 tbsp tomato purée',1.0,'tbsp');
insert into feedme.recipe_ingredients values('moroccan-meatball-tagine-with-lemon-and-olives','olive','100g pitted black kalamata olive',100.0,'gram');
insert into feedme.recipe_ingredients values('moroccan-meatball-tagine-with-lemon-and-olives','coriander','small bunch coriander, chopped',NULL,'null');
insert into feedme.recipe_ingredients values('moroccan-meatball-tagine-with-lemon-and-olives','bread','couscousor fresh crusty bread, to serve',NULL,'null');
insert into feedme.recipe_steps values('moroccan-meatball-tagine-with-lemon-and-olives',0,0, 'Put the onions in a food processor and blitz until finely chopped. Put the lamb, lemon zest, spices, parsley and half the onions in a large bowl, and season. Using your hands, mix until well combined, then shape into walnut-sized balls.');
insert into feedme.recipe_steps values('moroccan-meatball-tagine-with-lemon-and-olives',1,1, 'Heat the oil in a large flameproof dish, or tagine with a lid, then add the remaining onions, ginger, chilli and saffron. Cook for 5 mins until the onion is softened and starting to colour. Add the lemon juice, stock, tomato purée and olives, then bring to the boil. Add the meatballs, one at a time, then reduce the heat, cover with the lid and cook for 20 mins, turning the meatballs a couple of times.');
insert into feedme.recipe_steps values('moroccan-meatball-tagine-with-lemon-and-olives',2,2, 'Remove lid, then add the coriander and lemon wedges, tucking them in between the meatballs. Cook, uncovered, for a further 10 mins until the liquid has reduced and thickened slightly. Serve hot with couscous or fresh crusty bread.');
insert into feedme.recipes VALUES ('asian-pork-with-rice-noodle-salad', 'Asian pork with rice noodle salad', 'A weeknight supper that''s full of colour and spice and all things nice, and ready in just 20 minutes', 'vietnamese');
insert into feedme.recipe_ingredients values('asian-pork-with-rice-noodle-salad','lemongrass','1 tsp lemongrass paste (we used Bart''s)',1.0,'tsp');
insert into feedme.recipe_ingredients values('asian-pork-with-rice-noodle-salad','ginger','1 tsp finely-grated fresh root ginger',1.0,'tsp');
insert into feedme.recipe_ingredients values('asian-pork-with-rice-noodle-salad','chilli','0.5 red chilli, finely chopped',0.0,'unit');
insert into feedme.recipe_ingredients values('asian-pork-with-rice-noodle-salad','sunflower oil','1 tsp sunflower oil',1.0,'tsp');
insert into feedme.recipe_ingredients values('asian-pork-with-rice-noodle-salad','fish sauce','3 tbsp fish sauce',3.0,'tbsp');
insert into feedme.recipe_ingredients values('asian-pork-with-rice-noodle-salad','pork chop','4 pork chops',4.0,'unit');
insert into feedme.recipe_steps values('asian-pork-with-rice-noodle-salad',0,0, 'Mix together the lemongrass paste, ginger, chilli, oil and 1 tbsp of the fish sauce. Smear the mixture all over the pork chops, then set aside to marinate while you make the salad.');
insert into feedme.recipe_steps values('asian-pork-with-rice-noodle-salad',1,1, 'Tip the noodles into a large bowl. Pour over a kettleful of boiling water, then leave to sit for 5 mins until the noodles are soft (or prepare them according to pack instructions). Drain the noodles into a sieve. Run under the cold tap until cool, then drain again and tip back into the bowl. Whisk the remaining 2 tbsp fish sauce with the lime zest and juice and sugar. Pour over the cold noodles. Slice the cucumber into thin strips, then toss through the salad along with the mint leaves.');
insert into feedme.recipe_steps values('asian-pork-with-rice-noodle-salad',2,2, 'Heat a griddle pan until smoking hot. Griddle the chops for 5 mins on each side or until cooked through, then serve alongside the noodle salad.');
insert into feedme.recipes VALUES ('raspberry-white-chocolate-and-pistachio-profiterol', 'Raspberry, white chocolate & pistachio profiteroles', 'Once you''ve mastered choux pastry using our step-by-step guide, this French patisserie-style dessert will be a breeze', 'french');
insert into feedme.recipe_steps values('raspberry-white-chocolate-and-pistachio-profiterol',0,0, 'Heat oven to 180C/160C fan/ gas 4. To make the choux pastry, sift the flour onto a large sheet of baking parchment. This will stop lumps of flour forming in the dough. Put the butter, milk, water, sugar and salt in a pan, and heat gently until the butter has completely melted. Only once the butter has melted, increase the heat and bring the liquid to the boil. When bubbling, remove quickly from the heat and tip in the flour all at once. Immediately beat with a wooden spoon until the mixture forms a dough that leaves the side of the pan clean. Return to a low heat, stirring, for 1 min. Remove from the heat. Cool for 3 mins. Gradually add the beaten egg to the dough, beating well between each addition, until the dough is smooth and glossy.');
insert into feedme.recipe_steps values('raspberry-white-chocolate-and-pistachio-profiterol',1,1, 'Line a large flat baking sheet with baking parchment. Pipe or spoon about 24 balls of dough over the baking sheet, allowing space between each for rising. Bake for 20-25 mins until the profiteroles are puffed and golden. Remove the profiteroles from the oven and quickly poke a hole in the bottom of each to let out the steam, then return to the oven for 5 mins. Cool on a wire rack.');
insert into feedme.recipe_steps values('raspberry-white-chocolate-and-pistachio-profiterol',2,2, 'If using frozen raspberries for the filling, defrost in the microwave on Low for 4-5 mins. Tip the raspberries, icing sugar and lemon juice into a food processor or mini blender and blitz until smooth. Press through a sieve into a bowl.');
insert into feedme.recipe_steps values('raspberry-white-chocolate-and-pistachio-profiterol',3,3, 'Whip the cream until stiff, then fold in 4 tbsp of the raspberry sauce. Spoon the filling into a piping bag fitted with a nozzle a little smaller than the holes you made in the profiteroles. Or use a plastic food bag and snip off the tip of a corner. Poke into each hole in turn, and pipe in the filling until full. The profiteroles should be fully cool before you do this.');
insert into feedme.recipe_steps values('raspberry-white-chocolate-and-pistachio-profiterol',4,4, 'Break up the chocolate into a small bowl, then either melt in the microwave on Low for 1½-2 mins, stirring halfway through, or over a pan of simmering water. Leave to cool briefly until slightly thickened.');
insert into feedme.recipe_steps values('raspberry-white-chocolate-and-pistachio-profiterol',5,5, 'Carefully spoon the melted chocolate over each filled profiterole and sprinkle with pistachios. Leave to set. To serve, drizzle the remaining raspberry sauce over 6 serving plates and top each with 4 profiteroles, or simply serve the sauce in a bowl alongside for drizzling over.');
insert into feedme.recipes VALUES ('nofail-yorkies', 'No-fail Yorkies', 'Perfect for mopping up gravy, these soft but crisp Yorkshire puddings will rise every time thanks to a great batter', 'british');
insert into feedme.recipe_ingredients values('nofail-yorkies','milk','450ml milk',450.0,'ml');
insert into feedme.recipe_ingredients values('nofail-yorkies','egg','4 large eggs',4.0,'unit');
insert into feedme.recipe_ingredients values('nofail-yorkies','flour','250g plain flour',250.0,'gram');
insert into feedme.recipe_ingredients values('nofail-yorkies','vegetable oil','2 tbsp sunflower or vegetable oil',2.0,'tbsp');
insert into feedme.recipe_steps values('nofail-yorkies',0,0, 'Up to 3-4 hrs before cooking, mix together the milk, eggs, flour and 2 tsp salt in a food processor or blender until you have a smooth batter. If you don’t have a food processor or blender, whisk the eggs into the flour and salt, then gradually whisk in the milk until smooth. Transfer to a jug, cover and leave at room temperature for at least 15 mins.');
insert into feedme.recipe_steps values('nofail-yorkies',1,1, 'Heat oven to 220C/200C fan/gas 7 and brush 12 holes of a muffin tin with the oil. Heat the tin in the oven for 5 mins, then carefully lift out and quickly pour the batter into the holes. Bake in the oven for 5 mins, then reduce oven temperature to 200C/180C fan/ gas 6 and cook for a further 30 mins until puffed, risen and golden.');
insert into feedme.recipes VALUES ('creamy-veggie-korma', 'Creamy veggie korma', 'Creamy and healthy - a winning combination for this curry, which can be easily adapted to suit all the family if some want to add meat', 'indian');
insert into feedme.recipe_ingredients values('creamy-veggie-korma','vegetable oil','1 tbsp vegetable oil',1.0,'tbsp');
insert into feedme.recipe_ingredients values('creamy-veggie-korma','onion','1 onion, finely chopped',1.0,'unit');
insert into feedme.recipe_ingredients values('creamy-veggie-korma','cardamom','3 cardamom pods, bashed',3.0,'unit');
insert into feedme.recipe_ingredients values('creamy-veggie-korma','coriander','2 tsp each ground cuminand coriander',2.0,'tsp');
insert into feedme.recipe_ingredients values('creamy-veggie-korma','turmeric','0.5 tsp ground turmeric',5.0,'tsp');
insert into feedme.recipe_ingredients values('creamy-veggie-korma','chilli','1 green chilli, deseeded (if desired) and finely chopped',1.0,'unit');
insert into feedme.recipe_ingredients values('creamy-veggie-korma','garlic','1 garlic clove, crushed',1.0,'unit');
insert into feedme.recipe_ingredients values('creamy-veggie-korma','ginger','thumb-size piece ginger, finely chopped',NULL,'null');
insert into feedme.recipe_ingredients values('creamy-veggie-korma','potato','800g mixed vegetable, such as carrots, cauliflower, potato and courgette, chopped',800.0,'gram');
insert into feedme.recipe_ingredients values('creamy-veggie-korma','stock','300-500ml hot vegetable stock',500.0,'ml');
insert into feedme.recipe_ingredients values('creamy-veggie-korma','peas','200g frozen peas',200.0,'gram');
insert into feedme.recipe_ingredients values('creamy-veggie-korma','yoghurt','200ml yogurt',200.0,'ml');
insert into feedme.recipe_ingredients values('creamy-veggie-korma','almond','2 tbsp ground almonds(optional)',2.0,'tbsp');
insert into feedme.recipe_steps values('creamy-veggie-korma',0,0, 'Heat the oil in a large pan. Cook onion with the dry spices over a low heat for 5-6 mins until the onion is light golden. Add the chilli, garlic and ginger and cook for 1 min, then throw in the mixed vegetables and cook for a further 5 mins.');
insert into feedme.recipe_steps values('creamy-veggie-korma',1,1, 'Divide the mixture appropriately between two pans if serving vegetarians and meat eaters. Chop the chicken into small chunks and stir into one pan. Add the stock, dividing between the pans appropriately, and simmer for 10 mins (if only cooking the veggie version in one pan, use 300ml stock; if dividing between two pans, add 250ml to each). Divide the peas, if necessary, and add, cooking for 3 mins more until the veg are tender and the chicken is cooked through.');
insert into feedme.recipe_steps values('creamy-veggie-korma',2,2, 'Remove from the heat and stir through the yogurt and ground almonds, if using. Serve sprinkled with the toasted almonds and coriander, with basmati rice or naan bread on the side.');
insert into feedme.recipes VALUES ('teriyaki-tuna-skewers', 'Teriyaki tuna skewers', 'Colourful and quick, these simple skewers taste as good as they look!', 'japanese');
insert into feedme.recipe_ingredients values('teriyaki-tuna-skewers','soy sauce','30ml soy sauce',30.0,'ml');
insert into feedme.recipe_ingredients values('teriyaki-tuna-skewers','mirin','50ml mirin (See know-how below)',50.0,'ml');
insert into feedme.recipe_ingredients values('teriyaki-tuna-skewers','vegetable oil','1 tsp vegetable oil',1.0,'tsp');
insert into feedme.recipe_ingredients values('teriyaki-tuna-skewers','mango','1 mango, peeled',1.0,'unit');
insert into feedme.recipe_ingredients values('teriyaki-tuna-skewers','spring onion','3 spring onions',3.0,'unit');
insert into feedme.recipe_ingredients values('teriyaki-tuna-skewers','steak','250g pack tuna steak',250.0,'gram');
insert into feedme.recipe_steps values('teriyaki-tuna-skewers',0,0, 'Make a teriyaki sauce by mixing the soy sauce, mirin and sugar in a shallow dish. Put the tuna steaks in the dish, turn over a few times so they’re coated with the sauce, then leave in the fridge to marinate for at least 1 hr and up to 2 hrs.');
insert into feedme.recipe_steps values('teriyaki-tuna-skewers',1,1, 'To cook, heat a large frying pan. Take the tuna steaks out of the marinade and pat dry, then rub all over with the oil. Fry the tuna for 2 mins on each side, so they are still pink in the middle. Remove from the pan, then pour in the marinade and 2-3 tbsp water. Bring to the boil and cook for a few mins to heat through. Strain through a sieve into a small dipping bowl.');
insert into feedme.recipe_steps values('teriyaki-tuna-skewers',2,2, 'To serve, slice the cooked tuna, mango and spring onion into bite-size pieces. Thread onto wooden skewers, then serve warm or cold with the dipping sauce.');
insert into feedme.recipes VALUES ('healthier-chicken-balti', 'Healthier chicken balti', 'A lighter version of the Indian takeaway classic from Angela Nilsen, this tomato-based curry is packed with extra spinach and peppers', 'indian');
insert into feedme.recipe_ingredients values('healthier-chicken-balti','chicken','450g skinless, boneless chicken breast, cut into bite-sized pieces',450.0,'gram');
insert into feedme.recipe_ingredients values('healthier-chicken-balti','lime juice','1 tbsp lime juice',1.0,'tbsp');
insert into feedme.recipe_ingredients values('healthier-chicken-balti','paprika','1 tsp paprika',1.0,'tsp');
insert into feedme.recipe_ingredients values('healthier-chicken-balti','chillipowder','0.25 tsp hot chilli powder',25.0,'tsp');
insert into feedme.recipe_ingredients values('healthier-chicken-balti','nut','10.5 tbsp sunflower or groundnut oil',5.0,'tbsp');
insert into feedme.recipe_ingredients values('healthier-chicken-balti','cinnamon','1 cinnamon stick',1.0,'unit');
insert into feedme.recipe_ingredients values('healthier-chicken-balti','cardamom','3 cardamom pods, split',3.0,'unit');
insert into feedme.recipe_ingredients values('healthier-chicken-balti','chilli','1 small to medium green chilli',1.0,'unit');
insert into feedme.recipe_ingredients values('healthier-chicken-balti','cuminseed','0.5 tsp cumin seed',5.0,'tsp');
insert into feedme.recipe_ingredients values('healthier-chicken-balti','onion','1 medium onion, coarsely grated',1.0,'unit');
insert into feedme.recipe_ingredients values('healthier-chicken-balti','garlic','2 garlic cloves, very finely chopped',2.0,'unit');
insert into feedme.recipe_ingredients values('healthier-chicken-balti','ginger','20.5 cm-piece ginger, grated',20.0,'unit');
insert into feedme.recipe_ingredients values('healthier-chicken-balti','turmeric','0.5 tsp turmeric',5.0,'tsp');
insert into feedme.recipe_ingredients values('healthier-chicken-balti','cumin','1 tsp ground cumin',1.0,'tsp');
insert into feedme.recipe_ingredients values('healthier-chicken-balti','coriander','1 tsp ground coriander',1.0,'tsp');
insert into feedme.recipe_ingredients values('healthier-chicken-balti','garam masala','1 tsp garam masala',1.0,'tsp');
insert into feedme.recipe_ingredients values('healthier-chicken-balti','passata','250ml organic passata',250.0,'ml');
insert into feedme.recipe_ingredients values('healthier-chicken-balti','pepper','1 red pepper, deseeded, cut into small chunks',1.0,'unit');
insert into feedme.recipe_ingredients values('healthier-chicken-balti','tomato','1 medium tomato, chopped',1.0,'unit');
insert into feedme.recipe_ingredients values('healthier-chicken-balti','spinach','85g baby spinachleaves',85.0,'gram');
insert into feedme.recipe_ingredients values('healthier-chicken-balti','fresh coriander','handful fresh coriander, chopped',NULL,'null');
insert into feedme.recipe_ingredients values('healthier-chicken-balti','rice','chapatis or basmati rice, to serve (optional)',NULL,'null');
insert into feedme.recipe_steps values('healthier-chicken-balti',0,0, 'Put the chicken in a medium bowl. Mix in the lime juice, paprika, chilli powder and a grinding of black pepper (step 1), then leave to marinate for at least 15 mins, preferably a bit longer.');
insert into feedme.recipe_steps values('healthier-chicken-balti',1,1, 'Heat 1 tbsp of the oil in a large non-stick wok or sauté pan. Tip in the cinnamon stick, cardamom pods, whole chilli and cumin seeds, and stir-fry briefly just to colour and release their fragrance (step 2). Stir in the onion, garlic and ginger and fry over a medium-high heat for 3-4 mins until the onion starts to turn brown. Add the remaining oil, then drop in the chicken and stir-fry for 2-3 mins or until it no longer looks raw. Mix the turmeric, cumin, ground coriander and garam masala together. Tip into the pan, lower the heat to medium and cook for 2 mins (step 3). Pour in the passata and 150ml water, then drop in the chunks of pepper. When starting to bubble, lower the heat and simmer for 15-20 mins or until the chicken is tender.');
insert into feedme.recipe_steps values('healthier-chicken-balti',2,2, 'Stir in the tomato, simmer for 2-3 mins, then add the spinach and turn it over in the pan to just wilt. Season with a little salt. If you want to thin down the sauce, splash in a little more water. Remove the cinnamon stick, chilli and cardamom pods, if you wish, before serving. Scatter with fresh coriander and serve with warm chapatis or basmati rice, if you like.');
insert into feedme.recipes VALUES ('spanish-chicken', 'Spanish chicken', 'For maximum flavour and minimum washing up, try this tasty one-pot', 'spanish');
insert into feedme.recipe_ingredients values('spanish-chicken','chicken','8 chicken thighs, skin on and bone in',8.0,'unit');
insert into feedme.recipe_ingredients values('spanish-chicken','onion','3 onions, thinly sliced',3.0,'unit');
insert into feedme.recipe_ingredients values('spanish-chicken','paprika','2 tsp paprika',2.0,'tsp');
insert into feedme.recipe_ingredients values('spanish-chicken','lemon','zest and juice 1 lemon',NULL,'null');
insert into feedme.recipe_ingredients values('spanish-chicken','parsley','good handful parsley, roughly chopped',NULL,'null');
insert into feedme.recipe_ingredients values('spanish-chicken','stock','150ml stock',150.0,'ml');
insert into feedme.recipe_ingredients values('spanish-chicken','olive oil','1 tbsp olive oil',1.0,'tbsp');
insert into feedme.recipe_steps values('spanish-chicken',0,0, 'Heat oven to 190C/170C fan/gas 5. Put all the ingredients into a large, wide ovenproof dish. Mix everything together with your hands and season.');
insert into feedme.recipe_steps values('spanish-chicken',1,1, 'Bake for 45 mins, stirring the onions after 20 mins, until the chicken is golden and the onions tender. Serve with rice.');
insert into feedme.recipes VALUES ('miso-brown-rice-and-chicken-salad', 'Miso brown rice & chicken salad', 'Low in fat and a great source of iron, this Japanese-inspired meal gets the ''superhealthy'' tag', 'japanese');
insert into feedme.recipe_ingredients values('miso-brown-rice-and-chicken-salad','rice','120g brown basmati rice',120.0,'gram');
insert into feedme.recipe_ingredients values('miso-brown-rice-and-chicken-salad','chicken','2 skinless chicken breasts',2.0,'unit');
insert into feedme.recipe_ingredients values('miso-brown-rice-and-chicken-salad','broccoli','140g sprouting broccoli',140.0,'gram');
insert into feedme.recipe_ingredients values('miso-brown-rice-and-chicken-salad','spring onion','4 spring onions, cut into diagonal slices',4.0,'unit');
insert into feedme.recipe_ingredients values('miso-brown-rice-and-chicken-salad','sesame seeds','1 tbsp toasted sesame seeds',1.0,'tbsp');
insert into feedme.recipe_steps values('miso-brown-rice-and-chicken-salad',0,0, 'Cook the rice following the pack instructions, then drain and keep warm. While it’s cooking, place the chicken breasts into a pan of boiling water so they are completely covered. Boil for 1 min, then turn off the heat, place a lid on and let sit for 15 mins. When cooked through, cut into slices.');
insert into feedme.recipe_steps values('miso-brown-rice-and-chicken-salad',1,1, 'Boil the broccoli until tender. Drain, rinse under cold water and drain again.');
insert into feedme.recipe_steps values('miso-brown-rice-and-chicken-salad',2,2, 'For the dressing, mix the miso, rice vinegar, mirin and ginger together.');
insert into feedme.recipe_steps values('miso-brown-rice-and-chicken-salad',3,3, 'Divide the rice between two plates and scatter over the spring onions and sesame seeds. Place the broccoli and chicken slices on top. To finish, drizzle over the dressing.');
insert into feedme.recipes VALUES ('emilys-platesized-pancakes', 'Emily’s plate-sized pancakes', 'Fluffy American-style pancakes are a blank canvas for toppings like blueberries and bacon - great for breakfast in bed. Why not get the kids to help?', 'american');
insert into feedme.recipe_ingredients values('emilys-platesized-pancakes','pancetta','8 slices pancetta, to serve (optional)',8.0,'unit');
insert into feedme.recipe_ingredients values('emilys-platesized-pancakes','butter','sunflower oiland butter, for cooking',NULL,'null');
insert into feedme.recipe_ingredients values('emilys-platesized-pancakes','berry','blueberries, to serve (optional)',NULL,'null');
insert into feedme.recipe_steps values('emilys-platesized-pancakes',0,0, 'If serving with pancetta, heat oven to 200C/180C fan/gas 6. Line a baking tray with baking parchment and lay on the pancetta in a single layer. Put another piece of parchment on top, followed by a second baking tray, and bake for 12-15 mins until crisp.');
insert into feedme.recipe_steps values('emilys-platesized-pancakes',1,1, 'To make the pancakes, get a little helper to weigh out and tip the flour, baking powder and sugar into a large bowl with a small pinch of salt. Crack in the eggs and whisk until smooth. Add the maple syrup and milk while whisking.');
insert into feedme.recipe_steps values('emilys-platesized-pancakes',2,2, 'Heat a splash of oil and a small knob of butter in a non-stick frying pan until sizzling. Add spoonfuls of batter to make pancakes the size you like. Cook until bubbles start to form on the surface, then flip and cook the other side. Eat straight away or keep warm in a low oven while you cook another batch. Serve pancakes with pancetta or blueberries, drizzled with extra maple syrup.');
insert into feedme.recipes VALUES ('aubergine-and-houmous-grills', 'Aubergine & hummus grills', 'Try something new with aubergines and make them the main part of your meal with this veggie dish', 'turkish');
insert into feedme.recipe_ingredients values('aubergine-and-houmous-grills','aubergine','2 aubergines, thickly sliced lengthways',2.0,'unit');
insert into feedme.recipe_ingredients values('aubergine-and-houmous-grills','vegetable oil','2 tbsp vegetable oil, plus extra for brushing',2.0,'tbsp');
insert into feedme.recipe_ingredients values('aubergine-and-houmous-grills','bread','3 slices brown or white bread, crusts removed',3.0,'unit');
insert into feedme.recipe_ingredients values('aubergine-and-houmous-grills','hummus','300g tub hummus',300.0,'gram');
insert into feedme.recipe_ingredients values('aubergine-and-houmous-grills','nut','100g walnuts, toasted',100.0,'gram');
insert into feedme.recipe_ingredients values('aubergine-and-houmous-grills','parsley','half an 80g pack flat-leaf parsley, leaves roughly chopped',80.0,'gram');
insert into feedme.recipe_ingredients values('aubergine-and-houmous-grills','cherry','200g pack cherry tomatoes, quartered',200.0,'gram');
insert into feedme.recipe_ingredients values('aubergine-and-houmous-grills','lemon','juice 0.5 lemon',NULL,'null');
insert into feedme.recipe_ingredients values('aubergine-and-houmous-grills','olive oil','splash extra-virgin olive oil',NULL,'null');
insert into feedme.recipe_steps values('aubergine-and-houmous-grills',0,0, 'Lay the aubergine out in one layer on a large baking sheet. Brush sparingly with vegetable oil, then season generously. Grill for 15 mins, turning twice and brushing with oil until the slices are softened and cooked through. Meanwhile, whizz the bread into crumbs. Add 2 tsp oil and whizz briefly again, to coat.');
insert into feedme.recipe_steps values('aubergine-and-houmous-grills',1,1, 'Spread a couple of tsps of hummus on top of each slice of aubergine. Tip the breadcrumbs onto a large plate, then press the hummus side of the aubergines into the crumbs to coat. Grill again, crumb-side up, for about 3 mins until golden.');
insert into feedme.recipe_steps values('aubergine-and-houmous-grills',2,2, 'Toss the walnuts, parsley and cherry tomatoes in a bowl, season, then add the lemon juice and olive oil and toss again. Serve the grills with the salad, a dollop more hummus and some pitta bread.');
insert into feedme.recipes VALUES ('chicken-biryani-bake', 'Chicken biryani bake', 'Keep this biryani bake in the freezer for an easy midweek health kick', 'indian');
insert into feedme.recipe_ingredients values('chicken-biryani-bake','olive oil','1 tbsp olive oil',1.0,'tbsp');
insert into feedme.recipe_ingredients values('chicken-biryani-bake','chicken','4 skinless chicken breasts, chopped into chunks',4.0,'unit');
insert into feedme.recipe_ingredients values('chicken-biryani-bake','chicken','4 skinless, boneless chicken thighs, chopped into chunks',4.0,'unit');
insert into feedme.recipe_ingredients values('chicken-biryani-bake','onion','2 onions, sliced',2.0,'unit');
insert into feedme.recipe_ingredients values('chicken-biryani-bake','curry paste','4 tbsp curry paste(we used korma and tikka masala)',4.0,'tbsp');
insert into feedme.recipe_ingredients values('chicken-biryani-bake','cauliflower','300g cauliflower, chopped into small florets',300.0,'gram');
insert into feedme.recipe_ingredients values('chicken-biryani-bake','stock','700ml chicken stock',700.0,'ml');
insert into feedme.recipe_ingredients values('chicken-biryani-bake','tomato','400g can chopped tomato',400.0,'gram');
insert into feedme.recipe_ingredients values('chicken-biryani-bake','chickpea','400g can chickpea, rinsed and drained',400.0,'gram');
insert into feedme.recipe_ingredients values('chicken-biryani-bake','yoghurt','200g natural yogurt',200.0,'gram');
insert into feedme.recipe_ingredients values('chicken-biryani-bake','spinach','300g spinach',300.0,'gram');
insert into feedme.recipe_ingredients values('chicken-biryani-bake','rice','400g basmati rice, cooked following pack instructions',400.0,'gram');
insert into feedme.recipe_ingredients values('chicken-biryani-bake','almond','5 tbsp flaked almond',5.0,'tbsp');
insert into feedme.recipe_steps values('chicken-biryani-bake',0,0, 'Start by making the curry. Heat 1 tbsp of the oil in a large, deep frying pan. Season the chicken and fry until browned, then remove and set aside. Fry the onion in the rest of the oil for 10-12 mins until soft and starting to caramelise.');
insert into feedme.recipe_steps values('chicken-biryani-bake',1,1, 'Add the paste and cauliflower, stirring to coat everything, then return the chicken. Pour in the stock, tomatoes and chickpeas and simmer everything for 30 mins until the cauliflower is nearly tender. There should be just enough liquid to cover everything so add a splash more water or stock if you need to. Remove from the heat and stir in the yogurt.');
insert into feedme.recipe_steps values('chicken-biryani-bake',2,2, 'Assemble the bake in either 1 large or 2 smaller, deep ovenproof dishes. Start with a ¹/³ of the spinach leaves, season, then top with a ¹/³ of the curry. Finish with a ¹/³ of the rice then repeat twice more. Scatter on the almonds and either cool completely to freeze, or heat oven to 220C/200C fan/gas 7 and cook for 20-25 mins until the topping has crisped up and the dish is piping hot through.');
insert into feedme.recipes VALUES ('thai-green-chicken-curry', 'Thai green chicken curry', 'Making your own fragrant curry paste takes some special Asian ingredients and a little time but you''ll taste the difference', 'thai');
insert into feedme.recipe_steps values('thai-green-chicken-curry',0,0, 'To make the Thai green curry paste, put the lemon grass and lime juice into a small bowl and leave to soak for 30 mins. Put a small pan over a medium heat and add the peppercorns, coriander and cumin seeds. Dry fry for about 1 minute until fragrant then tip onto a saucer to cool.');
insert into feedme.recipe_steps values('thai-green-chicken-curry',1,1, 'Put the lemongrass, spices and all remaining curry paste ingredients into a mini food blender or small food processor and pulse the mixture. Scrape down the sides from time to time. Process as smooth as you can – the mixture will be slightly coarse and not as green as the purchased variety. Put the paste into a lidded jar and store in the fridge up to a week or freeze in portions.');
insert into feedme.recipe_steps values('thai-green-chicken-curry',2,2, 'To make the curry, open the cans of coconut milk and carefully remove the thick cream and spoon it into a warm wok or large frying pan (see tip below). Cook it slowly, stirring all the time, until it reduces to a thick paste and the oil starts to separate. Add the green curry paste and cook for 1-2 mins. Add the chicken and stir until well coated in the paste.');
insert into feedme.recipe_steps values('thai-green-chicken-curry',3,3, 'Slowly add the coconut water remaining in the cans together with the chicken stock. Add all the remaining ingredients and cook over a medium heat for about 10 mins until the chicken is cooked. Add more stock if necessary - the sauce should be slightly thinner than single cream.');
insert into feedme.recipe_steps values('thai-green-chicken-curry',4,4, 'To serve spoon some rice into a bowl, pour over the curry and sprinkle with the coriander leaves, sliced chilli and spring onion. Add a wedge of lime on the side.');
insert into feedme.recipes VALUES ('white-chocolate-crme-brle', 'White chocolate crème brûlée', 'Serve this super-creamy chocolate dessert on its own, or as part of an ''assiete'' of mini chocolate puddings', 'french');
insert into feedme.recipe_ingredients values('white-chocolate-crme-brle','double cream','568ml pot double cream',568.0,'ml');
insert into feedme.recipe_ingredients values('white-chocolate-crme-brle','chocolate','100g Green & Black''s white chocolate, broken into pieces',100.0,'gram');
insert into feedme.recipe_ingredients values('white-chocolate-crme-brle','vanilla extract','1 vanilla pod, split or 1 tsp vanilla extract',1.0,'tsp');
insert into feedme.recipe_ingredients values('white-chocolate-crme-brle','egg','6 egg yolks',6.0,'unit');
insert into feedme.recipe_ingredients values('white-chocolate-crme-brle','caster sugar','2 tbsp golden caster sugar, plus extra for topping',2.0,'tbsp');
insert into feedme.recipe_steps values('white-chocolate-crme-brle',0,0, 'Heat the cream, chocolate and vanilla pod in a pan until the chocolate has melted. Take off the heat and allow to infuse for 10 mins, scraping the pod seeds into the cream. If using the vanilla extract, add straight away. Heat oven to 160C/fan 140C/gas 3.');
insert into feedme.recipe_steps values('white-chocolate-crme-brle',1,1, 'Beat yolks and sugar until pale. stir in the chocolate cream. Strain into a jug and pour into ramekins. Place in a deep roasting tray and pour boiling water halfway up the sides. Bake for 15-20 mins until just set with a wobbly centre. Chill in the fridge for at least 4 hrs.');
insert into feedme.recipe_steps values('white-chocolate-crme-brle',2,2, 'To serve, sprinkle some sugar on top of the brûlées and caramelise with a blowtorch or briefly under a hot grill. Leave caramel to harden, then serve.');
insert into feedme.recipes VALUES ('mustmake-moussaka', 'Must-make moussaka', 'An easy version of the classic Greek dish moussaka, cheesy and comforting...', 'greek');
insert into feedme.recipe_ingredients values('mustmake-moussaka','beef','500g pack lean minced beef',500.0,'gram');
insert into feedme.recipe_ingredients values('mustmake-moussaka','aubergine','1 large aubergine',1.0,'unit');
insert into feedme.recipe_ingredients values('mustmake-moussaka','yoghurt','150g pot 0% Greek yogurt',150.0,'gram');
insert into feedme.recipe_ingredients values('mustmake-moussaka','egg','1 egg, beaten',1.0,'unit');
insert into feedme.recipe_ingredients values('mustmake-moussaka','parmesan','3 tbsp parmesan, finely grated',3.0,'tbsp');
insert into feedme.recipe_ingredients values('mustmake-moussaka','garlic','400g can chopped tomato with garlic and herbs',400.0,'gram');
insert into feedme.recipe_ingredients values('mustmake-moussaka','tomato','4 tbsp sun-dried tomato purée',4.0,'tbsp');
insert into feedme.recipe_ingredients values('mustmake-moussaka','potato','400g leftover boiled potato (or 350g/12oz uncooked weight boiled)',400.0,'gram');
insert into feedme.recipe_steps values('mustmake-moussaka',0,0, 'Heat the grill to high. Brown the beef in a deep ovenproof frying pan over a high heat for 5 mins. Meanwhile, prick the aubergine with a fork, then microwave on High for 3-5 mins until soft. Mix the yogurt, egg and parmesan together, then add a little seasoning.');
insert into feedme.recipe_steps values('mustmake-moussaka',1,1, 'Stir the tomatoes, purée and potatoes in with the beef with some seasoning and heat through. Smooth the surface of the beef mixture with the back of a spoon, then slice the cooked aubergine and arrange on top. Pour the yogurt mixture over the aubergines, smooth out evenly, then grill until the topping has set and turned golden.');
insert into feedme.recipes VALUES ('barbecue-pork-buns', 'Barbecue pork buns', 'Tastier than a take away and great for nibbling and sharing. These dim sum will impress your guests.', 'vietnamese');
insert into feedme.recipe_ingredients values('barbecue-pork-buns','sugar','85g sugar',85.0,'gram');
insert into feedme.recipe_ingredients values('barbecue-pork-buns','bread','500g pack white bread mix',500.0,'gram');
insert into feedme.recipe_steps values('barbecue-pork-buns',0,0, 'Heat the oven to 200C/fan 180C/gas. Mix the sugar into the bread mix in a large bowl, then add water as instructed on the pack. Bring the dough together with a wooden spoon, then knead on a lightly floured surface for 5 mins until smooth. Put into a large bowl, cover with oiled cling film then leave in a warm place until doubled in size.');
insert into feedme.recipe_steps values('barbecue-pork-buns',1,1, 'Meanwhile, heat the oil in a pan, then fry the bacon until crisp, about 5 mins. Add the ginger and garlic and fry for 1 min until soft, then tip in the soy, honey and tomato purée and stir well. Can be made up to 3 days ahead.');
insert into feedme.recipe_steps values('barbecue-pork-buns',2,2, 'Turn out the dough and knead briefly, then pull into 12 even-sized balls. Flatten with your hands, then put a teaspoon-size blob of the filling in the middle. Draw the dough up and pinch it closed like a purse, then turn the bun over and sit it on a large baking sheet. Cover with oiled cling film and leave to rise for about 30 mins until the dough feels pillowy. Brush with egg and bake for 20 mins until golden. Serve warm with dipping sauce. Can be frozen after second rise for up to 1 month or baked up to a day ahead and re-warmed.');
insert into feedme.recipes VALUES ('cardamom-butter-chicken', 'Cardamom butter chicken', 'A homemade curry to rival your favourite Indian takeaway - a rich blend of spices mixed with creamy ghee', 'indian');
insert into feedme.recipe_ingredients values('cardamom-butter-chicken','garlic','2 garlic cloves, roughly chopped',2.0,'unit');
insert into feedme.recipe_ingredients values('cardamom-butter-chicken','ginger','thumb-sized piece ginger, chopped',NULL,'null');
insert into feedme.recipe_ingredients values('cardamom-butter-chicken','seeds','2 green chillies, chopped (leave seeds in if you like it hot)',2.0,'unit');
insert into feedme.recipe_ingredients values('cardamom-butter-chicken','coriander','small bunch coriander, leaves picked, stalks roughly chopped',NULL,'null');
insert into feedme.recipe_ingredients values('cardamom-butter-chicken','vegetable oil','3 tbsp ghee, or vegetable oil',3.0,'tbsp');
insert into feedme.recipe_ingredients values('cardamom-butter-chicken','onion','2 onions, sliced',2.0,'unit');
insert into feedme.recipe_ingredients values('cardamom-butter-chicken','cumin','2 tsp each turmeric, garam masala and ground cumin',2.0,'tsp');
insert into feedme.recipe_ingredients values('cardamom-butter-chicken','fenugreek','1 tsp ground fenugreek',1.0,'tsp');
insert into feedme.recipe_ingredients values('cardamom-butter-chicken','chicken','4 chicken breasts, cut into 20.5 cm cubes',4.0,'unit');
insert into feedme.recipe_ingredients values('cardamom-butter-chicken','cloves','4 cloves',4.0,'unit');
insert into feedme.recipe_ingredients values('cardamom-butter-chicken','cardamom','12 cardamom pods, seeds removed',12.0,'unit');
insert into feedme.recipe_ingredients values('cardamom-butter-chicken','cinnamon','1 cinnamon stick',1.0,'unit');
insert into feedme.recipe_ingredients values('cardamom-butter-chicken','tomato','400g can tomato',400.0,'gram');
insert into feedme.recipe_ingredients values('cardamom-butter-chicken','yoghurt','150ml pot yogurt',150.0,'ml');
insert into feedme.recipe_ingredients values('cardamom-butter-chicken','double cream','50ml double cream',50.0,'ml');
insert into feedme.recipe_ingredients values('cardamom-butter-chicken','rice','chapatis and rice, to serve',NULL,'null');
insert into feedme.recipe_steps values('cardamom-butter-chicken',0,0, 'Place the garlic, ginger, chillies and coriander stalks in the small bowl of a food processor with a good pinch of salt, and blend to a fine paste – or grind to a paste using a pestle and mortar.');
insert into feedme.recipe_steps values('cardamom-butter-chicken',1,1, 'Heat 2 tbsp of the ghee or oil in a large pan, add the onions and cook slowly for 15-20 mins until golden and caramelised. Tip in the garlic-ginger paste, turn up the heat and cook for a further 5 mins. Add the ground spices and cook for 2 mins more until fragrant. Scoop the spicy onion mixture out of the pan into a bowl.');
insert into feedme.recipe_steps values('cardamom-butter-chicken',2,2, 'Add the remaining ghee or oil to the pan and cook the chicken over a medium-high heat until browned all over. Tip into a bowl, cover and set aside.');
insert into feedme.recipe_steps values('cardamom-butter-chicken',3,3, 'Return the onion mixture to the pan along with the whole spices, tomatoes and a can full of water (400ml). Bring to the boil, then cover and simmer gently for 40 mins.');
insert into feedme.recipe_steps values('cardamom-butter-chicken',4,4, 'Return the chicken to the pan and cook, uncovered, for a further 10 mins until the sauce has thickened and the chicken is cooked through. Stir in the yogurt and cream, season well and scatter with coriander leaves. Serve with chapatis and rice.');
insert into feedme.recipes VALUES ('turkish-delight-vodka', 'Turkish delight vodka', 'Infused spirits make great homemade gifts, or store yours in the cocktail cabinet for when guests come calling', 'turkish');
insert into feedme.recipe_ingredients values('turkish-delight-vodka','vodka','1l bottle vodka',1.0,'litre');
insert into feedme.recipe_ingredients values('turkish-delight-vodka','rosewater','1 tsp rosewater',1.0,'tsp');
insert into feedme.recipe_ingredients values('turkish-delight-vodka','caster sugar','1 tsp caster sugar',1.0,'tsp');
insert into feedme.recipe_ingredients values('turkish-delight-vodka','turkish delight','100g Turkish delight, chopped, plus a little extra for packaging',100.0,'gram');
insert into feedme.recipe_steps values('turkish-delight-vodka',0,0, 'Pour 100ml out of the vodka bottle – you can keep this in the freezer for Christmas cocktails. Mix the rose water and caster sugar together until the sugar dissolves, then pour into the vodka bottle using a funnel.');
insert into feedme.recipe_steps values('turkish-delight-vodka',1,1, 'Wash any sugar off the Turkish delight, then put it in the bottle. Screw on the lid and leave to stand for up to a month, swirling occasionally until the Turkish delight dissolves. Serve with soda water and ice, or drizzle neat over vanilla ice cream for a boozy treat.');
insert into feedme.recipes VALUES ('crisp-chinese-pork', 'Crisp Chinese pork', 'Belly is the cheapest pork roasting joint and, as it’s rich, a little goes a long way, making it perfect for Sundays and dinner parties', 'chinese');
insert into feedme.recipe_ingredients values('crisp-chinese-pork','pork','1.3kg piece boned pork belly, skin on and scored, ask the butcher for the thin end',1.0,'unit');
insert into feedme.recipe_ingredients values('crisp-chinese-pork','five-spice powder','2 tsp Chinese five-spice powder',2.0,'tsp');
insert into feedme.recipe_steps values('crisp-chinese-pork',0,0, 'Rub the pork with the five-spice and 2 tsp sea salt then leave, uncovered, in the fridge for at least 2 hrs, but preferably overnight. When ready to cook, heat oven to its maximum setting. Lay the pork on a rack over a roasting tin, making sure the skin is exposed. Roast for 10 mins before turning down the heat to 180C/fan 160C/gas 4, then leave to cook for a further 1½ hrs. Have a look at the pork – if the skin isn’t crisp, turn up the heat to 220C/fan 200C/gas 7, then cook for another 30 mins until crisp. Leave to rest on a board for at least 10 mins.');
insert into feedme.recipe_steps values('crisp-chinese-pork',1,1, 'To make the dipping sauce, mix all the ingredients together with 2 tbsp water. Cut the pork into small pieces, then serve with the sauce, plus boiled rice and steamed greens, if you like.');
insert into feedme.recipes VALUES ('spaghetti-with-spanish-flavours', 'Spaghetti with Spanish flavours', 'A 10 minute dish which infuses spaghetti with the flavours of Spain - you can make a vegetarian version too', 'spanish');
insert into feedme.recipe_ingredients values('spaghetti-with-spanish-flavours','chorizo','80g pack sliced chorizo sausage',80.0,'gram');
insert into feedme.recipe_ingredients values('spaghetti-with-spanish-flavours','spaghetti','300g bag fresh spaghetti',300.0,'gram');
insert into feedme.recipe_ingredients values('spaghetti-with-spanish-flavours','parsley','20g pack flatleaf parsley (a good handful)',20.0,'gram');
insert into feedme.recipe_ingredients values('spaghetti-with-spanish-flavours','pepper','2 red peppers from a jar, in brine or oil',2.0,'unit');
insert into feedme.recipe_ingredients values('spaghetti-with-spanish-flavours','olive oil','2 tbsp olive oil',2.0,'tbsp');
insert into feedme.recipe_ingredients values('spaghetti-with-spanish-flavours','parmesan','50g finely grated fresh parmesan, plus extra to serve',50.0,'gram');
insert into feedme.recipe_steps values('spaghetti-with-spanish-flavours',0,0, 'Put a pan of water on over a high heat to boil. Meanwhile, snip the chorizo into strips with scissors, and chop the parsley and peppers (check for stray seeds first).');
insert into feedme.recipe_steps values('spaghetti-with-spanish-flavours',1,1, 'When the water is boiling briskly, add the spaghetti with a good measure of salt, stir and return to the boil. Cook for 3 minutes.');
insert into feedme.recipe_steps values('spaghetti-with-spanish-flavours',2,2, 'In a large frying pan, heat the oil, add the chorizo and peppers and plenty of black pepper. Cook for a minute or so, until heated through and the juices are stained red from the paprika in the chorizo. Scoop half a mugful of pasta water from the pan, drain the remainder and tip the spaghetti into the frying pan.');
insert into feedme.recipe_steps values('spaghetti-with-spanish-flavours',3,3, 'Add the parsley and parmesan, toss well and splash in the pasta water, to moisten. Hand round extra parmesan at the table.');
insert into feedme.recipes VALUES ('festive-golden-fivespice-chicken', 'Festive golden five-spice chicken', 'Chinese five-spice gives this chicken a wonderfully warm, aromatic flavour that adults and children will love', 'chinese');
insert into feedme.recipe_ingredients values('festive-golden-fivespice-chicken','chicken','8 chicken thighsand 8 drumsticks, skin on',8.0,'unit');
insert into feedme.recipe_ingredients values('festive-golden-fivespice-chicken','spring onion','3 spring onions, shredded, to finish',3.0,'unit');
insert into feedme.recipe_steps values('festive-golden-fivespice-chicken',0,0, 'Whisk together the marinade ingredients, put the chicken into a dish, then pour over the marinade, mixing to coat. Cover, then chill for at least 20 mins, or up to a day if you have time.');
insert into feedme.recipe_steps values('festive-golden-fivespice-chicken',1,1, 'Heat oven to 180C/fan 160C/gas 4, and lift the chicken out of the marinade into a roasting tin. Roast for 40 mins, pour over the remaining marinade, then cook for a further 30 mins until golden and sticky. Sprinkle with the spring onion, then serve.');
insert into feedme.recipes VALUES ('rhubarb-pear-and-hazelnut-crumbles', 'Rhubarb, pear & hazelnut crumbles', 'Individual fruity puddings with a twist - try mixing chopped nuts with your crumble topping for extra crunch', 'british');
insert into feedme.recipe_ingredients values('rhubarb-pear-and-hazelnut-crumbles','butter','25g butter',25.0,'gram');
insert into feedme.recipe_ingredients values('rhubarb-pear-and-hazelnut-crumbles','pear','3 pears, cored and halved',3.0,'unit');
insert into feedme.recipe_ingredients values('rhubarb-pear-and-hazelnut-crumbles','rhubarb','500g rhubarb, cut into chunks',500.0,'gram');
insert into feedme.recipe_ingredients values('rhubarb-pear-and-hazelnut-crumbles','brown sugar','2 tbsp soft light brown sugar',2.0,'tbsp');
insert into feedme.recipe_ingredients values('rhubarb-pear-and-hazelnut-crumbles','cinnamon','0.5 tsp ground cinnamon',5.0,'tsp');
insert into feedme.recipe_ingredients values('rhubarb-pear-and-hazelnut-crumbles','cloves','0.25 tsp ground cloves',25.0,'tsp');
insert into feedme.recipe_ingredients values('rhubarb-pear-and-hazelnut-crumbles','double cream','vanilla ice cream or double cream, to serve',NULL,'null');
insert into feedme.recipe_steps values('rhubarb-pear-and-hazelnut-crumbles',0,0, 'Melt the butter in a pan, then add the pears, rhubarb, sugar, cinnamon and cloves, and cook over a low heat for 10-12 mins or until just tender. Divide the rhubarb between 4 ovenproof dishes (or use 1 large dish) and set aside.');
insert into feedme.recipe_steps values('rhubarb-pear-and-hazelnut-crumbles',1,1, 'Heat oven to 200C/180C fan/gas 6. To make the crumble topping, tip all the ingredients into a food processor and pulse to crumbs. Sprinkle the topping over the fruit filling, then bake for 30 mins or until golden brown on top. Serve with vanilla ice cream or double cream.');
insert into feedme.recipes VALUES ('tangy-couscous-salad', 'Tangy couscous salad', 'Throw together a deliciously tangy and superhealthy couscous salad in just 10 minutes', 'moroccan');
insert into feedme.recipe_ingredients values('tangy-couscous-salad','couscous','300g couscous or bulgur wheat',300.0,'gram');
insert into feedme.recipe_ingredients values('tangy-couscous-salad','stock','vegetable stock',NULL,'null');
insert into feedme.recipe_ingredients values('tangy-couscous-salad','courgette','2 courgettes',2.0,'unit');
insert into feedme.recipe_ingredients values('tangy-couscous-salad','olive oil','1 tbsp olive oil',1.0,'tbsp');
insert into feedme.recipe_ingredients values('tangy-couscous-salad','cheese','100g feta cheese, crumbled',100.0,'gram');
insert into feedme.recipe_ingredients values('tangy-couscous-salad','parsley','20g pack parsley, chopped',20.0,'gram');
insert into feedme.recipe_ingredients values('tangy-couscous-salad','lemon','juice 1 lemon',NULL,'null');
insert into feedme.recipe_steps values('tangy-couscous-salad',0,0, 'Cook the couscous or bulgur wheat in vegetable stock according to pack instructions. Trim the ends off the courgettes, then cut into slices.');
insert into feedme.recipe_steps values('tangy-couscous-salad',1,1, 'Heat a griddle pan or grill. Drizzle the courgette slices with oil, then season. Cook for 2 mins, then turn over and cook 1-2 mins more until softened and golden brown. Tip into a large bowl along with the cooked couscous or bulgur wheat, then mix through the remaining ingredients. Serve at room temperature.');
insert into feedme.recipes VALUES ('stuffed-tomatoes-with-lamb-mince-dill-and-rice', 'Stuffed tomatoes with lamb mince, dill & rice', 'These juicy tomatoes improve in flavour if cooked the day before you serve them. You can easily make them veggie too', 'greek');
insert into feedme.recipe_ingredients values('stuffed-tomatoes-with-lamb-mince-dill-and-rice','beef','4 beef tomatoes',4.0,'unit');
insert into feedme.recipe_ingredients values('stuffed-tomatoes-with-lamb-mince-dill-and-rice','sugar','pinch sugar',NULL,'null');
insert into feedme.recipe_ingredients values('stuffed-tomatoes-with-lamb-mince-dill-and-rice','olive oil','4 tbsp Greek extra virgin olive oil, plus extra to drizzle',4.0,'tbsp');
insert into feedme.recipe_ingredients values('stuffed-tomatoes-with-lamb-mince-dill-and-rice','onion','1 Spanish onion, finely chopped',1.0,'unit');
insert into feedme.recipe_ingredients values('stuffed-tomatoes-with-lamb-mince-dill-and-rice','garlic','2 garlic cloves, finely chopped',2.0,'unit');
insert into feedme.recipe_ingredients values('stuffed-tomatoes-with-lamb-mince-dill-and-rice','lamb','200g minced lamb',200.0,'gram');
insert into feedme.recipe_ingredients values('stuffed-tomatoes-with-lamb-mince-dill-and-rice','cinnamon','1 tsp ground cinnamon',1.0,'tsp');
insert into feedme.recipe_ingredients values('stuffed-tomatoes-with-lamb-mince-dill-and-rice','tomato','2 tbsp tomato purée',2.0,'tbsp');
insert into feedme.recipe_ingredients values('stuffed-tomatoes-with-lamb-mince-dill-and-rice','rice','50g long grain rice',50.0,'gram');
insert into feedme.recipe_ingredients values('stuffed-tomatoes-with-lamb-mince-dill-and-rice','stock','100ml chicken stock',100.0,'ml');
insert into feedme.recipe_ingredients values('stuffed-tomatoes-with-lamb-mince-dill-and-rice','dill','4 tbsp chopped dill',4.0,'tbsp');
insert into feedme.recipe_ingredients values('stuffed-tomatoes-with-lamb-mince-dill-and-rice','parsley','2 tbsp chopped flat-leaf parsley',2.0,'tbsp');
insert into feedme.recipe_ingredients values('stuffed-tomatoes-with-lamb-mince-dill-and-rice','mint','1 tbsp chopped mint',1.0,'tbsp');
insert into feedme.recipe_steps values('stuffed-tomatoes-with-lamb-mince-dill-and-rice',0,0, 'Heat oven to 180C/160C fan/gas 4. Slice the tops off the tomatoes and reserve. Scoop out most of the pulp with a teaspoon, being careful not to break the skin. Finely chop the pulp, and keep any juices. Sprinkle the insides of the tomatoes with a little sugar to take away the acidity, then place them on a baking tray.');
insert into feedme.recipe_steps values('stuffed-tomatoes-with-lamb-mince-dill-and-rice',1,1, 'Heat 2 tbsp olive oil in a large frying pan, add the onion and garlic, then gently cook for about 10 mins until soft but not coloured. Add the lamb, cinnamon and tomato purée, turn up the heat, then fry until the meat is browned. Add the tomato pulp and juice, the rice and the stock. Season generously. Bring to the boil, then simmer for 15 mins or until the rice is tender and the liquid has been absorbed. Set aside to cool a little, then stir in the herbs.');
insert into feedme.recipe_steps values('stuffed-tomatoes-with-lamb-mince-dill-and-rice',2,2, 'Stuff the tomatoes up to the brim, top tomatoes with their lids, drizzle with 2 tbsp more olive oil, sprinkle 3 tbsp water into the tray, then bake for 35 mins. Serve with salad and crusty bread, hot or cold.');
insert into feedme.recipes VALUES ('beef-wellington', 'Beef Wellington', 'Gordon Ramsay''s version of the classic steak dish - a show-stopping centrepiece on a special occasion', 'british');
insert into feedme.recipe_ingredients values('beef-wellington','beef','a good beef fillet(preferably Aberdeen Angus) of around 1kg/2lb 4oz',NULL,'null');
insert into feedme.recipe_ingredients values('beef-wellington','olive oil','3 tbsp olive oil',3.0,'tbsp');
insert into feedme.recipe_ingredients values('beef-wellington','nut','250g/ 9oz chestnut mushroom, include some wild ones if you like',250.0,'gram');
insert into feedme.recipe_ingredients values('beef-wellington','butter','50g/ 2oz butter',50.0,'gram');
insert into feedme.recipe_ingredients values('beef-wellington','thyme','1 large sprig fresh thyme',1.0,'unit');
insert into feedme.recipe_ingredients values('beef-wellington','wine','100ml/ 30.5 fl oz dry white wine',100.0,'ml');
insert into feedme.recipe_ingredients values('beef-wellington','prosciutto','12 slices prosciutto',12.0,'unit');
insert into feedme.recipe_ingredients values('beef-wellington','pastry','500g/1lb 2oz pack puff pastry, thawed if frozen',500.0,'gram');
insert into feedme.recipe_ingredients values('beef-wellington','flour','a little flour, for dusting',NULL,'null');
insert into feedme.recipe_ingredients values('beef-wellington','egg','2 egg yolks beaten with 1 tsp water',1.0,'tsp');
insert into feedme.recipe_steps values('beef-wellington',0,0, 'Heat oven to 220C/fan 200C/gas 7. Sit the 1kg beef fillet on a roasting tray, brush with 1 tbsp olive oil and season with pepper, then roast for 15 mins for medium-rare or 20 mins for medium. When the beef is cooked to your liking, remove from the oven to cool, then chill in the fridge for about 20 mins.');
insert into feedme.recipe_steps values('beef-wellington',1,1, 'While the beef is cooling, chop 250g chestnut (and wild, if you like) mushrooms as finely as possible so they have the texture of coarse breadcrumbs. You can use a food processor to do this, but make sure you pulse-chop the mushrooms so they don’t become a slurry.');
insert into feedme.recipe_steps values('beef-wellington',2,2, 'Heat 2 tbsp of the olive oil and 50g butter in a large pan and fry the mushrooms on a medium heat, with 1 large sprig fresh thyme, for about 10 mins stirring often, until you have a softened mixture. ');
insert into feedme.recipe_steps values('beef-wellington',3,3, 'Season the mushroom mixture, pour over 100ml dry white wine and cook for about 10 mins until all the wine has been absorbed. The mixture should hold its shape when stirred. Remove the mushroom duxelle from the pan to cool and discard the thyme.');
insert into feedme.recipe_steps values('beef-wellington',4,4, 'Overlap two pieces of cling film over a large chopping board. Lay 12 slices prosciutto on the cling film, slightly overlapping, in a double row. Spread half the duxelles over the prosciutto, then sit the fillet on it and spread the remaining duxelles over.');
insert into feedme.recipe_steps values('beef-wellington',5,5, 'Use the cling film’s edges to draw the prosciutto around the fillet, then roll it into a sausage shape, twisting the ends of cling film to tighten it as you go. Chill the fillet while you roll out the pastry.');
insert into feedme.recipe_steps values('beef-wellington',6,6, 'Dust your work surface with a little flour. Roll out a third of the 500g pack of puff pastry to a 18 x 30cm strip and place on a non-stick baking sheet.');
insert into feedme.recipe_steps values('beef-wellington',7,7, 'Roll out the remainder of the 500g pack of puff pastry to about 28 x 36cm. Unravel the fillet from the cling film and sit it in the centre of the smaller strip of pastry. Beat the 2 egg yolks with 1 tsp water and brush the pastry’s edges, and the top and sides of the wrapped fillet. ');
insert into feedme.recipe_steps values('beef-wellington',8,8, 'Using a rolling pin, carefully lift and drape the larger piece of pastry over the fillet, pressing well into the sides. Trim the joins to about a 4cm rim. Seal the rim with the edge of a fork or spoon handle. Glaze all over with more egg yolk and, using the back of a knife, mark the beef Wellington with long diagonal lines taking care not to cut into the pastry. Chill for at least 30 mins and up to 24 hrs.');
insert into feedme.recipe_steps values('beef-wellington',9,9, 'Heat oven to 200C/fan 180C/gas 6. Brush the Wellington with a little more egg yolk and cook until golden and crisp – 20-25 mins for medium-rare beef, 30 mins for medium. Allow to stand for 10 mins before serving in thick slices.');
insert into feedme.recipes VALUES ('chinese-braised-beef-with-ginger', 'Chinese braised beef with ginger', 'Slow cooking beef shin or brisket in Asian aromatic spices gives a melt-in-the-mouth main course that''s delicious with steamed rice and crisp stir-fried vegetables', 'chinese');
insert into feedme.recipe_ingredients values('chinese-braised-beef-with-ginger','sunflower oil','2 - 3 tbsp vegetable or sunflower oil',3.0,'tbsp');
insert into feedme.recipe_ingredients values('chinese-braised-beef-with-ginger','shin','10.25kg beef shin or brisket, cut into very large chunks',10.0,'unit');
insert into feedme.recipe_ingredients values('chinese-braised-beef-with-ginger','onion','2 onions',2.0,'unit');
insert into feedme.recipe_ingredients values('chinese-braised-beef-with-ginger','ginger','50g ginger',50.0,'gram');
insert into feedme.recipe_ingredients values('chinese-braised-beef-with-ginger','garlic','3 garlic cloves',3.0,'unit');
insert into feedme.recipe_ingredients values('chinese-braised-beef-with-ginger','coriander','small bunch coriander',NULL,'null');
insert into feedme.recipe_ingredients values('chinese-braised-beef-with-ginger','five-spice powder','2 tsp Chinese five-spice powder',2.0,'tsp');
insert into feedme.recipe_ingredients values('chinese-braised-beef-with-ginger','anise','6 whole star anise',6.0,'unit');
insert into feedme.recipe_ingredients values('chinese-braised-beef-with-ginger','peppercorn','1 tsp black peppercorn',1.0,'tsp');
insert into feedme.recipe_ingredients values('chinese-braised-beef-with-ginger','sugar','100g dark brown muscovado sugar',100.0,'gram');
insert into feedme.recipe_ingredients values('chinese-braised-beef-with-ginger','soy sauce','50ml light soy sauce',50.0,'ml');
insert into feedme.recipe_ingredients values('chinese-braised-beef-with-ginger','soy sauce','50ml dark soy sauce',50.0,'ml');
insert into feedme.recipe_ingredients values('chinese-braised-beef-with-ginger','tomato','2 tbsp tomato purée',2.0,'tbsp');
insert into feedme.recipe_ingredients values('chinese-braised-beef-with-ginger','stock','beef stock',NULL,'null');
insert into feedme.recipe_steps values('chinese-braised-beef-with-ginger',0,0, 'Heat a little of the oil in a large flameproof dish. Add the beef chunks, in batches, and fry until browned. When each batch is browned, transfer the beef to another dish. Very roughly chop the onions, ginger, garlic and coriander stalks. Put in a food processor and whizz to a paste.');
insert into feedme.recipe_steps values('chinese-braised-beef-with-ginger',1,1, 'Wipe any oil out of the dish you browned the beef in. Add the paste with a good splash of water and gently fry, scraping up any beef bits, until the paste is fragrant and softened (add more water if the paste sticks). Stir in the five-spice, star anise and peppercorns, cook for 1 min, then add the sugar, soy sauces and tomato purée. Return the beef and any juices to the dish, then stir in enough stock to just about cover. Bring to a gentle simmer. Heat oven to 160C/140C fan/gas 3. Cover the dish, put in the oven and cook for 21⁄2 hrs until the beef is really tender.');
insert into feedme.recipe_steps values('chinese-braised-beef-with-ginger',2,2, 'Lift the beef out of the sauce into a dish, to keep warm. Boil the sauce until reduced by about half and thickened. Meanwhile, fry the ginger in the oil until golden and crispy. Return the beef to the sauce. serve the beef spooned over rice and scattered with the crispy ginger.');
insert into feedme.recipes VALUES ('nofuss-shepherds-pie', 'No-fuss shepherd''s pie', 'We''ve got some lovely twists on shepherd''s pie on the site, but we were missing a definitive version of this classic family recipe - here it is!', 'british');
insert into feedme.recipe_ingredients values('nofuss-shepherds-pie','sunflower oil','1 tbsp sunflower oil',1.0,'tbsp');
insert into feedme.recipe_ingredients values('nofuss-shepherds-pie','onion','1 large onion, chopped',1.0,'unit');
insert into feedme.recipe_ingredients values('nofuss-shepherds-pie','carrot','2-3 medium carrots, chopped',2.0,'unit');
insert into feedme.recipe_ingredients values('nofuss-shepherds-pie','mince','500g pack lamb mince',500.0,'gram');
insert into feedme.recipe_ingredients values('nofuss-shepherds-pie','tomato','2 tbsp tomato purée',2.0,'tbsp');
insert into feedme.recipe_ingredients values('nofuss-shepherds-pie','worcestershire sauce','large splash Worcestershire sauce',NULL,'null');
insert into feedme.recipe_ingredients values('nofuss-shepherds-pie','stock','500ml beef stock',500.0,'ml');
insert into feedme.recipe_ingredients values('nofuss-shepherds-pie','potato','900g potato, cut into chunks',900.0,'gram');
insert into feedme.recipe_ingredients values('nofuss-shepherds-pie','butter','85g butter',85.0,'gram');
insert into feedme.recipe_ingredients values('nofuss-shepherds-pie','milk','3 tbsp milk',3.0,'tbsp');
insert into feedme.recipe_steps values('nofuss-shepherds-pie',0,0, 'Heat the oil in a medium saucepan, then soften the onion and carrots for a few mins. When soft, turn up the heat, crumble in the lamb and brown, tipping off any excess fat. Add the tomato purée and Worcestershire sauce, then fry for a few mins. Pour over the stock, bring to a simmer, then cover and cook for 40 mins, uncovering halfway.');
insert into feedme.recipe_steps values('nofuss-shepherds-pie',1,1, 'Meanwhile, heat the oven to 180C/ fan 160C/ gas 4, then make the mash. Boil the potatoes in salted water for 10-15 mins until tender. Drain, then mash with the butter and milk.');
insert into feedme.recipe_steps values('nofuss-shepherds-pie',2,2, 'Put the mince into an ovenproof dish, top with the mash and ruffle with a fork. The pie can now be chilled and frozen for up to a month. Bake for 20-25 mins until the top is starting to colour and the mince is bubbling through at the edges. (To bake from frozen, cook at 160C/fan 140C/gas 3 for 1 hr-1 hr 20 mins until piping hot in the centre. Flash under the grill to brown, if you like.) Leave to stand for 5 mins before serving.');
insert into feedme.recipes VALUES ('curry-coconut-fish-parcels', 'Curry coconut fish parcels', 'Steam sustainable fish like tilapia in foil parcels with curry paste, coconut and lime then serve with rice for a healthy supper', 'indian');
insert into feedme.recipe_ingredients values('curry-coconut-fish-parcels','tilapia','2 large tilapia fillets, about 125g/40.5oz each',125.0,'gram');
insert into feedme.recipe_ingredients values('curry-coconut-fish-parcels','curry paste','2 tsp yellow or red curry paste',2.0,'tsp');
insert into feedme.recipe_ingredients values('curry-coconut-fish-parcels','nut','2 tsp desiccated coconut',2.0,'tsp');
insert into feedme.recipe_ingredients values('curry-coconut-fish-parcels','lime','zest and juice 1 lime, plus wedges to serve',NULL,'null');
insert into feedme.recipe_ingredients values('curry-coconut-fish-parcels','soy sauce','1 tsp soy sauce',1.0,'tsp');
insert into feedme.recipe_ingredients values('curry-coconut-fish-parcels','rice','140g basmati rice',140.0,'gram');
insert into feedme.recipe_ingredients values('curry-coconut-fish-parcels','chilli','2 tbsp sweet chilli sauce',2.0,'tbsp');
insert into feedme.recipe_ingredients values('curry-coconut-fish-parcels','chilli','1 red chilli, sliced',1.0,'unit');
insert into feedme.recipe_ingredients values('curry-coconut-fish-parcels','broccoli','200g cooked thin-stemmed broccoli, to serve',200.0,'gram');
insert into feedme.recipe_steps values('curry-coconut-fish-parcels',0,0, 'Heat oven to 200C/180C fan/gas 6. Tear off 4 large pieces of foil, double them up, then place a fish fillet in the middle of each. Spread over the curry paste. Divide the coconut, lime zest and juice, and soy between each fillet. Bring up the sides of the foil, then scrunch the edges and sides together to make 2 sealed parcels.');
insert into feedme.recipe_steps values('curry-coconut-fish-parcels',1,1, 'Put the parcels on a baking tray and bake for 10-15 mins. Tip the rice into a pan with plenty of water, and boil for 12-15 mins or until cooked. Drain well. Serve the fish on the rice, drizzle over the chilli sauce and scatter with sliced chilli. Serve with broccoli and lime wedges.');
insert into feedme.recipes VALUES ('crispy-chilli-beef', 'Crispy chilli beef', 'Ditch the takeaway and whip up this Chinese flash-fried steak with a sweet gingery sauce and red peppers', 'chinese');
insert into feedme.recipe_ingredients values('crispy-chilli-beef','steak','350g thin-cut minute steak, very thinly sliced into strips',350.0,'gram');
insert into feedme.recipe_ingredients values('crispy-chilli-beef','flour','3 tbsp cornflour',3.0,'tbsp');
insert into feedme.recipe_ingredients values('crispy-chilli-beef','five-spice powder','2 tsp Chinese five-spice powder',2.0,'tsp');
insert into feedme.recipe_ingredients values('crispy-chilli-beef','vegetable oil','100ml vegetable oil',100.0,'ml');
insert into feedme.recipe_ingredients values('crispy-chilli-beef','pepper','1 red pepper, thinly sliced',1.0,'unit');
insert into feedme.recipe_ingredients values('crispy-chilli-beef','chilli','1 red chilli, thinly sliced',1.0,'unit');
insert into feedme.recipe_ingredients values('crispy-chilli-beef','spring onion','4 spring onions, sliced, green and white parts separated',4.0,'unit');
insert into feedme.recipe_ingredients values('crispy-chilli-beef','garlic','2 garlic cloves, crushed',2.0,'unit');
insert into feedme.recipe_ingredients values('crispy-chilli-beef','ginger','thumb-sized piece ginger, cut into matchsticks',NULL,'null');
insert into feedme.recipe_ingredients values('crispy-chilli-beef','wine','4 tbsp rice wine vinegaror white wine vinegar',4.0,'tbsp');
insert into feedme.recipe_ingredients values('crispy-chilli-beef','soy sauce','1 tbsp soy sauce',1.0,'tbsp');
insert into feedme.recipe_ingredients values('crispy-chilli-beef','chilli','2 tbsp sweet chilli sauce',2.0,'tbsp');
insert into feedme.recipe_ingredients values('crispy-chilli-beef','ketchup','2 tbsp tomato ketchup',2.0,'tbsp');
insert into feedme.recipe_ingredients values('crispy-chilli-beef','noodles','cooked noodles, to serve (optional)',NULL,'null');
insert into feedme.recipe_ingredients values('crispy-chilli-beef','crackers','prawncrackers, to serve (optional)',NULL,'null');
insert into feedme.recipe_steps values('crispy-chilli-beef',0,0, 'Put the beef in a bowl and toss in the cornflour and five-spice. Heat the oil in a wok or large frying pan until hot, then add the beef and fry until golden and crisp. Scoop out the beef and drain on kitchen paper. Pour away all but 1 tbsp oil.');
insert into feedme.recipe_steps values('crispy-chilli-beef',1,1, 'Add the pepper, half the chilli, the white ends of the spring onions, garlic and ginger to the pan. Stir-fry for 3 mins to soften, but don’t let the garlic and ginger burn. Mix the vinegar, soy, chilli sauce and ketchup in a jug with 2 tbsp water, then pour over the veg. Bubble for 2 mins, then add the beef back to the pan and toss well to coat. Serve the beef on noodles with prawn crackers, if you like, scattered with the remaining chilli and the green parts of the spring onions.');
insert into feedme.recipes VALUES ('hobtotable-moussaka', 'Hob-to-table moussaka', 'Try this rich, hearty and quick variation on the classic family favourite', 'greek');
insert into feedme.recipe_ingredients values('hobtotable-moussaka','olive oil','2 tbsp olive oil',2.0,'tbsp');
insert into feedme.recipe_ingredients values('hobtotable-moussaka','onion','1 large onion, finely chopped',1.0,'unit');
insert into feedme.recipe_ingredients values('hobtotable-moussaka','garlic','2 garlic cloves, finely chopped',2.0,'unit');
insert into feedme.recipe_ingredients values('hobtotable-moussaka','lamb','500g minced lamb',500.0,'gram');
insert into feedme.recipe_ingredients values('hobtotable-moussaka','plum','400g/14oz can chopped plum tomatoes',400.0,'gram');
insert into feedme.recipe_ingredients values('hobtotable-moussaka','tomato','2 tbsp tomato purée',2.0,'tbsp');
insert into feedme.recipe_ingredients values('hobtotable-moussaka','cinnamon','2 tsp ground cinnamon',2.0,'tsp');
insert into feedme.recipe_ingredients values('hobtotable-moussaka','olive oil','200g/7oz jar chargrilled aubergines in olive oil, drained and chopped',200.0,'gram');
insert into feedme.recipe_ingredients values('hobtotable-moussaka','cheese','200g/7oz pack feta cheese, crumbled',200.0,'gram');
insert into feedme.recipe_ingredients values('hobtotable-moussaka','mint','3 tbsp fresh mint, chopped',3.0,'tbsp');
insert into feedme.recipe_steps values('hobtotable-moussaka',0,0, 'Brown the mince: Heat the oil in a large, shallow pan. Toss in the onion and garlic and fry until soft. Add the mince and stir fry for 3-4 minutes until browned.');
insert into feedme.recipe_steps values('hobtotable-moussaka',1,1, 'Season and simmer: Tip the tomatoes into the pan and stir in the tomato purée and cinnamon, then season generously with salt and pepper. Leave the mince to simmer for 20 minutes, adding the aubergines half way through.');
insert into feedme.recipe_steps values('hobtotable-moussaka',2,2, 'To serve: Sprinkle the crumbled feta and chopped mint over the mince. Bring the moussaka to the table as the feta melts and serve it with a crunchy green salad and toasted pitta.');
insert into feedme.recipes VALUES ('roasted-chicken-with-creamy-walnut-sauce', 'Roasted chicken with creamy walnut sauce', 'This Turkish-inspired sauce makes a wonderful alternative to a classic bread sauce', 'turkish');
insert into feedme.recipe_ingredients values('roasted-chicken-with-creamy-walnut-sauce','chicken','8 chicken thighs, skin on and bone in',8.0,'unit');
insert into feedme.recipe_ingredients values('roasted-chicken-with-creamy-walnut-sauce','cumin','1 tsp cumin',1.0,'tsp');
insert into feedme.recipe_ingredients values('roasted-chicken-with-creamy-walnut-sauce','paprika','0.5 tsp paprika',5.0,'tsp');
insert into feedme.recipe_ingredients values('roasted-chicken-with-creamy-walnut-sauce','olive oil','2 tbsp olive oil',2.0,'tbsp');
insert into feedme.recipe_ingredients values('roasted-chicken-with-creamy-walnut-sauce','pitta bread','6 pitta breads',6.0,'unit');
insert into feedme.recipe_ingredients values('roasted-chicken-with-creamy-walnut-sauce','stock','150ml hot chicken stock',150.0,'ml');
insert into feedme.recipe_ingredients values('roasted-chicken-with-creamy-walnut-sauce','nut','175g walnuts',175.0,'gram');
insert into feedme.recipe_ingredients values('roasted-chicken-with-creamy-walnut-sauce','onion','1 small onion, chopped',1.0,'unit');
insert into feedme.recipe_ingredients values('roasted-chicken-with-creamy-walnut-sauce','garlic','2 garlic cloves, chopped',2.0,'unit');
insert into feedme.recipe_ingredients values('roasted-chicken-with-creamy-walnut-sauce','single cream','50ml single cream',50.0,'ml');
insert into feedme.recipe_ingredients values('roasted-chicken-with-creamy-walnut-sauce','lemon','juice 1 lemon',NULL,'null');
insert into feedme.recipe_ingredients values('roasted-chicken-with-creamy-walnut-sauce','coriander','handful coriander, chopped',NULL,'null');
insert into feedme.recipe_steps values('roasted-chicken-with-creamy-walnut-sauce',0,0, 'Heat oven to 200C/180C fan/gas 6. In a roasting tin, toss together the chicken, cumin, paprika, 1 tbsp olive oil and seasoning. Cook for 40 mins until the chicken is crisp and cooked through.');
insert into feedme.recipe_steps values('roasted-chicken-with-creamy-walnut-sauce',1,1, 'Meanwhile, tear up 1 pitta bread and place in a small bowl. Pour over a couple of tbsp chicken stock and leave to soak. Dry-fry the walnuts in a frying pan for about 3 mins until golden and toasted. Set aside. Heat the remaining oil in the pan and cook the onion and garlic until softened. Place the softened pitta bread, onion mixture and most of the nuts into a blender. Pour over the rest of the chicken stock and whizz together until a rough paste forms. Return the mixture to the pan. Add the cream and lemon juice, season and keep warm.');
insert into feedme.recipe_steps values('roasted-chicken-with-creamy-walnut-sauce',2,2, 'When the chicken is cooked, arrange on a platter. Stir the coriander through the sauce and spoon into a bowl. Roughly chop the remaining walnuts and scatter over the chicken. Toast the pittas, cut into wedges and serve alongside');
insert into feedme.recipes VALUES ('mediterranean-fig-and-mozzarella-salad', 'Coronation chicken naans with Indian slaw', 'Top Indian bread with shredded meat, fruity mild curry sauce and a carrot and cabbage slaw', 'mediterranean');
insert into feedme.recipe_ingredients values('mediterranean-fig-and-mozzarella-salad','chicken','1 ready-roasted chicken, about 950g',950.0,'gram');
insert into feedme.recipe_ingredients values('mediterranean-fig-and-mozzarella-salad','garlic','2 x packs 4 mini coriander and garlic naan bread',2.0,'unit');
insert into feedme.recipe_steps values('mediterranean-fig-and-mozzarella-salad',0,0, 'Shred the meat from the chicken and set aside. To make the sauce, mix the yogurt, mayonnaise, curry powder, chutney and some seasoning with half the coriander. Toss this through the chicken.');
insert into feedme.recipe_steps values('mediterranean-fig-and-mozzarella-salad',1,1, 'To make the slaw, mix the carrots, cabbage and mustard seeds in a large bowl with the remaining coriander, then season.');
insert into feedme.recipe_steps values('mediterranean-fig-and-mozzarella-salad',2,2, 'Heat the naan breads in the toaster. Allow 2 per person and top with some of the slaw, then the chicken mixture. Serve sprinkled with coriander, with extra slaw on the side.');
insert into feedme.recipes VALUES ('fragrant-thai-prawns', 'Fragrant Thai prawns', 'Add subtle Thai flavours to seafood in Curtis Stone''s prawn recipe', 'thai');
insert into feedme.recipe_ingredients values('fragrant-thai-prawns','prawn','20 raw king prawns',20.0,'unit');
insert into feedme.recipe_ingredients values('fragrant-thai-prawns','shallot','1 small shallot, finely chopped',1.0,'unit');
insert into feedme.recipe_ingredients values('fragrant-thai-prawns','garlic','4 garlic cloves, finely chopped',4.0,'unit');
insert into feedme.recipe_ingredients values('fragrant-thai-prawns','chilli','1 fresh red chilli, halved, seeded and finely chopped',1.0,'unit');
insert into feedme.recipe_ingredients values('fragrant-thai-prawns','ginger','20.5 cm piece of fresh root ginger, peeled and finely chopped',20.0,'unit');
insert into feedme.recipe_ingredients values('fragrant-thai-prawns','lemongrass','1 stem of lemongrass, tough outer leaves removed, finely chopped',1.0,'unit');
insert into feedme.recipe_ingredients values('fragrant-thai-prawns','mint','a good handful of fresh mint leaves, finely chopped',NULL,'null');
insert into feedme.recipe_ingredients values('fragrant-thai-prawns','fresh coriander','a good handful of fresh coriander leaves, finely chopped',NULL,'null');
insert into feedme.recipe_ingredients values('fragrant-thai-prawns','olive oil','100ml/30.5 fl oz olive oil',100.0,'ml');
insert into feedme.recipe_ingredients values('fragrant-thai-prawns','lime','grated zest and juice of 1 lime',NULL,'null');
insert into feedme.recipe_steps values('fragrant-thai-prawns',0,0, 'Tip all the chopped ingredients into a bowl, add the olive oil along with the lime zest and juice and mix well. Set aside for 30 minutes to allow the flavours to mingle.');
insert into feedme.recipe_steps values('fragrant-thai-prawns',1,1, 'To make fragrant Thai prawns, remove heads and peel shells from 20 raw king prawns but leave tail sections on. Toss in the marinade and cook on a really hot barbecue for 45 seconds on each side until they turn from grey-blue to pink.');
insert into feedme.recipes VALUES ('mediterranean-chicken-with-roasted-vegetables', 'Mediterranean chicken with roasted vegetables', 'A healthy dish, full of sunshine flavours', 'mediterranean');
insert into feedme.recipe_ingredients values('mediterranean-chicken-with-roasted-vegetables','new potatoes','250g baby new potatoes, thinly sliced',250.0,'gram');
insert into feedme.recipe_ingredients values('mediterranean-chicken-with-roasted-vegetables','courgette','1 large courgette, diagonally sliced',1.0,'unit');
insert into feedme.recipe_ingredients values('mediterranean-chicken-with-roasted-vegetables','red onion','1 red onion, cut into wedges',1.0,'unit');
insert into feedme.recipe_ingredients values('mediterranean-chicken-with-roasted-vegetables','pepper','1 yellow pepper, seeded and cut into chunks',1.0,'unit');
insert into feedme.recipe_ingredients values('mediterranean-chicken-with-roasted-vegetables','plum','6 firm plum tomatoes, halved',6.0,'unit');
insert into feedme.recipe_ingredients values('mediterranean-chicken-with-roasted-vegetables','olive','12 black olives, pitted',12.0,'unit');
insert into feedme.recipe_ingredients values('mediterranean-chicken-with-roasted-vegetables','chicken','2 skinless boneless chicken breast fillets, about 150g/5oz each',150.0,'gram');
insert into feedme.recipe_ingredients values('mediterranean-chicken-with-roasted-vegetables','olive oil','3 tbsp olive oil',3.0,'tbsp');
insert into feedme.recipe_ingredients values('mediterranean-chicken-with-roasted-vegetables','pesto','1 rounded tbsp green pesto',1.0,'unit');
insert into feedme.recipe_steps values('mediterranean-chicken-with-roasted-vegetables',0,0, 'Preheat the oven to 200C/ Gas 6/fan oven 180C. Spread the potatoes, courgette, onion, pepper and tomatoes in a shallow roasting tin and scatter over the olives. Season with salt and coarsely ground black pepper.');
insert into feedme.recipe_steps values('mediterranean-chicken-with-roasted-vegetables',1,1, 'Slash the flesh of each chicken breast 3-4 times using a sharp knife, then lay the chicken on top of the vegetables.');
insert into feedme.recipe_steps values('mediterranean-chicken-with-roasted-vegetables',2,2, 'Mix the olive oil and pesto together until well blended and spoon evenly over the chicken. Cover the tin with foil and cook for 30 minutes.');
insert into feedme.recipe_steps values('mediterranean-chicken-with-roasted-vegetables',3,3, 'Remove the foil from the tin. Return to the oven and cook for a further 10 minutes until the vegetables are juicy and look tempting to eat and the chicken is cooked through (the juices should run clear when pierced with a skewer).');
insert into feedme.recipes VALUES ('griddled-aubergines-with-sesame-dressing', 'Griddled aubergines with sesame dressing', 'Dress chargrilled aubergine with tahini, garlic, coriander and yoghurt for a versatile veggie side dish that you can cook on the hob or bbq', 'turkish');
insert into feedme.recipe_ingredients values('griddled-aubergines-with-sesame-dressing','aubergine','2 large aubergines, cut into 2cm slices',2.0,'unit');
insert into feedme.recipe_ingredients values('griddled-aubergines-with-sesame-dressing','olive oil','2 tbsp olive oil',2.0,'tbsp');
insert into feedme.recipe_ingredients values('griddled-aubergines-with-sesame-dressing','fat','250g full-fat Greek yogurt',250.0,'gram');
insert into feedme.recipe_ingredients values('griddled-aubergines-with-sesame-dressing','tahini','3 tbsp tahini paste',3.0,'tbsp');
insert into feedme.recipe_ingredients values('griddled-aubergines-with-sesame-dressing','garlic','1 large garlic clove, crushed',1.0,'unit');
insert into feedme.recipe_ingredients values('griddled-aubergines-with-sesame-dressing','lemon','juice 1 lemon',NULL,'null');
insert into feedme.recipe_ingredients values('griddled-aubergines-with-sesame-dressing','parsley','handful chopped coriander, parsley and mint, plus extra leaves to serve',NULL,'null');
insert into feedme.recipe_steps values('griddled-aubergines-with-sesame-dressing',0,0, 'Brush each aubergine slice with some oil, then season. Heat a griddle pan or barbecue. When hot, griddle the aubergine slices for 2-3 mins on each side until golden brown and tender.');
insert into feedme.recipe_steps values('griddled-aubergines-with-sesame-dressing',1,1, 'Mix the yogurt with the tahini, garlic, lemon juice and herbs, then season. Top the aubergines with the dressing and scatter over extra herb leaves.');
insert into feedme.recipes VALUES ('smoked-aubergine-pure', 'Smoked aubergine purée', 'This is great served with grilled meat or chicken and also makes a simple lunch for vegetarians', 'turkish');
insert into feedme.recipe_ingredients values('smoked-aubergine-pure','aubergine','2 medium aubergines',2.0,'unit');
insert into feedme.recipe_ingredients values('smoked-aubergine-pure','lemon','juice 1 lemon',NULL,'null');
insert into feedme.recipe_ingredients values('smoked-aubergine-pure','garlic','2-3 garlic cloves, crushed',2.0,'unit');
insert into feedme.recipe_ingredients values('smoked-aubergine-pure','yoghurt','150ml pot thick yogurt',150.0,'ml');
insert into feedme.recipe_ingredients values('smoked-aubergine-pure','dill','small bunch dill, leaves chopped',NULL,'null');
insert into feedme.recipe_steps values('smoked-aubergine-pure',0,0, 'Heat grill to very hot. Slice the aubergines in half lengthways, then grill for 25 mins, turning occasionally, until soft – the skin will remain firm, but the flesh will soften. Lift the aubergines off the grill and leave until cool enough to handle.');
insert into feedme.recipe_steps values('smoked-aubergine-pure',1,1, 'Using a sharp knife, score the grilled flesh and scoop out the flesh with a spoon. Tip into a bowl and mash with a fork until you get a thick pulp. Beat in the lemon juice and garlic. Add the yogurt and dill, and season. Serve while still warm.');
insert into feedme.recipes VALUES ('bok-choi-with-oyster-sauce-and-chilli', 'Bok choi with oyster sauce & chilli', 'John Torode''s speedy side makes the most of Oriental cabbage, served with fragrant spices', 'chinese');
insert into feedme.recipe_ingredients values('bok-choi-with-oyster-sauce-and-chilli','vegetable oil','2 tbsp vegetable oil',2.0,'tbsp');
insert into feedme.recipe_ingredients values('bok-choi-with-oyster-sauce-and-chilli','garlic','2 garlic cloves, minced',2.0,'unit');
insert into feedme.recipe_ingredients values('bok-choi-with-oyster-sauce-and-chilli','bok choi','6 heads bok choi, split in half lengthways, or 16 baby bok choi',6.0,'unit');
insert into feedme.recipe_ingredients values('bok-choi-with-oyster-sauce-and-chilli','oyster','3 tbsp oyster sauce',3.0,'tbsp');
insert into feedme.recipe_ingredients values('bok-choi-with-oyster-sauce-and-chilli','soy sauce','1 tbsp light soy sauce',1.0,'tbsp');
insert into feedme.recipe_ingredients values('bok-choi-with-oyster-sauce-and-chilli','chilli','1 long red chilli, deseeded and finely sliced',1.0,'unit');
insert into feedme.recipe_steps values('bok-choi-with-oyster-sauce-and-chilli',0,0, 'Heat the oil in a wok, add the garlic and fry for 2 mins. Tip in the bok choi and stir well over the heat for 4 mins until it wilts. Pour in the oyster sauce, soy and 50ml water. Simmer for a further 3 mins, then scatter over the chilli to serve.');
insert into feedme.recipes VALUES ('chicken-with-saffron-raisins-and-pine-nuts', 'Chicken with saffron, raisins & pine nuts', 'This full-flavoured Spanish dish is great for the family or entertaining friends midweek', 'spanish');
insert into feedme.recipe_ingredients values('chicken-with-saffron-raisins-and-pine-nuts','chicken','10.25kg chicken, jointed and on the bone (ask your butcher to do this for you or see Know-how)',10.0,'unit');
insert into feedme.recipe_ingredients values('chicken-with-saffron-raisins-and-pine-nuts','olive oil','2 tbsp olive oil',2.0,'tbsp');
insert into feedme.recipe_ingredients values('chicken-with-saffron-raisins-and-pine-nuts','onion','2 onions, chopped',2.0,'unit');
insert into feedme.recipe_ingredients values('chicken-with-saffron-raisins-and-pine-nuts','garlic','3 garlic cloves, chopped',3.0,'unit');
insert into feedme.recipe_ingredients values('chicken-with-saffron-raisins-and-pine-nuts','saffron','good pinch of saffron(about 20 strands)',NULL,'null');
insert into feedme.recipe_ingredients values('chicken-with-saffron-raisins-and-pine-nuts','dry sherry','125ml glass dry sherry(such as Fino or Manzanilla)',125.0,'ml');
insert into feedme.recipe_ingredients values('chicken-with-saffron-raisins-and-pine-nuts','stock','200ml chicken stock',200.0,'ml');
insert into feedme.recipe_ingredients values('chicken-with-saffron-raisins-and-pine-nuts','thyme','1 tbsp thyme leaves',1.0,'tbsp');
insert into feedme.recipe_ingredients values('chicken-with-saffron-raisins-and-pine-nuts','raisins','50g raisins',50.0,'gram');
insert into feedme.recipe_ingredients values('chicken-with-saffron-raisins-and-pine-nuts','nut','2 tbsp pine nuts',2.0,'tbsp');
insert into feedme.recipe_ingredients values('chicken-with-saffron-raisins-and-pine-nuts','parsley','handful chopped flat-leaf parsley, to serve',NULL,'null');
insert into feedme.recipe_steps values('chicken-with-saffron-raisins-and-pine-nuts',0,0, 'Heat a large frying pan on a high heat and season the chicken. Add the olive oil to the pan, then the chicken. Brown for about 5 mins on each side, remove onto a plate, then set aside.');
insert into feedme.recipe_steps values('chicken-with-saffron-raisins-and-pine-nuts',1,1, 'Lower the heat to medium. In the remaining fat, fry the onions for 3 mins, then add the garlic and saffron. Cook for 3-4 mins more. Add the Sherry, then simmer for 3-5 mins until syrupy.');
insert into feedme.recipe_steps values('chicken-with-saffron-raisins-and-pine-nuts',2,2, 'Put the chicken leg pieces back into the pan, tip in the stock, thyme and raisins, cover, then gently cook on a low heat for 20 mins. Add the breast meat and any juices left on the plate, simmer for 10 mins more until cooked through and the sauce in the pan has reduced.');
insert into feedme.recipe_steps values('chicken-with-saffron-raisins-and-pine-nuts',3,3, 'While the chicken is cooking, heat oven to 200C/180C fan/gas 6. Scatter the pine nuts over a baking sheet, then roast for 10 mins until golden and toasted. Once the chicken has cooked through, season to taste, scatter with pine nuts and parsley, then serve with rice.');
insert into feedme.recipes VALUES ('red-curry-chicken-kebabs-', 'Red curry chicken kebabs', 'Chicken and kebabs are two of our most popular choices so this former cover star is a real winner - for barbecues or the grill', 'thai');
insert into feedme.recipe_ingredients values('red-curry-chicken-kebabs-','chicken','2 boneless, skinless chicken breasts, cut into large chunks',2.0,'unit');
insert into feedme.recipe_ingredients values('red-curry-chicken-kebabs-','curry paste','2 tbsp Thai red curry paste',2.0,'tbsp');
insert into feedme.recipe_ingredients values('red-curry-chicken-kebabs-','nut','2 tbsp coconut milk',2.0,'tbsp');
insert into feedme.recipe_ingredients values('red-curry-chicken-kebabs-','pepper','1 red pepper, deseeded and chopped into chunks',1.0,'unit');
insert into feedme.recipe_ingredients values('red-curry-chicken-kebabs-','courgette','1 courgette, halved and cut into chunks',1.0,'unit');
insert into feedme.recipe_ingredients values('red-curry-chicken-kebabs-','red onion','1 red onion, cut into large wedges',1.0,'unit');
insert into feedme.recipe_ingredients values('red-curry-chicken-kebabs-','lime','1 lime, halved, to serve',1.0,'unit');
insert into feedme.recipe_steps values('red-curry-chicken-kebabs-',0,0, 'Fire up the barbecue or heat a griddle pan to high. Tip chicken, curry paste and coconut milk into a bowl, then mix well until the chicken is evenly coated. Thread vegetables and chicken onto skewers. Cook the skewers on the barbecue or griddle for 5-8 mins, turning every so often, until the chicken is cooked through and charred. Serve with herby rice, salad and a lime half to squeeze over.');
insert into feedme.recipes VALUES ('spicy-moroccan-rice-', 'Spicy Moroccan rice', 'Spice up plain old chicken with a taste of the exotic', 'moroccan');
insert into feedme.recipe_ingredients values('spicy-moroccan-rice-','chicken','4 skinless chicken breasts, diced',4.0,'unit');
insert into feedme.recipe_ingredients values('spicy-moroccan-rice-','seasoning','1 tbsp Moroccan spice seasoning',1.0,'tbsp');
insert into feedme.recipe_ingredients values('spicy-moroccan-rice-','onion','1 onion, finely sliced',1.0,'unit');
insert into feedme.recipe_ingredients values('spicy-moroccan-rice-','butter','50g butter',50.0,'gram');
insert into feedme.recipe_ingredients values('spicy-moroccan-rice-','rice','300g rice',300.0,'gram');
insert into feedme.recipe_ingredients values('spicy-moroccan-rice-','dried apricots','12 dried apricots, halved',12.0,'unit');
insert into feedme.recipe_ingredients values('spicy-moroccan-rice-','stock','chicken stock cube',NULL,'null');
insert into feedme.recipe_ingredients values('spicy-moroccan-rice-','peas','410g can chickpeas, drained and rinsed',410.0,'gram');
insert into feedme.recipe_ingredients values('spicy-moroccan-rice-','parsley','15g pack flatleaf parsley, chopped',15.0,'gram');
insert into feedme.recipe_steps values('spicy-moroccan-rice-',0,0, 'Coat chicken with the Moroccan spice. Fry the onion in butter until soft.');
insert into feedme.recipe_steps values('spicy-moroccan-rice-',1,1, 'Tip in the chicken, then cook for a few mins more. Stir in the rice and apricots, then pour in the stock cube with 700ml boiling water and the chickpeas.');
insert into feedme.recipe_steps values('spicy-moroccan-rice-',2,2, 'Cover the pan and simmer for 10 mins until the rice is tender and has absorbed most of the liquid. Toss in the chopped flat leaf parsley and serve.');
insert into feedme.recipes VALUES ('vietnamese-chicken-salad', 'Vietnamese chicken salad', 'A no-cook main course salad with all the light, fragrant flavours of Southeast Asia such as peanut, chilli, mint and soy', 'vietnamese');
insert into feedme.recipe_ingredients values('vietnamese-chicken-salad','rice','140g Thai rice noodle',140.0,'gram');
insert into feedme.recipe_ingredients values('vietnamese-chicken-salad','carrot','1 carrot',1.0,'unit');
insert into feedme.recipe_ingredients values('vietnamese-chicken-salad','cucumber','0.5 cucumber',0.0,'unit');
insert into feedme.recipe_ingredients values('vietnamese-chicken-salad','chicken','2 cooked chicken breasts, shredded',2.0,'unit');
insert into feedme.recipe_ingredients values('vietnamese-chicken-salad','radish','50g radish, thinly sliced',50.0,'gram');
insert into feedme.recipe_ingredients values('vietnamese-chicken-salad','red onion','0.5 red onion, finely sliced',0.0,'unit');
insert into feedme.recipe_ingredients values('vietnamese-chicken-salad','mint','small bunch mint, leaves picked',NULL,'null');
insert into feedme.recipe_ingredients values('vietnamese-chicken-salad','nut','25g natural roasted peanut, roughly chopped',25.0,'gram');
insert into feedme.recipe_steps values('vietnamese-chicken-salad',0,0, 'To make the dressing, whisk all the ingredients together in a large serving bowl. Cook the noodles following pack instructions, then drain and add to the bowl with the dressing.');
insert into feedme.recipe_steps values('vietnamese-chicken-salad',1,1, 'Peel the carrot into long strips using a vegetable peeler. Do the same for the cucumber, until you reach the seeds (discard them). Add the carrot and cucumber to the noodle mixture along with the shredded chicken, radishes, red onion and mint. Toss well to coat in the dressing, scatter over the peanuts and serve.');
insert into feedme.recipes VALUES ('indian-spiced-barbecued-lamb', 'Indian spiced barbecued lamb', 'Yogurt-based marinades create a delicious thin crust when they char, so are ideal for barbecues', 'indian');
insert into feedme.recipe_ingredients values('indian-spiced-barbecued-lamb','lamb','1 butterflied leg of lamb, about 20.5 kg/5lb 8oz (ask your butcher to do this for you)',1.0,'unit');
insert into feedme.recipe_steps values('indian-spiced-barbecued-lamb',0,0, 'Using a hand blender or mini food processor, whizz marinade ingredients to a smooth paste, then season with salt. Lay the lamb in a ceramic dish and coat with the marinade. Cover in cling film and leave to marinate in the fridge for at least 4 hrs or preferably overnight.');
insert into feedme.recipe_steps values('indian-spiced-barbecued-lamb',1,1, 'If you are using a charcoal barbecue, wait for the coals to become white and glowing (light the barbecue about an hour before you want to cook the lamb). If using a gas barbecue, cook the lamb over medium heat. Lift lamb out of the marinade and barbecue, flesh-side down, for about 25 mins. Flip it over and give the fat side about 15 mins. When the lamb has a dark brown crust and is cooked to your liking, leave it to rest on a board for 15 mins. (If the weather forces you indoors, the lamb can be roasted at 220C/200C fan/gas 7 for about an hour for pink meat; the breads can be cooked on a very hot griddle).');
insert into feedme.recipe_steps values('indian-spiced-barbecued-lamb',2,2, 'To serve, carve the lamb into thick slices and scatter with the coriander and some of the Pickled red onion (see below). Serve the remaining onion in a dish alongside the raita and breads (see recipes, below).');
insert into feedme.recipes VALUES ('grilled-salmon-teriyaki-with-cucumber-salad', 'Grilled salmon teriyaki with cucumber salad', 'Salmon fillets are cooked straight from frozen for convenience - the sticky glaze helps to keep the fish moist while it cooks', 'japanese');
insert into feedme.recipe_ingredients values('grilled-salmon-teriyaki-with-cucumber-salad','sunflower oil','1 tbsp sunflower oil',1.0,'tbsp');
insert into feedme.recipe_ingredients values('grilled-salmon-teriyaki-with-cucumber-salad','soy sauce','5 tbsp soy sauce',5.0,'tbsp');
insert into feedme.recipe_ingredients values('grilled-salmon-teriyaki-with-cucumber-salad','dry sherry','5 tbsp mirinor dry sherry',5.0,'tbsp');
insert into feedme.recipe_ingredients values('grilled-salmon-teriyaki-with-cucumber-salad','caster sugar','1 tbsp golden caster sugar',1.0,'tbsp');
insert into feedme.recipe_ingredients values('grilled-salmon-teriyaki-with-cucumber-salad','ginger','1 piece fresh root ginger, peeled and finely grated',1.0,'unit');
insert into feedme.recipe_ingredients values('grilled-salmon-teriyaki-with-cucumber-salad','garlic','2 garlic cloves, crushed to a paste',2.0,'unit');
insert into feedme.recipe_ingredients values('grilled-salmon-teriyaki-with-cucumber-salad','salmon','4 frozen boneless, skinless salmon fillets',4.0,'unit');
insert into feedme.recipe_steps values('grilled-salmon-teriyaki-with-cucumber-salad',0,0, 'Heat the grill to high and brush a sturdy baking tray with oil. In a large bowl, mix the soy, mirin, sugar, ginger and garlic together until the sugar has dissolved, then toss the frozen salmon in the soy mix until coated. Tip the remaining marinade into a small saucepan and bring to a simmer.');
insert into feedme.recipe_steps values('grilled-salmon-teriyaki-with-cucumber-salad',1,1, 'Place the tray about 4in away from the heat, then grill for 20 mins. Brush the fish every few mins with the simmering marinade until cooked through and glazed. If the fillets are thick, you may need to turn them on their sides so they cook evenly. Remove from the grill. Simmer the marinade until sticky, then pour it over the cooked salmon.');
insert into feedme.recipe_steps values('grilled-salmon-teriyaki-with-cucumber-salad',2,2, 'For the cucumber salad, use a swivel blade peeler to peel the cucumber into slices. Make the dressing by mixing the vinegar with the soy sauce, sugar and sesame seeds. Toss the cucumber with the dressing and serve with the salmon and boiled rice.');
insert into feedme.recipes VALUES ('smoked-salmon-and-avocado-sushi', 'Smoked salmon & avocado sushi', 'Fancy making sushi at home? This simple recipe makes 32 healthy little bites, ideal for lunch or served as canapés', 'japanese');
insert into feedme.recipe_ingredients values('smoked-salmon-and-avocado-sushi','rice','300g sushi rice',300.0,'gram');
insert into feedme.recipe_ingredients values('smoked-salmon-and-avocado-sushi','wine','2 tbsp rice or white wine vinegar',2.0,'tbsp');
insert into feedme.recipe_ingredients values('smoked-salmon-and-avocado-sushi','caster sugar','1 tsp caster sugar',1.0,'tsp');
insert into feedme.recipe_ingredients values('smoked-salmon-and-avocado-sushi','avocado','1 large avocado',1.0,'unit');
insert into feedme.recipe_ingredients values('smoked-salmon-and-avocado-sushi','lemon','juice 0.5 lemon',NULL,'null');
insert into feedme.recipe_ingredients values('smoked-salmon-and-avocado-sushi','seaweed','4 sheets noriseaweed',4.0,'unit');
insert into feedme.recipe_ingredients values('smoked-salmon-and-avocado-sushi','smoked salmon','4 large slices smoked salmon',4.0,'unit');
insert into feedme.recipe_ingredients values('smoked-salmon-and-avocado-sushi','chives','1 bunch chives',1.0,'unit');
insert into feedme.recipe_ingredients values('smoked-salmon-and-avocado-sushi','soy sauce','sweet soy sauce(kecap manis), to serve',NULL,'null');
insert into feedme.recipe_steps values('smoked-salmon-and-avocado-sushi',0,0, 'Put the rice in a small pan with 600ml water. Bring to the boil and cook for 10 mins until the water is absorbed and the rice is tender. Stir through the vinegar and sugar, cover and cool.');
insert into feedme.recipe_steps values('smoked-salmon-and-avocado-sushi',1,1, 'Skin, stone and slice the avocado. Put in a bowl and squeeze over the lemon juice, turning the avocado to ensure the pieces are covered.');
insert into feedme.recipe_steps values('smoked-salmon-and-avocado-sushi',2,2, 'Divide the rice between the nori sheets and spread it out evenly, leaving a 1cm border at the top and bottom. Lay the salmon over the rice, followed by the chives and finally position the avocado across the centre.');
insert into feedme.recipe_steps values('smoked-salmon-and-avocado-sushi',3,3, 'Fold the bottom edge of the seaweed over the filling, then roll it up firmly. Dampen the top border with a little water to help it seal the roll. Repeat to make 4 rolls. At this stage, the rolls can be wrapped individually in cling film and chilled until ready to serve.');
insert into feedme.recipe_steps values('smoked-salmon-and-avocado-sushi',4,4, 'Using a serrated knife, cut each roll into 8 rounds. Serve with sweet soy sauce for dipping.');
insert into feedme.recipes VALUES ('walnut-date-and-honey-cake', 'Walnut, date & honey cake', 'A tea-time treat - packed with sweet honey. A great way to use up those ripe bananas too', 'turkish');
insert into feedme.recipe_ingredients values('walnut-date-and-honey-cake','self-raising flour','225g self-raising flour',225.0,'gram');
insert into feedme.recipe_ingredients values('walnut-date-and-honey-cake','cinnamon','0.5 tsp ground cinnamon',5.0,'tsp');
insert into feedme.recipe_ingredients values('walnut-date-and-honey-cake','butter','175g softened butter',175.0,'gram');
insert into feedme.recipe_ingredients values('walnut-date-and-honey-cake','sugar','100g light muscovado sugar',100.0,'gram');
insert into feedme.recipe_ingredients values('walnut-date-and-honey-cake','honey','3 tbsp clear honey',3.0,'tbsp');
insert into feedme.recipe_ingredients values('walnut-date-and-honey-cake','egg','2 eggs, beaten',2.0,'unit');
insert into feedme.recipe_ingredients values('walnut-date-and-honey-cake','banana','2 medium, ripe bananas, about 250g/9oz total weight in their skins',250.0,'gram');
insert into feedme.recipe_ingredients values('walnut-date-and-honey-cake','date','100g stoned dates',100.0,'gram');
insert into feedme.recipe_ingredients values('walnut-date-and-honey-cake','nut','50g pack walnut pieces',50.0,'gram');
insert into feedme.recipe_steps values('walnut-date-and-honey-cake',0,0, 'Preheat the oven to 160C/Gas 3/fan oven 140C. Line the base and long sides of a 900g/2lb loaf tin with greaseproof paper, buttering the tin and paper.');
insert into feedme.recipe_steps values('walnut-date-and-honey-cake',1,1, 'Tip the flour, cinnamon, butter, sugar, 2 tablespoons of the honey and the eggs into a large mixing bowl. Mash the bananas and chop the dates (kitchen scissors are easiest for this) and add to the bowl. Beat the mixture for 2-3 minutes, using a wooden spoon or hand-held mixer, until well blended.');
insert into feedme.recipe_steps values('walnut-date-and-honey-cake',2,2, 'Spoon into the prepared tin and level the top. Scatter the walnut pieces over. Bake for 1 hour, then lightly press the top – it will feel firm if cooked. If not, bake for a further 10 minutes.');
insert into feedme.recipe_steps values('walnut-date-and-honey-cake',3,3, 'Cool for 15 minutes, then lift out of the tin using the paper. When cold, drizzle the remaining honey over. Cut into thick slices.');
insert into feedme.recipes VALUES ('spiced-bulghar-wheat-with-roasted-peppers', 'Spiced bulgur wheat with roasted peppers', 'This filling vegetarian salad has the spicy flavours of cumin and warming cinnamon', 'moroccan');
insert into feedme.recipe_ingredients values('spiced-bulghar-wheat-with-roasted-peppers','bulgur wheat','200g bulgur wheat',200.0,'gram');
insert into feedme.recipe_ingredients values('spiced-bulghar-wheat-with-roasted-peppers','stock','250ml hot vegetable stock',250.0,'ml');
insert into feedme.recipe_ingredients values('spiced-bulghar-wheat-with-roasted-peppers','cumin','0.5 tsp Morroccan spice mix, or large pinch each ground cumin and ground cinnamon',5.0,'tsp');
insert into feedme.recipe_ingredients values('spiced-bulghar-wheat-with-roasted-peppers','lemon','zest 1 lemon, juice of 0.5 lemon',NULL,'null');
insert into feedme.recipe_ingredients values('spiced-bulghar-wheat-with-roasted-peppers','olive oil','1 tbsp olive oil',1.0,'tbsp');
insert into feedme.recipe_ingredients values('spiced-bulghar-wheat-with-roasted-peppers','red onion','0.5 red onion, finely sliced',0.0,'unit');
insert into feedme.recipe_ingredients values('spiced-bulghar-wheat-with-roasted-peppers','chickpea','400g can chickpea, drained',400.0,'gram');
insert into feedme.recipe_ingredients values('spiced-bulghar-wheat-with-roasted-peppers','pepper','210g jar roasted pepper, drained and torn into shreds',210.0,'gram');
insert into feedme.recipe_ingredients values('spiced-bulghar-wheat-with-roasted-peppers','coriander','small bunch coriander, leaves only',NULL,'null');
insert into feedme.recipe_steps values('spiced-bulghar-wheat-with-roasted-peppers',0,0, 'In a large microwave proof bowl, cover the bulgur wheat with stock. Cover with cling film and microwave on High for 4 mins. Set aside, covered; it will absorb the stock within another 5 mins. Make a dressing with spice mix, lemon zest and juice and olive oil, then season to taste. Add the onion, chickpeas, peppers and coriander, toss through the cooked bulgur wheat and serve warm or cold.');
insert into feedme.recipes VALUES ('thai-pumpkin-soup', 'Thai pumpkin soup', 'Make the most of autumn''s harvest with this warming, seasonal soup', 'thai');
insert into feedme.recipe_ingredients values('thai-pumpkin-soup','squash','10.5 kg pumpkinor squash, peeled and roughly chopped',10.0,'unit');
insert into feedme.recipe_ingredients values('thai-pumpkin-soup','sunflower oil','4 tsp sunflower oil',4.0,'tsp');
insert into feedme.recipe_ingredients values('thai-pumpkin-soup','onion','1 onion, sliced',1.0,'unit');
insert into feedme.recipe_ingredients values('thai-pumpkin-soup','ginger','1 tbsp grated ginger',1.0,'tbsp');
insert into feedme.recipe_ingredients values('thai-pumpkin-soup','lemongrass','1 lemongrass, bashed a little',1.0,'unit');
insert into feedme.recipe_ingredients values('thai-pumpkin-soup','curry paste','3-4 tbsp Thai red curry paste',4.0,'tbsp');
insert into feedme.recipe_ingredients values('thai-pumpkin-soup','nut','400ml can coconut milk',400.0,'ml');
insert into feedme.recipe_ingredients values('thai-pumpkin-soup','stock','850ml vegetable stock',850.0,'ml');
insert into feedme.recipe_ingredients values('thai-pumpkin-soup','seasoning','limejuice and sugar, for seasoning',NULL,'null');
insert into feedme.recipe_ingredients values('thai-pumpkin-soup','chilli','1 red chilli, sliced, to serve (optional)',1.0,'unit');
insert into feedme.recipe_steps values('thai-pumpkin-soup',0,0, 'Heat oven to 200C/180C fan/gas 6. Toss the pumpkin or squash in a roasting tin with half the oil and seasoning, then roast for 30 mins until golden and tender.');
insert into feedme.recipe_steps values('thai-pumpkin-soup',1,1, 'Meanwhile, put the remaining oil in a pan with the onion, ginger and lemongrass. Gently cook for 8-10 mins until softened. Stir in the curry paste for 1 min, followed by the roasted pumpkin, all but 3 tbsp of the coconut milk and the stock. Bring to a simmer, cook for 5 mins, then fish out the lemongrass. Cool for a few mins, then whizz until smooth with a hand blender, or in a large blender in batches. Return to the pan to heat through, seasoning with salt, pepper, lime juice and sugar, if it needs it. Serve drizzled with the remaining coconut milk and scattered with chilli, if you like.');
insert into feedme.recipes VALUES ('moroccan-chicken-couscous-with-dates', 'Moroccan chicken couscous with dates', 'Wholewheat grains keep you fuller for longer - flavour with dates, lemon, cumin and cinnamon', 'moroccan');
insert into feedme.recipe_ingredients values('moroccan-chicken-couscous-with-dates','olive oil','2 tbsp olive oil',2.0,'tbsp');
insert into feedme.recipe_ingredients values('moroccan-chicken-couscous-with-dates','chicken','500g skinless chicken thigh',500.0,'gram');
insert into feedme.recipe_ingredients values('moroccan-chicken-couscous-with-dates','couscous','140g wholewheat couscous',140.0,'gram');
insert into feedme.recipe_ingredients values('moroccan-chicken-couscous-with-dates','onion','2 onions, sliced',2.0,'unit');
insert into feedme.recipe_ingredients values('moroccan-chicken-couscous-with-dates','garlic','4 garlic cloves, sliced',4.0,'unit');
insert into feedme.recipe_ingredients values('moroccan-chicken-couscous-with-dates','lemon','zest 2 lemons, juice of 1',NULL,'null');
insert into feedme.recipe_ingredients values('moroccan-chicken-couscous-with-dates','stock','350ml chicken stock',350.0,'ml');
insert into feedme.recipe_ingredients values('moroccan-chicken-couscous-with-dates','cumin','1 tsp ground cumin',1.0,'tsp');
insert into feedme.recipe_ingredients values('moroccan-chicken-couscous-with-dates','cinnamon','1 tsp ground cinnamon',1.0,'tsp');
insert into feedme.recipe_ingredients values('moroccan-chicken-couscous-with-dates','date','6 large dates, pitted and chopped (Medjool are nice)',6.0,'unit');
insert into feedme.recipe_ingredients values('moroccan-chicken-couscous-with-dates','almond','50g flaked almond',50.0,'gram');
insert into feedme.recipe_ingredients values('moroccan-chicken-couscous-with-dates','parsley','small bunch parsley, chopped',NULL,'null');
insert into feedme.recipe_steps values('moroccan-chicken-couscous-with-dates',0,0, 'Heat oven to 180C/160C fan/ gas 4. Heat 1 tbsp of the oil in a large frying pan over a high heat. Season the chicken, then cook for 10 mins or until browned on both sides. Remove and put in a large baking dish with the dry couscous.');
insert into feedme.recipe_steps values('moroccan-chicken-couscous-with-dates',1,1, 'Heat the remaining oil in the pan, add the onions and garlic, and cook for 5 mins until softened. Add the zest and juice of 1 lemon along with the stock, cumin, cinnamon and dates, then bring to the boil.');
insert into feedme.recipe_steps values('moroccan-chicken-couscous-with-dates',2,2, 'Pour the date mixture over the chicken and couscous, sprinkle over the almonds, cover with foil and bake for 20 mins. Serve sprinkled with the remaining zest and parsley.');
insert into feedme.recipes VALUES ('goan-prawn-and-coconut-curry-with-cumin-rice', 'Goan prawn & coconut curry with cumin rice', 'Spice up your mid-week meals with a curry in a hurry - throw in a handful of spices with shellfish, tomatoes, and spinach', 'indian');
insert into feedme.recipe_ingredients values('goan-prawn-and-coconut-curry-with-cumin-rice','sunflower oil','1 tbsp sunflower oil',1.0,'tbsp');
insert into feedme.recipe_ingredients values('goan-prawn-and-coconut-curry-with-cumin-rice','onion','1 onion, thinly sliced',1.0,'unit');
insert into feedme.recipe_ingredients values('goan-prawn-and-coconut-curry-with-cumin-rice','ginger','1 tbsp freshly grated ginger',1.0,'tbsp');
insert into feedme.recipe_ingredients values('goan-prawn-and-coconut-curry-with-cumin-rice','garlic','2 garlic cloves, crushed',2.0,'unit');
insert into feedme.recipe_ingredients values('goan-prawn-and-coconut-curry-with-cumin-rice','chilli','1 red chilli, deseeded and sliced',1.0,'unit');
insert into feedme.recipe_ingredients values('goan-prawn-and-coconut-curry-with-cumin-rice','turmeric','0.5 tsp turmeric',5.0,'tsp');
insert into feedme.recipe_ingredients values('goan-prawn-and-coconut-curry-with-cumin-rice','chilli','0.5 tsp chilli powder',5.0,'tsp');
insert into feedme.recipe_ingredients values('goan-prawn-and-coconut-curry-with-cumin-rice','coriander','1 tsp ground coriander',1.0,'tsp');
insert into feedme.recipe_ingredients values('goan-prawn-and-coconut-curry-with-cumin-rice','curry leaves','10 curry leaves',10.0,'unit');
insert into feedme.recipe_ingredients values('goan-prawn-and-coconut-curry-with-cumin-rice','potato','1 large potato, diced',1.0,'unit');
insert into feedme.recipe_ingredients values('goan-prawn-and-coconut-curry-with-cumin-rice','fat','400ml can half-fat coconut milk',400.0,'ml');
insert into feedme.recipe_ingredients values('goan-prawn-and-coconut-curry-with-cumin-rice','cherry','8 cherry tomatoes, halved',8.0,'unit');
insert into feedme.recipe_ingredients values('goan-prawn-and-coconut-curry-with-cumin-rice','spinach','handful baby spinach',NULL,'null');
insert into feedme.recipe_ingredients values('goan-prawn-and-coconut-curry-with-cumin-rice','prawn','200g pack raw peeled prawn',200.0,'gram');
insert into feedme.recipe_steps values('goan-prawn-and-coconut-curry-with-cumin-rice',0,0, 'Heat the oil and fry the onion, ginger, garlic and chilli for 5 mins until starting to soften. Add the spices, curry leaves and potato, then cook for 1 min more. Stir in the coconut milk and tomatoes, cover and leave to simmer for 10 mins until the potato is tender.');
insert into feedme.recipe_steps values('goan-prawn-and-coconut-curry-with-cumin-rice',1,1, 'Add the spinach and prawns. Cook for 1 min more until the spinach wilts and the prawns turn pink.');
insert into feedme.recipe_steps values('goan-prawn-and-coconut-curry-with-cumin-rice',2,2, 'Meanwhile, make the rice. Tip the cumin into a pan and toast over a dry heat for 30 secs. Add the rice, salt to taste and 400ml water, then cover and cook for 8-10 mins until the rice is tender and the water has been absorbed. Serve with the curry.');
insert into feedme.recipes VALUES ('watermelon-and-feta-salad-with-crispbread', 'Watermelon & feta salad with crispbread', 'A cool, refreshing combo for warm summer evenings', 'greek');
insert into feedme.recipe_ingredients values('watermelon-and-feta-salad-with-crispbread','melon','0.5 a watermelon(about 10.5 kg), peeled, deseeded and cut into chunks',0.0,'unit');
insert into feedme.recipe_ingredients values('watermelon-and-feta-salad-with-crispbread','cheese','200g block feta cheese, cubed',200.0,'gram');
insert into feedme.recipe_ingredients values('watermelon-and-feta-salad-with-crispbread','olive','large handful black olives',NULL,'null');
insert into feedme.recipe_ingredients values('watermelon-and-feta-salad-with-crispbread','mint','handful flat-leaf parsleyand mint leaves, roughly chopped',NULL,'null');
insert into feedme.recipe_ingredients values('watermelon-and-feta-salad-with-crispbread','red onion','1 red onion, finely sliced into rings',1.0,'unit');
insert into feedme.recipe_ingredients values('watermelon-and-feta-salad-with-crispbread','balsamic vinegar','olive oiland balsamic vinegar, to serve',NULL,'null');
insert into feedme.recipe_steps values('watermelon-and-feta-salad-with-crispbread',0,0, 'Make up the bread according to pack instructions with 1 tbsp olive oil. Leave to rise in a warm place for about 1 hr until doubled in size. Heat oven to 220C/200C fan/gas 7. Knock the bread back and divide into 6 pieces. On a floured surface, roll the breads out as thinly as possible, then transfer to baking trays. Brush with the egg white and scatter with the mixed seeds. Bake for about 15 mins until crisp and brown; if they puff up, even better. You may need to do this in batches. The breads can be made the previous day and kept in an airtight container.');
insert into feedme.recipe_steps values('watermelon-and-feta-salad-with-crispbread',1,1, 'In a large serving bowl, lightly toss the melon with the feta and olives. Scatter over the herbs and onion rings, then serve with the olive oil and balsamic for drizzling over. Serve the pile of crispbreads on the side for breaking up and using to scoop the salad.');
insert into feedme.recipes VALUES ('smoked-salmon-taramasalata', 'Smoked salmon taramasalata', 'This Greek dip is an original way to use up leftover smoked salmon and works well on a sharing mezze platter', 'greek');
insert into feedme.recipe_ingredients values('smoked-salmon-taramasalata','smoked salmon','100g smoked salmon',100.0,'gram');
insert into feedme.recipe_ingredients values('smoked-salmon-taramasalata','cheese','200g low-fat soft cheese',200.0,'gram');
insert into feedme.recipe_ingredients values('smoked-salmon-taramasalata','crème fraîche','100g crème fraîche',100.0,'gram');
insert into feedme.recipe_ingredients values('smoked-salmon-taramasalata','lemon','juice 1 lemon',NULL,'null');
insert into feedme.recipe_ingredients values('smoked-salmon-taramasalata','olive oil','drizzle of olive oil, to serve',NULL,'null');
insert into feedme.recipe_ingredients values('smoked-salmon-taramasalata','pitta bread','black kalamata oliveand toasted pitta bread, to serve',NULL,'null');
insert into feedme.recipe_steps values('smoked-salmon-taramasalata',0,0, 'Tip the salmon, soft cheese, crème fraîche and lemon juice into a food processor. Blitz everything until smooth, then stir in a very large pinch cracked pepper. Spoon the mix into a large bowl, drizzle with olive oil and serve with toasted pitta bread and olives.');
insert into feedme.recipes VALUES ('avocado-prawns-in-wasabi-dressing', 'Avocado prawns in wasabi dressing', 'Wasabi, the paste that adds a hot peppery bite to sushi, peps up this creamy combo of avocado and prawns', 'japanese');
insert into feedme.recipe_steps values('avocado-prawns-in-wasabi-dressing',0,0, 'In a large bowl, mix dressing ingredients until blended. Season with salt, then set aside.');
insert into feedme.recipe_steps values('avocado-prawns-in-wasabi-dressing',1,1, 'To serve, halve and stone the avocados. Dice the flesh while it’s still in the skin then scoop into a bowl. Add the prawns and toss in the dressing. Line six small serving bowls or tumblers with two or three chicory leaves. Pile in avocado and prawn mixture. Top with watercress and lime wedges. Serve with prawn crackers.');
insert into feedme.recipes VALUES ('pumpkin-pie', 'Pumpkin pie', 'Fill a sweet shortcrust pastry tart case with lightly spiced squash to make a traditional American treat', 'american');
insert into feedme.recipe_ingredients values('pumpkin-pie','butternut squash','750g/1lb 10oz pumpkinor butternut squash, peeled, deseeded and cut into chunks',750.0,'gram');
insert into feedme.recipe_ingredients values('pumpkin-pie','pastry','350g sweet shortcrust pastry',350.0,'gram');
insert into feedme.recipe_ingredients values('pumpkin-pie','flour','plain flour, for dusting',NULL,'null');
insert into feedme.recipe_ingredients values('pumpkin-pie','caster sugar','140g caster sugar',140.0,'gram');
insert into feedme.recipe_ingredients values('pumpkin-pie','salt','0.5 tsp salt',5.0,'tsp');
insert into feedme.recipe_ingredients values('pumpkin-pie','nutmeg','0.5 tsp fresh nutmeg, grated',5.0,'tsp');
insert into feedme.recipe_ingredients values('pumpkin-pie','cinnamon','1 tsp cinnamon',1.0,'tsp');
insert into feedme.recipe_ingredients values('pumpkin-pie','egg','2 eggs, beaten',2.0,'unit');
insert into feedme.recipe_ingredients values('pumpkin-pie','butter','25g butter, melted',25.0,'gram');
insert into feedme.recipe_ingredients values('pumpkin-pie','milk','175ml milk',175.0,'ml');
insert into feedme.recipe_ingredients values('pumpkin-pie','icing','1 tbsp icing sugar',1.0,'tbsp');
insert into feedme.recipe_steps values('pumpkin-pie',0,0, 'Place the pumpkin in a large saucepan, cover with water and bring to the boil. Cover with a lid and simmer for 15 mins or until tender. Drain pumpkin; let cool.');
insert into feedme.recipe_steps values('pumpkin-pie',1,1, 'Heat oven to 180C/160C fan/gas 4. Roll out the pastry on a lightly floured surface and use it to line a 22cm loose-bottomed tart tin. Chill for 15 mins. Line the pastry with baking parchment and baking beans, then bake for 15 mins. Remove the beans and paper, and cook for a further 10 mins until the base is pale golden and biscuity. Remove from the oven and allow to cool slightly.');
insert into feedme.recipe_steps values('pumpkin-pie',2,2, 'Increase oven to 220C/200C fan/gas 7. Push the cooled pumpkin through a sieve into a large bowl. In a separate bowl, combine the sugar, salt, nutmeg and half the cinnamon. Mix in the beaten eggs, melted butter and milk, then add to the pumpkin purée and stir to combine. Pour into the tart shell and cook for 10 mins, then reduce the temperature to 180C/160C fan/gas 4. Continue to bake for 35-40 mins until the filling has just set.');
insert into feedme.recipe_steps values('pumpkin-pie',3,3, 'Leave to cool, then remove the pie from the tin. Mix the remaining cinnamon with the icing sugar and dust over the pie. Serve chilled.');
insert into feedme.recipes VALUES ('caesar-salad-with-crispy-chicken', 'Caesar salad with crispy chicken', 'Have chicken left over from supper? Use it up in a crisp salad with creamy Caesar dressing', 'american');
insert into feedme.recipe_steps values('caesar-salad-with-crispy-chicken',0,0, 'In a large bowl, mix the mayonnaise, lemon juice, Worcestershire sauce, grated Parmesan and garlic, then season. When ready to eat, add the chicken slices, croutons and lettuce. Mix well and sprinkle with extra Parmesan, if you like.');
insert into feedme.recipes VALUES ('noodle-bowl-salad', 'Noodle bowl salad', 'Fresh and healthy salad for picnic fare', 'vietnamese');
insert into feedme.recipe_ingredients values('noodle-bowl-salad','rice','0.5 x 250g pack medium rice noodles',250.0,'gram');
insert into feedme.recipe_ingredients values('noodle-bowl-salad','peas','200g sugar snap peas, sliced lengthways',200.0,'gram');
insert into feedme.recipe_ingredients values('noodle-bowl-salad','sesame oil','2 tsp sesame oil',2.0,'tsp');
insert into feedme.recipe_ingredients values('noodle-bowl-salad','spring onion','4 spring onions, thinly sliced diagonally',4.0,'unit');
insert into feedme.recipe_ingredients values('noodle-bowl-salad','coriander','pack coriander, roughly torn',NULL,'null');
insert into feedme.recipe_ingredients values('noodle-bowl-salad','salmon','150g pack hot smoked flaked salmon',150.0,'gram');
insert into feedme.recipe_ingredients values('noodle-bowl-salad','bread roll','pretzel or bread roll, to serve',NULL,'null');
insert into feedme.recipe_steps values('noodle-bowl-salad',0,0, 'Drop the noodles into a large bowl, then pour over enough boiling water to cover. Tip in the sliced sugar snaps and leave for 4 minutes. Drain in a colander, put under cold running water to cool off, then drain again. Tip into a bowl and toss in the sesame oil.');
insert into feedme.recipe_steps values('noodle-bowl-salad',1,1, 'Mix the dressing ingredients. Pile spring onions, coriander and salmon onto the noodles, pour over the dressing and toss everything together. Serve with a pretzel or bread roll.');
insert into feedme.recipes VALUES ('brie-wrapped-in-prosciutto-and-brioche', 'Brie wrapped in prosciutto & brioche', 'This decadent bake from Paul Hollywood combines slightly sweet French bread with a creamy ham and cheese filling', 'french');
insert into feedme.recipe_ingredients values('brie-wrapped-in-prosciutto-and-brioche','flour','375g strong white bread flour',375.0,'gram');
insert into feedme.recipe_ingredients values('brie-wrapped-in-prosciutto-and-brioche','caster sugar','50g caster sugar',50.0,'gram');
insert into feedme.recipe_ingredients values('brie-wrapped-in-prosciutto-and-brioche','yeast','7g sachet fast-action dried yeast',7.0,'gram');
insert into feedme.recipe_ingredients values('brie-wrapped-in-prosciutto-and-brioche','milk','75ml milk',75.0,'ml');
insert into feedme.recipe_ingredients values('brie-wrapped-in-prosciutto-and-brioche','egg','3 large eggs, plus 2 beaten eggs for glazing',3.0,'unit');
insert into feedme.recipe_ingredients values('brie-wrapped-in-prosciutto-and-brioche','butter','185g unsalted butter, softened',185.0,'gram');
insert into feedme.recipe_steps values('brie-wrapped-in-prosciutto-and-brioche',0,0, 'Mix the flour, 1 tsp salt, caster sugar, yeast, milk and eggs together in a mixer using the dough attachment for 5 mins until the dough is smooth. Add the butter and mix for a further 4 mins on medium speed. Scrape the dough bowl and mix again for 1 min. Place the dough in a container, cover with cling film and leave in the fridge for at least 6 hrs before using.');
insert into feedme.recipe_steps values('brie-wrapped-in-prosciutto-and-brioche',1,1, 'Wrap the brie in the prosciutto and set aside. Turn out the dough onto a lightly floured surface. Roll into a 25cm circle. Place the wrapped brie in the middle of the circle and fold the edges in neatly. Put the parcel onto a baking tray lined with baking parchment and brush with beaten egg. Chill in the fridge for 30 mins, then brush again with beaten egg and chill for a further 30 mins. Leave to rise for 1 hr at room temperature. Heat oven to 200C/180C fan/gas 6, then bake for 22 mins. Serve warm.');
insert into feedme.recipes VALUES ('moroccan-chicken-onepot', 'Moroccan chicken one-pot', 'You don''t need a clay pot to whip up a fragrant, North Africa tagine - throw it all in a casserole dish and top with feta, mint and lemon', 'moroccan');
insert into feedme.recipe_ingredients values('moroccan-chicken-onepot','chicken','4 boneless, skinless chicken breasts',4.0,'unit');
insert into feedme.recipe_ingredients values('moroccan-chicken-onepot','olive oil','3 tbsp olive oil',3.0,'tbsp');
insert into feedme.recipe_ingredients values('moroccan-chicken-onepot','onion','2 onions, 1 roughly chopped, 1 sliced',2.0,'unit');
insert into feedme.recipe_ingredients values('moroccan-chicken-onepot','tomato','100g tomato',100.0,'gram');
insert into feedme.recipe_ingredients values('moroccan-chicken-onepot','ginger','100g ginger, roughly chopped',100.0,'gram');
insert into feedme.recipe_ingredients values('moroccan-chicken-onepot','garlic','3 garlic cloves',3.0,'unit');
insert into feedme.recipe_ingredients values('moroccan-chicken-onepot','turmeric','1 tsp turmeric',1.0,'tsp');
insert into feedme.recipe_ingredients values('moroccan-chicken-onepot','cumin','1 tbsp each ground cumin, coriander and cinnamon',1.0,'tbsp');
insert into feedme.recipe_ingredients values('moroccan-chicken-onepot','butternut squash','1 large butternut squash, deseeded and cut into big chunks',1.0,'unit');
insert into feedme.recipe_ingredients values('moroccan-chicken-onepot','stock','600ml chicken stock',600.0,'ml');
insert into feedme.recipe_ingredients values('moroccan-chicken-onepot','brown sugar','2 tbsp brown sugar',2.0,'tbsp');
insert into feedme.recipe_ingredients values('moroccan-chicken-onepot','wine','2 tbsp red wine vinegar',2.0,'tbsp');
insert into feedme.recipe_ingredients values('moroccan-chicken-onepot','cherry','100g dried cherry',100.0,'gram');
insert into feedme.recipe_steps values('moroccan-chicken-onepot',0,0, 'Season the chicken. Heat 2 tbsp of the oil in a flameproof dish, then brown the chicken on all sides. Remove the chicken to a plate. Whizz the chopped onion, tomatoes, ginger and garlic into a rough paste. Fry the sliced onion in the remaining oil in the dish until softened, then add the spices and fry for 1 min more until fragrant. Add the paste and fry for another few mins to soften.');
insert into feedme.recipe_steps values('moroccan-chicken-onepot',1,1, 'Return the chicken to the dish with the squash, stock, sugar and vinegar. Bring to a simmer, then cook for 30 mins until the chicken is cooked through. Lift the chicken out and stir in the cherries, then continue simmering the sauce to thicken while you shred the chicken into bite-sized chunks. Stir the chicken back into the sauce and season.');
insert into feedme.recipe_steps values('moroccan-chicken-onepot',2,2, 'Mix the red onion, lemon zest, mint and feta. Scatter over the dish, then serve with some couscous and yogurt.');
insert into feedme.recipes VALUES ('wrapyourown-spring-rolls', 'Wrap-your-own spring rolls', 'A superhealthy recipe that''s ideal for cooking with young children', 'chinese');
insert into feedme.recipe_ingredients values('wrapyourown-spring-rolls','rice','300g pack cooked rice noodlesfrom the chiller cabinet (see tips)',300.0,'gram');
insert into feedme.recipe_ingredients values('wrapyourown-spring-rolls','spring onion','about 400g/14oz mixed vegetables, thinly sliced and put in separate bowls, such as red peppers, beansprouts, carrots, shredded Chinese leaf cabbage, spring onions',400.0,'gram');
insert into feedme.recipe_ingredients values('wrapyourown-spring-rolls','prawn','140g cooked prawns',140.0,'gram');
insert into feedme.recipe_ingredients values('wrapyourown-spring-rolls','duck','100g cooked chickenor duck, shredded',100.0,'gram');
insert into feedme.recipe_ingredients values('wrapyourown-spring-rolls','garlic','2 garlic cloves, finely chopped',2.0,'unit');
insert into feedme.recipe_ingredients values('wrapyourown-spring-rolls','ginger','small piece ginger, finely chopped',NULL,'null');
insert into feedme.recipe_ingredients values('wrapyourown-spring-rolls','soy sauce','splash light soy sauce',NULL,'null');
insert into feedme.recipe_ingredients values('wrapyourown-spring-rolls','five-spice powder','Chinese five-spice powder, for sprinkling',NULL,'null');
insert into feedme.recipe_ingredients values('wrapyourown-spring-rolls','pastry','8-10 sheets of brik or filo pastry(see tips)',8.0,'unit');
insert into feedme.recipe_ingredients values('wrapyourown-spring-rolls','egg','1 egg, beaten',1.0,'unit');
insert into feedme.recipe_ingredients values('wrapyourown-spring-rolls','sesame seeds','sesame seeds, for sprinkling if you want',NULL,'null');
insert into feedme.recipe_steps values('wrapyourown-spring-rolls',0,0, 'Heat oven to 200C/180C fan/gas 6. Before you get the kids cooking, put the noodles, vegetables, prawns and chicken in individual bowls for everyone to help themselves. Wash hands, put aprons on, sit the kids down and give them their own mixing bowl and spoon. Let them choose which ingredients they want (noodles are essential) in their rolls and if they want to graze as they choose, that’s fine – all the ingredients are cooked or can be eaten raw. Add a bit of garlic and ginger, a tiny dash of soy and sprinkling of five-spice to each bowl and let them mix everything together.');
insert into feedme.recipe_steps values('wrapyourown-spring-rolls',1,1, 'Push the bowl aside and lay a sheet of pastry in front of each child. Ask them to spoon the filling down one side of each sheet then give them the beaten egg and a brush so they can brush around the edges. Then help them to roll them up neatly by folding both sides over the filling, then rolling them up.');
insert into feedme.recipe_steps values('wrapyourown-spring-rolls',2,2, 'Lift the spring rolls onto a baking tray, seam side down, brush with a little more egg and sprinkle with sesame seeds, if you want. Try to remember which child made which roll to save any arguments at the end! Bake the rolls for 20-25 mins or until golden.');
insert into feedme.recipe_steps values('wrapyourown-spring-rolls',3,3, 'While the rolls are in the oven, make the dipping sauce. Get the kids to mix all the ingredients together until the sugar has dissolved. When the spring rolls are golden and crisp, remove from the oven. Leave until cool enough to handle, cut into pieces for smaller kids, then let them eat, dipping the rolls into the sauce.');
insert into feedme.recipes VALUES ('mediterranean-chicken-tray-bake', 'Mediterranean chicken tray bake', 'A colourful all-in-one bake bursting with summer flavour', 'mediterranean');
insert into feedme.recipe_ingredients values('mediterranean-chicken-tray-bake','pepper','2 red peppers, deseeded and cut into chunks',2.0,'unit');
insert into feedme.recipe_ingredients values('mediterranean-chicken-tray-bake','red onion','1 red onion, cut into wedges',1.0,'unit');
insert into feedme.recipe_ingredients values('mediterranean-chicken-tray-bake','olive oil','2 tsp olive oil',2.0,'tsp');
insert into feedme.recipe_ingredients values('mediterranean-chicken-tray-bake','chicken','4 skin-on chicken breasts',4.0,'unit');
insert into feedme.recipe_ingredients values('mediterranean-chicken-tray-bake','garlic','0.5 x 150g pack full-fat garlic & herb soft cheese',150.0,'gram');
insert into feedme.recipe_ingredients values('mediterranean-chicken-tray-bake','cherry','200g pack cherry tomatoes',200.0,'gram');
insert into feedme.recipe_ingredients values('mediterranean-chicken-tray-bake','olive','handful black olives',NULL,'null');
insert into feedme.recipe_steps values('mediterranean-chicken-tray-bake',0,0, 'Heat oven to 200C/180C fan/gas 6. Mix the peppers and onion on a big baking tray with half the oil. Transfer to the oven and cook on the top shelf for 10 mins.');
insert into feedme.recipe_steps values('mediterranean-chicken-tray-bake',1,1, 'Meanwhile, carefully make a pocket between the skin and the flesh of each chicken breast, but don’t pull off the skin completely. Push equal amounts of cheese under the skin, smooth the skin back down, brush it with the rest of the oil, season, then add to the tray along with the tomatoes and olives. Return to the oven and cook for 25-30 mins more until the chicken is golden and cooked. Serve with baked potatoes, if you like.');
insert into feedme.recipes VALUES ('chorizo-and-chickpea-soup', 'Chorizo & chickpea soup', 'Sizzling Chorizo & chickpea soup', 'spanish');
insert into feedme.recipe_ingredients values('chorizo-and-chickpea-soup','tomato','400g can chopped tomato',400.0,'gram');
insert into feedme.recipe_ingredients values('chorizo-and-chickpea-soup','chorizo','110g pack of chorizo sausage (unsliced)',110.0,'gram');
insert into feedme.recipe_ingredients values('chorizo-and-chickpea-soup','savoy cabbage','140g wedge Savoy cabbage',140.0,'gram');
insert into feedme.recipe_ingredients values('chorizo-and-chickpea-soup','chilli','sprinkling dried chilli flakes',NULL,'null');
insert into feedme.recipe_ingredients values('chorizo-and-chickpea-soup','chickpea','410g can chickpea, drained and rinsed',410.0,'gram');
insert into feedme.recipe_ingredients values('chorizo-and-chickpea-soup','stock','1 chicken or vegetable stock cube',1.0,'unit');
insert into feedme.recipe_ingredients values('chorizo-and-chickpea-soup','garlic','crusty bread or garlic bread, to serve',NULL,'null');
insert into feedme.recipe_steps values('chorizo-and-chickpea-soup',0,0, 'Put a medium pan on the heat and tip in the tomatoes, followed by a can of water. While the tomatoes are heating, quickly chop the chorizo into chunky pieces (removing any skin) and shred the cabbage.');
insert into feedme.recipe_steps values('chorizo-and-chickpea-soup',1,1, 'Pile the chorizo and cabbage into the pan with the chilli flakes and chickpeas, then crumble in the stock cube. Stir well, cover and leave to bubble over a high heat for 6 mins or until the cabbage is just tender. Ladle into bowls and eat with crusty or garlic bread.');
insert into feedme.recipes VALUES ('chicken-soba-noodles', 'Chicken soba noodles', 'Quick and easy to prepare, whether you''re packing them up for work or grabbing a bite at home', 'japanese');
insert into feedme.recipe_ingredients values('chicken-soba-noodles','noodles','85g bundle soba or buckwheatnoodles',85.0,'gram');
insert into feedme.recipe_ingredients values('chicken-soba-noodles','sesame oil','drizzle of sesame oil',NULL,'null');
insert into feedme.recipe_ingredients values('chicken-soba-noodles','mangetout','8 mangetout',8.0,'unit');
insert into feedme.recipe_ingredients values('chicken-soba-noodles','carrot','1 small carrot',1.0,'unit');
insert into feedme.recipe_ingredients values('chicken-soba-noodles','chilli','0.5 red chilli',0.0,'unit');
insert into feedme.recipe_ingredients values('chicken-soba-noodles','sesame seeds','1 tbsp toasted sesame seeds',1.0,'tbsp');
insert into feedme.recipe_ingredients values('chicken-soba-noodles','chicken','handful shredded cooked chicken',NULL,'null');
insert into feedme.recipe_ingredients values('chicken-soba-noodles','soy sauce','soy sauce',NULL,'null');
insert into feedme.recipe_steps values('chicken-soba-noodles',0,0, 'Cook the noodles, drain well, then toss with a drizzle sesame oil.');
insert into feedme.recipe_steps values('chicken-soba-noodles',1,1, 'Finely slice the mangetout, cut the carrot into matchsticks and deseed and slice the red chilli.');
insert into feedme.recipe_steps values('chicken-soba-noodles',2,2, 'Add to the noodles with the toasted sesame seeds and some shredded cooked chicken, if you have it. Pack with a small portion of soy sauce.');
insert into feedme.recipes VALUES ('spicy-falafels', 'Spicy falafels', 'Cheap and dead easy to make - stuff them into pittas with salad, or serve with couscous and hummus', 'turkish');
insert into feedme.recipe_ingredients values('spicy-falafels','vegetable oil','2 tbsp sunflower or vegetable oil',2.0,'tbsp');
insert into feedme.recipe_ingredients values('spicy-falafels','onion','1 small onion, finely chopped',1.0,'unit');
insert into feedme.recipe_ingredients values('spicy-falafels','garlic','1 garlic clove, crushed',1.0,'unit');
insert into feedme.recipe_ingredients values('spicy-falafels','chickpea','400g can chickpea, washed and drained',400.0,'gram');
insert into feedme.recipe_ingredients values('spicy-falafels','cumin','1 tsp ground cumin',1.0,'tsp');
insert into feedme.recipe_ingredients values('spicy-falafels','cumin','1 tsp ground coriander (or use more cumin)',1.0,'tsp');
insert into feedme.recipe_ingredients values('spicy-falafels','parsley','handful parsley, chopped, or 1 tsp dried mixed herbs',1.0,'tsp');
insert into feedme.recipe_ingredients values('spicy-falafels','egg','1 egg, beaten',1.0,'unit');
insert into feedme.recipe_steps values('spicy-falafels',0,0, 'Heat 1 tbsp oil in a large pan, then fry the onion and garlic over a low heat for 5 mins until softened. Tip into a large mixing bowl with the chickpeas and spices, then mash together with a fork or potato masher until the chickpeas are totally broken down. Stir in the parsley or dried herbs, with seasoning to taste. Add the egg, then squish the mixture together with your hands.');
insert into feedme.recipe_steps values('spicy-falafels',1,1, 'Mould the mix into 6 balls, then flatten into patties. Heat the remaining oil in the pan, then fry the falafels on a medium heat for 3 mins on each side, until golden brown and firm. Serve hot or cold with couscous, pitta bread or salad.');
insert into feedme.recipes VALUES ('chinese-roast-duck', 'Chinese roast duck', 'John Torode''s whole crispy duck with oriental marinade makes for an impressive and alternative Christmas centrepiece', 'chinese');
insert into feedme.recipe_ingredients values('chinese-roast-duck','duck','2 ducks',2.0,'unit');
insert into feedme.recipe_ingredients values('chinese-roast-duck','caster sugar','140g caster sugar',140.0,'gram');
insert into feedme.recipe_ingredients values('chinese-roast-duck','seeds','6 star aniseseeds',6.0,'unit');
insert into feedme.recipe_ingredients values('chinese-roast-duck','ginger','large piece ginger, sliced',NULL,'null');
insert into feedme.recipe_ingredients values('chinese-roast-duck','spring onion','small bunch spring onions, roughly chopped',NULL,'null');
insert into feedme.recipe_ingredients values('chinese-roast-duck','syrup','2 tbsp maltsyrup or maltose (available in most Chinese supermarkets)',2.0,'tbsp');
insert into feedme.recipe_ingredients values('chinese-roast-duck','wine','3 tbsp red wine vinegar',3.0,'tbsp');
insert into feedme.recipe_steps values('chinese-roast-duck',0,0, 'The day before, soak 4 wooden skewers in water for 20 mins. Wash the ducks inside and out with cold water, drain and pat dry with kitchen paper. Mix together the sugar, star anise, ginger, spring onions and a few good pinches of salt, then use this to fill the cavities. Close the cavities with wooden skewers and set aside.');
insert into feedme.recipe_steps values('chinese-roast-duck',1,1, 'In a small bowl, mix the maltose and 2 tbsp of the vinegar. Add the remaining vinegar to a jug of boiling water and pour over the ducks. (The boiling water opens up the pores, while the vinegar helps to strip some of the waxiness from the skin, so it will be more receptive to the maltose, which adds sweetness and a lacquered caramel colour.) Smear the maltose mixture over the ducks, then place them in a large plastic container and put in the fridge overnight, uncovered.');
insert into feedme.recipe_steps values('chinese-roast-duck',2,2, 'To cook, heat oven to 220C/200C fan/ gas 7. (Fan ovens are particularly effective for roasting duck.) Put a little water in the bottom of a large roasting tin, place the ducks on a rack over the top and cover with foil. Roast for about 45 mins. Take off the foil and roast for another 45 mins – the duck must be well done, there is no such thing as a rare Chinese roast duck! Take the duck out of the oven and let it rest for a good 20 mins before carving.');
insert into feedme.recipes VALUES ('st-clements-pie', 'St Clement’s pie', 'A very British version of Key lime pie - an indulgent, creamy tart with tangy oranges and lemons', 'british');
insert into feedme.recipe_steps values('st-clements-pie',0,0, 'Heat oven to 180C/160C fan/gas 4. Sit a fluted 20cm round loose-bottomed tin (about 5cm deep, or a slightly shallower 22cm tin) on a baking sheet. Break the biscuits into a big bowl, or double-bag them in food bags, and bash to big crumbs with the end of a rolling pin or small saucepan. Add the cornflakes and bash a bit more to crumbs. Mix with the melted butter and sugar and press into the base and sides of the tin. Bake for 15 mins, then remove and reduce oven temperature to 160C/140C fan/gas 3.');
insert into feedme.recipe_steps values('st-clements-pie',1,1, 'Whisk egg and yolks in a big bowl until pale and frothy. Whisk in the condensed milk, followed by the zests and juices. Pour in the tin and bake for 20 mins. Cool in the tin, then chill for at least 5 hrs, or overnight.');
insert into feedme.recipe_steps values('st-clements-pie',2,2, 'Whip the cream, yogurt and icing sugar together. Dollop on the pie and scatter with zest to serve.');
insert into feedme.recipes VALUES ('tandoori-roast-chicken', 'Tandoori roast chicken', 'Serve this Indian spiced bird with rice, potatoes and vegetables for an alternative roast dinner', 'indian');
insert into feedme.recipe_ingredients values('tandoori-roast-chicken','chicken','1.8kg chicken',1.0,'unit');
insert into feedme.recipe_ingredients values('tandoori-roast-chicken','onion','2 onions, thickly sliced',2.0,'unit');
insert into feedme.recipe_ingredients values('tandoori-roast-chicken','lemon','1 lemon, halved',1.0,'unit');
insert into feedme.recipe_ingredients values('tandoori-roast-chicken','ginger','thumb-sized piece ginger, peeled and thickly sliced',NULL,'null');
insert into feedme.recipe_ingredients values('tandoori-roast-chicken','nut','400g can coconut milk',400.0,'gram');
insert into feedme.recipe_ingredients values('tandoori-roast-chicken','coriander','small bunch coriander, roughly chopped',NULL,'null');
insert into feedme.recipe_steps values('tandoori-roast-chicken',0,0, 'Mix the marinade ingredients with 2 tsp salt and 1 tsp black pepper. Slash the legs of the chicken a few times, then rub the marinade all over, including under the skin of the breast. Marinate in the fridge for up to 24 hrs.');
insert into feedme.recipe_steps values('tandoori-roast-chicken',1,1, 'Heat oven to 200C/180C fan/gas 6. Put the onions, lemon halves and ginger in a roasting tin. Sit the chicken on top and roast for 1½ hrs or until the thigh juices run clear when tested with a skewer.');
insert into feedme.recipe_steps values('tandoori-roast-chicken',2,2, 'When the chicken is done, lift out of the tin, sit in a new dish, cover loosely with foil and leave to rest. Fish out the ginger from the tin and discard. Scrape out the roasted middles from the lemons into a food processor, add the onions and any pan juices, and whizz to a purée. Scrape the purée back into the tin and sit on the hob. Stir in the coconut milk and bubble gently, scraping up any chicken bits that have stuck. You can add a splash of water if the sauce is too thick. Stir in the coriander and serve with the chicken.');
insert into feedme.recipes VALUES ('speedy-moroccan-meatballs', 'Speedy Moroccan meatballs', 'A great twist on meatballs for a quick and tasty supper', 'moroccan');
insert into feedme.recipe_ingredients values('speedy-moroccan-meatballs','olive oil','1 tbsp olive oil',1.0,'tbsp');
insert into feedme.recipe_ingredients values('speedy-moroccan-meatballs','chicken','350g pack ready-made beef or chicken meatballs (approx 16)',350.0,'gram');
insert into feedme.recipe_ingredients values('speedy-moroccan-meatballs','onion','1 large onion, sliced',1.0,'unit');
insert into feedme.recipe_ingredients values('speedy-moroccan-meatballs','apricot','100g dried apricot, halved',100.0,'gram');
insert into feedme.recipe_ingredients values('speedy-moroccan-meatballs','cinnamon','1 small cinnamon stick',1.0,'unit');
insert into feedme.recipe_ingredients values('speedy-moroccan-meatballs','garlic','400g tin chopped tomato with garlic',400.0,'gram');
insert into feedme.recipe_ingredients values('speedy-moroccan-meatballs','almond','25g toasted flaked almond',25.0,'gram');
insert into feedme.recipe_ingredients values('speedy-moroccan-meatballs','coriander','handful coriander, roughly chopped',NULL,'null');
insert into feedme.recipe_steps values('speedy-moroccan-meatballs',0,0, 'Heat the oil in a large deep frying pan, then fry the meatballs for 10 mins, turning occasionally until cooked through. Scoop out of the pan and set aside, then cook the onion for 5 mins, until softened.');
insert into feedme.recipe_steps values('speedy-moroccan-meatballs',1,1, 'Add the dried apricots, cinnamon stick, tomatoes and half a can of water to the pan, then bring to the boil and simmer for 10 mins. Remove the cinnamon stick. Return the meatballs to the pan and coat well with the tomato sauce. Serve sprinkled with the almonds and coriander.');
insert into feedme.recipes VALUES ('salmon-and-herby-potato-coulibiacs', 'Salmon & herby potato coulibiacs', 'An impressive addition to any buffet, this puff pastry parcel is filled with fish in a creamy parsley, dill and lemon sauce', 'french');
insert into feedme.recipe_ingredients values('salmon-and-herby-potato-coulibiacs','potato','600g potato, thinly sliced',600.0,'gram');
insert into feedme.recipe_ingredients values('salmon-and-herby-potato-coulibiacs','onion','1 large onion, thinly sliced',1.0,'unit');
insert into feedme.recipe_ingredients values('salmon-and-herby-potato-coulibiacs','olive oil','1 tbsp olive oil',1.0,'tbsp');
insert into feedme.recipe_ingredients values('salmon-and-herby-potato-coulibiacs','double cream','100ml double cream',100.0,'ml');
insert into feedme.recipe_ingredients values('salmon-and-herby-potato-coulibiacs','crème fraîche','100ml crème fraîche',100.0,'ml');
insert into feedme.recipe_ingredients values('salmon-and-herby-potato-coulibiacs','parsley','4 tbsp chopped parsley',4.0,'tbsp');
insert into feedme.recipe_ingredients values('salmon-and-herby-potato-coulibiacs','dill','4 tbsp chopped dill',4.0,'tbsp');
insert into feedme.recipe_ingredients values('salmon-and-herby-potato-coulibiacs','lemon','zest 1 lemon',NULL,'null');
insert into feedme.recipe_ingredients values('salmon-and-herby-potato-coulibiacs','pastry','2 x 375g packs all-butter puff pastry',375.0,'gram');
insert into feedme.recipe_ingredients values('salmon-and-herby-potato-coulibiacs','flour','a little flour, for dusting',NULL,'null');
insert into feedme.recipe_ingredients values('salmon-and-herby-potato-coulibiacs','salmon','650g side of salmon, skinned and pin-boned, cut crossways into 3cm slices, then cut in half lengthways to get thick fingers',650.0,'gram');
insert into feedme.recipe_ingredients values('salmon-and-herby-potato-coulibiacs','egg','1 egg, beaten',1.0,'unit');
insert into feedme.recipe_steps values('salmon-and-herby-potato-coulibiacs',0,0, 'Heat oven to 180C/160C fan/gas 4. Par-boil the potato slices for 2-3 mins. Drain and set aside in a large bowl.');
insert into feedme.recipe_steps values('salmon-and-herby-potato-coulibiacs',1,1, 'Gently fry the onion in the oil for 10 mins until lightly golden. Cool slightly, then stir in the cream, crème fraîche, herbs, zest and plenty of seasoning. Pour over the potatoes and mix very gently.');
insert into feedme.recipe_steps values('salmon-and-herby-potato-coulibiacs',2,2, 'Roll out one pack of pastry on a lightly floured surface to a 35 x 35cm, then lay on a large baking sheet. Pack ¼ of the potato mixture down one side of the pastry, leaving a 5cm border. Arrange ½ of the salmon on top to cover the potato. Cover the salmon with ¼ of the potato. Brush the pastry all the way round with beaten egg and fold over the pastry. Trim the edges and crimp with your fingers, or seal with a fork. Make decorative fish for the top, if you like.');
insert into feedme.recipe_steps values('salmon-and-herby-potato-coulibiacs',3,3, 'Roll out the remaining pastry and repeat the process, using up the rest of the potato and salmon. Chill for 30 mins.');
insert into feedme.recipe_steps values('salmon-and-herby-potato-coulibiacs',4,4, 'Brush the pastries all over with beaten egg. Bake for 40 mins. Leave to rest for 10 mins if serving hot, or serve cold.');
insert into feedme.recipes VALUES ('ultimate-kedgeree', 'Ultimate kedgeree', 'There are a million versions of kedgeree, but we reckon this is the best and easiest of the lot', 'indian');
insert into feedme.recipe_steps values('ultimate-kedgeree',0,0, 'For the crisp onions, heat the oil in a frying pan, add the onions with a pinch of salt (which helps to stop them burning), then cook on a medium heat, stirring occasionally, until deep golden, about 20-25 mins. When done, spread on paper towels and leave to crisp up. While the onions are frying, put both fish in a frying pan with the chilli. Pour the milk over, cover and simmer for 4 mins. Take off the heat and let stand, covered, for 10 mins to finish gently cooking the fish.');
insert into feedme.recipe_steps values('ultimate-kedgeree',1,1, 'Meanwhile, heat the 3 tbsp oil in a deep frying or sauté pan (with a lid). Add the onion, curry leaves, cardamom and cinnamon and fry until the onion is soft and golden, about 7-8 mins, stirring often. Lift the fish from the milk with a slotted spoon, discard the skin and any bones, cover to keep warm and set aside. Make the milk up to 600ml/1pt with water. Rinse the rice in warm water in a colander, drain, then stir it into the onion. Keep stirring for 1 min so the rice is coated. Pour in the milk mix and stir in the turmeric. Bring to a boil, then simmer for 10 mins, covered, lowering the heat if it starts to stick on the bottom.');
insert into feedme.recipe_steps values('ultimate-kedgeree',2,2, 'When the rice is tender, remove from the heat, drop the butter on top so it melts in, then lay the whole pieces of fish on the rice. Cover and leave for the flavours to mingle. Meanwhile, put the eggs in a pan, cover with cold water and bring to the boil, then boil for 6 mins (for lightly boiled). Remove from the heat and plunge into cold water, cracking the shells against the side of the pan. Peel off the shells and quarter the eggs.');
insert into feedme.recipe_steps values('ultimate-kedgeree',3,3, 'To serve, break the fish into big pieces with a fork, throw in the coriander and stir gently to mix without breaking up the fish, adding the eggs at the end. Season, scatter crisp onions on top and serve with lime wedges for squeezing over.');
insert into feedme.recipes VALUES ('cheesy-french-toast-with-ham-and-grilled-vine-toma', 'Cheesy French toast with ham & grilled vine tomatoes', 'This twist on the classic croque-monsieur is much easier to prepare for a large crowd', 'french');
insert into feedme.recipe_ingredients values('cheesy-french-toast-with-ham-and-grilled-vine-toma','egg','5 eggs, beaten',5.0,'unit');
insert into feedme.recipe_ingredients values('cheesy-french-toast-with-ham-and-grilled-vine-toma','milk','4 tbsp milk',4.0,'tbsp');
insert into feedme.recipe_ingredients values('cheesy-french-toast-with-ham-and-grilled-vine-toma','gruyère','140g Gruyère, grated',140.0,'gram');
insert into feedme.recipe_ingredients values('cheesy-french-toast-with-ham-and-grilled-vine-toma','chives','1 tbsp chopped chives',1.0,'tbsp');
insert into feedme.recipe_ingredients values('cheesy-french-toast-with-ham-and-grilled-vine-toma','butter','knob of butter',NULL,'null');
insert into feedme.recipe_ingredients values('cheesy-french-toast-with-ham-and-grilled-vine-toma','baguette','1 large baguette, thickly sliced',1.0,'unit');
insert into feedme.recipe_ingredients values('cheesy-french-toast-with-ham-and-grilled-vine-toma','cherry','250g cherry tomatoeson the vine',250.0,'gram');
insert into feedme.recipe_ingredients values('cheesy-french-toast-with-ham-and-grilled-vine-toma','olive oil','olive oil, for grilling',NULL,'null');
insert into feedme.recipe_ingredients values('cheesy-french-toast-with-ham-and-grilled-vine-toma','ham','250g sliced ham- about 25 slices (James used Serrano)',250.0,'gram');
insert into feedme.recipe_ingredients values('cheesy-french-toast-with-ham-and-grilled-vine-toma','watercress','watercress, to serve',NULL,'null');
insert into feedme.recipe_steps values('cheesy-french-toast-with-ham-and-grilled-vine-toma',0,0, 'Heat the grill to high. In a shallow bowl, whisk together the eggs, milk, cheese and chives. Melt the butter in a large non-stick pan over a medium heat. Dip slices of bread into the egg mixture, then place them in the pan. Cook for 3-5 mins on each side, or until golden and crisp, then keep warm in a low oven if you need to.');
insert into feedme.recipe_steps values('cheesy-french-toast-with-ham-and-grilled-vine-toma',1,1, 'Place the vine tomatoes on a baking tray, drizzle with a little oil and season with salt and pepper. Grill until they start to soften and blister. On a large platter, pile up the cheesy French bread, grilled tomatoes, sliced ham and a pile of watercress, and let everyone dig in.');
insert into feedme.recipes VALUES ('creamy-masala-chicken', 'Creamy masala chicken', 'A mild chicken curry with oodles of flavour', 'indian');
insert into feedme.recipe_ingredients values('creamy-masala-chicken','chicken','4 boneless, skinless chicken breasts, cubed',4.0,'unit');
insert into feedme.recipe_ingredients values('creamy-masala-chicken','ginger','3cm piece fresh root ginger, peeled and chopped',3.0,'unit');
insert into feedme.recipe_ingredients values('creamy-masala-chicken','garlic','2 garlic clove, chopped',2.0,'unit');
insert into feedme.recipe_ingredients values('creamy-masala-chicken','chilli','1 tsp mild chilli powder',1.0,'tsp');
insert into feedme.recipe_ingredients values('creamy-masala-chicken','fresh coriander','2 tbsp fresh coriander, chopped',2.0,'tbsp');
insert into feedme.recipe_ingredients values('creamy-masala-chicken','lime','juice of 1 lime',NULL,'null');
insert into feedme.recipe_ingredients values('creamy-masala-chicken','vegetable oil','2 tbsp vegetable oil',2.0,'tbsp');
insert into feedme.recipe_ingredients values('creamy-masala-chicken','onion','1 onion',1.0,'unit');
insert into feedme.recipe_ingredients values('creamy-masala-chicken','chilli','1 red chilli',1.0,'unit');
insert into feedme.recipe_ingredients values('creamy-masala-chicken','turmeric','1 tsp ground turmeric',1.0,'tsp');
insert into feedme.recipe_ingredients values('creamy-masala-chicken','double cream','284ml carton double cream',284.0,'ml');
insert into feedme.recipe_ingredients values('creamy-masala-chicken','lemon','juice of 0.5 lemon',NULL,'null');
insert into feedme.recipe_ingredients values('creamy-masala-chicken','rice','basmati rice and naan bread, to serve',NULL,'null');
insert into feedme.recipe_steps values('creamy-masala-chicken',0,0, 'Put the chicken, ginger, garlic, chilli powder, coriander, lime juice and 1 tbsp of oil in a bowl. Stir, and set aside. Chop the onion, and seed and chop the chilli.');
insert into feedme.recipe_steps values('creamy-masala-chicken',1,1, 'Heat a large shallow pan. Tip in the chicken and marinade and fry for about 6-8 minutes, stirring occasionally.');
insert into feedme.recipe_steps values('creamy-masala-chicken',2,2, 'Meanwhile, heat the remaining oil in a pan and fry the onion and chilli for 3-4 minutes until just soft. Add the turmeric and stir fry for 1 minute. Lower the heat, pour in the cream and simmer for 2-3 minutes.');
insert into feedme.recipe_steps values('creamy-masala-chicken',3,3, 'Add the chicken and simmer for 5 minutes, or until cooked. Season and stir in the lemon juice. Serve with rice and naan bread.');
insert into feedme.recipes VALUES ('salt-and-pepper-prawns', 'Salt & pepper prawns', 'Use Sichuan peppercorns to flavour shellfish with a celery-like taste, then create a Chinese-style chilli garlic dip', 'chinese');
insert into feedme.recipe_ingredients values('salt-and-pepper-prawns','flour','2 tbsp cornflour',2.0,'tbsp');
insert into feedme.recipe_ingredients values('salt-and-pepper-prawns','vegetable oil','vegetable oil, for deep frying',NULL,'null');
insert into feedme.recipe_ingredients values('salt-and-pepper-prawns','prawn','24 large king prawns, peeled and deveined, with tails',24.0,'unit');
insert into feedme.recipe_ingredients values('salt-and-pepper-prawns','pea shoots','pea shoots or baby saladleaves, to serve (optional)',NULL,'null');
insert into feedme.recipe_steps values('salt-and-pepper-prawns',0,0, 'First, make the dipping sauce. Crush the garlic, coriander and chilli using a pestle and mortar. (If you don’t have one, just finely chop.) Pound in the sugar until dissolved, then add the lime juice and fish sauce. It should taste sweet, salty and spicy.');
insert into feedme.recipe_steps values('salt-and-pepper-prawns',1,1, 'To make the spice mix, toast the Sichuan and black peppercorns in a frying pan over a medium heat for 1 min or until fragrant. Finely grind in a spice mill or using a pestle and mortar, then add the salt and chilli flakes and mix together. Transfer to a bowl.');
insert into feedme.recipe_steps values('salt-and-pepper-prawns',2,2, 'Combine the spice mix with the cornflour. Heat 7.5cm of oil in a large wok or deep heavy-based saucepan. You can test the temperature by frying a small piece of bread – it should sizzle instantly and take 20 secs to turn golden. Toss the prawns with the spice mix, shake off the excess and deep-fry, in batches, for 1-2 mins or until crisp at the edges and starting to curl. Remove with a slotted spoon, drain on a baking tray lined with kitchen paper and keep warm while you cook the remaining prawns. Scatter over the pea shoots or salad leaves, if you like, and serve with the dipping sauce.');
insert into feedme.recipes VALUES ('roast-lamb-with-spring-herb-crumbs', 'Roast lamb with spring herb crumbs', 'This iron-rich dish is ideal for a weekend with family and friends', 'british');
insert into feedme.recipe_steps values('roast-lamb-with-spring-herb-crumbs',0,0, 'Heat oven to 160C/140C fan/gas 3. For the lamb, scatter the vegetables, bay leaves and a few rosemary sprigs into a large roasting tin and drizzle with the olive oil. Use a sharp knife to make little slits all over the lamb. Poke a slice of garlic and a small sprig of rosemary into each one. Sit the lamb on top of the veg in the roasting tin, add a splash of wine, then cook for 2 hrs if you like it pink, or 2 hrs 15 mins for cooked through. Remove from the oven, transfer to a board and cover with foil, then rest for 45 mins-1 hr.');
insert into feedme.recipe_steps values('roast-lamb-with-spring-herb-crumbs',1,1, 'To make the gravy, pour off most of the fat from the tin but leave the veg and any meat juices. Place the tin on the heat to caramelise the veg, then stir in the flour and cook to a paste. Gradually stir in the wine, cook for a min, then stir in the stock and simmer until you have a tasty gravy. Push the gravy through a sieve into a small pan and keep warm.');
insert into feedme.recipe_steps values('roast-lamb-with-spring-herb-crumbs',2,2, 'Turn oven up to 220C/200C fan/gas 7. For the crumbs, blitz everything in a food processor and scatter over a tray. Bake in the oven for 10-15 mins until crisp, then re-blitz or break up with your fingers to crumbs again. These can be made in advance and kept in an airtight container for 2 days. Serve the lamb carved into slices, sprinkled with crumbs and a jug of gravy alongside.');
insert into feedme.recipes VALUES ('crunchy-prawn-noodle-salad', 'Crunchy prawn noodle salad', 'This Vietnamese-style dressing is best made just before serving to preserve the vibrant colour of the mint.', 'vietnamese');
insert into feedme.recipe_ingredients values('crunchy-prawn-noodle-salad','rice','100g rice noodle',100.0,'gram');
insert into feedme.recipe_ingredients values('crunchy-prawn-noodle-salad','sugar','100g sugar snap pea, shredded',100.0,'gram');
insert into feedme.recipe_ingredients values('crunchy-prawn-noodle-salad','carrot','2 carrots, coarsely grated',2.0,'unit');
insert into feedme.recipe_ingredients values('crunchy-prawn-noodle-salad','spinach','100g baby spinach',100.0,'gram');
insert into feedme.recipe_ingredients values('crunchy-prawn-noodle-salad','prawn','85g peeled, cooked prawn, defrosted if frozen',85.0,'gram');
insert into feedme.recipe_steps values('crunchy-prawn-noodle-salad',0,0, 'Pour boiling water over the noodles to cover, leave for 4 mins, then cool under cold running water. Drain well. Mix the sugar snaps, carrots, spinach, noodles and prawns in a shallow bowl. Mix together the dressing ingredients until the sugar has dissolved, pour over the salad, then toss everything together. Serve straight away.');
insert into feedme.recipes VALUES ('vegetable-tempura-with-soy-and-dipping-sauce', 'Vegetable tempura with soy & dipping sauce', 'These crisp Japanese-style treats are great for nibbles or a veggie main course.', 'japanese');
insert into feedme.recipe_ingredients values('vegetable-tempura-with-soy-and-dipping-sauce','aubergine','100g (approx) each of a mix of firm vegetables, cut into bite-size pieces, such as aubergine, broccoli, courgette, mushrooms, red pepper and sweet potatoes',100.0,'gram');
insert into feedme.recipe_ingredients values('vegetable-tempura-with-soy-and-dipping-sauce','tempura','tempura batter (see below)',NULL,'null');
insert into feedme.recipe_ingredients values('vegetable-tempura-with-soy-and-dipping-sauce','nut','groundnut or sunflower oil, for deep frying',NULL,'null');
insert into feedme.recipe_steps values('vegetable-tempura-with-soy-and-dipping-sauce',0,0, 'Heat oven to 150C/fan 130C/gas 2. Mix together the sauce ingredients in a small bowl. Make the batter (see right). Cover a baking tray with sheets of kitchen paper. Start to heat a deep-fat frying pan or large wok a third full of oil and have the frying basket, or slotted spoon to hand');
insert into feedme.recipe_steps values('vegetable-tempura-with-soy-and-dipping-sauce',1,1, 'When the oil reaches 190C dip some of the prepared veg briefly into the batter, shake off any excess, then lower straight into the hot oil. Don’t crowd the frying basket. Fry for about 2 mins until light golden and crisp, then drain on kitchen paper.');
insert into feedme.recipe_steps values('vegetable-tempura-with-soy-and-dipping-sauce',2,2, 'Repeat with the remaining vegetables in batches, dipping into the batter just before you fry them and remember to let the oil heat back up to temperature between each batch. Keep the tempura warm in the oven, leaving the door slightly ajar so that they stay crisp. They are best served immediately on a warm plate with the sauce alongside for dipping.');
insert into feedme.recipes VALUES ('mediterranean-slices', 'Mediterranean slices', 'Keep sliced roasted peppers, puff pastry and artichokes in the freezer and you''ll never be stuck for a smart starter again', 'mediterranean');
insert into feedme.recipe_ingredients values('mediterranean-slices','pastry','375g pack ready-rolled puff pastry',375.0,'gram');
insert into feedme.recipe_ingredients values('mediterranean-slices','pesto','4 tbsp green pesto',4.0,'tbsp');
insert into feedme.recipe_ingredients values('mediterranean-slices','pepper','140g frozen sliced roasted peppers',140.0,'gram');
insert into feedme.recipe_ingredients values('mediterranean-slices','artichoke','140g frozen artichokes (about 3 wedges per serving)',140.0,'gram');
insert into feedme.recipe_ingredients values('mediterranean-slices','mozzarella','125g ball mozzarella, or 85g cheddar, grated',125.0,'gram');
insert into feedme.recipe_steps values('mediterranean-slices',0,0, 'Heat oven to 200C/fan 180C/gas 6. Unroll the pastry and cut into 4 rectangles. Take a sharp knife and score a 1cm edge inside each rectangle, taking care that you don’t cut all the way through the pastry. Place on a baking sheet.');
insert into feedme.recipe_steps values('mediterranean-slices',1,1, 'Spread 1 tbsp pesto onto each slice, staying inside the border, then pile up the peppers and artichokes. Cook in the oven for 15 mins until the pastry is starting to brown.');
insert into feedme.recipe_steps values('mediterranean-slices',2,2, 'Tear the mozzarella ball into small pieces, then scatter it (or use cheddar, if you prefer) over the veg. Return to the oven for 5-7 mins until the pastry is crisp and the cheese has melted. Serve with a green salad.');
insert into feedme.recipes VALUES ('churros-with-chocolate-dipping-sauce', 'Churros with chocolate dipping sauce', 'Try a fresh spin on doughnuts by piping them Spanish-style and serving with a thick and indulgent dark chocolate sauce', 'spanish');
insert into feedme.recipe_ingredients values('churros-with-chocolate-dipping-sauce','butter','50g butter, melted',50.0,'gram');
insert into feedme.recipe_ingredients values('churros-with-chocolate-dipping-sauce','vanilla extract','0.5 tsp vanilla extract',5.0,'tsp');
insert into feedme.recipe_ingredients values('churros-with-chocolate-dipping-sauce','flour','250g plain flour, from a new bag.',250.0,'gram');
insert into feedme.recipe_ingredients values('churros-with-chocolate-dipping-sauce','baking','1 tsp baking powder',1.0,'tsp');
insert into feedme.recipe_ingredients values('churros-with-chocolate-dipping-sauce','sunflower oil','about 1 litre sunflower oil',1.0,'litre');
insert into feedme.recipe_ingredients values('churros-with-chocolate-dipping-sauce','bread','few chunks of bread',NULL,'null');
insert into feedme.recipe_steps values('churros-with-chocolate-dipping-sauce',0,0, 'Boil the kettle, then measure 350ml boiling water into a jug and add the melted butter and vanilla extract. Sift the flour and baking powder into a big mixing bowl with a big pinch of salt. Make a well in the centre, then pour in the contents of the jug and very quickly beat into the flour with a wooden spoon until lump-free. Rest for 10-15 mins while you make the sauce.');
insert into feedme.recipe_steps values('churros-with-chocolate-dipping-sauce',1,1, 'Put all the sauce ingredients into a pan and gently melt together, stirring occasionally until you have a smooth shiny sauce. Keep warm on a low heat.');
insert into feedme.recipe_steps values('churros-with-chocolate-dipping-sauce',2,2, 'Fill a large deep saucepan one-third full of oil. Cooking with hot oil can be dangerous, read up on how to deep-fry safely to avoid accidents in the kitchen. Heat until a cube of bread browns in 45 seconds – 1 min. Cover a tray with kitchen paper and mix the caster sugar and cinnamon together.');
insert into feedme.recipe_steps values('churros-with-chocolate-dipping-sauce',3,3, 'Fit a star nozzle to a piping bag, 1.5-2cm wide is a good size. Fill with the rested dough, then pipe 2-3 strips directly into the pan, snipping off each dough strip with a pair of kitchen scissors. Fry until golden brown and crisp. Be very careful here as if air bubbles form in the churros they can explode - keep children out of the kitchen and protect yourself by wearing long sleeves, eye protection and keeping your face away from the pan. then remove with a slotted spoon and drain on the kitchen paper-lined tray. Carry on cooking the rest of the dough in batches, sprinkling the cooked churros with some cinnamon sugar as you go. Once you’ve cooked all the churros, toss with any remaining cinnamon sugar and serve with the chocolate sauce, for dipping. It''s important to take care when cooking with hot oil - read our guide on how to deep-fry safely to avoid accidents in the kitchen.');
insert into feedme.recipes VALUES ('crab-and-sweetcorn-chowder', 'Crab & sweetcorn chowder', 'This low-fat soup is packed with flavour and so simple to cook', 'american');
insert into feedme.recipe_ingredients values('crab-and-sweetcorn-chowder','onion','1 onion, finely chopped',1.0,'unit');
insert into feedme.recipe_ingredients values('crab-and-sweetcorn-chowder','leek','1 leek, green and white parts separated and sliced',1.0,'unit');
insert into feedme.recipe_ingredients values('crab-and-sweetcorn-chowder','carrot','2 carrots, chopped',2.0,'unit');
insert into feedme.recipe_ingredients values('crab-and-sweetcorn-chowder','stock','850ml-1 litre/10.5 pints - 10.75 pints low-sodium chicken or vegetable stock',1.0,'litre');
insert into feedme.recipe_ingredients values('crab-and-sweetcorn-chowder','potato','1 large potato, diced',1.0,'unit');
insert into feedme.recipe_ingredients values('crab-and-sweetcorn-chowder','sweetcorn','175g/ 6oz frozen sweetcorn',175.0,'gram');
insert into feedme.recipe_ingredients values('crab-and-sweetcorn-chowder','meat','170g can white crabmeat, drained',170.0,'gram');
insert into feedme.recipe_ingredients values('crab-and-sweetcorn-chowder','crème fraîche','4 tbsp light crème fraîche',4.0,'tbsp');
insert into feedme.recipe_ingredients values('crab-and-sweetcorn-chowder','chives','1 tsp chopped chives',1.0,'tsp');
insert into feedme.recipe_steps values('crab-and-sweetcorn-chowder',0,0, 'Put the onion, white part of the leek and carrots in a large pan and pour on a few tbsp of the stock. Cook over a medium heat for about 10 mins, stirring regularly until soft. Add a splash more stock if the vegetables start to stick.');
insert into feedme.recipe_steps values('crab-and-sweetcorn-chowder',1,1, 'Add the potato, green leek and most of the stock, and simmer for 10-15 mins, until the potato is tender. Tip in the sweetcorn and crab meat, then cook for a further 1-2 mins. Remove from the heat and stir in the crème fraîche and some seasoning. Add the rest of the stock if the soup is too thick. Sprinkle with the chives and serve with brown bread, if you like.');
insert into feedme.recipes VALUES ('vietnamese-seafood-salad', 'Vietnamese seafood salad', 'A spicy zingy dressing lifts a bag of defrosted fish and a few fresh ingredients into a super, feelgood supper that''s ready in just 10 minutes', 'vietnamese');
insert into feedme.recipe_ingredients values('vietnamese-seafood-salad','seafood','400g pack cooked seafood mix',400.0,'gram');
insert into feedme.recipe_ingredients values('vietnamese-seafood-salad','rice','300g pack cooked thin rice noodles',300.0,'gram');
insert into feedme.recipe_ingredients values('vietnamese-seafood-salad','beansprouts','300g pack cooked beansprouts',300.0,'gram');
insert into feedme.recipe_ingredients values('vietnamese-seafood-salad','carrot','3 carrots, thinly sliced',3.0,'unit');
insert into feedme.recipe_ingredients values('vietnamese-seafood-salad','spring onion','1 bunch spring onions, sliced lengthways',1.0,'unit');
insert into feedme.recipe_ingredients values('vietnamese-seafood-salad','coriander','bunch mintand coriander, leaves chopped',NULL,'null');
insert into feedme.recipe_steps values('vietnamese-seafood-salad',0,0, 'To make the salad put all of the vegetables and seafood in a large bowl, reserving a few of the herbs for the top. Mix everything together, so that the seafood and noodles are combined.');
insert into feedme.recipe_steps values('vietnamese-seafood-salad',1,1, 'Make the dressing by mixing all of the ingredients, toss this through the salad, then divide between 5 deep bowls or pile on to a large platter and let everyone help themselves. Scatter the reserved herbs over the top to serve.');
insert into feedme.recipes VALUES ('gunpowder-chicken-with-dried-red-chillies-and-pean', 'Gunpowder chicken with dried red chillies & peanuts', 'This classic dish from the Chinese province of Sichuan isn''t as spicy if you remove the chilli seeds - complete with a sticky, sweet and rich sauce', 'chinese');
insert into feedme.recipe_ingredients values('gunpowder-chicken-with-dried-red-chillies-and-pean','chicken','12 boneless, skinless chicken thighs(about 900g), cut into pieces',900.0,'gram');
insert into feedme.recipe_ingredients values('gunpowder-chicken-with-dried-red-chillies-and-pean','soy sauce','1 tbsp dark soy sauce',1.0,'tbsp');
insert into feedme.recipe_ingredients values('gunpowder-chicken-with-dried-red-chillies-and-pean','vegetable oil','2 tbsp vegetable oil',2.0,'tbsp');
insert into feedme.recipe_ingredients values('gunpowder-chicken-with-dried-red-chillies-and-pean','bird''s-eye chillies','15 whole dried bird''s-eye chillies',15.0,'unit');
insert into feedme.recipe_ingredients values('gunpowder-chicken-with-dried-red-chillies-and-pean','peppercorn','1 tsp Sichuan peppercorn, ground',1.0,'tsp');
insert into feedme.recipe_ingredients values('gunpowder-chicken-with-dried-red-chillies-and-pean','spring onion','bunch spring onions, cut into 20.5 cm pieces',NULL,'null');
insert into feedme.recipe_ingredients values('gunpowder-chicken-with-dried-red-chillies-and-pean','nut','50g whole roasted peanut',50.0,'gram');
insert into feedme.recipe_steps values('gunpowder-chicken-with-dried-red-chillies-and-pean',0,0, 'Marinate the chicken with the soy and leave to sit for 1 hr. If you don’t have time to marinate, don’t worry – but it does add extra flavour. Mix the sauce ingredients in a small bowl and set aside.');
insert into feedme.recipe_steps values('gunpowder-chicken-with-dried-red-chillies-and-pean',1,1, 'Using a wok and a small amount of the oil, sear the chicken in batches. When it’s all browned, remove from the pan and set aside. Snip the stems from the chillies and shake out the seeds. Add the remaining oil and cook the chillies for 1 min, shaking the pan constantly to prevent them burning. Add the chicken back in with the peppercorns, spring onions and peanuts, then stir-fry for 1 min more. Add the sauce and stir for a few mins more until thickened.');
insert into feedme.recipes VALUES ('prawns-with-romesco-sauce', 'Prawns with Romesco sauce', 'Succulent tiger prawns are spiced up with chilli and peppers in this Spanish-style tapas dish', 'spanish');
insert into feedme.recipe_ingredients values('prawns-with-romesco-sauce','pepper','1 red pepper, preferably the long, thin skinned romano variety',1.0,'unit');
insert into feedme.recipe_ingredients values('prawns-with-romesco-sauce','garlic','3 fat garlic cloves',3.0,'unit');
insert into feedme.recipe_ingredients values('prawns-with-romesco-sauce','chilli','1 fat, fresh red chilli',1.0,'unit');
insert into feedme.recipe_ingredients values('prawns-with-romesco-sauce','tomato','1 large ripe tomato',1.0,'unit');
insert into feedme.recipe_ingredients values('prawns-with-romesco-sauce','nut','10 shelled hazelnuts',10.0,'unit');
insert into feedme.recipe_ingredients values('prawns-with-romesco-sauce','almond','10 blanched almonds',10.0,'unit');
insert into feedme.recipe_ingredients values('prawns-with-romesco-sauce','parsley','3 sprigs parsley',3.0,'unit');
insert into feedme.recipe_ingredients values('prawns-with-romesco-sauce','bread','small slice day-old bread',NULL,'null');
insert into feedme.recipe_ingredients values('prawns-with-romesco-sauce','olive oil','8 tbsp olive oil',8.0,'tbsp');
insert into feedme.recipe_ingredients values('prawns-with-romesco-sauce','wine','2 tbsp red wine vinegar',2.0,'tbsp');
insert into feedme.recipe_ingredients values('prawns-with-romesco-sauce','prawn','400g pack large, cooked, peeled tiger prawn, defrosted if frozen',400.0,'gram');
insert into feedme.recipe_steps values('prawns-with-romesco-sauce',0,0, 'Prepare ahead - halve the pepper lengthways and remove the seeds and stalk. Line a grill pan with foil and put the pepper halves, skin side up, on the grill pan with the whole garlic cloves, chilli and tomato. Grill for 2 minutes, turn the tomato, then grill for a further 2 minutes. Remove the tomato with a large spoon, then peel, quarter and remove the seeds. Then chop the tomato roughly.');
insert into feedme.recipe_steps values('prawns-with-romesco-sauce',1,1, 'Continue grilling the pepper, chilli and garlic for 4-5 minutes, until the pepper and chilli skins have blackened and the garlic is starting to soften (the garlic skin will start to split when it is ready). When cool enough to handle, peel and halve the chilli, and scrape out and discard the seeds. Peel the pepper and roughly chop both the pepper and chilli.');
insert into feedme.recipe_steps values('prawns-with-romesco-sauce',2,2, 'Spread nuts over the foil and grill until toasted. Finely chop the nuts and parsley in a food processor. Tip into a small bowl.');
insert into feedme.recipe_steps values('prawns-with-romesco-sauce',3,3, 'Heat 3 tablespoons of oil in a frying pan, add the pepper, garlic and chilli and fry for 3 minutes. Tear up the bread and add to the pan, turning it in the oil until lightly browned. Pulse in food processor with the tomatoes, salt, vinegar and oil until roughly chopped. Tip into a bowl. Leave to cool and store in fridge for up to 3 days.');
insert into feedme.recipe_steps values('prawns-with-romesco-sauce',4,4, 'On the day add the nuts and parsley to the sauce and mix. Serve in a small bowl on a plate with the peeled prawns. Supply cocktail sticks for spearing the prawns.');
insert into feedme.recipes VALUES ('baked-aubergine-stuffed-with-roast-pumpkin-feta-an', 'Baked aubergine stuffed with roast pumpkin, feta & walnut with minted courgettes', 'New Zealand chef Peter Gordon showcases seasonal flavours in this impressive, healthy vegetarian meal', 'turkish');
insert into feedme.recipe_ingredients values('baked-aubergine-stuffed-with-roast-pumpkin-feta-an','aubergine','2 large aubergines',2.0,'unit');
insert into feedme.recipe_ingredients values('baked-aubergine-stuffed-with-roast-pumpkin-feta-an','butternut squash','200g/7oz pumpkinor squash (try kabocha or butternut squash)',200.0,'gram');
insert into feedme.recipe_ingredients values('baked-aubergine-stuffed-with-roast-pumpkin-feta-an','red onion','100g red onion, thinly sliced',100.0,'gram');
insert into feedme.recipe_ingredients values('baked-aubergine-stuffed-with-roast-pumpkin-feta-an','cumin','1 tsp cumin seeds(or use slightly less ground cumin)',1.0,'tsp');
insert into feedme.recipe_ingredients values('baked-aubergine-stuffed-with-roast-pumpkin-feta-an','garlic','2 garlic cloves, thinly sliced',2.0,'unit');
insert into feedme.recipe_ingredients values('baked-aubergine-stuffed-with-roast-pumpkin-feta-an','olive oil','4 tbsp virgin rapeseed or olive oil',4.0,'tbsp');
insert into feedme.recipe_ingredients values('baked-aubergine-stuffed-with-roast-pumpkin-feta-an','oregano','2 tsp fresh herb(thyme, oregano, sage or rosemary work well)',2.0,'tsp');
insert into feedme.recipe_ingredients values('baked-aubergine-stuffed-with-roast-pumpkin-feta-an','cheese','100g feta cheese, diced',100.0,'gram');
insert into feedme.recipe_ingredients values('baked-aubergine-stuffed-with-roast-pumpkin-feta-an','nut','large handful walnuts, lightly toasted and halved',NULL,'null');
insert into feedme.recipe_steps values('baked-aubergine-stuffed-with-roast-pumpkin-feta-an',0,0, 'Heat oven to 200C/180C fan/gas 6. Cut the aubergines in half lengthways, leaving the stem in place (this can be removed when eating). Using a small sharp knife, scoop out the inner flesh leaving a shell about 1cm thick. Chop the flesh and put into a bowl.');
insert into feedme.recipe_steps values('baked-aubergine-stuffed-with-roast-pumpkin-feta-an',1,1, 'Peel the pumpkin or squash, remove the seeds, then cut into 1-2cm dice and mix with the chopped aubergine and red onion. Mix in the cumin, garlic and 2 tbsp oil and toss together with some salt and black pepper. Lay on a baking tray lined with parchment paper. Rub 1 tbsp oil over the aubergine halves and season. Place, cut-side down, on another tray lined with parchment paper. Bake for 15-20 mins, until the pumpkin and aubergine shells are almost cooked through.');
insert into feedme.recipe_steps values('baked-aubergine-stuffed-with-roast-pumpkin-feta-an',2,2, 'Tip the pumpkin mixture back into the bowl and stir through the mixed herbs, feta and walnuts. Turn over the aubergine halves, then pile up the mixture inside. Drizzle with the remaining 1 tbsp oil and bake for 20 mins, at which point the pumpkin will be fully cooked. If it starts to colour too much, cover loosely with foil.');
insert into feedme.recipe_steps values('baked-aubergine-stuffed-with-roast-pumpkin-feta-an',3,3, 'Meanwhile, make the courgette salad. Top and tail the courgettes, then slice very thinly into wide ribbons. Toss with the zest, juice of half the lemon and the oil. Leave for 10 mins to let the courgettes soften a little.');
insert into feedme.recipe_steps values('baked-aubergine-stuffed-with-roast-pumpkin-feta-an',4,4, 'To serve, toss the herbs through the courgettes and cut the remaining lemon half into wedges. Place an aubergine half on each plate, pile up the salad beside it and serve with a lemon wedge.');
insert into feedme.recipes VALUES ('thai-rice-noodle-salad', 'Thai rice noodle salad', 'This salad is full of flavour and is healthy too, as it''s served with an oil-free dressing', 'thai');
insert into feedme.recipe_ingredients values('thai-rice-noodle-salad','rice','0.5 a 250g pack fine vermicelli rice noodles',250.0,'gram');
insert into feedme.recipe_ingredients values('thai-rice-noodle-salad','beansprouts','200g bag beansprouts',200.0,'gram');
insert into feedme.recipe_ingredients values('thai-rice-noodle-salad','lime','zest and juice 2 limes',NULL,'null');
insert into feedme.recipe_ingredients values('thai-rice-noodle-salad','soy sauce','2-3 tbsp fish or soy sauce, to taste',3.0,'tbsp');
insert into feedme.recipe_ingredients values('thai-rice-noodle-salad','brown sugar','1 tsp muscovado sugar or soft brown sugar',1.0,'tsp');
insert into feedme.recipe_ingredients values('thai-rice-noodle-salad','red onion','1 red onion, halved and thinly sliced',1.0,'unit');
insert into feedme.recipe_ingredients values('thai-rice-noodle-salad','little gem lettuce','2 Little Gem lettuce hearts, leaves separated and roughly torn',2.0,'unit');
insert into feedme.recipe_steps values('thai-rice-noodle-salad',0,0, 'Place the noodles and beansprouts in a heatproof bowl and cover with boiling water. Leave for 4 mins, or until the noodles are tender. Drain, then cool under cold running water and drain again. Return to the bowl.');
insert into feedme.recipe_steps values('thai-rice-noodle-salad',1,1, 'Stir together the lime zest and juice, fish or soy sauce and sugar. Stir into the noodles with the red onion and lettuce.');
insert into feedme.recipe_steps values('thai-rice-noodle-salad',2,2, 'To make with mince, heat a little oil in a non-stick frying pan and stir-fry 500g minced pork, a small knob of grated ginger and pinch cayenne pepper or chilli powder for 10 mins, until the mince is browned and cooked through. Mix into the noodles, divide between four bowls and serve warm.');
insert into feedme.recipe_steps values('thai-rice-noodle-salad',3,3, 'To make with steak, make the rice noodle salad. Heat 1 tsp sunflower oil in a frying pan. Tip 2 tbsp sesame seeds onto a plate. Rub 1 tsp oil into 4 x 175g sirloin steaks and press into sesame seeds. Fry for 5 mins for medium rare, turning halfway. Leave to rest for 5 mins, then thinly slice. Toss 1 deseeded and shredded red chilli, and a handful mint leaves into noodles. Top with steak to serve.');
insert into feedme.recipes VALUES ('moroccan-roast-chicken-with-apricots', 'Moroccan roast chicken with apricots', 'Come rain or shine, serve up this sumac-marinated spatchcocked bird, that can be barbecued or oven cooked, with a sweet, fruity accompaniment', 'moroccan');
insert into feedme.recipe_ingredients values('moroccan-roast-chicken-with-apricots','chicken','2 small chicken',2.0,'unit');
insert into feedme.recipe_ingredients values('moroccan-roast-chicken-with-apricots','olive oil','6 tbsp olive oil',6.0,'tbsp');
insert into feedme.recipe_ingredients values('moroccan-roast-chicken-with-apricots','coriander','small bunch coriander',NULL,'null');
insert into feedme.recipe_ingredients values('moroccan-roast-chicken-with-apricots','sumac','2 tbsp sumac(see tip, below)',2.0,'tbsp');
insert into feedme.recipe_ingredients values('moroccan-roast-chicken-with-apricots','cumin','1 tbsp cumin seed',1.0,'tbsp');
insert into feedme.recipe_ingredients values('moroccan-roast-chicken-with-apricots','fennel','1 tbsp fennel seed',1.0,'tbsp');
insert into feedme.recipe_ingredients values('moroccan-roast-chicken-with-apricots','chilli','2 tsp chilli flakes',2.0,'tsp');
insert into feedme.recipe_ingredients values('moroccan-roast-chicken-with-apricots','garlic','2 garlic cloves',2.0,'unit');
insert into feedme.recipe_ingredients values('moroccan-roast-chicken-with-apricots','lemon','zest and juice 2 lemons',NULL,'null');
insert into feedme.recipe_ingredients values('moroccan-roast-chicken-with-apricots','molasses','4 tbsp pomegranate molasses',4.0,'tbsp');
insert into feedme.recipe_ingredients values('moroccan-roast-chicken-with-apricots','saffron','250g natural yogurt, mixed with 1-2 pinches saffron, to serve',250.0,'gram');
insert into feedme.recipe_steps values('moroccan-roast-chicken-with-apricots',0,0, 'Spatchcock the chickens – see tip, below, or ask your butcher to do it for you. Put the olive oil, coriander, sumac, cumin, fennel, chilli, garlic, lemon zest and juice, pomegranate molasses and some ground black pepper into a food processor or blender, and whizz to a paste. Divide between 2 large food bags, add a chicken to each, then squeeze out the air and seal before giving everything a good squelch to coat thoroughly. Marinate for 24-48 hrs in the fridge, turning and squishing every so often.');
insert into feedme.recipe_steps values('moroccan-roast-chicken-with-apricots',1,1, 'Fire up a barbecue and let the flames die down, or heat oven to 220C/200C fan/gas 7. Lift the chicken out of the bags and season well with salt. Barbecue or roast the chicken for 25-30 mins on each side until crisp and cooked through. Set aside on a platter, loosely cover with foil, to rest while you do the apricots. If you roasted rather than barbecued, keep the tin juices warm for serving.');
insert into feedme.recipe_steps values('moroccan-roast-chicken-with-apricots',2,2, 'Put the apricots in a small, snug roasting tin. Whisk the preserved lemon, honey, orange blossom and vinegar with some seasoning. Pour over the apricots and roast, or sit on the barbecue, for 20 mins until soft.');
insert into feedme.recipe_steps values('moroccan-roast-chicken-with-apricots',3,3, 'Serve the chicken with any cooking juices, the apricots and saffron yogurt.');
insert into feedme.recipes VALUES ('spiced-tortilla', 'Spiced tortilla', 'Perfect for dinners, lunches and snacks, this spiced tortilla is a great in-the-fridge standby for many occasions', 'spanish');
insert into feedme.recipe_ingredients values('spiced-tortilla','sunflower oil','1 tbsp sunflower oil',1.0,'tbsp');
insert into feedme.recipe_ingredients values('spiced-tortilla','onion','1 onion, sliced',1.0,'unit');
insert into feedme.recipe_ingredients values('spiced-tortilla','chilli','1 red chilli, deseeded and shredded',1.0,'unit');
insert into feedme.recipe_ingredients values('spiced-tortilla','cumin','2 tsp curry spice (we used coriander, cumin and turmeric)',2.0,'tsp');
insert into feedme.recipe_ingredients values('spiced-tortilla','cherry','300g cherry tomato',300.0,'gram');
insert into feedme.recipe_ingredients values('spiced-tortilla','potato','500g cooked potato, sliced',500.0,'gram');
insert into feedme.recipe_ingredients values('spiced-tortilla','coriander','bunch coriander, stalks finely chopped, leaves roughly chopped',NULL,'null');
insert into feedme.recipe_ingredients values('spiced-tortilla','egg','8 eggs, beaten',8.0,'unit');
insert into feedme.recipe_steps values('spiced-tortilla',0,0, 'Heat the oil in a large frying pan. Fry the onion and half the chilli for 5 mins until softened. Tip in the spices, fry for 1 min more, then add the cherry tomatoes, potatoes and coriander stalks to the pan. Season the eggs well, pour over the top of the veg and leave to cook gently for 8-10 mins until almost set.');
insert into feedme.recipe_steps values('spiced-tortilla',1,1, 'Heat the grill and flash the tortilla underneath for 1-2 mins until the top is set. Scatter the coriander leaves and remaining chilli over the top, slice into wedges and serve with a green salad.');
insert into feedme.recipes VALUES ('slowcooked-duck-legs-in-port-with-celeriac-gratin', 'Slow-cooked duck legs in Port with celeriac gratin', 'A cross between confit and a casserole, this slow-braised duck dish is served with a dauphinoise-like potato bake', 'french');
insert into feedme.recipe_ingredients values('slowcooked-duck-legs-in-port-with-celeriac-gratin','duck','2 duck legs',2.0,'unit');
insert into feedme.recipe_ingredients values('slowcooked-duck-legs-in-port-with-celeriac-gratin','carrot','2 carrots, roughly chopped',2.0,'unit');
insert into feedme.recipe_ingredients values('slowcooked-duck-legs-in-port-with-celeriac-gratin','onion','1 small onion, roughly chopped',1.0,'unit');
insert into feedme.recipe_ingredients values('slowcooked-duck-legs-in-port-with-celeriac-gratin','flour','1 tbsp plain flour',1.0,'tbsp');
insert into feedme.recipe_ingredients values('slowcooked-duck-legs-in-port-with-celeriac-gratin','bay leaf','1 bay leaf',1.0,'unit');
insert into feedme.recipe_ingredients values('slowcooked-duck-legs-in-port-with-celeriac-gratin','anise','1 star anise',1.0,'unit');
insert into feedme.recipe_ingredients values('slowcooked-duck-legs-in-port-with-celeriac-gratin','cloves','2 cloves',2.0,'unit');
insert into feedme.recipe_ingredients values('slowcooked-duck-legs-in-port-with-celeriac-gratin','potato','2 strips orangeskin (with a potato peeler)',2.0,'unit');
insert into feedme.recipe_ingredients values('slowcooked-duck-legs-in-port-with-celeriac-gratin','port','150ml port',150.0,'ml');
insert into feedme.recipe_ingredients values('slowcooked-duck-legs-in-port-with-celeriac-gratin','stock','500ml chicken stock',500.0,'ml');
insert into feedme.recipe_steps values('slowcooked-duck-legs-in-port-with-celeriac-gratin',0,0, 'Heat oven to 160C/140C fan/gas 3. Put the duck legs in a flameproof casserole set over a medium heat. Brown all over, then remove from the casserole and set aside. Pour off all but 1 tbsp of the fat, leave more fat in the pan if you are doubling or tripling (save the drained fat for your Christmas roasties). Add the carrots and onion to the casserole and cook for 5-10 mins or until starting to caramelise. Stir in the flour and cook for 1 min more. Return the duck along with the remaining ingredients. Bring to a simmer, then cover with a lid and put in the oven for 2 hrs.');
insert into feedme.recipe_steps values('slowcooked-duck-legs-in-port-with-celeriac-gratin',1,1, 'Meanwhile, prepare the gratin. Put the milk, cream, garlic and rosemary in a pan set over a low heat. Bring to a gentle simmer for 5 mins, then remove from the heat and leave to infuse for 30 mins. Grease 2 ramekins (about 8cm diameter, 5cm deep). Arrange the celeriac and potato slices in the ramekins, seasoning the layers as you go. Remove the garlic and rosemary from the milk, pour over the veg, then dot with butter. Cover tightly with foil and bake with the duck for 1½ hrs.');
insert into feedme.recipe_steps values('slowcooked-duck-legs-in-port-with-celeriac-gratin',2,2, 'Once cooked, remove the duck and gratins from the oven. To freeze the duck, cool, then pack into a freezer container, pushing the duck under the sauce. If it doesn’t cover it, lay cling film on top. Use within 2 months. Thaw in the fridge, then reheat in the casserole and complete from Step 4. Increase oven to 220C/200C fan/gas 7. Put a heavy can on top of each foil-wrapped gratin and stand for 15-20 mins, or chill like this until needed. Once pressed, turn the gratins out onto a baking tray, sprinkle with a little Parmesan and bake for 20 mins until golden.');
insert into feedme.recipe_steps values('slowcooked-duck-legs-in-port-with-celeriac-gratin',3,3, 'Meanwhile, remove the duck legs from the casserole, strain the cooking liquid into a clean pan and bring to a rapid boil. Reduce the sauce by half until thickened and glossy. Add the duck legs and heat through. Put a duck leg on each plate with a little sauce spooned over the top. Serve with the gratins and seasonal veg.');
insert into feedme.recipes VALUES ('guinea-fowl-tagine-with-chickpeas-squash-and-apric', 'Guinea fowl tagine with chickpeas, squash & apricots', 'This game bird is a lot like chicken, so can be cooked in the same way - give it a whirl in this fragrant Moroccan-spiced casserole', 'moroccan');
insert into feedme.recipe_ingredients values('guinea-fowl-tagine-with-chickpeas-squash-and-apric','olive oil','3 tbsp olive oil',3.0,'tbsp');
insert into feedme.recipe_ingredients values('guinea-fowl-tagine-with-chickpeas-squash-and-apric','chicken','2 guinea fowl, jointed like a chicken (ask your butcher to do this or see our video on how to joint a chicken, in ''Try'', below)',2.0,'unit');
insert into feedme.recipe_ingredients values('guinea-fowl-tagine-with-chickpeas-squash-and-apric','onion','2 onions, roughly chopped',2.0,'unit');
insert into feedme.recipe_ingredients values('guinea-fowl-tagine-with-chickpeas-squash-and-apric','garlic','2 garlic cloves, chopped',2.0,'unit');
insert into feedme.recipe_ingredients values('guinea-fowl-tagine-with-chickpeas-squash-and-apric','nut','1 butternut squashor small pumpkin, peeled, deseeded and cut into large chunks',1.0,'unit');
insert into feedme.recipe_ingredients values('guinea-fowl-tagine-with-chickpeas-squash-and-apric','ras-el-hanout','1 tbsp ras-el-hanout',1.0,'tbsp');
insert into feedme.recipe_ingredients values('guinea-fowl-tagine-with-chickpeas-squash-and-apric','cumin','1 tsp ground cumin',1.0,'tsp');
insert into feedme.recipe_ingredients values('guinea-fowl-tagine-with-chickpeas-squash-and-apric','coriander','1 tsp ground coriander',1.0,'tsp');
insert into feedme.recipe_ingredients values('guinea-fowl-tagine-with-chickpeas-squash-and-apric','ground ginger','0.25 tsp ground ginger',25.0,'tsp');
insert into feedme.recipe_ingredients values('guinea-fowl-tagine-with-chickpeas-squash-and-apric','cinnamon','1 large cinnamon stick',1.0,'unit');
insert into feedme.recipe_ingredients values('guinea-fowl-tagine-with-chickpeas-squash-and-apric','honey','small squeeze of clear honey',NULL,'null');
insert into feedme.recipe_ingredients values('guinea-fowl-tagine-with-chickpeas-squash-and-apric','saffron','large pinch of saffron, soaked in 1 tbsp boiling water',1.0,'tbsp');
insert into feedme.recipe_ingredients values('guinea-fowl-tagine-with-chickpeas-squash-and-apric','lemon','juice 1 lemon',NULL,'null');
insert into feedme.recipe_ingredients values('guinea-fowl-tagine-with-chickpeas-squash-and-apric','stock','850ml chicken stock',850.0,'ml');
insert into feedme.recipe_ingredients values('guinea-fowl-tagine-with-chickpeas-squash-and-apric','chickpea','400g can chickpea, drained and rinsed',400.0,'gram');
insert into feedme.recipe_ingredients values('guinea-fowl-tagine-with-chickpeas-squash-and-apric','apricot','200g dried apricot',200.0,'gram');
insert into feedme.recipe_ingredients values('guinea-fowl-tagine-with-chickpeas-squash-and-apric','coriander','small bunch coriander',NULL,'null');
insert into feedme.recipe_ingredients values('guinea-fowl-tagine-with-chickpeas-squash-and-apric','rice','couscousor rice, to serve',NULL,'null');
insert into feedme.recipe_steps values('guinea-fowl-tagine-with-chickpeas-squash-and-apric',0,0, 'Heat the oil in a large, shallow flameproof casserole dish. Season the guinea fowl pieces and brown them – in batches, if necessary – then remove to a plate.');
insert into feedme.recipe_steps values('guinea-fowl-tagine-with-chickpeas-squash-and-apric',1,1, 'Fry the onions in the same dish until softened, then add the garlic and squash, cooking for 1-2 mins. Tip in the spices and cook for a few mins before adding the honey, saffron and lemon juice. Pour in the chicken stock and the chickpeas.');
insert into feedme.recipe_steps values('guinea-fowl-tagine-with-chickpeas-squash-and-apric',2,2, 'Submerge the guinea fowl pieces in the stock and add the apricots. Cover the dish and simmer everything very gently for 50 mins-1 hr, until the fowl and squash are both tender. Stir through the coriander and serve with couscous or rice.');
insert into feedme.recipes VALUES ('toffee-and-sesame-bananas', 'Toffee & sesame bananas', 'Traditional toffee bananas are deep-fried, but this is a lighter version, served with caramel sauce', 'chinese');
insert into feedme.recipe_ingredients values('toffee-and-sesame-bananas','banana','6 large bananas, peeled and quartered',6.0,'unit');
insert into feedme.recipe_ingredients values('toffee-and-sesame-bananas','sugar','8 tbsp light muscovado sugar',8.0,'tbsp');
insert into feedme.recipe_ingredients values('toffee-and-sesame-bananas','butter','2 tbsp butter',2.0,'tbsp');
insert into feedme.recipe_ingredients values('toffee-and-sesame-bananas','double cream','200ml double cream',200.0,'ml');
insert into feedme.recipe_ingredients values('toffee-and-sesame-bananas','sesame seeds','2 tbsp sesame seeds, toasted',2.0,'tbsp');
insert into feedme.recipe_ingredients values('toffee-and-sesame-bananas','ice cream','good-quality vanillaice cream, to serve',NULL,'null');
insert into feedme.recipe_steps values('toffee-and-sesame-bananas',0,0, 'Toss the bananas in 2 tbsp of the sugar. Heat a griddle to medium, then cook the bananas, a few pieces at a time, until sticky and showing griddle marks. Keep warm in a low oven.');
insert into feedme.recipe_steps values('toffee-and-sesame-bananas',1,1, 'For the sauce, put the remaining sugar, the butter and cream into a saucepan and stir over a gentle heat until the sugar has melted. Stir in the sesame seeds. Can be made up to a day ahead. To serve, put a few pieces of banana into 8 serving dishes, spoon over some of the caramel, then top with a scoop of ice cream.');
insert into feedme.recipes VALUES ('lemon-syllabub', 'Lemon syllabub', 'A quick dessert than can be as indulgent or as healthy as you want, experiment with your favourites', 'british');
insert into feedme.recipe_ingredients values('lemon-syllabub','whipping cream','284ml tub whipping cream',284.0,'ml');
insert into feedme.recipe_ingredients values('lemon-syllabub','caster sugar','50g caster sugar',50.0,'gram');
insert into feedme.recipe_ingredients values('lemon-syllabub','wine','50ml white wine',50.0,'ml');
insert into feedme.recipe_ingredients values('lemon-syllabub','lemon','zest and juice from 0.5 lemon',NULL,'null');
insert into feedme.recipe_ingredients values('lemon-syllabub','almond','almond thins or berries, to serve',NULL,'null');
insert into feedme.recipe_steps values('lemon-syllabub',0,0, 'Whip the cream and sugar together until soft peaks form. Stir in the wine, most of the lemon zest and the juice. Spoon into glasses or bowls, sprinkle with the remaining zest and serve with almond thins or berries.');
insert into feedme.recipes VALUES ('cauliflower-and-chickpea-pilaf', 'Cauliflower & chickpea pilaf', 'Delicious served on its own, or as a side dish with grilled chicken breasts rubbed with a little curry paste', 'indian');
insert into feedme.recipe_ingredients values('cauliflower-and-chickpea-pilaf','sunflower oil','1 tbsp sunflower oil',1.0,'tbsp');
insert into feedme.recipe_ingredients values('cauliflower-and-chickpea-pilaf','onion','2 large onions, sliced',2.0,'unit');
insert into feedme.recipe_ingredients values('cauliflower-and-chickpea-pilaf','curry','1 tbsp curry pasteof your choice',1.0,'tbsp');
insert into feedme.recipe_ingredients values('cauliflower-and-chickpea-pilaf','rice','200g basmati rice',200.0,'gram');
insert into feedme.recipe_ingredients values('cauliflower-and-chickpea-pilaf','cauliflower','350g cauliflower florets',350.0,'gram');
insert into feedme.recipe_ingredients values('cauliflower-and-chickpea-pilaf','peas','400g can chickpeas, rinsed and drained',400.0,'gram');
insert into feedme.recipe_ingredients values('cauliflower-and-chickpea-pilaf','stock','500ml vegetable stock',500.0,'ml');
insert into feedme.recipe_ingredients values('cauliflower-and-chickpea-pilaf','flaked almonds','50g toasted flaked almonds',50.0,'gram');
insert into feedme.recipe_ingredients values('cauliflower-and-chickpea-pilaf','coriander','handful chopped coriander',NULL,'null');
insert into feedme.recipe_steps values('cauliflower-and-chickpea-pilaf',0,0, 'Heat the oil in a large, non-stick pan and add the onions. Cook over a medium heat for 5 mins until starting to turn golden. Stir in the curry paste and cook for 1 min. Add the rice, cauliflower and chickpeas, stirring to coat in the paste.');
insert into feedme.recipe_steps values('cauliflower-and-chickpea-pilaf',1,1, 'Pour in the stock and stir. Cover and simmer for 10-15 mins until the rice and cauliflower are tender and all the liquid has been absorbed. Stir in the almonds and coriander, then serve.');
insert into feedme.recipes VALUES ('roast-cod-with-paella-and-saffron-olive-oil', 'Roast cod with paella & saffron olive oil', 'Impress your friends with Jason Atherton''s luxurious shellfish rice, topped with juicy cod and drizzled with saffron', 'spanish');
insert into feedme.recipe_steps values('roast-cod-with-paella-and-saffron-olive-oil',0,0, 'Heat oven to 180C/160C fan/gas 4. To make the paella, put the olive oil, chorizo, shallots and garlic in a large pan and fry for 7-8 mins until the chorizo releases its oil and the shallots have softened. Add the paprika and cook, stirring, for 5 mins, then add the rice, mix well and cook for a few mins more.');
insert into feedme.recipe_steps values('roast-cod-with-paella-and-saffron-olive-oil',1,1, 'Pour in 600ml of the stock, season and mix well to combine. Cover and put in the oven for 15 mins. Uncover, give everything a good stir, then return to the oven for 10 mins. When almost cooked, remove from the oven and spread onto a tray to cool. Chill and reserve for later. You can make this a day ahead.');
insert into feedme.recipe_steps values('roast-cod-with-paella-and-saffron-olive-oil',2,2, 'To make the saffron oil, whisk the olive oil and lemon juice together in a small saucepan. Add the saffron and heat gently for 5 mins, then remove from the heat and leave to infuse and cool. You can make this a day ahead.');
insert into feedme.recipe_steps values('roast-cod-with-paella-and-saffron-olive-oil',3,3, 'Heat oven to 200C/180C fan/gas 6. For the cod, put the olive oil and butter in an ovenproof frying pan over a high heat, but don’t let it burn. Season the cod with sea salt and black pepper, then cook, skin-side down, in the hot pan, for 4 mins, then carefully turn the fish over and put the pan in the oven for a further 3 mins, finishing with a little more sea salt and a squeeze of lemon juice.');
insert into feedme.recipe_steps values('roast-cod-with-paella-and-saffron-olive-oil',4,4, 'Meanwhile, put the paella back in the pan, stir in the remaining fish stock and cook for 2 mins until completely heated through. Stir through a knob of butter,the prawns and shelled mussels. Taste, season with lemon juice, salt and pepper if necessary, and finish with the chopped parsley. Serve with the roast cod, mussels in their shells and steamed purple broccoli or a green vegetable, if you like. Finally, drizzle everything with the saffron dressing.');
insert into feedme.recipes VALUES ('spiced-glazed-pineapple-with-cinnamon-fromage-frai', 'Spiced glazed pineapple with cinnamon fromage frais', 'Try this tropical treat when you want to end a meal on a sweet note without too much effort or too many calories', 'caribbean');
insert into feedme.recipe_ingredients values('spiced-glazed-pineapple-with-cinnamon-fromage-frai','lime','zest and juice 1 lime',NULL,'null');
insert into feedme.recipe_ingredients values('spiced-glazed-pineapple-with-cinnamon-fromage-frai','honey','2 tbsp clear honey',2.0,'tbsp');
insert into feedme.recipe_ingredients values('spiced-glazed-pineapple-with-cinnamon-fromage-frai','cinnamon','2 pinches ground cinnamon',2.0,'unit');
insert into feedme.recipe_ingredients values('spiced-glazed-pineapple-with-cinnamon-fromage-frai','nutmeg','few gratings whole nutmeg',NULL,'null');
insert into feedme.recipe_ingredients values('spiced-glazed-pineapple-with-cinnamon-fromage-frai','icing','2 tsp icing sugar, sifted',2.0,'tsp');
insert into feedme.recipe_ingredients values('spiced-glazed-pineapple-with-cinnamon-fromage-frai','fat','200g very low-fat fromage frais',200.0,'gram');
insert into feedme.recipe_ingredients values('spiced-glazed-pineapple-with-cinnamon-fromage-frai','butter','2 tsp butter',2.0,'tsp');
insert into feedme.recipe_ingredients values('spiced-glazed-pineapple-with-cinnamon-fromage-frai','pineapple','1 fresh pineapple, cut into 8 long wedges, skin and core removed',1.0,'unit');
insert into feedme.recipe_steps values('spiced-glazed-pineapple-with-cinnamon-fromage-frai',0,0, 'Mix the lime juice and half the lime zest with 1 tbsp honey, a pinch of cinnamon and nutmeg. Set this sauce aside. Stir the icing sugar and a pinch of cinnamon into the fromage frais.');
insert into feedme.recipe_steps values('spiced-glazed-pineapple-with-cinnamon-fromage-frai',1,1, 'Heat the butter and remaining honey in a non-stick frying pan until melted. Add the pineapple and cook over a high heat for 8 mins, turning regularly until caramelised. Pour in the spiced lime sauce and bubble for a few secs, tossing the pineapple to glaze in the sauce.');
insert into feedme.recipe_steps values('spiced-glazed-pineapple-with-cinnamon-fromage-frai',2,2, 'Serve immediately, sprinkled with the remaining lime zest and accompanied by a dollop of the cinnamon fromage frais');
insert into feedme.recipes VALUES ('grilled-lamb-with-wintry-rice-salad', 'Grilled lamb with wintry rice salad', 'A taste-packed and superhealthy supper. Leftovers are great for lunch, too', 'turkish');
insert into feedme.recipe_ingredients values('grilled-lamb-with-wintry-rice-salad','red onion','2 red onions',2.0,'unit');
insert into feedme.recipe_ingredients values('grilled-lamb-with-wintry-rice-salad','cinnamon','1 tsp cinnamon',1.0,'tsp');
insert into feedme.recipe_ingredients values('grilled-lamb-with-wintry-rice-salad','olive oil','1 tbsp olive oil',1.0,'tbsp');
insert into feedme.recipe_ingredients values('grilled-lamb-with-wintry-rice-salad','steak','400g lean lambsteak',400.0,'gram');
insert into feedme.recipe_ingredients values('grilled-lamb-with-wintry-rice-salad','rice','140g wild and basmati rice',140.0,'gram');
insert into feedme.recipe_ingredients values('grilled-lamb-with-wintry-rice-salad','mint','handful mint, chopped',NULL,'null');
insert into feedme.recipe_ingredients values('grilled-lamb-with-wintry-rice-salad','parsley','handful parsley, chopped',NULL,'null');
insert into feedme.recipe_ingredients values('grilled-lamb-with-wintry-rice-salad','seeds','120g pack fresh pomegranateseeds',120.0,'gram');
insert into feedme.recipe_ingredients values('grilled-lamb-with-wintry-rice-salad','pistachio','50g chopped pistachio',50.0,'gram');
insert into feedme.recipe_ingredients values('grilled-lamb-with-wintry-rice-salad','berry','50g dried cranberries',50.0,'gram');
insert into feedme.recipe_ingredients values('grilled-lamb-with-wintry-rice-salad','orange','juice 0.5 orange',NULL,'null');
insert into feedme.recipe_ingredients values('grilled-lamb-with-wintry-rice-salad','lemon','juice 0.5 lemon',NULL,'null');
insert into feedme.recipe_steps values('grilled-lamb-with-wintry-rice-salad',0,0, 'Grate 1 onion into a shallow dish, add the cinnamon and ½ tbsp olive oil, then mix. Place the lamb steaks in the dish, rub in the marinade, then cover and set aside while you make the rice.');
insert into feedme.recipe_steps values('grilled-lamb-with-wintry-rice-salad',1,1, 'Place the rice and 600ml water in a pan. Bring to the boil, then turn down and simmer, covered, until all the water has evaporated – about 20 mins. Take off the lid and cool. Finely chop the remaining red onion and mix into the rice along with the herbs, pomegranate, pistachios and cranberries. Finally mix in the orange and lemon juice, the remaining olive oil and season well.');
insert into feedme.recipe_steps values('grilled-lamb-with-wintry-rice-salad',2,2, 'Remove the lamb from the marinade, shaking off any excess. Griddle for about 5 mins on each side, depending on how you like your meat, and serve with a big spoonful of rice.');
insert into feedme.recipes VALUES ('diy-kofta-burgers', 'DIY kofta burgers', 'With these quick, easy and cheap koftas, there''s no excuse for hitting the kebab van', 'turkish');
insert into feedme.recipe_steps values('diy-kofta-burgers',0,0, 'Tip the mince into a large bowl (use a clean washing-up bowl if you don’t have anything big enough) with all the other burger ingredients and a good pinch of salt. Roll up your sleeves, get your hands into the mix and squelch everything together through your fingers until completely mixed. Pat the mix into 16 small burgers. These may now be frozen for up to 1 month or chilled up to a day ahead.');
insert into feedme.recipe_steps values('diy-kofta-burgers',1,1, 'To cook, heat grill to its highest setting and lay the burgers in a single layer on a baking tray (you may need to do this in batches, depending on how big your tray is). Grill on the highest shelf for 5-6 mins on each side until browned and cooked through. Pile burgers onto a platter and serve with all the accompaniments, so everyone can construct their own sandwich.');
insert into feedme.recipes VALUES ('ultimate-french-omelette', 'Ultimate French omelette', 'The omelette is one of the most fundamental dishes in any cook''s arsenal, and this recipe will teach you how to get it right every time', 'french');
insert into feedme.recipe_ingredients values('ultimate-french-omelette','egg','3 eggs, as fresh as possible, preferably organic and free-range, room temperature',3.0,'unit');
insert into feedme.recipe_ingredients values('ultimate-french-omelette','butter','2 knobs unsalted butter',2.0,'unit');
insert into feedme.recipe_ingredients values('ultimate-french-omelette','parmesan','1 tsp finely, freshly grated parmesan(or vegetarian alternative)',1.0,'tsp');
insert into feedme.recipe_steps values('ultimate-french-omelette',0,0, 'Get everything ready. Warm a 20cm (measured across the top) non-stick frying pan on a medium heat. Crack the eggs into a bowl and beat them with a fork so they break up and mix, but not as completely as you would for scrambled egg. With the heat on medium-hot, drop one knob of butter into the pan. It should bubble and sizzle, but not brown. Season the eggs with the Parmesan and a little salt and pepper, and pour into the pan.');
insert into feedme.recipe_steps values('ultimate-french-omelette',1,1, 'Let the eggs bubble slightly for a couple of seconds, then take a wooden fork or spatula and gently draw the mixture in from the sides of the pan a few times, so it gathers in folds in the centre. Leave for a few seconds, then stir again to lightly combine uncooked egg with cooked. Leave briefly again, and when partly cooked, stir a bit faster, stopping while there’s some barely cooked egg left. With the pan flat on the heat, shake it back and forth a few times to settle the mixture. It should slide easily in the pan and look soft and moist on top. A quick burst of heat will brown the underside.');
insert into feedme.recipe_steps values('ultimate-french-omelette',2,2, 'Grip the handle underneath. Tilt the pan down away from you and let the omelette fall to the edge. Fold the side nearest to you over by a third with your fork, and keep it rolling over, so the omelette tips onto a plate – or fold it in half, if that’s easier. For a neat finish, cover the omelette with a piece of kitchen paper and plump it up a bit with your fingers. Rub the other knob of butter over to glaze. Serve immediately.');
insert into feedme.recipes VALUES ('the-ultimate-makeover-french-onion-soup', 'The ultimate makeover: French onion soup', 'Angela Nilsen gives the French classic a makeover, losing the butter and beef stock to make a lighter soup that''s still full of flavour', 'french');
insert into feedme.recipe_steps values('the-ultimate-makeover-french-onion-soup',0,0, 'Cut the onions in half lengthways, then slice down into very thin slices. Heat a very large pan, add the oil when hot, stir in the onions, 3 of the thyme sprigs and the bay leaves, then season with a little salt. It will seem like a lot of onions, but they reduce right down. Cook over a high heat for 5 mins, stirring often. The onions shouldn’t brown yet, just start to soften. Lower the heat, then cook slowly for 35 mins, uncovered, stirring often until the onions have reduced right down and are very soft.');
insert into feedme.recipe_steps values('the-ultimate-makeover-french-onion-soup',1,1, 'While the onions are cooking, bring the wine to a boil in a small pan, then bubble away for 30 secs. Remove and leave to cool. Tip the flour into a small heavy pan and toast over a medium heat for a few mins, stirring occasionally, until light brown in colour. Set aside.');
insert into feedme.recipe_steps values('the-ultimate-makeover-french-onion-soup',2,2, 'When the onions are very soft and reduced, turn up the heat so they caramelise, then cook for another 12-15 mins, stirring along the bottom of the pan occasionally to mix in the brown sticky bits. When the bottom of the pan and all the onions are sticky and a rich brown colour, stir in the flour. With the heat still high, gradually pour in the wine, again stirring in the bits from the bottom. Pour in 1.2 litres of cold water. Stir in the bouillon, then slowly bring everything to the boil. Skim off any froth from the surface. Simmer for 15 mins so all the flavours can mingle.');
insert into feedme.recipe_steps values('the-ultimate-makeover-french-onion-soup',3,3, 'While the soup simmers, make the topping. Heat oven to 200C/fan 180C/gas 6. Mix the garlic and the oil together. Brush all over the bread slices, then cut each one into cubes. Scatter over a baking sheet, then bake for 8-10 mins until golden. Set aside. Line a baking sheet with baking parchment or a sheet of non-stick silicone. Remove the leaves from the remaining thyme sprig, then mix with the grated Parmesan. Scatter and spread over the lined baking sheet into a 13 x 8cm rectangle. Bake for about 8 mins until melted and turning golden. Remove, leave to firm up, then snap into jagged pieces.');
insert into feedme.recipe_steps values('the-ultimate-makeover-french-onion-soup',4,4, 'To serve, remove and discard the herbs from the soup. Ladle the soup into bowls – scatter over a few croutons, the Gruyère and a grinding of pepper, then perch a Parmesan crisp on top. Serve any remaining croutons separately.');
insert into feedme.recipes VALUES ('cherry-and-almond-tarts', 'Cherry & almond tarts', 'These mini Bakewell bites combine pastry and cake are designed for making in batches and cooking from frozen', 'british');
insert into feedme.recipe_ingredients values('cherry-and-almond-tarts','pastry','375g pack all-butter puff pastry',375.0,'gram');
insert into feedme.recipe_ingredients values('cherry-and-almond-tarts','self-raising flour','75g self-raising flour, plus extra for dusting',75.0,'gram');
insert into feedme.recipe_ingredients values('cherry-and-almond-tarts','jam','12 rounded tsp morello cherryjam',12.0,'unit');
insert into feedme.recipe_ingredients values('cherry-and-almond-tarts','cake','75g plain sponge or madeira cake',75.0,'gram');
insert into feedme.recipe_ingredients values('cherry-and-almond-tarts','butter','100g butter, softened',100.0,'gram');
insert into feedme.recipe_ingredients values('cherry-and-almond-tarts','almond','75g ground almond',75.0,'gram');
insert into feedme.recipe_ingredients values('cherry-and-almond-tarts','caster sugar','75g golden caster sugar',75.0,'gram');
insert into feedme.recipe_ingredients values('cherry-and-almond-tarts','egg','2 medium eggs',2.0,'unit');
insert into feedme.recipe_ingredients values('cherry-and-almond-tarts','almond','0.5 tsp almond extract',5.0,'tsp');
insert into feedme.recipe_ingredients values('cherry-and-almond-tarts','almond','25g flaked almond',25.0,'gram');
insert into feedme.recipe_ingredients values('cherry-and-almond-tarts','icing','100g icing sugar, sieved to decorate',100.0,'gram');
insert into feedme.recipe_steps values('cherry-and-almond-tarts',0,0, 'Very thinly roll out the pastry on a lightly floured surface. Stamp out 12 x 9cm circles with a fluted cutter, if you have one, and use to line a bun tin. Spread 1 rounded tsp of the cherry jam in the bottom of each tart.');
insert into feedme.recipe_steps values('cherry-and-almond-tarts',1,1, 'Whizz the cake to crumbs in a food processor, then tip into a mixing bowl and add the flour, butter, ground almonds, caster sugar, eggs and almond extract. Beat together until smooth, then divide between the tarts, spreading a little with the back of a teaspoon to cover the jam. Scatter over the flaked almonds and openfreeze for 2 hrs, then wrap well in cling film and freeze for up to 2 months.');
insert into feedme.recipe_steps values('cherry-and-almond-tarts',2,2, 'Heat oven to 180C/160C fan/gas 4 with a baking sheet in it. Unwrap the tarts from the freezer and loosely top with a sheet of foil. Bake on the heated baking sheet for 30-40 mins until golden, removing the foil halfway. Cool the tarts a little, then add a splash of water into the icing sugar to make a runny icing. Drizzle over the tarts and leave to set.');
insert into feedme.recipes VALUES ('versatile-dip', 'Versatile dip', 'Use spicy harissa to make a quick and easy dip for veggies and potato skins', 'moroccan');
insert into feedme.recipe_ingredients values('versatile-dip','yoghurt','225g thick and creamy plain yogurt, strained',225.0,'gram');
insert into feedme.recipe_ingredients values('versatile-dip','harissa','3-4 tbsp harissa (depending on taste)',4.0,'tbsp');
insert into feedme.recipe_ingredients values('versatile-dip','coriander','small handful coriander, finely chopped',NULL,'null');
insert into feedme.recipe_ingredients values('versatile-dip','mint','small handful fresh mint, finely chopped',NULL,'null');
insert into feedme.recipe_steps values('versatile-dip',0,0, 'Spoon the yogurt into a bowl and beat in as much harissa as you like. Stir in most of the coriander and mint, and season if you like. Tip into a serving bowl, garnish with the rest of the herbs and serve.');
insert into feedme.recipes VALUES ('clam-chorizo-and-white-bean-stew', 'Clam, chorizo & white bean stew', 'The spicy kick of chorizo works brilliantly with seafood in this Spanish dish from James Martin', 'spanish');
insert into feedme.recipe_ingredients values('clam-chorizo-and-white-bean-stew','chorizo','50g chorizo, diced',50.0,'gram');
insert into feedme.recipe_ingredients values('clam-chorizo-and-white-bean-stew','onion','1 onion, finely chopped',1.0,'unit');
insert into feedme.recipe_ingredients values('clam-chorizo-and-white-bean-stew','garlic','1 garlic clove, crushed',1.0,'unit');
insert into feedme.recipe_ingredients values('clam-chorizo-and-white-bean-stew','parsley','small bunch flat-leaf parsley, 0.5 finely chopped, 0.5 roughly chopped',NULL,'null');
insert into feedme.recipe_ingredients values('clam-chorizo-and-white-bean-stew','stock','200ml hot stock, fish or vegetable',200.0,'ml');
insert into feedme.recipe_ingredients values('clam-chorizo-and-white-bean-stew','chopped tomatoes','400g can chopped tomatoes',400.0,'gram');
insert into feedme.recipe_ingredients values('clam-chorizo-and-white-bean-stew','butter','400g can butter beansor white beans, rinsed and drained',400.0,'gram');
insert into feedme.recipe_ingredients values('clam-chorizo-and-white-bean-stew','vinegar','1 tsp sherry vinegar',1.0,'tsp');
insert into feedme.recipe_ingredients values('clam-chorizo-and-white-bean-stew','clams','600g clams, cleaned',600.0,'gram');
insert into feedme.recipe_ingredients values('clam-chorizo-and-white-bean-stew','bread','crusty bread, to serve',NULL,'null');
insert into feedme.recipe_steps values('clam-chorizo-and-white-bean-stew',0,0, 'Fry the chorizo in a large frying pan with a lid, over a medium heat until it is starting to crisp up and release its oil. Add the onion and cook for 5 mins until starting to soften. Then add the garlic and finely chopped parsley, and fry for 1 min more.');
insert into feedme.recipe_steps values('clam-chorizo-and-white-bean-stew',1,1, 'Pour on the stock and tomatoes. Bring to the boil, reduce the heat, then add the beans and sherry vinegar. Simmer for 10 mins until the liquid is slightly reduced.');
insert into feedme.recipe_steps values('clam-chorizo-and-white-bean-stew',2,2, 'Scatter over the clams, cover with the lid and steam for 2-4 mins, shaking the pan occasionally until the clams are open. Have a little taste before seasoning, as the clams can be quite salty. Then scatter over the chopped parsley. Eat with lots of crusty bread.');
insert into feedme.recipes VALUES ('hot-and-sour-pork-and-pepper-stirfry', 'Hot & sour pork & pepper stir-fry', 'A fresh, simple dish with a refreshing hint of lime, and it''s superhealthy too - so what''s not to love?', 'chinese');
insert into feedme.recipe_ingredients values('hot-and-sour-pork-and-pepper-stirfry','rice','100g Thai jasmine rice',100.0,'gram');
insert into feedme.recipe_ingredients values('hot-and-sour-pork-and-pepper-stirfry','sesame seeds','1 tbsp sesame seeds',1.0,'tbsp');
insert into feedme.recipe_ingredients values('hot-and-sour-pork-and-pepper-stirfry','sunflower oil','1 tsp sunflower oil',1.0,'tsp');
insert into feedme.recipe_ingredients values('hot-and-sour-pork-and-pepper-stirfry','pork','250g pork fillet, cut into finger-width strips',250.0,'gram');
insert into feedme.recipe_ingredients values('hot-and-sour-pork-and-pepper-stirfry','pepper','1 red and 1 yellow pepper, deseeded and sliced',1.0,'unit');
insert into feedme.recipe_ingredients values('hot-and-sour-pork-and-pepper-stirfry','flour','2 tsp cornflour',2.0,'tsp');
insert into feedme.recipe_ingredients values('hot-and-sour-pork-and-pepper-stirfry','soy sauce','2 tsp soy sauce',2.0,'tsp');
insert into feedme.recipe_ingredients values('hot-and-sour-pork-and-pepper-stirfry','lime','juice 1 lime',NULL,'null');
insert into feedme.recipe_ingredients values('hot-and-sour-pork-and-pepper-stirfry','honey','2 tbsp clear honey',2.0,'tbsp');
insert into feedme.recipe_ingredients values('hot-and-sour-pork-and-pepper-stirfry','chilli','0.5 red chilli, sliced',0.0,'unit');
insert into feedme.recipe_steps values('hot-and-sour-pork-and-pepper-stirfry',0,0, 'Place the rice in a saucepan with 300ml boiling water. Bring to the boil, stir once, cover with a lid and reduce the heat to low. Cook for 15 mins, by which time the rice should have absorbed all the liquid and be perfectly cooked. Dry-fry the sesame seeds until toasted golden, then tip into a small bowl and set aside.');
insert into feedme.recipe_steps values('hot-and-sour-pork-and-pepper-stirfry',1,1, 'Heat the oil in a non-stick frying pan or wok, add the pork and peppers and stir-fry for 5-6 mins over a high heat until the pork is lightly browned and cooked through. Mix the cornflour and soy sauce together in a bowl, then add the lime juice, honey, chilli and sesame seeds, plus 6 tbsp cold water. Pour into the wok and cook until the sauce has slightly thickened, tossing the pan to coat the pork and peppers. Serve with the rice.');
insert into feedme.recipes VALUES ('paneer-with-broccoli-and-sesame', 'Paneer with broccoli & sesame', 'Serve this vegetarian dish made with Indian cheese, paneer, as part of a curry night or with rice as a midweek supper', 'indian');
insert into feedme.recipe_ingredients values('paneer-with-broccoli-and-sesame','broccoli','200g sprouting broccoli',200.0,'gram');
insert into feedme.recipe_ingredients values('paneer-with-broccoli-and-sesame','vegetable oil','1 tbsp vegetable oil',1.0,'tbsp');
insert into feedme.recipe_ingredients values('paneer-with-broccoli-and-sesame','asafoetida','pinch asafoetida',NULL,'null');
insert into feedme.recipe_ingredients values('paneer-with-broccoli-and-sesame','cumin','1 tsp cumin seed',1.0,'tsp');
insert into feedme.recipe_ingredients values('paneer-with-broccoli-and-sesame','sesame seeds','1 tbsp sesame seeds',1.0,'tbsp');
insert into feedme.recipe_ingredients values('paneer-with-broccoli-and-sesame','chilli','1 large green chilli, finely chopped',1.0,'unit');
insert into feedme.recipe_ingredients values('paneer-with-broccoli-and-sesame','mustard seeds','0.5 tsp brown mustard seeds',5.0,'tsp');
insert into feedme.recipe_ingredients values('paneer-with-broccoli-and-sesame','curry leaves','6 curry leaves',6.0,'unit');
insert into feedme.recipe_ingredients values('paneer-with-broccoli-and-sesame','onion','1 medium onion, sliced',1.0,'unit');
insert into feedme.recipe_ingredients values('paneer-with-broccoli-and-sesame','cheese','140g paneercheese, cut into strips',140.0,'gram');
insert into feedme.recipe_ingredients values('paneer-with-broccoli-and-sesame','ginger','1 tbsp grated ginger',1.0,'tbsp');
insert into feedme.recipe_ingredients values('paneer-with-broccoli-and-sesame','garlic','2 garlic cloves, crushed',2.0,'unit');
insert into feedme.recipe_ingredients values('paneer-with-broccoli-and-sesame','lemon juice','1 tsp lemon juice',1.0,'tsp');
insert into feedme.recipe_ingredients values('paneer-with-broccoli-and-sesame','mace','0.5 tsp ground mace',5.0,'tsp');
insert into feedme.recipe_steps values('paneer-with-broccoli-and-sesame',0,0, 'Trim the broccoli and boil or steam for 5-6 mins until tender but firm to the bite. Drain and cool under cold running water. Heat the oil in a wok or large frying pan, then add the asafoetida, cumin seeds, sesame seeds, chilli, mustard seeds and the curry leaves. Cook for 1 min until fragrant, then add the onion and cook another 2 mins.');
insert into feedme.recipe_steps values('paneer-with-broccoli-and-sesame',1,1, 'Throw in the paneer, ginger and garlic, and season. Cook on a medium heat for 4-5 mins until the paneer turns golden. Add the broccoli and mix through the paneer and spices, then add lemon juice, mace and twist of pepper.');
insert into feedme.recipes VALUES ('chorizo-potato-and-cheese-omelette', 'Sticky jerk & brown sugar ribs with pineapple rice', 'Stacks of pork ribs make an alternative to chicken in this Caribbean feast with sticky marinade and fruity basmati accompaniment', 'spanish');
insert into feedme.recipe_steps values('chorizo-potato-and-cheese-omelette',0,0, 'Lay the ribs in a roasting tin and rub all over with 2 tbsp of the jerk spice mix. Leave to marinate for at least 2 hrs, or preferably overnight.');
insert into feedme.recipe_steps values('chorizo-potato-and-cheese-omelette',1,1, 'Heat oven to 160C/140C fan/gas 3. Put the ribs in a clean roasting tin, season and pour over 500ml of the pineapple juice. Cover the tin with foil and bake for 2 hrs.');
insert into feedme.recipe_steps values('chorizo-potato-and-cheese-omelette',2,2, 'Mix the remaining jerk spice mix, pineapple juice, sugar, vinegar, lime juice and a good pinch of salt in a saucepan. Heat until the sugar dissolves, then bubble until thick and sticky.');
insert into feedme.recipe_steps values('chorizo-potato-and-cheese-omelette',3,3, 'To make the rice, heat the oil in a large pan or wok. Add the shallot and pepper, and stir-fry until softened. Add the pineapple, brown sugar and plenty of seasoning, and continue cooking until the pineapple starts to caramelise.');
insert into feedme.recipe_steps values('chorizo-potato-and-cheese-omelette',4,4, 'Remove the ribs from the oven and pour away the cooking liquid. Increase oven to 200C/180C fan/gas 6. Pat the ribs dry with some kitchen paper, then brush the jerk sauce all over them. Return to the oven for 20 mins, brushing with any remaining sauce every 5 mins or so, until the ribs are really sticky.');
insert into feedme.recipe_steps values('chorizo-potato-and-cheese-omelette',5,5, 'Add the cooked rice to the pan with the pineapple and vegetables, stir-fry for a further 2 mins, stirring occasionally, then add the coriander. Serve with the sticky ribs – and some napkins!');
insert into feedme.recipes VALUES ('sea-bass-with-sizzled-ginger-chilli-and-spring-oni', 'Sea bass with sizzled ginger, chilli & spring onions', 'The aromas released while cooking this dish will have everyone licking their lips in anticipation', 'vietnamese');
insert into feedme.recipe_ingredients values('sea-bass-with-sizzled-ginger-chilli-and-spring-oni','sea bass','6 x sea bass fillets, about 140g/5oz each, skin on and scaled',140.0,'gram');
insert into feedme.recipe_ingredients values('sea-bass-with-sizzled-ginger-chilli-and-spring-oni','sunflower oil','about 3 tbsp sunflower oil',3.0,'tbsp');
insert into feedme.recipe_ingredients values('sea-bass-with-sizzled-ginger-chilli-and-spring-oni','ginger','large knob of ginger, peeled and shredded into matchsticks',NULL,'null');
insert into feedme.recipe_ingredients values('sea-bass-with-sizzled-ginger-chilli-and-spring-oni','garlic','3 garlic cloves, thinly sliced',3.0,'unit');
insert into feedme.recipe_ingredients values('sea-bass-with-sizzled-ginger-chilli-and-spring-oni','fat','3 fat, fresh red chilliesdeseeded and thinly shredded',3.0,'unit');
insert into feedme.recipe_ingredients values('sea-bass-with-sizzled-ginger-chilli-and-spring-oni','spring onion','bunch spring onion, shredded long-ways',NULL,'null');
insert into feedme.recipe_ingredients values('sea-bass-with-sizzled-ginger-chilli-and-spring-oni','soy sauce','1 tbsp soy sauce',1.0,'tbsp');
insert into feedme.recipe_steps values('sea-bass-with-sizzled-ginger-chilli-and-spring-oni',0,0, 'Season the fish with salt and pepper, then slash the skin 3 times. Heat a heavy-based frying pan and add 1 tbsp oil. Once hot, fry the fish, skin-side down, for 5 mins or until the skin is very crisp and golden. The fish will be almost cooked through. Turn over, cook for another 30 secs-1 min, then transfer to a serving plate and keep warm. You’ll need to fry the fish in 2 batches.');
insert into feedme.recipe_steps values('sea-bass-with-sizzled-ginger-chilli-and-spring-oni',1,1, 'Heat the remaining oil, then fry the ginger, garlic and chillies for about 2 mins until golden. Take off the heat and toss in the spring onions. Splash the fish with a little soy sauce and spoon over the contents of the pan.');
insert into feedme.recipes VALUES ('lamb-squash-and-apricot-tagine', 'Lamb, squash & apricot tagine', 'A Moroccan mainstay, this slow-cooked casserole is flavoured with coriander and ras-el-hanout spices', 'moroccan');
insert into feedme.recipe_ingredients values('lamb-squash-and-apricot-tagine','oil','2 tbsp oil',2.0,'tbsp');
insert into feedme.recipe_ingredients values('lamb-squash-and-apricot-tagine','onion','1 large onion, finely chopped',1.0,'unit');
insert into feedme.recipe_ingredients values('lamb-squash-and-apricot-tagine','garlic','2 garlic cloves, finely chopped',2.0,'unit');
insert into feedme.recipe_ingredients values('lamb-squash-and-apricot-tagine','ras-el-hanout','1 tbsp ras-el-hanout',1.0,'tbsp');
insert into feedme.recipe_ingredients values('lamb-squash-and-apricot-tagine','coriander','1 tsp ground coriander',1.0,'tsp');
insert into feedme.recipe_ingredients values('lamb-squash-and-apricot-tagine','fat','600g lambleg, diced into 2cm pieces, excess fat trimmed',600.0,'gram');
insert into feedme.recipe_ingredients values('lamb-squash-and-apricot-tagine','butternut squash','200g butternut squash, diced',200.0,'gram');
insert into feedme.recipe_ingredients values('lamb-squash-and-apricot-tagine','apricot','200g soft dried apricot',200.0,'gram');
insert into feedme.recipe_ingredients values('lamb-squash-and-apricot-tagine','tomato','400g can chopped tomato',400.0,'gram');
insert into feedme.recipe_ingredients values('lamb-squash-and-apricot-tagine','stock','500ml lamb or beef stock',500.0,'ml');
insert into feedme.recipe_ingredients values('lamb-squash-and-apricot-tagine','lemon','zest 1 lemon',NULL,'null');
insert into feedme.recipe_ingredients values('lamb-squash-and-apricot-tagine','coriander','small bunch coriander',NULL,'null');
insert into feedme.recipe_ingredients values('lamb-squash-and-apricot-tagine','couscous','couscous and natural yogurt, to serve',NULL,'null');
insert into feedme.recipe_steps values('lamb-squash-and-apricot-tagine',0,0, 'Heat oven to 200C/180C fan/gas 6. Heat the oil in a flameproof casserole dish, add the onion and cook for 5 mins until softened. Add the garlic and spices and cook for a couple mins more, stirring to prevent them catching and burning.');
insert into feedme.recipe_steps values('lamb-squash-and-apricot-tagine',1,1, 'Add the lamb, squash and apricots to the casserole, pour over the tomatoes and stock, season well and bring to the boil. Put the lid on and transfer to the oven. After 1 hr, stir the tagine and return to the oven, uncovered, for a further 30 mins.');
insert into feedme.recipe_steps values('lamb-squash-and-apricot-tagine',2,2, 'Check the seasoning. Sprinkle over the zest and coriander, and serve in warm bowls with couscous and yogurt.');
insert into feedme.recipes VALUES ('vietnamese-prawn-summer-rolls', 'Vietnamese prawn summer rolls', 'Fresh-tasting rice paper rolls packed with shellfish, light vermicelli noodles, carrots, cucumber and herbs, with a sweet ginger chilli dipping sauce', 'vietnamese');
insert into feedme.recipe_steps values('vietnamese-prawn-summer-rolls',0,0, 'Make the dipping sauce by pounding the garlic, chilli, ginger and sugar together using a pestle and mortar, then stir in the fish sauce and lime juice. Alternatively, just blitz everything together in a mini blender.');
insert into feedme.recipe_steps values('vietnamese-prawn-summer-rolls',1,1, 'Soak the rice noodles in a bowl of hot water for 15 mins, then drain well. Assemble all the prepared filling ingredients – once you start, you will need everything to hand.');
insert into feedme.recipe_steps values('vietnamese-prawn-summer-rolls',2,2, 'When you are ready to make the rolls, dip one of the rice papers in a bowl of hot water, moving it around until the whole wrapper is soft – about 10-15 secs – then drain on a tea towel.');
insert into feedme.recipe_steps values('vietnamese-prawn-summer-rolls',3,3, 'Place a rice paper wrapper on a board and at one edge of the wrapper, add a few mint leaves, then three prawn halves.');
insert into feedme.recipe_steps values('vietnamese-prawn-summer-rolls',4,4, 'Place some lettuce on top of the prawns, followed by some noodles, a few strips of carrot and cucumber, some more herbs and finally some beansprouts. Don’t overfill the pancakes or they will be hard to roll.');
insert into feedme.recipe_steps values('vietnamese-prawn-summer-rolls',5,5, 'Lift the edge of the rice paper wrapper nearest to you over the filling and, holding the filling in position with your fingers, start rolling up tightly.');
insert into feedme.recipe_steps values('vietnamese-prawn-summer-rolls',6,6, 'When you’re about halfway, fold the ends of the rice paper in and over the filling so that it is completely enclosed.');
insert into feedme.recipe_steps values('vietnamese-prawn-summer-rolls',7,7, 'Keep on rolling tightly until the whole rice paper wrapper is rolled up. To serve, cut the rolls in half on the diagonal.');
insert into feedme.recipes VALUES ('superfast-pad-thai', 'Super-fast Pad Thai', 'This classic Thai dish from Good Food reader Emily Cramer is made using mainly storecupboard ingredients, and it''s on the table in under 15 minutes', 'thai');
insert into feedme.recipe_ingredients values('superfast-pad-thai','rice','200g rice noodles',200.0,'gram');
insert into feedme.recipe_ingredients values('superfast-pad-thai','peas','140g frozen peas',140.0,'gram');
insert into feedme.recipe_ingredients values('superfast-pad-thai','prawn','200g frozen prawns',200.0,'gram');
insert into feedme.recipe_ingredients values('superfast-pad-thai','sunflower oil','2 tbsp sunflower oil',2.0,'tbsp');
insert into feedme.recipe_ingredients values('superfast-pad-thai','beansprouts','100g beansprouts',100.0,'gram');
insert into feedme.recipe_ingredients values('superfast-pad-thai','spring onion','small bunch spring onions, sliced',NULL,'null');
insert into feedme.recipe_ingredients values('superfast-pad-thai','egg','2 beaten eggs',2.0,'unit');
insert into feedme.recipe_ingredients values('superfast-pad-thai','nut','3 tbsp roasted peanuts',3.0,'tbsp');
insert into feedme.recipe_ingredients values('superfast-pad-thai','soy sauce','2 tbsp soy sauce',2.0,'tbsp');
insert into feedme.recipe_ingredients values('superfast-pad-thai','chilli','2 tbsp sweet chilli sauce',2.0,'tbsp');
insert into feedme.recipe_ingredients values('superfast-pad-thai','coriander','small bunch coriander, leaves only',NULL,'null');
insert into feedme.recipe_steps values('superfast-pad-thai',0,0, 'Bring a pan of water to the boil, add the noodles and cook for 3 mins, adding the peas and prawns for the final min. Drain, and set aside while you heat the oil in a large frying pan.');
insert into feedme.recipe_steps values('superfast-pad-thai',1,1, 'Fry the noodles, prawns, peas, beansprouts and spring onions, tossing to coat in the oil for a few mins. Push everything to one side of the pan and pour in the egg. Stir until cooked, then mix everything well. Toss through the peanuts, soy and sweet chilli sauce so everything is combined. Scatter with the coriander and serve.');
insert into feedme.recipes VALUES ('chicken-liver-and-pineau-pt', 'Chicken liver & pineau pâté', 'A rich, sweet pâté recipe using pineau - a sherry-like aperitif - great as a dinner party starter.', 'french');
insert into feedme.recipe_ingredients values('chicken-liver-and-pineau-pt','chicken','500g chicken livers',500.0,'gram');
insert into feedme.recipe_ingredients values('chicken-liver-and-pineau-pt','butter','100g butter',100.0,'gram');
insert into feedme.recipe_ingredients values('chicken-liver-and-pineau-pt','lardons','100g unsmoked lardons or chopped streaky bacon',100.0,'gram');
insert into feedme.recipe_ingredients values('chicken-liver-and-pineau-pt','garlic','1 garlic clove, crushed or finely chopped',1.0,'unit');
insert into feedme.recipe_ingredients values('chicken-liver-and-pineau-pt','thyme','1 tbsp fresh thyme leaves or 1 tsp dried',1.0,'tbsp');
insert into feedme.recipe_ingredients values('chicken-liver-and-pineau-pt','sherry','5 tbsp pineau or sherry',5.0,'tbsp');
insert into feedme.recipe_ingredients values('chicken-liver-and-pineau-pt','rosemary','herb sprigs to garnish, rosemary, thyme or bay',NULL,'null');
insert into feedme.recipe_ingredients values('chicken-liver-and-pineau-pt','bread','toasted brioche or raisin bread and cornichons, to serve',NULL,'null');
insert into feedme.recipe_steps values('chicken-liver-and-pineau-pt',0,0, 'Rinse the chicken livers and cut away any dark patches and small stringy threads. Pat dry with kitchen paper. Heat 25g of the butter in a frying pan until foaming, then add the lardons or bacon and fry until crisp. Add the garlic, chicken livers and thyme and fry briskly for about 5 mins, until they are evenly browned. They should be nicely browned on the outside, but pink inside and should feel squashy when pressed.');
insert into feedme.recipe_steps values('chicken-liver-and-pineau-pt',1,1, 'Add the pineau, salt and pepper, then bubble for a few mins. Remove from the heat. Blitz the mixture in a food processor until smooth, then spoon into a jar or dish. Smooth the top.');
insert into feedme.recipe_steps values('chicken-liver-and-pineau-pt',2,2, 'Melt the remaining butter, put a herb sprig or bay leaf on top of the pâté and pour over the butter, leaving the sediment behind. Leave to cool, then chill until set.4 Serve spooned from the dish with toasted brioche, a bowl of cornichons and a little sea salt.');
insert into feedme.recipes VALUES ('fragrant-thai-drumsticks', 'Fragrant Thai drumsticks', 'Marinate chicken drummers in aromatic Thai red curry paste and sweet chilli for super tasty fingerfood', 'thai');
insert into feedme.recipe_ingredients values('fragrant-thai-drumsticks','chilli','2 tbsp sweet chilli sauce',2.0,'tbsp');
insert into feedme.recipe_ingredients values('fragrant-thai-drumsticks','orange','grated zest 1 orange, plus 2 tbsp juice',2.0,'tbsp');
insert into feedme.recipe_ingredients values('fragrant-thai-drumsticks','garlic','1 garlic clove, crushed',1.0,'unit');
insert into feedme.recipe_ingredients values('fragrant-thai-drumsticks','curry paste','1 tbsp good-quality Thai red curry paste',1.0,'tbsp');
insert into feedme.recipe_ingredients values('fragrant-thai-drumsticks','chicken','8 chicken drumsticks, skin removed, the flesh slashed',8.0,'unit');
insert into feedme.recipe_steps values('fragrant-thai-drumsticks',0,0, 'Heat oven to 200C/180C fan/gas 6 and line a baking tray with foil. Mix the chilli sauce with the orange zest and juice, garlic, curry paste and ¼ tsp salt. Add the chicken and coat really well.');
insert into feedme.recipe_steps values('fragrant-thai-drumsticks',1,1, 'Arrange the drumsticks on the foil, spaced apart. Coat the chicken with any marinade left in the bowl, then roast for 35-40 mins until tender. Wrap in foil or pack into a food container.');
insert into feedme.recipes VALUES ('almond-and-honey-pastries-with-orange-cream', 'Almond & honey pastries with orange cream', 'Serve these prepare-ahead puds with coffee, dessert wine or fresh mint tea for a real after-dinner treat', 'turkish');
insert into feedme.recipe_ingredients values('almond-and-honey-pastries-with-orange-cream','almond','200g whole blanched almond, toasted and roughly chopped',200.0,'gram');
insert into feedme.recipe_ingredients values('almond-and-honey-pastries-with-orange-cream','icing','85g icing sugar, plus extra to serve',85.0,'gram');
insert into feedme.recipe_ingredients values('almond-and-honey-pastries-with-orange-cream','butter','85g butter, melted',85.0,'gram');
insert into feedme.recipe_ingredients values('almond-and-honey-pastries-with-orange-cream','orange','2 tsp orange flower water',2.0,'tsp');
insert into feedme.recipe_ingredients values('almond-and-honey-pastries-with-orange-cream','cinnamon','1 tsp cinnamon',1.0,'tsp');
insert into feedme.recipe_ingredients values('almond-and-honey-pastries-with-orange-cream','egg','1 egg yolk',1.0,'unit');
insert into feedme.recipe_ingredients values('almond-and-honey-pastries-with-orange-cream','pastry','190g pack brik pastry',190.0,'gram');
insert into feedme.recipe_ingredients values('almond-and-honey-pastries-with-orange-cream','honey','clear honey, to serve',NULL,'null');
insert into feedme.recipe_steps values('almond-and-honey-pastries-with-orange-cream',0,0, 'Set aside a quarter of the almonds. Put the rest in a food processor with the icing sugar, half the butter, orange blossom water, cinnamon and egg yolk. Whizz to a paste. Scrape into a bowl and stir in most of the remaining chopped nuts.');
insert into feedme.recipe_steps values('almond-and-honey-pastries-with-orange-cream',1,1, 'Unroll one sheet of brik pastry at a time, brush all over with melted butter and halve into 2 semi-circles. Shape 1 heaped tbsp of the nut mixture to look like a thin wedge shape along half the pastry edge you’ve just cut (leaving 2.5cm border on the rounded side), so it looks as if you’re cutting the semi-circle of pastry into pieces like a cake. Roll up like a pastry cone, sealing the nut mixture inside, then brush all over with more butter and fold over the open, wide end to seal. Sit on a baking tray, end flap side down to help it stay closed, and repeat until nut mixture is used up. Cover with cling film and chill. Put zest, yogurt, cream and icing sugar in a mixing bowl. Cover and chill.');
insert into feedme.recipe_steps values('almond-and-honey-pastries-with-orange-cream',2,2, 'Heat oven to 200C/180C fan/gas 6, put the pastries in and bake for 15 mins until golden and crisp. Just before they’re ready, beat together the yogurt mixture until thick. Pile pastries onto a serving dish, drizzle with honey and scatter with remaining nuts. Serve with the orange cream and Honeyed orange & grapefruit (see ''Goes well with'').');
insert into feedme.recipes VALUES ('thai-chicken-and-sweet-potato-soup', 'Thai chicken and sweet potato soup', 'A smooth and and spicy chicken soup with a vibrant colour that''s packed full of Asian aromatics', 'thai');
insert into feedme.recipe_ingredients values('thai-chicken-and-sweet-potato-soup','rapeseed oil','1 tsp olive or rapeseed oil',1.0,'tsp');
insert into feedme.recipe_ingredients values('thai-chicken-and-sweet-potato-soup','garlic','2 garlic cloves, chopped',2.0,'unit');
insert into feedme.recipe_ingredients values('thai-chicken-and-sweet-potato-soup','chilli','1 red chilli, deseeded and chopped',1.0,'unit');
insert into feedme.recipe_ingredients values('thai-chicken-and-sweet-potato-soup','ginger','2cm chunk root ginger, chopped',2.0,'unit');
insert into feedme.recipe_ingredients values('thai-chicken-and-sweet-potato-soup','lemongrass','1 stalk lemongrass, bashed',1.0,'unit');
insert into feedme.recipe_ingredients values('thai-chicken-and-sweet-potato-soup','coriander','1 x 25g pack coriander, leaves and stalks chopped separately',25.0,'gram');
insert into feedme.recipe_ingredients values('thai-chicken-and-sweet-potato-soup','curry paste','2 tbsp red Thai curry paste',2.0,'tbsp');
insert into feedme.recipe_ingredients values('thai-chicken-and-sweet-potato-soup','stock','750ml chicken stock(made with 2 stock cubes)',750.0,'ml');
insert into feedme.recipe_ingredients values('thai-chicken-and-sweet-potato-soup','coconut cream','1 small can (160ml) coconut cream',160.0,'ml');
insert into feedme.recipe_ingredients values('thai-chicken-and-sweet-potato-soup','sweet potato','500g sweet potato, peeled and roughly chopped',500.0,'gram');
insert into feedme.recipe_ingredients values('thai-chicken-and-sweet-potato-soup','chicken','2 skinless chicken breasts, sliced',2.0,'unit');
insert into feedme.recipe_ingredients values('thai-chicken-and-sweet-potato-soup','lime','1 lime, juice only',1.0,'unit');
insert into feedme.recipe_ingredients values('thai-chicken-and-sweet-potato-soup','sugar','1 tsp sugar',1.0,'tsp');
insert into feedme.recipe_ingredients values('thai-chicken-and-sweet-potato-soup','fish sauce','0.5 tsp fish sauce',5.0,'tsp');
insert into feedme.recipe_ingredients values('thai-chicken-and-sweet-potato-soup','bread','crusty bread, to serve (optional)',NULL,'null');
insert into feedme.recipe_steps values('thai-chicken-and-sweet-potato-soup',0,0, 'Heat the oil in a large saucepan. Add the garlic, chilli, ginger, lemongrass, coriander stalks and curry paste and cook for 2 - 3 minutes until the aromas are released.');
insert into feedme.recipe_steps values('thai-chicken-and-sweet-potato-soup',1,1, 'Add the chicken stock, coconut cream and sweet potatoes and cook for 15 minutes or until the potatoes are soft. Remove the lemongrass and discard. Carefully transfer to a blender and blitz until smooth. If freezing, for best results freeze at this point, otherwise as an easier option freeze complete recipe.');
insert into feedme.recipe_steps values('thai-chicken-and-sweet-potato-soup',2,2, 'Return to the saucepan, add the chicken and cook gently for 5 - 10 minutes or until the chicken is cooked through. Stir through the lime juice, sugar and fish sauce, scatter with the coriander leaves and serve.');
insert into feedme.recipes VALUES ('easy-thai-prawn-curry', 'Easy Thai prawn curry', 'Perfect Friday night food, a warming low fat prawn curry ready in just 20 minutes', 'thai');
insert into feedme.recipe_ingredients values('easy-thai-prawn-curry','vegetable oil','1 tbsp vegetable oil',1.0,'tbsp');
insert into feedme.recipe_ingredients values('easy-thai-prawn-curry','onion','1 onion, chopped',1.0,'unit');
insert into feedme.recipe_ingredients values('easy-thai-prawn-curry','ginger','1 tsp fresh root ginger',1.0,'tsp');
insert into feedme.recipe_ingredients values('easy-thai-prawn-curry','curry paste','1-2 tsp Thai red curry paste(we used Sharwood''s)',2.0,'tsp');
insert into feedme.recipe_ingredients values('easy-thai-prawn-curry','chopped tomatoes','400g can chopped tomatoes',400.0,'gram');
insert into feedme.recipe_ingredients values('easy-thai-prawn-curry','coconut cream','50g sachet coconut cream',50.0,'gram');
insert into feedme.recipe_ingredients values('easy-thai-prawn-curry','prawn','400g raw frozen prawns',400.0,'gram');
insert into feedme.recipe_ingredients values('easy-thai-prawn-curry','coriander','coriander, chopped, to serve (optional)',NULL,'null');
insert into feedme.recipe_steps values('easy-thai-prawn-curry',0,0, 'Heat the oil in a medium saucepan. Tip in the onion and ginger, then cook for a few mins until softened. Stir in the curry paste, then cook for 1 min more. Pour over the chopped tomatoes and coconut cream. Bring to the boil, then leave to simmer for 5 mins, adding a little boiling water if the mixture gets too thick.');
insert into feedme.recipe_steps values('easy-thai-prawn-curry',1,1, 'Tip in the prawns, then cook for 5-10 mins more, depending on how large they are. Serve alongside some plain rice and sprinkle with a little chopped coriander, if you like.');
insert into feedme.recipes VALUES ('summer-fish-stew-with-rouille', 'Summer fish stew with rouille', 'Conjure up thoughts of Mediterranean holidays with this light but indulgent meal for two', 'french');
insert into feedme.recipe_ingredients values('summer-fish-stew-with-rouille','prawn','6 large, raw, shell-on prawns',6.0,'unit');
insert into feedme.recipe_ingredients values('summer-fish-stew-with-rouille','olive oil','3 tbsp mild olive oil',3.0,'tbsp');
insert into feedme.recipe_ingredients values('summer-fish-stew-with-rouille','wine','150ml dry white wine',150.0,'ml');
insert into feedme.recipe_ingredients values('summer-fish-stew-with-rouille','stock','200ml fish stock(a cube is fine)',200.0,'ml');
insert into feedme.recipe_ingredients values('summer-fish-stew-with-rouille','fennel','1 small fennel bulb(about 140g/5oz) halved and thinly sliced',140.0,'gram');
insert into feedme.recipe_ingredients values('summer-fish-stew-with-rouille','onion','1 small onion, thinly sliced',1.0,'unit');
insert into feedme.recipe_ingredients values('summer-fish-stew-with-rouille','garlic','3 garlic cloves, thinly sliced',3.0,'unit');
insert into feedme.recipe_ingredients values('summer-fish-stew-with-rouille','potato','1 large potato, about 200g/8oz (I used King Edward)',200.0,'gram');
insert into feedme.recipe_ingredients values('summer-fish-stew-with-rouille','orange','1 orange',1.0,'unit');
insert into feedme.recipe_ingredients values('summer-fish-stew-with-rouille','anise','1 star anise',1.0,'unit');
insert into feedme.recipe_ingredients values('summer-fish-stew-with-rouille','bay leaf','1 bay leaf',1.0,'unit');
insert into feedme.recipe_ingredients values('summer-fish-stew-with-rouille','harissa','10.5 tsp harissa paste(I used Belazu)',5.0,'tsp');
insert into feedme.recipe_ingredients values('summer-fish-stew-with-rouille','tomato','2 tbsp tomato purée',2.0,'tbsp');
insert into feedme.recipe_ingredients values('summer-fish-stew-with-rouille','chopped tomatoes','400g can chopped tomatoes',400.0,'gram');
insert into feedme.recipe_ingredients values('summer-fish-stew-with-rouille','clams','handful musselsor clams',NULL,'null');
insert into feedme.recipe_ingredients values('summer-fish-stew-with-rouille','white fish','200g skinless, sustainably-caught white fish(I used cod loin), cut into very chunky pieces',200.0,'gram');
insert into feedme.recipe_ingredients values('summer-fish-stew-with-rouille','thyme','few thyme leaves',NULL,'null');
insert into feedme.recipe_steps values('summer-fish-stew-with-rouille',0,0, 'Twist the heads from the prawns, then peel away the legs and shells, but leave the tails intact. Devein each prawn. Fry the shells in 1 tbsp oil for 5 mins, until dark pink and golden in patches. Add the wine, boil down by two thirds, then pour in the stock. Strain into a jug, discarding the shells.');
insert into feedme.recipe_steps values('summer-fish-stew-with-rouille',1,1, 'Heat the rest of the oil in a deep frying pan or casserole. Add the fennel, onion and garlic, season, then cover and gently cook for 10 mins until softened. Meanwhile, peel the potato and cut into 2cm-ish chunks. Put into a pan of cold water, bring to the boil and cook for 5 mins until almost tender. Drain in a colander.');
insert into feedme.recipe_steps values('summer-fish-stew-with-rouille',2,2, 'Peel a strip of zest from the orange. Put the zest, star anise, bay and ½ tsp harissa into the pan. Fry gently, uncovered, for 5-10 mins, until the vegetables are soft, sweet and golden.');
insert into feedme.recipe_steps values('summer-fish-stew-with-rouille',3,3, 'Stir in the tomato purée, cook for 2 mins, then add the tomatoes and stock. Simmer for 10 mins until the sauce thickens slightly. Season to taste. The sauce can be made ahead, then reheated later in the day. Meantime, scrub the mussels or clams and pull away any stringy beards. Any that are open should be tapped sharply on the worktop – if they don’t close after a few seconds, discard them.');
insert into feedme.recipe_steps values('summer-fish-stew-with-rouille',4,4, 'Reheat the sauce if necessary, then stir the potato, chunks of fish and prawns very gently into the stew. Bring back to the boil, then cover and gently simmer for 3 mins. Scatter the mussels or clams over the stew, then cover and cook for 2 mins more or until the shells have opened wide. Discard any that remain closed. The chunks of fish should flake easily and the prawns should be pink through. Scatter with the thyme leaves.');
insert into feedme.recipe_steps values('summer-fish-stew-with-rouille',5,5, 'To make the quick rouille, stir the rest of the harissa through the mayonnaise. Serve the stew in bowls, topped with spoonfuls of rouille, which will melt into the sauce and enrich it. Have some good bread ready, as you’ll definitely want to mop up the juices.');
insert into feedme.recipes VALUES ('pistachio-pilau-rice', 'Pistachio pilau rice', 'This lightly spiced, vibrant side dish is the perfect accompaniment to curry', 'indian');
insert into feedme.recipe_ingredients values('pistachio-pilau-rice','onion','1 onion, sliced',1.0,'unit');
insert into feedme.recipe_ingredients values('pistachio-pilau-rice','vegetable oil','1 tbsp vegetable oil',1.0,'tbsp');
insert into feedme.recipe_ingredients values('pistachio-pilau-rice','turmeric','1 tsp turmeric',1.0,'tsp');
insert into feedme.recipe_ingredients values('pistachio-pilau-rice','rice','300g basmati rice',300.0,'gram');
insert into feedme.recipe_ingredients values('pistachio-pilau-rice','stock','600ml hot vegetable stock',600.0,'ml');
insert into feedme.recipe_ingredients values('pistachio-pilau-rice','spring onion','bunch spring onions, sliced',NULL,'null');
insert into feedme.recipe_ingredients values('pistachio-pilau-rice','pistachio','50g pistachio, roughly chopped',50.0,'gram');
insert into feedme.recipe_steps values('pistachio-pilau-rice',0,0, 'Fry the onion in the oil in a large frying pan or saucepan until softened. Stir in the turmeric, then the rice, and keep stirring until the grains are golden.');
insert into feedme.recipe_steps values('pistachio-pilau-rice',1,1, 'Pour in the hot stock, bring to the boil, then reduce the heat, cover and cook for 15 mins until the rice is tender. Remove from the heat and stir through the spring onions, pistachios and some seasoning before serving hot, or allowing to cool.');
insert into feedme.recipes VALUES ('basic-hollandaise', 'Basic hollandaise', 'This sauce, from Gordon Ramsay, takes some time to prepare, but think of it as a workout with a whisk', 'french');
insert into feedme.recipe_ingredients values('basic-hollandaise','wine','500ml white wine vinegar',500.0,'ml');
insert into feedme.recipe_ingredients values('basic-hollandaise','peppercorn','1 tbsp peppercorn',1.0,'tbsp');
insert into feedme.recipe_ingredients values('basic-hollandaise','tarragon','bunch tarragon',NULL,'null');
insert into feedme.recipe_ingredients values('basic-hollandaise','egg','3 large free-range egg yolks',3.0,'unit');
insert into feedme.recipe_ingredients values('basic-hollandaise','butter','200ml melted and skimmed unsalted butter(see Secrets for success, below)',200.0,'ml');
insert into feedme.recipe_ingredients values('basic-hollandaise','lemon juice','squeeze lemon juice',NULL,'null');
insert into feedme.recipe_steps values('basic-hollandaise',0,0, 'Boil the vinegar together with peppercorns and tarragon, reduce by half. Strain and reserve (see Secrets for success on storing, below).');
insert into feedme.recipe_steps values('basic-hollandaise',1,1, 'Boil a large pan of water, then reduce to a simmer. Using a large balloon whisk, beat together the yolks and 2 tsp of the reduced wine vinegar in a heatproof bowl that fits snugly over the pan.');
insert into feedme.recipe_steps values('basic-hollandaise',2,2, 'Beat vigorously until the mixture forms a foam, but make sure that it doesn’t get too hot. To prevent the sauce from overheating, take it on and off the heat while you whisk, scraping around the sides with a plastic spatula. The aim is to achieve a golden, airy foam (called a sabayon), which forms ribbons when the whisk is lifted.');
insert into feedme.recipe_steps values('basic-hollandaise',3,3, 'Whisk in a small ladle of the warmed butter, a little at a time, then return the bowl over a gentle heat to cook a little more. Remove from the heat again and whisk in another ladle of butter. Repeat until all the butter is incorporated and you have a texture as thick as mayonnaise. Finally, whisk in lemon juice, salt and pepper to taste plus a little warm water from the pan if the mixture is too thick.');
insert into feedme.recipes VALUES ('moroccan-spiced-cauliflower-and-almond-soup', 'Moroccan spiced cauliflower & almond soup', 'Satisfying and comforting soup is the ultimate meal in a bowl, like this smooth and spicy North African blend', 'moroccan');
insert into feedme.recipe_ingredients values('moroccan-spiced-cauliflower-and-almond-soup','cauliflower','1 large cauliflower',1.0,'unit');
insert into feedme.recipe_ingredients values('moroccan-spiced-cauliflower-and-almond-soup','olive oil','2 tbsp olive oil',2.0,'tbsp');
insert into feedme.recipe_ingredients values('moroccan-spiced-cauliflower-and-almond-soup','cumin','0.5 tsp each ground cinnamon, cumin and coriander',5.0,'tsp');
insert into feedme.recipe_ingredients values('moroccan-spiced-cauliflower-and-almond-soup','harissa','2 tbsp harissa paste, plus extra drizzle',2.0,'tbsp');
insert into feedme.recipe_ingredients values('moroccan-spiced-cauliflower-and-almond-soup','stock','1l hot vegetable or chicken stock',1.0,'litre');
insert into feedme.recipe_ingredients values('moroccan-spiced-cauliflower-and-almond-soup','almond','50g toasted flaked almond, plus extra to serve',50.0,'gram');
insert into feedme.recipe_steps values('moroccan-spiced-cauliflower-and-almond-soup',0,0, 'Cut the cauliflower into small florets. Fry olive oil, ground cinnamon, cumin and coriander and harissa paste for 2 mins in a large pan. Add the cauliflower, stock and almonds. Cover and cook for 20 mins until the cauliflower is tender. Blend soup until smooth, then serve with an extra drizzle of harissa and a sprinkle of toasted almonds.');
insert into feedme.recipes VALUES ('cauliflower-tempura', 'Cauliflower tempura', 'A healthy, Japanese-style snack', 'japanese');
insert into feedme.recipe_ingredients values('cauliflower-tempura','cauliflower','1 small cauliflower, cut into medium-size florets',1.0,'unit');
insert into feedme.recipe_ingredients values('cauliflower-tempura','flour','100g plain flour',100.0,'gram');
insert into feedme.recipe_ingredients values('cauliflower-tempura','egg','2 eggs, separated',2.0,'unit');
insert into feedme.recipe_ingredients values('cauliflower-tempura','vegetable oil','2 tbsp vegetable oil',2.0,'tbsp');
insert into feedme.recipe_ingredients values('cauliflower-tempura','vegetable oil','vegetable oil for deep frying',NULL,'null');
insert into feedme.recipe_steps values('cauliflower-tempura',0,0, 'Cook the cauliflower in boiling water for 2 mins. Tip into a colander, rinse under the cold tap, drain well and set aside.');
insert into feedme.recipe_steps values('cauliflower-tempura',1,1, 'Put the flour in a large bowl with a pinch of salt. Whisk the egg yolks and 175ml iced water together, then whisk into the flour with the oil to make a smooth batter. In a separate, clean bowl whisk the egg whites until stiff, then fold into the batter.');
insert into feedme.recipe_steps values('cauliflower-tempura',2,2, 'Heat the oil in a deep fryer or large wok to 190C. Coat some of the cauliflower in the batter then use a slotted spoon to carefully lift into the hot oil. Deep-fry the cauliflower for 2-3 mins until golden and puffy. Drain on kitchen towel and continue with the rest in batches. Sprinkle the cauliflower with salt and serve with a bowl of aïoli or mayonnaise.');
insert into feedme.recipes VALUES ('butternut-and-harissa-houmous', 'Butternut & harissa hummus', 'Roast pumpkin with plenty of garlic then blitz into a Moroccan-style dip with spicy harissa and sesame paste', 'moroccan');
insert into feedme.recipe_ingredients values('butternut-and-harissa-houmous','butternut squash','0.5 butternut squash(about 400g), peeled and cut into 2cm pieces',400.0,'gram');
insert into feedme.recipe_ingredients values('butternut-and-harissa-houmous','garlic','3 garlic cloves, unpeeled',3.0,'unit');
insert into feedme.recipe_ingredients values('butternut-and-harissa-houmous','olive oil','2 tbsp olive oil',2.0,'tbsp');
insert into feedme.recipe_ingredients values('butternut-and-harissa-houmous','tahini','3 tbsp tahini paste',3.0,'tbsp');
insert into feedme.recipe_ingredients values('butternut-and-harissa-houmous','harissa','1 tbsp harissa, plus a little extra for drizzling',1.0,'tbsp');
insert into feedme.recipe_ingredients values('butternut-and-harissa-houmous','peas','400g can chickpeas, drained and rinsed',400.0,'gram');
insert into feedme.recipe_steps values('butternut-and-harissa-houmous',0,0, 'Heat oven to 200C/180C fan/gas 6. Put the butternut squash and garlic cloves in a roasting tin, season well and add 100ml water. Cover the tin with foil and bake for 45 mins, until the squash is really tender. Leave to cool.');
insert into feedme.recipe_steps values('butternut-and-harissa-houmous',1,1, 'Tip the squash into a food processor with any juices from the tin. Add the garlic cloves, squeezed out of their skins. Add the remaining ingredients, season with salt and blend to a paste.');
insert into feedme.recipe_steps values('butternut-and-harissa-houmous',2,2, 'Scrape the hummus into a bowl. Drizzle with extra harissa before serving.');
insert into feedme.recipes VALUES ('moroccan-chard-and-lamb-panfry', 'Moroccan chard & lamb pan-fry', 'A quick, exotic one-pan dish', 'moroccan');
insert into feedme.recipe_ingredients values('moroccan-chard-and-lamb-panfry','chard','1 bunch chard',1.0,'unit');
insert into feedme.recipe_ingredients values('moroccan-chard-and-lamb-panfry','olive oil','1oz olive oil',1.0,'unit');
insert into feedme.recipe_ingredients values('moroccan-chard-and-lamb-panfry','lamb','600g diced shoulder of lamb',600.0,'gram');
insert into feedme.recipe_ingredients values('moroccan-chard-and-lamb-panfry','onion','1 onion, sliced',1.0,'unit');
insert into feedme.recipe_ingredients values('moroccan-chard-and-lamb-panfry','garlic','2 garlic cloves, sliced',2.0,'unit');
insert into feedme.recipe_ingredients values('moroccan-chard-and-lamb-panfry','cumin','1 tsp each ground turmeric, cumin seeds, coriander seeds',1.0,'tsp');
insert into feedme.recipe_ingredients values('moroccan-chard-and-lamb-panfry','chilli','pinch chilli flakes',NULL,'null');
insert into feedme.recipe_ingredients values('moroccan-chard-and-lamb-panfry','stock','400ml stock, lamb or chicken',400.0,'ml');
insert into feedme.recipe_ingredients values('moroccan-chard-and-lamb-panfry','raisins','handful raisins',NULL,'null');
insert into feedme.recipe_ingredients values('moroccan-chard-and-lamb-panfry','nut','handful toasted pine nuts',NULL,'null');
insert into feedme.recipe_steps values('moroccan-chard-and-lamb-panfry',0,0, 'Strip the chard leaves from the stalk. Cut the stalk into batons and roughly shred the leaves. Set aside separately.');
insert into feedme.recipe_steps values('moroccan-chard-and-lamb-panfry',1,1, 'Heat the oil in a sauté pan and fry the lamb for 5-6 mins over a high heat until browned. Add the onion, garlic, chard stalks and spices and continue to cook for 3-4 mins until softened. Pour over the stock and scatter in the raisins, then simmer for 4-5 mins to make a sauce. Wilt chard leaves through the stock, season and serve scattered with pine nuts.');
insert into feedme.recipes VALUES ('spanish-seafood-rice', 'Spanish seafood rice', 'This quick version of paella is superhealthy, plus it''s all cooked in one pan so there''s minimal washing-up', 'spanish');
insert into feedme.recipe_ingredients values('spanish-seafood-rice','olive oil','1 tbsp olive oil',1.0,'tbsp');
insert into feedme.recipe_ingredients values('spanish-seafood-rice','onion','1 onion, finely chopped',1.0,'unit');
insert into feedme.recipe_ingredients values('spanish-seafood-rice','pepper','1 red and 1 yellow pepper, deseeded and sliced',1.0,'unit');
insert into feedme.recipe_ingredients values('spanish-seafood-rice','garlic','2 garlic cloves, sliced',2.0,'unit');
insert into feedme.recipe_ingredients values('spanish-seafood-rice','rice','250g paella rice',250.0,'gram');
insert into feedme.recipe_ingredients values('spanish-seafood-rice','salt','850ml hot vegetable stock(we used Kallo very low salt stock)',850.0,'ml');
insert into feedme.recipe_ingredients values('spanish-seafood-rice','saffron','pinch saffron',NULL,'null');
insert into feedme.recipe_ingredients values('spanish-seafood-rice','seafood','400g seafood mix(we used a bag of frozen mixed seafood, defrosted before use)',400.0,'gram');
insert into feedme.recipe_ingredients values('spanish-seafood-rice','lemon','juice 0.5 small lemon',NULL,'null');
insert into feedme.recipe_ingredients values('spanish-seafood-rice','parsley','small handful flat-leaf parsley, roughly chopped',NULL,'null');
insert into feedme.recipe_steps values('spanish-seafood-rice',0,0, 'Heat the oil in a large saucepan and soften the onion for 6-7 mins. Add the pepper and garlic, cook for 2 mins more, then stir in the paella rice and cook for 1 min, stirring to coat.');
insert into feedme.recipe_steps values('spanish-seafood-rice',1,1, 'Pour in the stock, add the saffron and bring to the boil. Cook, uncovered, at a gentle bubble, for 20 mins, stirring occasionally until the rice is tender.');
insert into feedme.recipe_steps values('spanish-seafood-rice',2,2, 'Stir in the seafood and lemon juice and cook for 2 mins or until piping hot and completely cooked through. Serve in warm bowls scattered with the parsley.');
insert into feedme.recipes VALUES ('indian-lamb-with-spiced-pumpkin-pickle', 'Indian lamb with spiced pumpkin pickle', 'A spicy new way to serve up lamb, with a pickle that makes a delicious accompaniment to Indian dishes', 'indian');
insert into feedme.recipe_ingredients values('indian-lamb-with-spiced-pumpkin-pickle','lamb','8 lamb cutlets',8.0,'unit');
insert into feedme.recipe_ingredients values('indian-lamb-with-spiced-pumpkin-pickle','yoghurt','150ml tub natural yogurt',150.0,'ml');
insert into feedme.recipe_ingredients values('indian-lamb-with-spiced-pumpkin-pickle','coriander','1 tsp ground coriander',1.0,'tsp');
insert into feedme.recipe_ingredients values('indian-lamb-with-spiced-pumpkin-pickle','turmeric','1 tsp turmeric',1.0,'tsp');
insert into feedme.recipe_ingredients values('indian-lamb-with-spiced-pumpkin-pickle','coriander','small bunch coriander, chopped',NULL,'null');
insert into feedme.recipe_ingredients values('indian-lamb-with-spiced-pumpkin-pickle','lemon','juice and zest 1 lemon',NULL,'null');
insert into feedme.recipe_ingredients values('indian-lamb-with-spiced-pumpkin-pickle','naan bread','naan bread, to serve',NULL,'null');
insert into feedme.recipe_steps values('indian-lamb-with-spiced-pumpkin-pickle',0,0, 'To make the pickle, cook the squash in boiling water for 6-10 mins until just tender but not falling apart. Drain gently.');
insert into feedme.recipe_steps values('indian-lamb-with-spiced-pumpkin-pickle',1,1, 'Heat the oil in a pan and fry the onions, ginger and chilli for 10-12 mins until soft. Add the mustard seeds, ground coriander and turmeric for 1 min. Stir in the cornflour, then the sugar, then gradually stir in the vinegar. Simmer for 6-8 mins until thickened. Gently stir in the squash until just hot, then divide between 2 hot, sterilised jars and cover. Cool completely. The pickle can be eaten straight away or left in the fridge, unopened, for up to 1 month.');
insert into feedme.recipe_steps values('indian-lamb-with-spiced-pumpkin-pickle',2,2, 'Put the lamb into a dish. Mix together the yogurt, ground coriander, turmeric, nearly all the chopped coriander, the lemon zest and juice and marinate for 1 hr or up to 1 day.');
insert into feedme.recipe_steps values('indian-lamb-with-spiced-pumpkin-pickle',3,3, 'Griddle or pan-fry the lamb for 1-2 mins on each side until cooked to your liking, then scatter over the remaining coriander and eat with spoonfuls of pickle and naan.');
insert into feedme.recipes VALUES ('sweet-potato-salad', 'Sweet potato salad', 'This simple healthy salad of baked potato chunks makes a great alternative to mayonnaise-based versions', 'turkish');
insert into feedme.recipe_ingredients values('sweet-potato-salad','sweet potato','1.2kg sweet potato, peeled and cut into biggish chunks',1.0,'unit');
insert into feedme.recipe_ingredients values('sweet-potato-salad','olive oil','1 tbsp olive oil',1.0,'tbsp');
insert into feedme.recipe_steps values('sweet-potato-salad',0,0, 'Heat oven to 200C/180C fan/gas 6. Toss the sweet potato chunks with the olive oil and some seasoning, and spread on a baking parchment-lined baking sheet. Roast for 30 - 35 mins until tender and golden. Cool at room temperature.');
insert into feedme.recipe_steps values('sweet-potato-salad',1,1, 'When just about cool whisk together all the dressing ingredients with a little more seasoning and gently toss through the potato chunks – use your hands to avoid breaking them up.');
insert into feedme.recipes VALUES ('chinese-steamed-bass-with-cabbage', 'Chinese steamed bass with cabbage', 'Full of flavour and void of guilt, this low-fat fish dish is full of omega 3 and counts as 1 of your 5-a-day. A perfect mid week meal', 'chinese');
insert into feedme.recipe_ingredients values('chinese-steamed-bass-with-cabbage','white fish','2 sea bass, or other white fish fillets',2.0,'unit');
insert into feedme.recipe_ingredients values('chinese-steamed-bass-with-cabbage','chilli','1 green or red chilli, deseeded and finely chopped',1.0,'unit');
insert into feedme.recipe_ingredients values('chinese-steamed-bass-with-cabbage','ginger','1 tsp fresh root ginger',1.0,'tsp');
insert into feedme.recipe_ingredients values('chinese-steamed-bass-with-cabbage','green cabbage','300g green cabbage, finely shredded',300.0,'gram');
insert into feedme.recipe_ingredients values('chinese-steamed-bass-with-cabbage','sunflower oil','2 tsp sunflower oil',2.0,'tsp');
insert into feedme.recipe_ingredients values('chinese-steamed-bass-with-cabbage','sesame oil','1 tsp sesame oil',1.0,'tsp');
insert into feedme.recipe_ingredients values('chinese-steamed-bass-with-cabbage','garlic','2 garlic cloves, thinly sliced',2.0,'unit');
insert into feedme.recipe_ingredients values('chinese-steamed-bass-with-cabbage','salt','2 tsp low salt soy sauce',2.0,'tsp');
insert into feedme.recipe_steps values('chinese-steamed-bass-with-cabbage',0,0, 'Sprinkle the fish with the chilli, ginger and a little salt. Steam the cabbage for 5 mins. Lay fish on top of the cabbage and steam for a further 5 mins until cooked through.');
insert into feedme.recipe_steps values('chinese-steamed-bass-with-cabbage',1,1, 'Meanwhile, heat the oils in a small pan, add the garlic and quickly cook, stirring until lightly browned. Transfer the cabbage and fish to serving plates, sprinkle each with 1 tsp of soy sauce, then pour over the garlicky oil.');
insert into feedme.recipes VALUES ('ginger-sweet-tofu-with-pak-choi', 'Ginger sweet tofu with pak choi', 'If you thought tofu was bland, this is the recipe to convince you otherwise', 'chinese');
insert into feedme.recipe_ingredients values('ginger-sweet-tofu-with-pak-choi','tofu','250g fresh firm tofu, drained',250.0,'gram');
insert into feedme.recipe_ingredients values('ginger-sweet-tofu-with-pak-choi','nut','2 tbsp groundnut oil',2.0,'tbsp');
insert into feedme.recipe_ingredients values('ginger-sweet-tofu-with-pak-choi','ginger','1cm piece ginger, sliced',1.0,'unit');
insert into feedme.recipe_ingredients values('ginger-sweet-tofu-with-pak-choi','pak choi','200g pak choi, leaves separated',200.0,'gram');
insert into feedme.recipe_ingredients values('ginger-sweet-tofu-with-pak-choi','wine','1 tbsp Shaohsing rice wine',1.0,'tbsp');
insert into feedme.recipe_ingredients values('ginger-sweet-tofu-with-pak-choi','rice','1 tbsp rice vinegar',1.0,'tbsp');
insert into feedme.recipe_ingredients values('ginger-sweet-tofu-with-pak-choi','chilli','0.5 tsp dried chilli flakes',5.0,'tsp');
insert into feedme.recipe_ingredients values('ginger-sweet-tofu-with-pak-choi','rice','cooked jasmine rice, to serve',NULL,'null');
insert into feedme.recipe_steps values('ginger-sweet-tofu-with-pak-choi',0,0, 'Gently prick a few holes in the tofu with a toothpick (this will help the marinade to soak into it, giving better flavour), then cut into bite-size cubes.');
insert into feedme.recipe_steps values('ginger-sweet-tofu-with-pak-choi',1,1, 'Mix the marinade ingredients together in a bowl and toss in the tofu pieces. Set aside to marinate for 10-15 mins.');
insert into feedme.recipe_steps values('ginger-sweet-tofu-with-pak-choi',2,2, 'Heat a wok over high heat and add half the groundnut oil. When the oil starts to smoke, add the ginger slices and stir-fry for a few secs. Add the pak choy leaves and stir-fry for 1-2 mins. Add a small splash of water to create some steam and cook for 2 mins more. When the leaves have wilted and the stems are cooked but still a little crunchy, season with salt and transfer to a serving dish.');
insert into feedme.recipe_steps values('ginger-sweet-tofu-with-pak-choi',3,3, 'Rinse the wok under cold water, then reheat it and add the remaining oil. When it starts to smoke, add the tofu pieces (retaining the marinade liquid) and stir-fry for 5-10 mins. Take care not to break up the tofu as you toss it to get it browned evenly on all sides. Season with the rice wine and rice vinegar. Add the remaining marinade liquid, bring to the bubble and let the liquid reduce. Sprinkle over the chilli flakes and toss well. Spoon onto the pak choy and serve immediately with jasmine rice, if you like.');
insert into feedme.recipes VALUES ('turkishstyle-lamb', 'Turkish-style lamb', 'Bring the taste of Turkey to your kitchen with these fun lamb pittas, great hand held food', 'turkish');
insert into feedme.recipe_ingredients values('turkishstyle-lamb','lamb','4 lamb leg steaks',4.0,'unit');
insert into feedme.recipe_ingredients values('turkishstyle-lamb','hake','2 tsp seasoning (we used Schwartz Lamb Simply Shake seasoning)',2.0,'tsp');
insert into feedme.recipe_ingredients values('turkishstyle-lamb','oregano','1 tsp dried oregano, or mixed herbs would do',1.0,'tsp');
insert into feedme.recipe_ingredients values('turkishstyle-lamb','mint','small bunch mint, leaves only, chopped',NULL,'null');
insert into feedme.recipe_ingredients values('turkishstyle-lamb','fat','2 x 150g tubs low-fat plain yogurt',150.0,'gram');
insert into feedme.recipe_ingredients values('turkishstyle-lamb','pitta bread','4 pitta breads, white or wholemeal',4.0,'unit');
insert into feedme.recipe_ingredients values('turkishstyle-lamb','iceberg lettuce','0.5 iceberg lettuce, shredded',0.0,'unit');
insert into feedme.recipe_ingredients values('turkishstyle-lamb','red onion','1 red onion, thinly sliced',1.0,'unit');
insert into feedme.recipe_ingredients values('turkishstyle-lamb','lemon','1 lemon, cut into wedges for squeezing, optional',1.0,'unit');
insert into feedme.recipe_steps values('turkishstyle-lamb',0,0, 'Heat grill to high. Season the lamb, then grill for 2 mins on each side until browned, but still very rare. Meanwhile, mix the seasoning, oregano and half of the mint into one of the tubs of yogurt, smother this over the lamb, then return to the grill for another 2-3 mins or until the yogurt is blistered and the meat is cooked to your liking.');
insert into feedme.recipe_steps values('turkishstyle-lamb',1,1, 'Leave the meat to rest on a board for a few mins while you toast the pittas, shred the lettuce and thinly slice the red onion. Stir the rest of the mint into the second tub of yogurt. Thickly slice the meat and stuff into the pitta bread with the salad and minted yogurt. Squeeze over lemon juice before tucking in, if you like.');
insert into feedme.recipes VALUES ('honey-yogurt-cheesecake', 'Honey yogurt cheesecake', 'Bring a touch of Mediterranean magic to your table with this no-fuss bake that can be made ahead', 'greek');
insert into feedme.recipe_ingredients values('honey-yogurt-cheesecake','ginger','100g amaretti biscuits(or ginger or digestive biscuits)',100.0,'gram');
insert into feedme.recipe_ingredients values('honey-yogurt-cheesecake','flaked almonds','85g toasted, flaked almonds',85.0,'gram');
insert into feedme.recipe_ingredients values('honey-yogurt-cheesecake','butter','85g butter, melted',85.0,'gram');
insert into feedme.recipe_ingredients values('honey-yogurt-cheesecake','yoghurt','250ml Greek yogurt',250.0,'ml');
insert into feedme.recipe_ingredients values('honey-yogurt-cheesecake','mascarpone','750g mascarpone',750.0,'gram');
insert into feedme.recipe_ingredients values('honey-yogurt-cheesecake','egg','2 eggs',2.0,'unit');
insert into feedme.recipe_ingredients values('honey-yogurt-cheesecake','lemon','zest 1 lemon',NULL,'null');
insert into feedme.recipe_ingredients values('honey-yogurt-cheesecake','orange','zest 1 orange',NULL,'null');
insert into feedme.recipe_ingredients values('honey-yogurt-cheesecake','orange','250ml honey(orange blossom is particularly good)',250.0,'ml');
insert into feedme.recipe_ingredients values('honey-yogurt-cheesecake','fruit','fresh fruit, to serve',NULL,'null');
insert into feedme.recipe_steps values('honey-yogurt-cheesecake',0,0, 'Heat oven to 160C/140C fan/gas 3. Crush the biscuits and most of the almonds inside a plastic food bag using a rolling pin. Mix with the butter, then press into the bottom of a deep, oval, 23cm dish (or something similar in size – a roasting tin, baking dish or cake tin will work). Bake for 10 mins until crisp.');
insert into feedme.recipe_steps values('honey-yogurt-cheesecake',1,1, 'Stir or mash together the yogurt and mascarpone, then whisk in the eggs, one at a time. Stir in the lemon and orange zests, then stir in most of the honey, reserving about 3 tbsp. Spread over the biscuit base, cover loosely with foil and cook for 1 hr. Remove the foil and cook for 15 mins more until lightly golden and the top is firm with just the slightest wobble in the middle. Leave to cool. Can be kept in the fridge for up to 2 days.');
insert into feedme.recipe_steps values('honey-yogurt-cheesecake',2,2, 'To serve, scatter with almonds, drizzle over the remaining honey, and hand around fresh fruit to go with it.');
insert into feedme.recipes VALUES ('spicy-thai-prawn-noodles', 'Spicy Thai prawn noodles', 'These low-fat noodles make for a no-fuss supper and will be on the table in 30 minutes', 'thai');
insert into feedme.recipe_ingredients values('spicy-thai-prawn-noodles','rice','200g Thai rice noodle',200.0,'gram');
insert into feedme.recipe_ingredients values('spicy-thai-prawn-noodles','sesame oil','1 tbsp sesame oil',1.0,'tbsp');
insert into feedme.recipe_ingredients values('spicy-thai-prawn-noodles','egg','2 eggs, beaten',2.0,'unit');
insert into feedme.recipe_ingredients values('spicy-thai-prawn-noodles','chilli','1 red chilli, finely chopped',1.0,'unit');
insert into feedme.recipe_ingredients values('spicy-thai-prawn-noodles','ginger','1 thumb-size piece ginger, grated',1.0,'unit');
insert into feedme.recipe_ingredients values('spicy-thai-prawn-noodles','prawn','450g peeled cooked Atlantic prawn',450.0,'gram');
insert into feedme.recipe_ingredients values('spicy-thai-prawn-noodles','soy sauce','1 tbsp soy sauce',1.0,'tbsp');
insert into feedme.recipe_ingredients values('spicy-thai-prawn-noodles','coriander','small bunch coriander, roughly chopped',NULL,'null');
insert into feedme.recipe_ingredients values('spicy-thai-prawn-noodles','mint','0.5 small bunch mint, roughly chopped',0.0,'unit');
insert into feedme.recipe_ingredients values('spicy-thai-prawn-noodles','lime','zest and juice 1 lime',NULL,'null');
insert into feedme.recipe_ingredients values('spicy-thai-prawn-noodles','nut','small handful roasted peanuts, chopped',NULL,'null');
insert into feedme.recipe_steps values('spicy-thai-prawn-noodles',0,0, 'Cook the noodles following the pack instructions, drain and set aside for later. Heat the oil in a large frying pan and pour in the beaten egg. Swirl around the pan to make a thin omelette, cook for 1-2 mins, then flip over and cook the other side for 1 min. Tip out and slice into thin strips.');
insert into feedme.recipe_steps values('spicy-thai-prawn-noodles',1,1, 'Add the chilli and ginger to the pan, fry for 1-2 mins then tip in the noodles, prawns and egg. Splash in the soy sauce and stir-fry for 1 min more. Throw in the chopped herbs and pour over the lime juice and zest, then sprinkle over the chopped peanuts and serve.');
insert into feedme.recipes VALUES ('the-ultimate-makeover-steak-and-kidney-pie', 'The ultimate makeover: Steak & kidney pie', 'The ultimate comfort dish gets a superhealthy makeover', 'british');
insert into feedme.recipe_steps values('the-ultimate-makeover-steak-and-kidney-pie',0,0, 'Cut out and discard the thin tubes from the kidneys. Rinse the kidneys in cold water until the water runs clear, then chop them into small pieces. Heat the oil in a large saucepan or deep sauté pan. Add the onions, bay and thyme sprigs and fry over a medium heat for 8-10 mins until the onions are really golden, stirring often. Put the kettle on.');
insert into feedme.recipe_steps values('the-ultimate-makeover-steak-and-kidney-pie',1,1, 'Add the steak and kidney to the pan and stir-fry briefly, just until it loses its pink colour. Turn up the heat, pour in the wine, stir to deglaze the bottom of the pan, then let it boil over a high heat for 2-3 mins until reduced and absorbed into the meat. Stir in the tomato purée and mustard powder. Sift in the flour, stirring, then stir for a couple of mins. Pour in 400ml boiling water and continue stirring until the mixture starts to boil and is thickened. Tip in the carrot and both mushrooms, reduce the heat, cover with a lid, then leave to simmer gently for about 1 hr, stirring occasionally. Remove the lid and simmer another 25-30 mins or until the meat is very tender and the gravy has thickened slightly.');
insert into feedme.recipe_steps values('the-ultimate-makeover-steak-and-kidney-pie',2,2, 'Remove from the heat and remove the bay leaves and thyme sprigs. Stir in the parsley, season to taste, then transfer to a pie or ovenproof dish (22-23cm in diameter, 6cm deep, 1.7-litre capacity or similar), then leave to cool slightly. Heat oven to 200C/180C fan/gas 6.');
insert into feedme.recipe_steps values('the-ultimate-makeover-steak-and-kidney-pie',3,3, 'While the meat is cooling, make the pastry. Put the flour, and thyme if using, into a bowl. Grate in the cold or frozen butter, make a well in the centre, then add the yogurt, olive oil, a pinch of salt and a good grinding of black pepper. Using a round-bladed knife, mix together with 2 tsp cold water, then gently gather together with your hands to form a dough. Remove from the bowl and knead briefly until smooth.');
insert into feedme.recipe_steps values('the-ultimate-makeover-steak-and-kidney-pie',4,4, 'Roll out the pastry on a lightly floured surface so it’s slightly bigger than the top of the pie dish. Lay the pastry over the meat and trim the edges with scissors so it slightly overhangs the edge of the dish. Make 2 small slits in the centre. Flute the edges, then roll out the trimmings and cut out 6 diamond-shaped leaves. Dampen one side and lay them on the pastry lid. Place the dish on a baking sheet, then bake in the oven for about 25 mins or until the pastry is golden.');
insert into feedme.recipes VALUES ('warm-salad-of-chicken-livers-smoked-paprika-and-sh', 'Warm salad of chicken livers, smoked paprika & Sherry', 'A delicious warm salad, full of smoky, garlicky flavours', 'spanish');
insert into feedme.recipe_steps values('warm-salad-of-chicken-livers-smoked-paprika-and-sh',0,0, 'Put all the ingredients for the sauce into a food processor, then whizz to a rough paste. Heat oven to 200C/180C fan/ gas 6. Put the remaining hazelnuts onto a baking sheet, roast for 5 mins until lightly toasted, roughly chop, then set aside.');
insert into feedme.recipe_steps values('warm-salad-of-chicken-livers-smoked-paprika-and-sh',1,1, 'Now make the salad – add the olive oil and butter to a large frying pan over a medium-high heat. Quickly fry the livers in 2 batches until browned, about 2 mins per batch. Add the Sherry, then bubble for a few mins over a high heat. Stir in the sauce and allow it to thicken and heat through. Splash in the lemon juice, then season to taste. Dress the salad leaves with a little Sherry vinegar. Spoon the livers and sauce onto serving plates, sprinkle with the toasted chopped hazelnuts, then scatter over the dressed salad leaves and serve straightaway.');
insert into feedme.recipes VALUES ('turkish-lamb-pilau', 'Turkish lamb pilau', 'Serve up a basmati rice one pot flavoured with cinnamon, mint and apricot, studded with tender lamb', 'turkish');
insert into feedme.recipe_ingredients values('turkish-lamb-pilau','flaked almonds','small handful pine nuts or flaked almonds',NULL,'null');
insert into feedme.recipe_ingredients values('turkish-lamb-pilau','olive oil','1 tbsp olive oil',1.0,'tbsp');
insert into feedme.recipe_ingredients values('turkish-lamb-pilau','onion','1 large onion, halved and sliced',1.0,'unit');
insert into feedme.recipe_ingredients values('turkish-lamb-pilau','cinnamon','2 cinnamon sticks, broken in half',2.0,'unit');
insert into feedme.recipe_ingredients values('turkish-lamb-pilau','lamb','500g lean lamb neck fillet, cubed',500.0,'gram');
insert into feedme.recipe_ingredients values('turkish-lamb-pilau','rice','250g basmati rice',250.0,'gram');
insert into feedme.recipe_ingredients values('turkish-lamb-pilau','stock','1 lamb or vegetable stock cube',1.0,'unit');
insert into feedme.recipe_ingredients values('turkish-lamb-pilau','dried apricots','12 ready-to-eat dried apricots',12.0,'unit');
insert into feedme.recipe_ingredients values('turkish-lamb-pilau','mint','handful fresh mint leaves, roughly chopped',NULL,'null');
insert into feedme.recipe_steps values('turkish-lamb-pilau',0,0, 'Dry-fry the pine nuts or almonds in a large pan until lightly toasted, then tip onto a plate. Add the oil to the pan, then fry the onion and cinnamon together until starting to turn golden. Turn up the heat, stir in the lamb, fry until the meat changes colour, then tip in the rice and cook for 1 min, stirring all the time.');
insert into feedme.recipe_steps values('turkish-lamb-pilau',1,1, 'Pour in 500ml boiling water, crumble in the stock cube, add the apricots, then season to taste. Turn the heat down, cover and simmer for 12 mins until the rice is tender and the stock has been absorbed. Toss in the pine nuts and mint and serve.');
insert into feedme.recipes VALUES ('custard-and-white-chocolate-biscuits', 'Custard & white chocolate biscuits', 'A chocolate chip cookie and custard cream in one - super-simple and they''ll keep in the biscuit tin for a fortnight', 'american');
insert into feedme.recipe_ingredients values('custard-and-white-chocolate-biscuits','butter','140g butter, softened',140.0,'gram');
insert into feedme.recipe_ingredients values('custard-and-white-chocolate-biscuits','caster sugar','175g caster sugar',175.0,'gram');
insert into feedme.recipe_ingredients values('custard-and-white-chocolate-biscuits','egg','1 egg',1.0,'unit');
insert into feedme.recipe_ingredients values('custard-and-white-chocolate-biscuits','vanilla extract','0.5 tsp vanilla extract',5.0,'tsp');
insert into feedme.recipe_ingredients values('custard-and-white-chocolate-biscuits','self-raising flour','225g self-raising flour',225.0,'gram');
insert into feedme.recipe_ingredients values('custard-and-white-chocolate-biscuits','custard','85g custard powder',85.0,'gram');
insert into feedme.recipe_ingredients values('custard-and-white-chocolate-biscuits','chocolate','85g white chocolate, chopped into small chunks',85.0,'gram');
insert into feedme.recipe_steps values('custard-and-white-chocolate-biscuits',0,0, 'Heat oven to 180C/160C fan/gas 4. Line 2-3 baking sheets with baking parchment. Put the butter and sugar in a food processor and whizz until light and fluffy. Add the egg and vanilla, and mix well. Sift together the flour and custard powder, then tip into the bowl and pulse to mix into a dough. Scrape out the food processor and work the chocolate in by hand.');
insert into feedme.recipe_steps values('custard-and-white-chocolate-biscuits',1,1, 'Roll the dough into balls a little smaller than a walnut, then place on the baking sheets, a little apart to allow for spreading. Press each biscuit down lightly with your fingers.');
insert into feedme.recipe_steps values('custard-and-white-chocolate-biscuits',2,2, 'Bake for 12-15 mins until lightly golden. Remove and cool on a wire rack.');
insert into feedme.recipes VALUES ('teriyaki-beef-and-lettuce-cups', 'Teriyaki beef & lettuce cups', 'Add a taste of Asia to your next party with these irresistible, easy-to-pick-up finger food favourites', 'japanese');
insert into feedme.recipe_ingredients values('teriyaki-beef-and-lettuce-cups','sirloin','350g trimmed sirloin steak',350.0,'gram');
insert into feedme.recipe_ingredients values('teriyaki-beef-and-lettuce-cups','teriyaki','2 tbsp teriyaki sauce marinade, (we used Kikkoman teriyaki marinade)',2.0,'tbsp');
insert into feedme.recipe_ingredients values('teriyaki-beef-and-lettuce-cups','cucumber','1⁄2 cucumber',1.0,'unit');
insert into feedme.recipe_ingredients values('teriyaki-beef-and-lettuce-cups','coriander','2 tbsp coriander, roughly chopped',2.0,'tbsp');
insert into feedme.recipe_ingredients values('teriyaki-beef-and-lettuce-cups','lime','juice 1⁄2 lime',NULL,'null');
insert into feedme.recipe_ingredients values('teriyaki-beef-and-lettuce-cups','little gem lettuce','6 Little Gem lettuces leaves',6.0,'unit');
insert into feedme.recipe_ingredients values('teriyaki-beef-and-lettuce-cups','chilli','1 red chilli, deseeded and thinly sliced',1.0,'unit');
insert into feedme.recipe_ingredients values('teriyaki-beef-and-lettuce-cups','red onion','1⁄2 red onion, thinly sliced',1.0,'unit');
insert into feedme.recipe_steps values('teriyaki-beef-and-lettuce-cups',0,0, 'Put the steak between two sheets of cling film and beat with a rolling pin until half its original thickness. Thinly slice the steak, then mix with the teriyaki marinade in a bowl. Leave to marinate for 5-10 mins.');
insert into feedme.recipe_steps values('teriyaki-beef-and-lettuce-cups',1,1, 'Roughly dice the cucumber and mix with the chopped coriander and lime juice. Season with a little salt.');
insert into feedme.recipe_steps values('teriyaki-beef-and-lettuce-cups',2,2, 'Heat a frying pan until very hot, then fry the steak slices for 1½-2½ mins for rare to medium, turning the slices halfway through.');
insert into feedme.recipe_steps values('teriyaki-beef-and-lettuce-cups',3,3, 'Pile the cucumber mixture into the lettuce leaves, then top with the seared teriyaki beef, chilli and red onion.');
insert into feedme.recipes VALUES ('vietnamese-veg-parcels', 'Vietnamese veg parcels', 'Fancy something a bit different? Try these healthy vegetable parcels, based on Vietnamese rolls', 'vietnamese');
insert into feedme.recipe_ingredients values('vietnamese-veg-parcels','rice','2 x 50g pack rice flour pancakes',50.0,'gram');
insert into feedme.recipe_ingredients values('vietnamese-veg-parcels','rice','50g thin rice noodles',50.0,'gram');
insert into feedme.recipe_ingredients values('vietnamese-veg-parcels','chicken','1 cooked skinless chicken breast, shredded',1.0,'unit');
insert into feedme.recipe_ingredients values('vietnamese-veg-parcels','carrot','1 carrot, finely sliced',1.0,'unit');
insert into feedme.recipe_ingredients values('vietnamese-veg-parcels','cucumber','0.5 cucumber, finely shredded',0.0,'unit');
insert into feedme.recipe_ingredients values('vietnamese-veg-parcels','pepper','1 red pepper, deseeded and finely sliced',1.0,'unit');
insert into feedme.recipe_ingredients values('vietnamese-veg-parcels','coriander','Baby Gem lettuce, coriander and mint leaves, to serve',NULL,'null');
insert into feedme.recipe_steps values('vietnamese-veg-parcels',0,0, 'Soak the rice fl our pancakes according to pack instructions or in boiling water for 30 secs. Remove and cool on kitchen paper. Soak the rice noodles according to pack instructions, then drain and cut them into 2cm strips.');
insert into feedme.recipe_steps values('vietnamese-veg-parcels',1,1, 'Lay a rice pancake on a board and arrange some rice noodles, chicken, vegetables and herb leaves about a third of the way up, leaving a 1cm edge at the side. Fold the sides over the vegetables and then roll up lengthways to make a cigar. Store in the fridge, covered with moist kitchen paper for up to 1 day.');
insert into feedme.recipe_steps values('vietnamese-veg-parcels',2,2, 'Mix together the ingredients for the dipping sauce and put into a bowl. To serve, arrange the rolls on a plate along with the leaves and herbs. To eat, wrap each roll in a piece of lettuce with a herb sprig and dunk into the sauce.');
insert into feedme.recipes VALUES ('japanese-salad-with-ginger-soy-dressing', 'Japanese salad with ginger soy dressing', 'Serve this salad alongside grilled tuna or steak or tuck into it as a superhealthy lunch', 'japanese');
insert into feedme.recipe_ingredients values('japanese-salad-with-ginger-soy-dressing','lettuce','4 baby gem lettuces, halved lengthways',4.0,'unit');
insert into feedme.recipe_ingredients values('japanese-salad-with-ginger-soy-dressing','beans','200g frozen shelled edamame(soy) beans, defrosted',200.0,'gram');
insert into feedme.recipe_ingredients values('japanese-salad-with-ginger-soy-dressing','carrot','4 carrots, cut into long matchsticks',4.0,'unit');
insert into feedme.recipe_ingredients values('japanese-salad-with-ginger-soy-dressing','radish','140g radish, thinly sliced',140.0,'gram');
insert into feedme.recipe_steps values('japanese-salad-with-ginger-soy-dressing',0,0, 'Place all the dressing ingredients in a blender or food processor and add 1 tbsp water. Blend until smooth. Arrange the halved lettuce, edamame, carrots and radishes on 4 plates or 1 big platter. When ready to serve, drizzle the dressing over.');
insert into feedme.recipes VALUES ('indian-lamb-cutlets', 'Indian lamb cutlets', 'This aromatic dish will have everyone''s mouthwatering', 'indian');
insert into feedme.recipe_ingredients values('indian-lamb-cutlets','parmesan','50g parmesan, finely grated',50.0,'gram');
insert into feedme.recipe_ingredients values('indian-lamb-cutlets','curry','1 tbsp curry powder (we used Schwartz Mild Curry Powder)',1.0,'tbsp');
insert into feedme.recipe_ingredients values('indian-lamb-cutlets','lamb','4 lamb cutlets',4.0,'unit');
insert into feedme.recipe_ingredients values('indian-lamb-cutlets','olive oil','1 tbsp olive oil',1.0,'tbsp');
insert into feedme.recipe_ingredients values('indian-lamb-cutlets','chutney','tomato chutney, to serve',NULL,'null');
insert into feedme.recipe_steps values('indian-lamb-cutlets',0,0, 'Heat oven to 200C/fan 180C/gas 6. Mix the parmesan and curry powder together. Season the lamb, then press the meat into the cheese mix to coat. Heat the oil in an ovenproof frying pan or heavy baking tray and fry the cutlets for 2 mins on each side to brown.');
insert into feedme.recipe_steps values('indian-lamb-cutlets',1,1, 'Transfer to the oven for 5 mins for medium, or longer for well done. Delicious served with a dollop of tomato chutney and some mash flavoured with chopped coriander.');
insert into feedme.recipes VALUES ('sizzling-spare-ribs-with-bbq-sauce', 'Sizzling spare ribs with BBQ sauce', 'Some like it hot, so sizzle your ribs with BBQ sauce', 'caribbean');
insert into feedme.recipe_ingredients values('sizzling-spare-ribs-with-bbq-sauce','pork','4 x 500g packs pork spare ribs',500.0,'gram');
insert into feedme.recipe_ingredients values('sizzling-spare-ribs-with-bbq-sauce','spring onion','1 bunch of spring onions, roughly chopped (green stems and all)',1.0,'unit');
insert into feedme.recipe_ingredients values('sizzling-spare-ribs-with-bbq-sauce','chilli','1 Scotch bonnet chilli, seeded and finely chopped',1.0,'unit');
insert into feedme.recipe_ingredients values('sizzling-spare-ribs-with-bbq-sauce','garlic','4 garlic cloves, roughly chopped',4.0,'unit');
insert into feedme.recipe_ingredients values('sizzling-spare-ribs-with-bbq-sauce','rum','6 tbsp Appleton rum',6.0,'tbsp');
insert into feedme.recipe_ingredients values('sizzling-spare-ribs-with-bbq-sauce','demerara sugar','6 tbsp demerara sugar',6.0,'tbsp');
insert into feedme.recipe_ingredients values('sizzling-spare-ribs-with-bbq-sauce','soy sauce','6 tbsp dark soy sauce',6.0,'tbsp');
insert into feedme.recipe_ingredients values('sizzling-spare-ribs-with-bbq-sauce','honey','6 tbsp clear honey',6.0,'tbsp');
insert into feedme.recipe_ingredients values('sizzling-spare-ribs-with-bbq-sauce','mustard','6 tsp Dijon mustard',6.0,'tsp');
insert into feedme.recipe_ingredients values('sizzling-spare-ribs-with-bbq-sauce','allspice','1 tsp ground allspice',1.0,'tsp');
insert into feedme.recipe_steps values('sizzling-spare-ribs-with-bbq-sauce',0,0, 'Tip the ribs into a big bowl (not a metal one) and throw in the spring onions, chilli and garlic. Now spoon all the remaining ingredients over the ribs and sprinkle with salt and pepper. Get your hands in the bowl and turn the ribs over and over again until they’re coated in the sauce. (You can keep the ribs uncooked – tightly covered – in the fridge for up to 3 days. You can also freeze them for several months.)');
insert into feedme.recipe_steps values('sizzling-spare-ribs-with-bbq-sauce',1,1, 'Cook the ribs over a moderate barbecue for 20-30 minutes depending on their size, turning them over frequently and brushing with sauce each time. The turning and brushing is important, so that all four sides of each rib get encrusted with the sauce, which builds up like a lacquer. If you have any leftover sauce at the end, heat it up in a pan and pour it over the ribs just before serving.');
insert into feedme.recipes VALUES ('warm-honey-cup-puddings', 'Warm honey cup puddings', 'Serve these classic make-ahead Greek desserts with a scoop of quick homemade pistachio ice cream', 'greek');
insert into feedme.recipe_ingredients values('warm-honey-cup-puddings','butter','2 tbsp butter, melted, plus extra for greasing the cups',2.0,'tbsp');
insert into feedme.recipe_ingredients values('warm-honey-cup-puddings','honey','2 tsp clear honey, plus extra for drizzling',2.0,'tsp');
insert into feedme.recipe_ingredients values('warm-honey-cup-puddings','self-raising flour','50g self-raising flour',50.0,'gram');
insert into feedme.recipe_ingredients values('warm-honey-cup-puddings','almond','25g ground almond',25.0,'gram');
insert into feedme.recipe_ingredients values('warm-honey-cup-puddings','sugar','50g light muscovado sugar',50.0,'gram');
insert into feedme.recipe_ingredients values('warm-honey-cup-puddings','soda','0.25 tsp bicarbonate of soda',25.0,'tsp');
insert into feedme.recipe_ingredients values('warm-honey-cup-puddings','egg','1 egg',1.0,'unit');
insert into feedme.recipe_ingredients values('warm-honey-cup-puddings','ice cream','85g Greek yogurt(take this from the 500g tub for the ice cream, below)',85.0,'gram');
insert into feedme.recipe_ingredients values('warm-honey-cup-puddings','pistachio','1 tbsp pistachios, roughly chopped',1.0,'tbsp');
insert into feedme.recipe_steps values('warm-honey-cup-puddings',0,0, 'Butter two large coffee cups or individual ramekins, then line the bottoms with a circle of non-stick paper. Add 1 tsp honey to the bottom of each.');
insert into feedme.recipe_steps values('warm-honey-cup-puddings',1,1, 'Heat oven to 180C/fan160C/gas 4. Mix the dry ingredients in a large bowl, squashing any lumps of sugar with your fingers. Beat the egg, butter and yogurt together, then stir into the dry mix until smooth. Spoon the mix into the cups, sit them on a baking tray, then bake for 20 mins until risen and golden. Test if they’re ready by inserting a skewer – it should come out clean. You can make the puddings up to a day ahead, then reheat briefly in the microwave, or leave them in a warm oven, still in the cups.');
insert into feedme.recipe_steps values('warm-honey-cup-puddings',2,2, 'Loosen the edge of each pud with a round-bladed knife, then up-turn them onto serving plates. Remove lining disc, drizzle over a little more honey, then serve with a scoop of the pistachio ice cream (below) and a sprinkling of pistachios.');
insert into feedme.recipes VALUES ('marzipan-eccles-tarts', 'Marzipan Eccles tarts', 'This fun spin on the traditional Lancashire fruit cakes uses leftover marzipan to make irresistible individual pies', 'british');
insert into feedme.recipe_ingredients values('marzipan-eccles-tarts','sultanas','300g leftover dried fruit, like raisins, sultanas, currants',300.0,'gram');
insert into feedme.recipe_ingredients values('marzipan-eccles-tarts','pear','2 eating applesor pears, cored and grated',2.0,'unit');
insert into feedme.recipe_ingredients values('marzipan-eccles-tarts','demerara sugar','50g demerara sugar, plus a little extra to decorate',50.0,'gram');
insert into feedme.recipe_ingredients values('marzipan-eccles-tarts','marzipan','100g leftover marzipan, grated',100.0,'gram');
insert into feedme.recipe_ingredients values('marzipan-eccles-tarts','cloves','0.5 tsp ground cloves',5.0,'tsp');
insert into feedme.recipe_ingredients values('marzipan-eccles-tarts','cinnamon','1 tsp ground cinnamon',1.0,'tsp');
insert into feedme.recipe_ingredients values('marzipan-eccles-tarts','pastry','500g block puff pastry',500.0,'gram');
insert into feedme.recipe_ingredients values('marzipan-eccles-tarts','flour','little flour, for rolling',NULL,'null');
insert into feedme.recipe_ingredients values('marzipan-eccles-tarts','egg','1 egg, beaten',1.0,'unit');
insert into feedme.recipe_steps values('marzipan-eccles-tarts',0,0, 'Mix the dried fruit, grated apple or pear, Demerara sugar, marzipan, cloves and cinnamon.');
insert into feedme.recipe_steps values('marzipan-eccles-tarts',1,1, 'Divide the pastry into 6. Then assemble tarts one by one. Halve a chunk of pastry and roll out each halve to a circle about the thickness of a 50p piece on a floured surface. Spoon a sixth of the filling into the centre of one circle in a pile. Brush edges with egg, and lift the second pastry circle on top. Gently press down on the fruit to push out as much air as you can. Press the pastry edges to seal. Use an appropriate sized bowl, glass etc to help you trim the edges to a neat circle. At this stage, you can cover and chill the tarts overnight, or freeze for 1 month. Defrost before continuing. Brush with beaten egg, then make three small slashes with a knife in a row. Repeat with remaining pastry and filling.');
insert into feedme.recipe_steps values('marzipan-eccles-tarts',2,2, 'Pop the tarts in the fridge while you heat oven to 200C/180C fan/gas 6. When the oven is nice and hot, brush tarts all over with egg and sprinkle each with a little more sugar. Bake for 20 - 25 mins, until golden, then eat warm with ice cream or custard.');
insert into feedme.recipes VALUES ('indianspiced-fish-cakes', 'Indian-spiced fish cakes', 'Spice up leftover cooked salmon in these simple, freezable fishcakes with cooling raita', 'indian');
insert into feedme.recipe_ingredients values('indianspiced-fish-cakes','potato','600g potato, quartered if large',600.0,'gram');
insert into feedme.recipe_ingredients values('indianspiced-fish-cakes','cumin','0.5 tsp cumin seeds',5.0,'tsp');
insert into feedme.recipe_ingredients values('indianspiced-fish-cakes','spring onion','2 spring onions, finely chopped',2.0,'unit');
insert into feedme.recipe_ingredients values('indianspiced-fish-cakes','chilli','1 red chilli, deseeded and finely chopped',1.0,'unit');
insert into feedme.recipe_ingredients values('indianspiced-fish-cakes','coriander','2 tbsp chopped coriander',2.0,'tbsp');
insert into feedme.recipe_ingredients values('indianspiced-fish-cakes','egg','1 egg, beaten',1.0,'unit');
insert into feedme.recipe_ingredients values('indianspiced-fish-cakes','salmon','100g cooked leftover salmon, flaked into large pieces',100.0,'gram');
insert into feedme.recipe_ingredients values('indianspiced-fish-cakes','flour','plain flour, for coating',NULL,'null');
insert into feedme.recipe_ingredients values('indianspiced-fish-cakes','sunflower oil','25g butterand 1 tbsp sunflower oil',25.0,'gram');
insert into feedme.recipe_ingredients values('indianspiced-fish-cakes','chutney','leftover avocadomayo, raita or mango chutney, to serve',NULL,'null');
insert into feedme.recipe_steps values('indianspiced-fish-cakes',0,0, 'Boil the potatoes. Meanwhile, dry-fry the cumin seeds for a couple of secs in a large non-stick frying pan. When soft, drain the potatoes, return to the saucepan, add the cumin, onions, chilli and coriander with plenty of seasoning, then mash well. When cooled a little, beat in 2 tbsp of the egg, then carefully stir through the salmon. Shape into 4 rough cakes, then coat in flour. If freezing, freeze on a baking sheet until solid, then pack up.');
insert into feedme.recipe_steps values('indianspiced-fish-cakes',1,1, 'In the frying pan, melt the butter with the oil. Fry the cakes for about 2 mins each side until golden. Serve with the mayo, raita (recipe below) or mango chutney and some salad leaves.');
insert into feedme.recipes VALUES ('roasted-autumn-vegetables-with-lancashire-cheese', 'Roasted autumn vegetables with Lancashire cheese', 'A colourful and hearty vegetarian supper dish for autumn - made in one roasting tin, from Roz Denny', 'british');
insert into feedme.recipe_ingredients values('roasted-autumn-vegetables-with-lancashire-cheese','butternut squash','1 large butternut squash (about 600-700g/1lb 5oz - 1lb 9oz in weight)',700.0,'gram');
insert into feedme.recipe_ingredients values('roasted-autumn-vegetables-with-lancashire-cheese','red onion','1 medium red onion',1.0,'unit');
insert into feedme.recipe_ingredients values('roasted-autumn-vegetables-with-lancashire-cheese','olive oil','6 tbsp olive oil',6.0,'tbsp');
insert into feedme.recipe_ingredients values('roasted-autumn-vegetables-with-lancashire-cheese','sage','1 large sprig fresh sage',1.0,'unit');
insert into feedme.recipe_ingredients values('roasted-autumn-vegetables-with-lancashire-cheese','courgette','1 large courgette',1.0,'unit');
insert into feedme.recipe_ingredients values('roasted-autumn-vegetables-with-lancashire-cheese','vinegar','1 tbsp balsamic or sherry vinegar',1.0,'tbsp');
insert into feedme.recipe_ingredients values('roasted-autumn-vegetables-with-lancashire-cheese','cheese','100g Lancashire cheese',100.0,'gram');
insert into feedme.recipe_steps values('roasted-autumn-vegetables-with-lancashire-cheese',0,0, 'Preheat the oven to fan180C/conventional 200C/ gas 6. Using a sharp knife, cut the squash in half and scoop out the seeds. Cut the halves into smaller pieces so you can peel them more easily. Chop the flesh into big bite-sized pieces – they don’t have to be neat.');
insert into feedme.recipe_steps values('roasted-autumn-vegetables-with-lancashire-cheese',1,1, 'Halve the onion and trim the root end leaving a little on to hold the segments together. Peel and then cut each half into four wedges. Scatter the squash and onion in a large roasting tin so they have plenty of room to roast, drizzle over 5 tbsp of the oil and toss together. Strip the sage leaves from the stem and roughly chop – you should have about 2 tbsps. Scatter over the vegetables and season. Roast for 20 minutes, stirring once halfway through.');
insert into feedme.recipe_steps values('roasted-autumn-vegetables-with-lancashire-cheese',2,2, 'Meanwhile, slice the courgette thickly and toss with the remaining oil. Remove the roasting tin from the oven and push the partly cooked squash and onion to the side. Put the courgette slices flat on the base and season. Roast for a further 10 minutes, until all the vegetables are tender.');
insert into feedme.recipe_steps values('roasted-autumn-vegetables-with-lancashire-cheese',3,3, 'Remove tin from the oven, sprinkle the vinegar over the vegetables and toss. Crumble over the Lancashire cheese. Toss lightly so the cheese melts a little and serve.');
insert into feedme.recipes VALUES ('grannys-victoria-sponge', 'Granny''s Victoria sponge', 'The classic Victoria sandwich is always a teatime winner, every bite brings a taste of nostalgia', 'british');
insert into feedme.recipe_ingredients values('grannys-victoria-sponge','butter','200g unsalted butter, softened, plus extra for greasing',200.0,'gram');
insert into feedme.recipe_ingredients values('grannys-victoria-sponge','caster sugar','200g caster sugar',200.0,'gram');
insert into feedme.recipe_ingredients values('grannys-victoria-sponge','vanilla extract','1 tsp vanilla extract',1.0,'tsp');
insert into feedme.recipe_ingredients values('grannys-victoria-sponge','egg','4 medium eggs',4.0,'unit');
insert into feedme.recipe_ingredients values('grannys-victoria-sponge','self-raising flour','200g self-raising flour, plus extra for dusting',200.0,'gram');
insert into feedme.recipe_ingredients values('grannys-victoria-sponge','jam','about 6 tbsp raspberryjam',6.0,'tbsp');
insert into feedme.recipe_ingredients values('grannys-victoria-sponge','double cream','250ml double cream, whipped',250.0,'ml');
insert into feedme.recipe_ingredients values('grannys-victoria-sponge','icing','icing sugar, for dusting',NULL,'null');
insert into feedme.recipe_steps values('grannys-victoria-sponge',0,0, 'Heat oven to 190C/170C fan/gas 5. Grease and flour 2 x 20cm sandwich tins. Place the butter, sugar and vanilla extract into a bowl and beat well to a creamy consistency. Slowly beat in the eggs, one by one, then fold in the flour and mix well. ');
insert into feedme.recipe_steps values('grannys-victoria-sponge',1,1, 'Divide the mix between the cake tins, place into the oven and bake for about 20 mins until risen and golden brown. The cakes should spring back when gently pushed in the middle. When ready, remove from the oven and allow to cool for 5 mins in the tin, before turning out onto a wire rack and cooling completely.');
insert into feedme.recipe_steps values('grannys-victoria-sponge',2,2, 'Spread the jam onto one cake and top with the cream. Sandwich the cakes together and dust with icing sugar.');
insert into feedme.recipes VALUES ('thai-beef-stirfry', 'Thai beef stir-fry', 'A no fuss shopping list and super-quick to prepare, the perfect after work meal', 'thai');
insert into feedme.recipe_ingredients values('thai-beef-stirfry','vegetable oil','2 tbsp vegetable oil',2.0,'tbsp');
insert into feedme.recipe_ingredients values('thai-beef-stirfry','beef','400g beef strips, or steak cut into thin strips',400.0,'gram');
insert into feedme.recipe_ingredients values('thai-beef-stirfry','chilli','1 red chilli, deseeded and finely sliced',1.0,'unit');
insert into feedme.recipe_ingredients values('thai-beef-stirfry','oyster','2 tbsp oyster sauce',2.0,'tbsp');
insert into feedme.recipe_ingredients values('thai-beef-stirfry','basil','handful basil leaves',NULL,'null');
insert into feedme.recipe_steps values('thai-beef-stirfry',0,0, 'Heat a wok or large frying pan until smoking hot. Pour in the oil and swirl around the pan, then tip in the beef strips and chilli. Cook, stirring all the time, until the meat is lightly browned, about 3 mins, then pour over the oyster sauce. Cook until heated through and the sauce coats the meat. stir in the basil leaves and serve with plain rice.');
insert into feedme.recipes VALUES ('vietnamese-prawn-salad', 'Vietnamese prawn salad', 'Inspired by the Vietnamese dish, Bun xao, this fresh-tasting salad has very little fat but is so satisfying', 'vietnamese');
insert into feedme.recipe_steps values('vietnamese-prawn-salad',0,0, 'To make the dressing, mash the garlic, chilli and sugar using a pestle and mortar. Add the lime juice and 3 tbsp water and stir together. Set aside.');
insert into feedme.recipe_steps values('vietnamese-prawn-salad',1,1, 'Get the kettle on, put the noodles into a bowl, then cover with boiling water. Leave to stand for 10 mins until tender, then drain and divide between 2 bowls. 3 Mix the prawns and veg together and divide between the bowls, too. Finish by topping each salad with the herbs and peanuts, then pour over the dressing to serve.');
insert into feedme.recipes VALUES ('minty-asian-chicken-salad', 'Minty Asian chicken salad', 'Cheer up a chicken salad, make it minty and then add some Asian flavours', 'vietnamese');
insert into feedme.recipe_ingredients values('minty-asian-chicken-salad','rice','250g pack thin rice noodle',250.0,'gram');
insert into feedme.recipe_ingredients values('minty-asian-chicken-salad','chicken','2 cooked skinless chicken breasts, shredded',2.0,'unit');
insert into feedme.recipe_ingredients values('minty-asian-chicken-salad','cucumber','0.5 cucumber, halved and sliced',0.0,'unit');
insert into feedme.recipe_ingredients values('minty-asian-chicken-salad','spring onion','3 spring onions, sliced',3.0,'unit');
insert into feedme.recipe_ingredients values('minty-asian-chicken-salad','chilli','1 red chilli, finely chopped',1.0,'unit');
insert into feedme.recipe_ingredients values('minty-asian-chicken-salad','fish sauce','4 tbsp soy or fish sauce',4.0,'tbsp');
insert into feedme.recipe_ingredients values('minty-asian-chicken-salad','lime','zest and juice 1 lime',NULL,'null');
insert into feedme.recipe_ingredients values('minty-asian-chicken-salad','sugar','2 tsp sugar',2.0,'tsp');
insert into feedme.recipe_ingredients values('minty-asian-chicken-salad','mint','20g pack mint, leaves only, roughly chopped',20.0,'gram');
insert into feedme.recipe_steps values('minty-asian-chicken-salad',0,0, 'Soak the noodles for 5 mins in boiling water or according to pack instructions, until softened. Drain, then cool under a running tap. Drain again well, then tip into a large bowl along with the chicken, cucumber, spring onions and chilli.');
insert into feedme.recipe_steps values('minty-asian-chicken-salad',1,1, 'Mix together the soy or fish sauce, lime zest and juice and sugar, then pour over the noodles. Toss through the mint leaves and serve.');
insert into feedme.recipes VALUES ('crispy-greekstyle-pie-', 'Crispy Greek-style pie', 'A crispy pie that you can adapt for your needs, add chicken or keep it veggie. A good fail-safe for your repertoire', 'greek');
insert into feedme.recipe_ingredients values('crispy-greekstyle-pie-','spinach','200g bag spinach leaves',200.0,'gram');
insert into feedme.recipe_ingredients values('crispy-greekstyle-pie-','tomato','175g jar sundried tomato in oil',175.0,'gram');
insert into feedme.recipe_ingredients values('crispy-greekstyle-pie-','cheese','100g feta cheese, crumbled',100.0,'gram');
insert into feedme.recipe_ingredients values('crispy-greekstyle-pie-','egg','2 eggs',2.0,'unit');
insert into feedme.recipe_ingredients values('crispy-greekstyle-pie-','pastry','0.5 250g pack filo pastry',250.0,'gram');
insert into feedme.recipe_steps values('crispy-greekstyle-pie-',0,0, '1 Put the spinach into a large pan. Pour over a couple tbsp water, then cook until just wilted. Tip into a sieve, leave to cool a little, then squeeze out any excess water and roughly chop. Roughly chop the tomatoes and put into a bowl along with the spinach, feta and eggs. Mix well.');
insert into feedme.recipe_steps values('crispy-greekstyle-pie-',1,1, 'Carefully unroll the filo pastry. Cover with some damp sheets of kitchen paper to stop it drying out. Take a sheet of pastry and brush liberally with some of the sundried tomato oil. Drape oil-side down in a 22cm loosebottomed cake tin so that some of the pastry hangs over the side. Brush oil on another piece of pastry and place in the tin, just a little further round. Keep placing the pastry pieces in the tin until you have roughly three layers, then spoon over the filling. Pull the sides into the middle, scrunch up and make sure the filling is covered. Brush with a little more oil.');
insert into feedme.recipe_steps values('crispy-greekstyle-pie-',2,2, 'Heat oven to 180C/fan 160C/gas 4. Cook the pie for 30 mins until the pastry is crisp and golden brown. Remove from the cake tin, slice into wedges and serve with salad.');
insert into feedme.recipes VALUES ('bang-bang-prawn-salad', 'Bang bang prawn salad', 'Ready in 20 minutes, this lovely salad can just as easily be made with chicken if you''re not in a seafood mood', 'vietnamese');
insert into feedme.recipe_ingredients values('bang-bang-prawn-salad','rice','140g flat rice noodles',140.0,'gram');
insert into feedme.recipe_ingredients values('bang-bang-prawn-salad','nut','3 tbsp crunchy peanut butter',3.0,'tbsp');
insert into feedme.recipe_ingredients values('bang-bang-prawn-salad','fat','4 tbsp half-fat coconut milk',4.0,'tbsp');
insert into feedme.recipe_ingredients values('bang-bang-prawn-salad','chilli','3 tbsp sweet chilli sauce',3.0,'tbsp');
insert into feedme.recipe_ingredients values('bang-bang-prawn-salad','spring onion','3 spring onions, sliced',3.0,'unit');
insert into feedme.recipe_ingredients values('bang-bang-prawn-salad','cucumber','1 cucumber, peeled, halved lengthways, deseeded and sliced',1.0,'unit');
insert into feedme.recipe_ingredients values('bang-bang-prawn-salad','beansprouts','300g pack beansprouts',300.0,'gram');
insert into feedme.recipe_ingredients values('bang-bang-prawn-salad','prawn','200g pack cooked peeled prawn',200.0,'gram');
insert into feedme.recipe_steps values('bang-bang-prawn-salad',0,0, 'Cook the noodles following pack instructions, then rinse under cold water and drain thoroughly. In a small saucepan melt together the peanut butter, coconut milk, sweet chilli sauce and half the spring onions, adding 1-2 tbsp of water to loosen the mixture to a drizzling consistency.');
insert into feedme.recipe_steps values('bang-bang-prawn-salad',1,1, 'Mix the noodles, cucumber and beansprouts in a serving dish. Top with the prawns, drizzle over the peanut sauce and scatter over the remaining spring onions.');
insert into feedme.recipes VALUES ('chinese-braised-pork-with-double-spring-onions-', 'Chinese braised pork with double spring onions', 'Use spring onions as a vegetable in their own right in this slow-cooked pork dish', 'chinese');
insert into feedme.recipe_ingredients values('chinese-braised-pork-with-double-spring-onions-','pork shoulder','4 pieces porkosso buco (about 850kg/1lb 14oz) or the equivalent of pork shoulder',4.0,'unit');
insert into feedme.recipe_ingredients values('chinese-braised-pork-with-double-spring-onions-','olive oil','1 tbsp olive oil',1.0,'tbsp');
insert into feedme.recipe_ingredients values('chinese-braised-pork-with-double-spring-onions-','dry sherry','250ml Shaohsing rice wineor dry sherry',250.0,'ml');
insert into feedme.recipe_ingredients values('chinese-braised-pork-with-double-spring-onions-','ginger','100g ginger, finely sliced',100.0,'gram');
insert into feedme.recipe_ingredients values('chinese-braised-pork-with-double-spring-onions-','garlic','2 garlic cloves, sliced',2.0,'unit');
insert into feedme.recipe_ingredients values('chinese-braised-pork-with-double-spring-onions-','spring onion','12 spring onions, 8 fat ones and 4 thinner ones',12.0,'unit');
insert into feedme.recipe_ingredients values('chinese-braised-pork-with-double-spring-onions-','chilli','1 dried red chilli(look for Kashmiri chillies for a good flavour)',1.0,'unit');
insert into feedme.recipe_ingredients values('chinese-braised-pork-with-double-spring-onions-','stock','500ml chicken or vegetable stock',500.0,'ml');
insert into feedme.recipe_ingredients values('chinese-braised-pork-with-double-spring-onions-','miso','1 tbsp miso paste(optional)',1.0,'tbsp');
insert into feedme.recipe_ingredients values('chinese-braised-pork-with-double-spring-onions-','soy sauce','2 tbsp soy sauce',2.0,'tbsp');
insert into feedme.recipe_ingredients values('chinese-braised-pork-with-double-spring-onions-','rice','steamed rice and bok choi, to serve',NULL,'null');
insert into feedme.recipe_steps values('chinese-braised-pork-with-double-spring-onions-',0,0, 'Heat oven to 190C/170C fan/gas 5. Brown the pork on both sides in the oil, then transfer it to a large casserole dish. Deglaze the pan you used for browning with the rice wine or Sherry and add it to the casserole. Add the ginger and garlic. Trim the ends off the 8 fat spring onions and add these to the pan whole along with the chilli, stock, miso paste (if using) and soy sauce. Bring everything to a simmer, then cover and put the casserole in the oven for 2 hrs.');
insert into feedme.recipe_steps values('chinese-braised-pork-with-double-spring-onions-',1,1, 'Remove the lid from the casserole and cook for a further 20 mins. Chop the 4 thinner spring onions and add them to the casserole just before serving with steamed rice and bok choi.');
insert into feedme.recipes VALUES ('greek-pasta-salad', 'Greek pasta salad', 'For a fresh idea with pasta, try Greek salad', 'greek');
insert into feedme.recipe_ingredients values('greek-pasta-salad','fusilli','300g fusilli pasta spirals – or you can use farfalle (butterflies) or penne (quill tubes)',300.0,'gram');
insert into feedme.recipe_ingredients values('greek-pasta-salad','spinach','225g bag prepared fresh baby spinach',225.0,'gram');
insert into feedme.recipe_ingredients values('greek-pasta-salad','cherry','250g punnet cherry tomatoes, halved',250.0,'gram');
insert into feedme.recipe_ingredients values('greek-pasta-salad','olive','100g black olive(we bought kalamata from the deli counter)',100.0,'gram');
insert into feedme.recipe_ingredients values('greek-pasta-salad','cheese','200g feta cheese, broken into rough chunks',200.0,'gram');
insert into feedme.recipe_ingredients values('greek-pasta-salad','olive oil','3 tbsp olive oil',3.0,'tbsp');
insert into feedme.recipe_steps values('greek-pasta-salad',0,0, 'Tip the fusilli into a large pan of boiling salted water and boil for 10 minutes. Throw in the spinach, stir well and boil for another 2 minutes. Drain into a colander or sieve and leave to drip-dry.');
insert into feedme.recipe_steps values('greek-pasta-salad',1,1, 'Tip the tomatoes, olives and feta into a big bowl, grind lots of black pepper over and then drizzle with the olive oil.');
insert into feedme.recipe_steps values('greek-pasta-salad',2,2, 'Toss in the drained pasta and spinach, and let everyone help themselves.');
insert into feedme.recipes VALUES ('bombay-potato-and-spinach-pies', 'Bombay potato & spinach pies', 'These spicy filo pies are ideal for a lunch with salad or as part of a buffet', 'indian');
insert into feedme.recipe_steps values('bombay-potato-and-spinach-pies',0,0, 'Heat oven to 190C/170C fan/gas 5. To make the filling, heat a pan of salted water. When boiling, add potatoes and boil for 15 mins until tender. Melt the butter then fry the onions for a few mins. Add cumin, mustard seeds, ginger and chillies and fry, stirring occasionally, for about 7 mins until soft. Stir in curry paste.');
insert into feedme.recipe_steps values('bombay-potato-and-spinach-pies',1,1, 'Cook the spinach in the microwave on High for 5 mins. Drain and squeeze out as much liquid as you can, then chop it. Drain the potatoes and tip them into the spice mixture. Crush lightly to break them up into chunks rather than mash. Toss in the spice mixture with plenty of salt to coat them, then add the spinach, tomatoes and coriander.');
insert into feedme.recipe_steps values('bombay-potato-and-spinach-pies',2,2, 'Carefully unroll the pastry and brush 2 x 20cm loose-bottomed sandwich tins with some butter. Brush the first sheet of pastry and lay it in and across the tin so that it hangs over the side. Do the same with another sheet of pastry to cover the other side of the tin (so the two form a cross), butter and fold the final sheet in half and lay it in the base of the tin to create a firm base. Do the same with the other tin and remaining pastry.');
insert into feedme.recipe_steps values('bombay-potato-and-spinach-pies',3,3, 'Spoon the filling into the tin and fold up the pastry that is overhanging so that it covers the filling. Brush generously with the remaining butter and sprinkle with seeds. Bake for 35 mins until golden and crisp. Serve with a salad and mango chutney, or a raita made by mixing plain yogurt with mint sauce or jelly, if you like.');
insert into feedme.recipes VALUES ('thai-chicken-cakes-with-sweet-chilli-sauce', 'Thai chicken cakes with sweet chilli sauce', 'A light and healthy supper that''s a great variation on the chicken theme', 'thai');
insert into feedme.recipe_ingredients values('thai-chicken-cakes-with-sweet-chilli-sauce','chicken','2 large boneless, skinless chicken breasts (about 175g/6oz each), cubed',175.0,'gram');
insert into feedme.recipe_ingredients values('thai-chicken-cakes-with-sweet-chilli-sauce','garlic','1 garlic clove, roughly chopped',1.0,'unit');
insert into feedme.recipe_ingredients values('thai-chicken-cakes-with-sweet-chilli-sauce','ginger','small piece fresh root ginger, peeled and roughly chopped',NULL,'null');
insert into feedme.recipe_ingredients values('thai-chicken-cakes-with-sweet-chilli-sauce','onion','1 small onion, roughly chopped',1.0,'unit');
insert into feedme.recipe_ingredients values('thai-chicken-cakes-with-sweet-chilli-sauce','fresh coriander','4 tbsp fresh coriander, plus a few sprigs to garnish',4.0,'tbsp');
insert into feedme.recipe_ingredients values('thai-chicken-cakes-with-sweet-chilli-sauce','chilli','1 green chilli, seeded and roughly chopped',1.0,'unit');
insert into feedme.recipe_ingredients values('thai-chicken-cakes-with-sweet-chilli-sauce','olive oil','2 tbsp olive oil',2.0,'tbsp');
insert into feedme.recipe_ingredients values('thai-chicken-cakes-with-sweet-chilli-sauce','spring onion','sweet chilli sauce, lime wedges, shredded spring onion and red chilli, to serve',NULL,'null');
insert into feedme.recipe_steps values('thai-chicken-cakes-with-sweet-chilli-sauce',0,0, 'Toss the chicken, garlic, ginger, onion, coriander and chilli into a food processor and season well. Blitz until the chicken is finely ground and everything is well mixed. Use your hands to shape six small cakes.');
insert into feedme.recipe_steps values('thai-chicken-cakes-with-sweet-chilli-sauce',1,1, 'Heat the oil in a frying pan, then fry the cakes over a medium heat for about 6-8 minutes, turning once. Serve hot, with sweet chilli sauce, lime wedges, coriander, shredded spring onion and red chilli.');
insert into feedme.recipes VALUES ('halloumi-kebabs-with-thyme-and-lemon-baste', 'Halloumi kebabs with thyme & lemon baste', 'A veggie treat for your barbecue', 'greek');
insert into feedme.recipe_steps values('halloumi-kebabs-with-thyme-and-lemon-baste',0,0, 'Halve the courgettes lengthways, then thickly slice. Cut the onion into wedges and separate into pieces. Thread the halloumi, cherry tomatoes, courgettes and onion onto eight skewers. Cover and chill the kebabs until you are ready to cook (You can do this up to half a day ahead.)');
insert into feedme.recipe_steps values('halloumi-kebabs-with-thyme-and-lemon-baste',1,1, 'To make the baste, mix together the olive oil, lemon juice, thyme, mustard and seasoning. (This can be made ahead and chilled too.)');
insert into feedme.recipe_steps values('halloumi-kebabs-with-thyme-and-lemon-baste',2,2, 'Preheat the barbecue or grill and arrange the kebabs on the rack. Brush with the baste, stirring it first to make sure the ingredients are blended. Cook for 4-5 minutes, turning often, until the cheese begins to turn golden and the vegetables are just tender. Serve while still hot with warm pitta bread.');
insert into feedme.recipes VALUES ('the-ultimate-makeover-thai-green-chicken-curry', 'The ultimate makeover: Thai green chicken curry', 'This normally super-rich curry can be turned into one that’s healthier but still creamy', 'thai');
insert into feedme.recipe_steps values('the-ultimate-makeover-thai-green-chicken-curry',0,0, 'For the curry paste, finely chop the coriander stalks and put them into a mini food blender or small food processor with the shallots, lemongrass, garlic and ginger. Pulse until as smooth as you can get it. Add the chillies, basil, threequarters of the coriander leaves, the ground cumin, coriander, pepper, lime leaves and lime juice, then pulse again. Mix in the oil and set aside. You will only be using half of this paste, the rest can be chilled for a couple of days, or frozen for up to a month.');
insert into feedme.recipe_steps values('the-ultimate-makeover-thai-green-chicken-curry',1,1, 'For the curry, heat the oil in a non-stick wok or deep sauté pan, stir in half the curry paste, then stir-fry for 1 min. Shake the coconut milk in its can, open, then pour two-thirds into the pan. Let it bubble away for 4-5 mins, stirring now and then, until reduced and thickened slightly.');
insert into feedme.recipe_steps values('the-ultimate-makeover-thai-green-chicken-curry',2,2, 'Tip in the chicken, then stir-fry for 1-2 mins until no longer pink. Stir in the fish sauce and sugar, then pour in the rest of the coconut milk to give a thinnish, creamy sauce. Lower the heat, add the lime leaves, then simmer for 5 mins to gently finish cooking the chicken. Can be frozen at this point for up to 1 month.');
insert into feedme.recipe_steps values('the-ultimate-makeover-thai-green-chicken-curry',3,3, 'While the chicken simmers, steam the mangetout and beans for a couple of mins. Serve the curry in bowls with some of the veg piled on top (serve the rest separately) along with the spring onions and the rest of the coriander leaves. Serve with a bowl of Thai fragrant rice and lime wedges for squeezing over.');
insert into feedme.recipes VALUES ('fried-rice-with-egg-and-ginger', 'Fried rice with egg & ginger', 'John Torode''s delicious egg fried rice is dotted with juicy prawns and makes a great addition to a Chinese feast', 'chinese');
insert into feedme.recipe_ingredients values('fried-rice-with-egg-and-ginger','sunflower oil','3 tbsp sunflower oil',3.0,'tbsp');
insert into feedme.recipe_ingredients values('fried-rice-with-egg-and-ginger','ginger','100g ginger, finely sliced',100.0,'gram');
insert into feedme.recipe_ingredients values('fried-rice-with-egg-and-ginger','garlic','50g garlic, finely sliced',50.0,'gram');
insert into feedme.recipe_ingredients values('fried-rice-with-egg-and-ginger','prawn','100g small peeled cooked prawn',100.0,'gram');
insert into feedme.recipe_ingredients values('fried-rice-with-egg-and-ginger','spring onion','bunch spring onions, whites and greens separated and sliced',NULL,'null');
insert into feedme.recipe_ingredients values('fried-rice-with-egg-and-ginger','egg','3 eggs, beaten',3.0,'unit');
insert into feedme.recipe_ingredients values('fried-rice-with-egg-and-ginger','rice','600g cooked rice(300g/11oz uncooked)',600.0,'gram');
insert into feedme.recipe_ingredients values('fried-rice-with-egg-and-ginger','dry sherry','50ml rice wine or dry sherry',50.0,'ml');
insert into feedme.recipe_ingredients values('fried-rice-with-egg-and-ginger','soy sauce','50ml light soy sauce',50.0,'ml');
insert into feedme.recipe_steps values('fried-rice-with-egg-and-ginger',0,0, 'Heat the oil in a wok. Add the ginger and garlic, then stir-fry briefly until just coloured. Add the prawns and the whites of the spring onions, and cook for a further 3 mins. Pour in the beaten egg, let it set for a couple of secs, then break it up and stir well to scramble. Add the cooked rice and stir-fry everything together for 10 mins. Add the rest of the spring onions, rice wine and soy, then toss everything together well. Serve immediately.');
insert into feedme.recipes VALUES ('asian-noodle-and-turkey-soup', 'Asian noodle & turkey soup', 'This soup is a little like pho, a Vietnamese broth traditionally made with beef and served with a plate of zingy fresh herbs, chilli and lime on the side.', 'vietnamese');
insert into feedme.recipe_ingredients values('asian-noodle-and-turkey-soup','stock','10.5 l turkey or chicken stock',10.5,'litre');
insert into feedme.recipe_ingredients values('asian-noodle-and-turkey-soup','ginger','thumb-size piece root ginger, peeled and sliced',NULL,'null');
insert into feedme.recipe_ingredients values('asian-noodle-and-turkey-soup','anise','2 star anise',2.0,'unit');
insert into feedme.recipe_ingredients values('asian-noodle-and-turkey-soup','cinnamon','1 cinnamon stick',1.0,'unit');
insert into feedme.recipe_ingredients values('asian-noodle-and-turkey-soup','cloves','3 cloves',3.0,'unit');
insert into feedme.recipe_ingredients values('asian-noodle-and-turkey-soup','rice','200g dried rice noodles, any type',200.0,'gram');
insert into feedme.recipe_ingredients values('asian-noodle-and-turkey-soup','fish sauce','2-3tbsp fish sauce',2.0,'unit');
insert into feedme.recipe_ingredients values('asian-noodle-and-turkey-soup','lime','2 limes, one juiced, one for wedges',2.0,'unit');
insert into feedme.recipe_ingredients values('asian-noodle-and-turkey-soup','turkey','400g roast turkey, shredded',400.0,'gram');
insert into feedme.recipe_ingredients values('asian-noodle-and-turkey-soup','beansprouts','100g bag beansprouts',100.0,'gram');
insert into feedme.recipe_ingredients values('asian-noodle-and-turkey-soup','coriander','bunch coriander',NULL,'null');
insert into feedme.recipe_ingredients values('asian-noodle-and-turkey-soup','mint','bunch mint',NULL,'null');
insert into feedme.recipe_ingredients values('asian-noodle-and-turkey-soup','spring onion','4 spring onions, thinly sliced',4.0,'unit');
insert into feedme.recipe_ingredients values('asian-noodle-and-turkey-soup','seeds','2 red chillies, sliced (leave the seeds in if you like it hot)',2.0,'unit');
insert into feedme.recipe_steps values('asian-noodle-and-turkey-soup',0,0, 'Pour the stock into a large pan, add the ginger and spices, then leave to simmer and infuse for 10 mins. Soak the noodles according to the pack instructions, then drain and rinse.');
insert into feedme.recipe_steps values('asian-noodle-and-turkey-soup',1,1, 'Add the fish sauce and lime juice to the stock and taste for seasoning – add more fish sauce if you like. Divide the noodles between four bowls, then top with the shredded meat, beansprouts and a scattering of herbs, spring onion and chillies. Ladle the hot stock over the noodles, then serve with more herbs and lime wedges for squeezing.');
insert into feedme.recipes VALUES ('lamb-and-apricot-meatballs', 'Lamb & apricot meatballs', 'No one can resist meatballs, and this recipe is so moreish you might want to make double and freeze for another time', 'turkish');
insert into feedme.recipe_ingredients values('lamb-and-apricot-meatballs','olive oil','2 tbsp olive oil',2.0,'tbsp');
insert into feedme.recipe_ingredients values('lamb-and-apricot-meatballs','red onion','2 red onions, very finely chopped',2.0,'unit');
insert into feedme.recipe_ingredients values('lamb-and-apricot-meatballs','garlic','4 garlic cloves, crushed',4.0,'unit');
insert into feedme.recipe_ingredients values('lamb-and-apricot-meatballs','coriander','2 tsp each ground cuminand coriander (or 4 tsp Moroccan spice blend)',2.0,'tsp');
insert into feedme.recipe_ingredients values('lamb-and-apricot-meatballs','chopped tomatoes','400g can chopped tomatoes',400.0,'gram');
insert into feedme.recipe_ingredients values('lamb-and-apricot-meatballs','sugar','0.5 tsp sugar',5.0,'tsp');
insert into feedme.recipe_ingredients values('lamb-and-apricot-meatballs','mint','0.5 20g pack mint, finely chopped',20.0,'gram');
insert into feedme.recipe_ingredients values('lamb-and-apricot-meatballs','mince','500g pack lean lambmince',500.0,'gram');
insert into feedme.recipe_ingredients values('lamb-and-apricot-meatballs','dried apricots','8 dried apricots, finely chopped',8.0,'unit');
insert into feedme.recipe_ingredients values('lamb-and-apricot-meatballs','breadcrumbs','50g fresh breadcrumbs',50.0,'gram');
insert into feedme.recipe_ingredients values('lamb-and-apricot-meatballs','salad','pitta breadand salad, to serve',NULL,'null');
insert into feedme.recipe_steps values('lamb-and-apricot-meatballs',0,0, 'Heat 2 tsp oil in a pan and soften the onions for 5 mins. Add the garlic and spices and cook for a few mins more. Spoon half the onion mixture into a bowl and set aside to cool. Add the tomatoes, sugar and seasoning to the remaining onions in the pan and simmer for about 10 mins until reduced.');
insert into feedme.recipe_steps values('lamb-and-apricot-meatballs',1,1, 'Meanwhile, add the mint, lamb, apricots and breadcrumbs to the cooled onions, season and mix well with your hands. Shape into little meatballs.');
insert into feedme.recipe_steps values('lamb-and-apricot-meatballs',2,2, 'Heat the rest of the oil in a non-stick pan and fry the meatballs until golden (in batches if you need to). Stir in the sauce with a splash of water and gently cook everything for a few mins until the meatballs are cooked through. Serve with pitta bread and salad.');
insert into feedme.recipes VALUES ('easy-stuffed-peppers', 'Easy stuffed peppers', 'A fifteen minute supper that won''t leave you feeling stuffed', 'mediterranean');
insert into feedme.recipe_ingredients values('easy-stuffed-peppers','pepper','4 red peppers',4.0,'unit');
insert into feedme.recipe_ingredients values('easy-stuffed-peppers','rice','2 x 250g pouches cooked tomato rice (we used Tilda Rizazz Mediterranean Tomato)',250.0,'gram');
insert into feedme.recipe_ingredients values('easy-stuffed-peppers','pesto','2 tbsp pesto',2.0,'tbsp');
insert into feedme.recipe_ingredients values('easy-stuffed-peppers','olive','handful pitted black olives, chopped',NULL,'null');
insert into feedme.recipe_ingredients values('easy-stuffed-peppers','cheese','200g goat''s cheese, sliced',200.0,'gram');
insert into feedme.recipe_steps values('easy-stuffed-peppers',0,0, 'Use a small knife to cut the top out of the peppers, then scoop out the seeds. Sit the peppers on a plate, cut-side up, and cook in the microwave on High for 5-6 mins until they have wilted and softened.');
insert into feedme.recipe_steps values('easy-stuffed-peppers',1,1, 'While the peppers are cooking, mix the rice together with the pesto and olives and two-thirds of the cheese. Scoop the rice into the peppers, top with the remaining goat’s cheese and continue to cook for 8-10 mins');
insert into feedme.recipes VALUES ('john-torodes-big-burger', 'John Torode''s big burger', 'Not just any burger, John Torode''s ultimate burger, with a spicy tomato relish and all the trimmings', 'american');
insert into feedme.recipe_steps values('john-torodes-big-burger',0,0, 'First make the relish: heat oil in a large frying pan and add the cumin. Let it cook for a moment, then throw in the bay, garlic, shallots and chilli. Cook for 2 mins until soft but not coloured, then add the celery and tomatoes. Sweat everything down gently for 20-30 mins until the tomatoes lose their shape. Remove from the heat, season and leave to cool. This can be made a few days in advance and kept in the fridge.');
insert into feedme.recipe_steps values('john-torodes-big-burger',1,1, 'To make the burgers, mix the egg yolks in a large bowl with the oyster sauce and soaked breadcrumbs. Season with lots of fresh, black pepper then add the meat, breaking it up a little. Mix everything, trying not to overwork the mixture or the burger will become heavy.');
insert into feedme.recipe_steps values('john-torodes-big-burger',2,2, 'Take the mix and divide in half, then into half and half again so you end up with 8 even pieces. Roll each one into a ball and place a 10 x 10cm square of greaseproof paper on the bottom and top. Put in the fridge for 15 mins to firm.');
insert into feedme.recipe_steps values('john-torodes-big-burger',3,3, 'Heat a griddle pan. Take 4 burger balls from the fridge and push them down so they become discs about the size of a burger bun, about 2cm thick, between the 2 pieces of greaseproof. Peel the paper off one side and slap down in the pan with no oil or seasoning. Peel the top layer of paper off and cook on a very high heat for a couple of mins. In total, a burger this size will take about 5-10 mins to cook, but you need a bit of colour and texture, so exactly how long is up to you. I turn my burgers after 2 mins, but I cook on a very high heat, and I like them a little pink. Keep warm in a low oven while you cook the rest, topping with the cheese slices, if you like, for the final few mins so they melt a little.');
insert into feedme.recipe_steps values('john-torodes-big-burger',4,4, 'Lay the 2 halves of the warm bun in front of you and butter them lightly. Spread the mayo on both sides followed by a little mustard on the top bun and tomato relish on the bottom. Place a slice of tomato on the bottom bun followed by the first burger with the cheese, then some bacon, then the next burger, more relish, lettuce and finally the guacamole. Top with the bun and eat with the gherkins on the side.');
insert into feedme.recipes VALUES ('patatas-bravas', 'Patatas bravas', 'A classic tapas dish - cubes of potato in a spicy, tomato sauce', 'spanish');
insert into feedme.recipe_steps values('patatas-bravas',0,0, 'Prepare ahead by heating the oil in a pan, add the onion and fry for about 5 minutes until softened. Add the garlic, tomatoes, tomato purée, paprika, chilli powder, sugar and salt and bring to the boil, stirring. Simmer for 10 minutes until pulpy. Set aside for up to 24 hours.');
insert into feedme.recipe_steps values('patatas-bravas',1,1, 'To serve, preheat the oven to 200C/gas 6/fan oven 180C. Cut the potatoes into small cubes and pat dry with kitchen paper. Spread over a roasting tin and toss in the oil, then season. Roast for 40-50 minutes, until the potatoes are crisp and golden.');
insert into feedme.recipe_steps values('patatas-bravas',2,2, 'Tip the potatoes into dishes and spoon over the reheated sauce. Sprinkle with the parsley. Serve with cocktail sticks.');
insert into feedme.recipes VALUES ('aubergine-couscous-salad', 'Aubergine couscous salad', 'A handful of mint leaves really brings this salad alive, veggies will love it, but if you do need some meat lamb cutlets are the perfect addition', 'turkish');
insert into feedme.recipe_ingredients values('aubergine-couscous-salad','aubergine','1 large aubergine, sliced into 1cm rounds',1.0,'unit');
insert into feedme.recipe_ingredients values('aubergine-couscous-salad','olive oil','3 tbsp olive oil',3.0,'tbsp');
insert into feedme.recipe_ingredients values('aubergine-couscous-salad','couscous','140g couscous',140.0,'gram');
insert into feedme.recipe_ingredients values('aubergine-couscous-salad','stock','225ml hot vegetable stock',225.0,'ml');
insert into feedme.recipe_ingredients values('aubergine-couscous-salad','cherry','200g cherry tomato, halved',200.0,'gram');
insert into feedme.recipe_ingredients values('aubergine-couscous-salad','mint','handful mint leaves, roughly chopped',NULL,'null');
insert into feedme.recipe_ingredients values('aubergine-couscous-salad','cheese','100g log firm goat''s cheese, cubed',100.0,'gram');
insert into feedme.recipe_ingredients values('aubergine-couscous-salad','lemon','juice 1⁄2 lemon',NULL,'null');
insert into feedme.recipe_steps values('aubergine-couscous-salad',0,0, 'Heat grill to high. Put the aubergine on a baking sheet, brush with oil and season. Grill for about 15 mins, turning and brushing with more oil halfway, until browned and softened.');
insert into feedme.recipe_steps values('aubergine-couscous-salad',1,1, 'Meanwhile, tip the couscous into a large bowl, pour over the stock, then cover and leave for 10 mins. Mix the tomatoes, mint, goat’s cheese and remaining oil together. Fluff the couscous up with a fork, then stir in the aubergines, tomato mixture and lemon juice.');
insert into feedme.recipes VALUES ('chicken-wings-with-cumin-lemon-and-garlic', 'Chicken wings with cumin, lemon & garlic', 'Pick up a few extras from the deli and turn this into a Middle Eastern mezze spread to share with friends', 'turkish');
insert into feedme.recipe_ingredients values('chicken-wings-with-cumin-lemon-and-garlic','chicken','12 chicken wings',12.0,'unit');
insert into feedme.recipe_ingredients values('chicken-wings-with-cumin-lemon-and-garlic','garlic','2 garlic cloves, crushed',2.0,'unit');
insert into feedme.recipe_ingredients values('chicken-wings-with-cumin-lemon-and-garlic','lemon','zest and juice 1 lemon',NULL,'null');
insert into feedme.recipe_ingredients values('chicken-wings-with-cumin-lemon-and-garlic','cumin','1 tsp cumin seed',1.0,'tsp');
insert into feedme.recipe_ingredients values('chicken-wings-with-cumin-lemon-and-garlic','olive oil','2 tbsp olive oil',2.0,'tbsp');
insert into feedme.recipe_ingredients values('chicken-wings-with-cumin-lemon-and-garlic','honey','1 tbsp honey',1.0,'tbsp');
insert into feedme.recipe_steps values('chicken-wings-with-cumin-lemon-and-garlic',0,0, 'Using a pair of sharp kitchen scissors, cut each wing at the knuckle into two pieces. Mix the garlic, lemon zest and juice, cumin and oil with plenty of seasoning, then tip into a dish with the chicken wings and toss to coat. Cover and put in the fridge to marinate for at least 1 hr, or overnight if you have time.');
insert into feedme.recipe_steps values('chicken-wings-with-cumin-lemon-and-garlic',1,1, 'Heat oven to 200C/180C fan/gas 6, or heat an outdoor barbecue. Bake the chicken wings on an oven tray for 45-50 mins until crisp, or barbecue for 20 mins, drizzling over the honey for the final 10 mins of each method. Serve on a platter with plenty of paper napkins. Fill small bowls with olives, pistachios or almonds, dates and pickled chillies and flatbreads to serve alongside, along with the dishes below.');
insert into feedme.recipes VALUES ('moroccan-chickpea-soup', 'Moroccan chickpea soup', 'Try something different for vegetarians with Moroccan chickpea soup', 'moroccan');
insert into feedme.recipe_ingredients values('moroccan-chickpea-soup','olive oil','1 tbsp olive oil',1.0,'tbsp');
insert into feedme.recipe_ingredients values('moroccan-chickpea-soup','onion','1 medium onion, chopped',1.0,'unit');
insert into feedme.recipe_ingredients values('moroccan-chickpea-soup','celery','2 celery sticks, chopped',2.0,'unit');
insert into feedme.recipe_ingredients values('moroccan-chickpea-soup','cumin','2 tsp ground cumin',2.0,'tsp');
insert into feedme.recipe_ingredients values('moroccan-chickpea-soup','stock','600ml hot vegetable stock',600.0,'ml');
insert into feedme.recipe_ingredients values('moroccan-chickpea-soup','garlic','400g can chopped plum tomatoes with garlic',400.0,'gram');
insert into feedme.recipe_ingredients values('moroccan-chickpea-soup','peas','400g can chickpeas, rinsed and drained',400.0,'gram');
insert into feedme.recipe_ingredients values('moroccan-chickpea-soup','beans','100g frozen broad beans',100.0,'gram');
insert into feedme.recipe_ingredients values('moroccan-chickpea-soup','lemon','zest and juice 0.5 lemon',NULL,'null');
insert into feedme.recipe_ingredients values('moroccan-chickpea-soup','flatbread','large handful coriander or parsley and flatbread, to serve',NULL,'null');
insert into feedme.recipe_steps values('moroccan-chickpea-soup',0,0, 'Heat the oil in a large saucepan, then fry the onion and celery gently for 10 mins until softened, stirring frequently. Tip in the cumin and fry for another min.');
insert into feedme.recipe_steps values('moroccan-chickpea-soup',1,1, 'Turn up the heat, then add the stock, tomatoes and chickpeas, plus a good grind of black pepper. Simmer for 8 mins. Throw in broad beans and lemon juice, cook for a further 2 mins. Season to taste, then top with a sprinkling of lemon zest and chopped herbs. Serve with flatbread.');
insert into feedme.recipes VALUES ('spiced-roast-side-of-salmon', 'Spiced roast side of salmon', 'Serve salmon with paprika, ginger and honey mustard glaze for an Indian-inspired, original roast', 'indian');
insert into feedme.recipe_ingredients values('spiced-roast-side-of-salmon','salmon','10.5 kg side of salmon, skin on',10.0,'unit');
insert into feedme.recipe_ingredients values('spiced-roast-side-of-salmon','olive oil','1 tbsp olive oil, plus extra for brushing',1.0,'tbsp');
insert into feedme.recipe_ingredients values('spiced-roast-side-of-salmon','ground ginger','0.5 tsp ground ginger',5.0,'tsp');
insert into feedme.recipe_ingredients values('spiced-roast-side-of-salmon','paprika','1 tsp paprika',1.0,'tsp');
insert into feedme.recipe_ingredients values('spiced-roast-side-of-salmon','peppercorn','0.5 tsp coarsely ground black peppercorns',5.0,'tsp');
insert into feedme.recipe_ingredients values('spiced-roast-side-of-salmon','mustard','1 tbsp wholegrain mustard',1.0,'tbsp');
insert into feedme.recipe_ingredients values('spiced-roast-side-of-salmon','honey','1 tsp honey',1.0,'tsp');
insert into feedme.recipe_ingredients values('spiced-roast-side-of-salmon','lemon','1 lemon, cut into wedges, to serve',1.0,'unit');
insert into feedme.recipe_steps values('spiced-roast-side-of-salmon',0,0, 'Heat oven to 200C/180C fan/gas 6. Line a big roasting tin with baking parchment or foil – this helps to prevent the fish from sticking to the tin as it cooks. Brush the salmon skin with oil and place it, skin-side down, on the paper.');
insert into feedme.recipe_steps values('spiced-roast-side-of-salmon',1,1, 'Mix the ground ginger with the paprika, pepper, mustard, honey and 1 tbsp olive oil. Spread it evenly over the fleshy side of the fish.');
insert into feedme.recipe_steps values('spiced-roast-side-of-salmon',2,2, 'Roast, uncovered, for about 20 mins until the salmon is cooked through – check by poking a knife into the fillet and making sure the fish flakes easily.');
insert into feedme.recipe_steps values('spiced-roast-side-of-salmon',3,3, 'Serve directly from the tin, or use a couple of fish slices to carefully lift the salmon onto a big plate. Slice into portions and serve with lemon wedges on the side.');
insert into feedme.recipes VALUES ('polenta-roasted-vegetables-and-peppered-parmesan-c', 'Polenta, roasted vegetables & peppered Parmesan crisps', 'Emma Booth won our Home Cooking Month competition with this sumptuous and creamy polenta dish, topped with vibrant roasted squash and beetroot', 'mediterranean');
insert into feedme.recipe_ingredients values('polenta-roasted-vegetables-and-peppered-parmesan-c','butternut squash','1 small butternut squash(about 450g), peeled and cut into 2cm cubes',450.0,'gram');
insert into feedme.recipe_ingredients values('polenta-roasted-vegetables-and-peppered-parmesan-c','beetroot','3 raw beetroot(about 200g), cut into 2cm cubes',200.0,'gram');
insert into feedme.recipe_ingredients values('polenta-roasted-vegetables-and-peppered-parmesan-c','red onion','2 small red onions, cut into slim wedges',2.0,'unit');
insert into feedme.recipe_ingredients values('polenta-roasted-vegetables-and-peppered-parmesan-c','olive oil','3 tbsp olive oil',3.0,'tbsp');
insert into feedme.recipe_ingredients values('polenta-roasted-vegetables-and-peppered-parmesan-c','lemon','juice of 0.5 lemon',NULL,'null');
insert into feedme.recipe_ingredients values('polenta-roasted-vegetables-and-peppered-parmesan-c','polenta','200g fine polenta',200.0,'gram');
insert into feedme.recipe_ingredients values('polenta-roasted-vegetables-and-peppered-parmesan-c','salt','0.5 tsp salt',5.0,'tsp');
insert into feedme.recipe_ingredients values('polenta-roasted-vegetables-and-peppered-parmesan-c','butter','50g butter',50.0,'gram');
insert into feedme.recipe_ingredients values('polenta-roasted-vegetables-and-peppered-parmesan-c','taleggio','60g grated cheese(we used a 50/50 mix of Parmesan and Taleggio)',60.0,'gram');
insert into feedme.recipe_ingredients values('polenta-roasted-vegetables-and-peppered-parmesan-c','rocket','a big handful of rocket, to serve',NULL,'null');
insert into feedme.recipe_ingredients values('polenta-roasted-vegetables-and-peppered-parmesan-c','thyme','2 tsp freshly picked thyme leaf',2.0,'tsp');
insert into feedme.recipe_steps values('polenta-roasted-vegetables-and-peppered-parmesan-c',0,0, 'To make the crisps, turn the oven down to 200C/ 180C fan/ gas mark 6. Season the grated Parmesan with a large pinch of black pepper and scatter evenly over a baking tray covered with a silicone sheet or lightly oiled greaseproof paper and bake for 5 minutes, until golden but not browned. After cooling for five minutes, break into crisp pieces with your fingers.');
insert into feedme.recipe_steps values('polenta-roasted-vegetables-and-peppered-parmesan-c',1,1, 'Turn the oven up to 220C/ 200C fan/ gas mark 7. Toss the squash and beetroot chunks in the lemon juice and oil, season lightly with salt and pepper, and bake in a roasting tin for 20 minutes. Add the onion wedges and continue to bake for a further 25 minutes.');
insert into feedme.recipe_steps values('polenta-roasted-vegetables-and-peppered-parmesan-c',2,2, 'Meanwhile bring a litre of water, salt and half the butter to a simmer in a large pot, then slowly add the polenta in a fine stream, stirring all the time. Continue to simmer over a low heat for 35 minutes (or according to the instructions if using quick cook polenta), stirring often to prevent it sticking to the bottom. The polenta should be thickened but still soft at this point. If it starts to dry out too much, add a cup of water. When cooked, stir through the rest of the butter, the Parmesan and Taleggio and a pinch of white pepper.');
insert into feedme.recipe_steps values('polenta-roasted-vegetables-and-peppered-parmesan-c',3,3, 'To serve, spoon the polenta over a board or onto plates, scatter over the vegetables and any roasting juices, then the rocket and thyme. Tuck the parmesan crisps in between the veg, and eat hot.');
insert into feedme.recipes VALUES ('spicy-indian-rice', 'Spicy Indian rice', 'Sara Buenfeld’s delicious spicy rice is perfect served with your favourite curry', 'indian');
insert into feedme.recipe_ingredients values('spicy-indian-rice','rice','a mugful of American long grain rice',NULL,'null');
insert into feedme.recipe_ingredients values('spicy-indian-rice','onion','2 onions, sliced',2.0,'unit');
insert into feedme.recipe_ingredients values('spicy-indian-rice','sunflower oil','2 tbsp sunflower oil',2.0,'tbsp');
insert into feedme.recipe_ingredients values('spicy-indian-rice','turmeric','0.5 tsp turmeric',5.0,'tsp');
insert into feedme.recipe_ingredients values('spicy-indian-rice','cinnamon','1 cinnamon stick',1.0,'unit');
insert into feedme.recipe_ingredients values('spicy-indian-rice','cardamom','6 cardamom pods',6.0,'unit');
insert into feedme.recipe_ingredients values('spicy-indian-rice','cumin','1 tsp cumin seeds',1.0,'tsp');
insert into feedme.recipe_ingredients values('spicy-indian-rice','cashew','large handful of sultanas and roasted cashew nuts',NULL,'null');
insert into feedme.recipe_steps values('spicy-indian-rice',0,0, 'Fry the onions in the sunflower oil in a large frying pan for 10-12 minutes until golden.');
insert into feedme.recipe_steps values('spicy-indian-rice',1,1, 'Fill a roomy saucepan with water, bring to the boil and tip in a heaped teaspoon of salt - the water will bubble furiously. Add turmeric and the cinnamon stick to the water. Pour in the rice, stir once and return to the boil, then turn the heat down a little so that the water is boiling steadily, but not vigorously. Boil uncovered, without stirring (this makes for sticky rice) for 10 minutes. Lift some out with a slotted spoon and nibble a grain or two. If they''re too crunchy, cook for another minute and taste again. They should be tender but with a little bite. Drain the rice into a large sieve and rinse by pouring over a kettle of very hot water');
insert into feedme.recipe_steps values('spicy-indian-rice',2,2, 'Stir the crushed seeds of the cardamom pods into the onions with the cumin seeds and fry briefly.');
insert into feedme.recipe_steps values('spicy-indian-rice',3,3, 'Toss in a large handful of sultanas and roasted cashew nuts, then the hot drained rice. This is great with curry and will also give an exotic edge to grilled meat or fish.');
insert into feedme.recipes VALUES ('hoisin-pork-with-garlic-and-ginger-greens', 'Hoisin pork with garlic & ginger greens', 'Marinate lean loin steaks with Chinese sauce then serve with plenty of bok choi cabbage for a healthy supper', 'chinese');
insert into feedme.recipe_ingredients values('hoisin-pork-with-garlic-and-ginger-greens','pork','500g pork loin steak, cut into 2cm-thick slices',500.0,'gram');
insert into feedme.recipe_ingredients values('hoisin-pork-with-garlic-and-ginger-greens','hoisin sauce','4 tbsp hoisin sauce',4.0,'tbsp');
insert into feedme.recipe_ingredients values('hoisin-pork-with-garlic-and-ginger-greens','soy sauce','1 tbsp light soy sauce, plus a dash',1.0,'tbsp');
insert into feedme.recipe_ingredients values('hoisin-pork-with-garlic-and-ginger-greens','broccoli','350g thin-stemmed broccoli',350.0,'gram');
insert into feedme.recipe_ingredients values('hoisin-pork-with-garlic-and-ginger-greens','sunflower oil','1 tbsp sunflower oil',1.0,'tbsp');
insert into feedme.recipe_ingredients values('hoisin-pork-with-garlic-and-ginger-greens','garlic','2 garlic cloves, thinly sliced',2.0,'unit');
insert into feedme.recipe_ingredients values('hoisin-pork-with-garlic-and-ginger-greens','ginger','5cm-piece ginger, shredded',5.0,'unit');
insert into feedme.recipe_ingredients values('hoisin-pork-with-garlic-and-ginger-greens','spring onion','1 bunch spring onion, halved lengthways',1.0,'unit');
insert into feedme.recipe_ingredients values('hoisin-pork-with-garlic-and-ginger-greens','bok choi','350g bok choi, halved lengthways',350.0,'gram');
insert into feedme.recipe_ingredients values('hoisin-pork-with-garlic-and-ginger-greens','noodles','riceor noodles, to serve (optional)',NULL,'null');
insert into feedme.recipe_steps values('hoisin-pork-with-garlic-and-ginger-greens',0,0, 'Put the pork, hoisin and soy sauce in a bowl and allow to stand for 10 mins. Heat the grill to high, shake off any excess sauce, then lay the pork on a tray. Grill for 5 mins, turning halfway, until cooked through. Remove and leave to rest in a warm place for 5 mins.');
insert into feedme.recipe_steps values('hoisin-pork-with-garlic-and-ginger-greens',1,1, 'Meanwhile, put the broccoli in a microwave-safe bowl with 4 tbsp water, cover with cling film, then microwave on High for 3 mins. Heat the oil in a wok, add the garlic and ginger, and stir-fry for 1 min. Add the spring onions and bok choi, then stir-fry for a further 2 mins. Tip in the broccoli with a dash of soy sauce and stir-fry for 1-2 mins more until the veg is warmed through. Serve the pork with the greens, a drizzle of any resting pork juices and rice or noodles, if you like.');
insert into feedme.recipes VALUES ('thaistyle-steamed-fish', 'Thai-style steamed fish', 'Serve with Thai jasmine rice for a flavour-packed low-fat meal', 'thai');
insert into feedme.recipe_ingredients values('thaistyle-steamed-fish','trout','2 trout fillets, each weighing about 140g/5oz',140.0,'gram');
insert into feedme.recipe_ingredients values('thaistyle-steamed-fish','ginger','a small knob of fresh root ginger, peeled and chopped',NULL,'null');
insert into feedme.recipe_ingredients values('thaistyle-steamed-fish','garlic','1 small garlic clove, chopped',1.0,'unit');
insert into feedme.recipe_ingredients values('thaistyle-steamed-fish','chilli','1 small red chilli (not bird''s eye), seeded and finely chopped',1.0,'unit');
insert into feedme.recipe_ingredients values('thaistyle-steamed-fish','lime','grated zest and juice of 1 lime',NULL,'null');
insert into feedme.recipe_ingredients values('thaistyle-steamed-fish','pak choi','3 baby pak choi, each quartered lengthways',3.0,'unit');
insert into feedme.recipe_ingredients values('thaistyle-steamed-fish','soy sauce','2 tbsp soy sauce',2.0,'tbsp');
insert into feedme.recipe_steps values('thaistyle-steamed-fish',0,0, 'Nestle the fish fillets side by side on a large square of foil and scatter the ginger, garlic, chilli and lime zest over them. Drizzle the lime juice on top and then scatter the pieces of pak choi around and on top of the fish. Pour the soy sauce over the pak choi and loosely seal the foil to make a package, making sure you leave space at the top for the steam to circulate as the fish cooks.');
insert into feedme.recipe_steps values('thaistyle-steamed-fish',1,1, 'Steam for 15 minutes. (If you haven’t got a steamer, put the parcel on a heatproof plate over a pan of gently simmering water, cover with a lid and steam.)');
insert into feedme.recipes VALUES ('boulangre-potatoes', 'Boulangère potatoes', 'Lighter and healthier than Dauphinoise and just as impressive, perfect for a cold winter''s night', 'french');
insert into feedme.recipe_ingredients values('boulangre-potatoes','onion','2 onions, thinly sliced',2.0,'unit');
insert into feedme.recipe_ingredients values('boulangre-potatoes','thyme','few thyme sprigs',NULL,'null');
insert into feedme.recipe_ingredients values('boulangre-potatoes','olive oil','2 tbsp olive oil',2.0,'tbsp');
insert into feedme.recipe_ingredients values('boulangre-potatoes','potato','10.5 kg floury potato, such as Maris Piper or Desirée, peeled and sliced thinly, by hand or using a food processor',10.0,'unit');
insert into feedme.recipe_ingredients values('boulangre-potatoes','stock','425ml vegetable stock',425.0,'ml');
insert into feedme.recipe_steps values('boulangre-potatoes',0,0, 'Heat oven to 200C/fan 180C/gas 6. Fry the onions and thyme sprigs in the oil until softened and lightly coloured (about 5 mins).');
insert into feedme.recipe_steps values('boulangre-potatoes',1,1, 'Spread a layer of potatoes over the base of a 1.5-litre oiled gratin dish. Sprinkle over a few onions (see picture, above) and continue layering, finishing with a layer of potatoes. Pour over the stock and bake for 50-60 mins until the potatoes are cooked and the top is golden and crisp.');
insert into feedme.recipes VALUES ('greekstyle-lamb-with-lemon-and-thyme', 'Greek-style lamb with lemon & thyme', 'Marinate lamb chops in a zesty mixture for 15 minutes, then coat with yogurt and grill over the coals', 'greek');
insert into feedme.recipe_ingredients values('greekstyle-lamb-with-lemon-and-thyme','lamb','18 lamb cutletsor chops',18.0,'unit');
insert into feedme.recipe_ingredients values('greekstyle-lamb-with-lemon-and-thyme','lemon','zest and juice 2 lemons',NULL,'null');
insert into feedme.recipe_ingredients values('greekstyle-lamb-with-lemon-and-thyme','thyme','few sprigs thyme, leaves stripped, plus extra to serve if you like',NULL,'null');
insert into feedme.recipe_ingredients values('greekstyle-lamb-with-lemon-and-thyme','garlic','2 garlic cloves, crushed',2.0,'unit');
insert into feedme.recipe_ingredients values('greekstyle-lamb-with-lemon-and-thyme','yoghurt','200ml Greek yogurt',200.0,'ml');
insert into feedme.recipe_ingredients values('greekstyle-lamb-with-lemon-and-thyme','pitta bread','houmous, pitta bread and a bag of salad leaves, to serve',NULL,'null');
insert into feedme.recipe_steps values('greekstyle-lamb-with-lemon-and-thyme',0,0, 'Sit lamb in a large shallow dish, then mix the lemon zest and juice, thyme leaves and garlic together. Pour over, turn to coat, and marinate at room temp for 15 mins.');
insert into feedme.recipe_steps values('greekstyle-lamb-with-lemon-and-thyme',1,1, 'Mix the yogurt with some seasoning, lift the lamb out of the marinade and rub with the yogurt. Barbecue or grill for 5-15 mins, turning once, until the lamb is cooked to your liking. Scatter with a little more thyme, if you like, and serve with dollops of houmous, some pitta bread and salad leaves.');
insert into feedme.recipes VALUES ('mango-chicken-bean-and-rice-bake', 'Mango chicken, bean & rice bake', 'Drumsticks are ideal for an all-in-one traybake - the rice, beans and spicy seasoning make it a little like Caribbean jerk chicken', 'caribbean');
insert into feedme.recipe_ingredients values('mango-chicken-bean-and-rice-bake','spring onion','small bunch spring onions, chopped',NULL,'null');
insert into feedme.recipe_ingredients values('mango-chicken-bean-and-rice-bake','ginger','thumb-sized piece ginger, chopped',NULL,'null');
insert into feedme.recipe_ingredients values('mango-chicken-bean-and-rice-bake','garlic','3 garlic cloves',3.0,'unit');
insert into feedme.recipe_ingredients values('mango-chicken-bean-and-rice-bake','chilli','1 red chilli, seeds removed if you don''t like it too hot',1.0,'unit');
insert into feedme.recipe_ingredients values('mango-chicken-bean-and-rice-bake','coriander','small handful coriander, stalks roughly chopped and leaves picked',NULL,'null');
insert into feedme.recipe_ingredients values('mango-chicken-bean-and-rice-bake','thyme','1 tbsp thyme leaf',1.0,'tbsp');
insert into feedme.recipe_ingredients values('mango-chicken-bean-and-rice-bake','lime','zest and juice 1 lime, plus 1 lime cut into wedges to serve',NULL,'null');
insert into feedme.recipe_ingredients values('mango-chicken-bean-and-rice-bake','allspice','10.5 tsp ground allspice',5.0,'tsp');
insert into feedme.recipe_ingredients values('mango-chicken-bean-and-rice-bake','sunflower oil','2 tbsp sunflower oil',2.0,'tbsp');
insert into feedme.recipe_ingredients values('mango-chicken-bean-and-rice-bake','chicken','8 chicken drumsticks',8.0,'unit');
insert into feedme.recipe_ingredients values('mango-chicken-bean-and-rice-bake','rice','300g long grain rice',300.0,'gram');
insert into feedme.recipe_ingredients values('mango-chicken-bean-and-rice-bake','kidney','400g can kidney bean, rinsed and drained',400.0,'gram');
insert into feedme.recipe_ingredients values('mango-chicken-bean-and-rice-bake','stock','500ml chicken stock',500.0,'ml');
insert into feedme.recipe_ingredients values('mango-chicken-bean-and-rice-bake','mango','1 ripe mango, peeled, stoned and cut into bite-sized chunks',1.0,'unit');
insert into feedme.recipe_ingredients values('mango-chicken-bean-and-rice-bake','chutney','200g mango chutney, plus extra to serve (optional)',200.0,'gram');
insert into feedme.recipe_steps values('mango-chicken-bean-and-rice-bake',0,0, 'Put the spring onions, ginger, garlic, chilli, coriander stalks, thyme, lime zest and juice, allspice and oil in a food processor, then blend to a paste. Pour over the chicken drumsticks and leave to marinate for at least 1 hr, or preferably up to 1 day.');
insert into feedme.recipe_steps values('mango-chicken-bean-and-rice-bake',1,1, 'Heat oven to 180C/160C fan/gas 4. Tip the rice and beans into a large roasting tin with deep sides. Remove the chicken from the marinade and set aside. Mix the stock into the marinade in the bowl and stir well. Pour the stock over the rice and beans, then put the chicken drumsticks and the mango pieces on top. Cover the tray tightly with foil and bake for 30 mins.');
insert into feedme.recipe_steps values('mango-chicken-bean-and-rice-bake',2,2, 'Take the tray out of the oven and remove the foil. Increase the temperature to 220C/200C fan/gas 7. Spoon the mango chutney over the drumsticks and return to the oven, uncovered, for 35-40 mins, to brown the chicken pieces and allow the rice to absorb all the liquid. Before serving, fluff up the rice a little with a fork and scatter with the coriander leaves. Serve with lime wedges and extra mango chutney, if you like.');
insert into feedme.recipes VALUES ('sugared-scones', 'Sugared scones', 'A simple and quick scone recipe that has a great sweet finish - the crunchy sugar top adds texture too', 'british');
insert into feedme.recipe_ingredients values('sugared-scones','butter','85g diced butter',85.0,'gram');
insert into feedme.recipe_ingredients values('sugared-scones','self-raising flour','350g self-raising flour',350.0,'gram');
insert into feedme.recipe_ingredients values('sugared-scones','salt','0.25 tsp salt',25.0,'tsp');
insert into feedme.recipe_ingredients values('sugared-scones','soda','10.5 tsp bicarbonate of soda',5.0,'tsp');
insert into feedme.recipe_ingredients values('sugared-scones','caster sugar','4 tbsp caster sugar',4.0,'tbsp');
insert into feedme.recipe_ingredients values('sugared-scones','milk','200ml milk, warmed to room temperature, plus a splash extra',200.0,'ml');
insert into feedme.recipe_ingredients values('sugared-scones','sugar','crushed sugar cubes, to decorate',NULL,'null');
insert into feedme.recipe_steps values('sugared-scones',0,0, 'Heat oven to 200C/180C fan/gas 6. Whizz butter into flour. Tip into a bowl and stir in salt with bicarbonate of soda and sugar. Using a cutlery knife, quickly stir in milk – don’t over-mix.');
insert into feedme.recipe_steps values('sugared-scones',1,1, 'Tip out onto a lightly floured surface and turn over a couple of times to very gently bring together with your hands. Gently pat to about 1in thick, then stamp out rounds with a floured cutter. Pat together trimmings to stamp out more. Brush the tops with a splash more milk, then scatter with crushed sugar cubes. Bake on a baking sheet for 10-12 mins until risen and golden.');
insert into feedme.recipes VALUES ('spiced-chicken-meatballs-with-noodles-basil-and-br', 'Spiced chicken meatballs with noodles, basil & broth', 'This fragrant and filling dish is an excellent dinner party choice', 'vietnamese');
insert into feedme.recipe_ingredients values('spiced-chicken-meatballs-with-noodles-basil-and-br','onion','1 large onion, roughly chopped',1.0,'unit');
insert into feedme.recipe_ingredients values('spiced-chicken-meatballs-with-noodles-basil-and-br','ginger','thumb-size piece fresh root ginger',NULL,'null');
insert into feedme.recipe_ingredients values('spiced-chicken-meatballs-with-noodles-basil-and-br','seeds','1-2 long red chillies, finely chopped (seeds in or out, you decide)',1.0,'unit');
insert into feedme.recipe_ingredients values('spiced-chicken-meatballs-with-noodles-basil-and-br','garlic','1 garlic clove, crushed',1.0,'unit');
insert into feedme.recipe_ingredients values('spiced-chicken-meatballs-with-noodles-basil-and-br','peppercorn','6 white peppercorns, crushed',6.0,'unit');
insert into feedme.recipe_ingredients values('spiced-chicken-meatballs-with-noodles-basil-and-br','coriander','20g pack coriander, stalks, roots if you have them and leaves, chopped and kept separate, plus sprigs to finish',20.0,'gram');
insert into feedme.recipe_ingredients values('spiced-chicken-meatballs-with-noodles-basil-and-br','milk','50ml milk',50.0,'ml');
insert into feedme.recipe_ingredients values('spiced-chicken-meatballs-with-noodles-basil-and-br','breadcrumbs','100g fresh white breadcrumbs',100.0,'gram');
insert into feedme.recipe_ingredients values('spiced-chicken-meatballs-with-noodles-basil-and-br','mince','1kg quality chickenmince',1.0,'unit');
insert into feedme.recipe_ingredients values('spiced-chicken-meatballs-with-noodles-basil-and-br','vegetable oil','3 tbsp vegetable oil',3.0,'tbsp');
insert into feedme.recipe_steps values('spiced-chicken-meatballs-with-noodles-basil-and-br',0,0, 'Whizz the onion, ginger, chillies, garlic, white pepper and half of the coriander stalks, roots (if using) and leaves in a food processor until finely chopped. Mix the milk and breadcrumbs together in a large bowl. Add the onion mix and chicken into the breadcrumbs, then season. Now, really mix – I mean mix – until the whole thing becomes a paste rather than lumpy. If you have a tabletop mixer, then use that instead of your hands. Shape into small balls around the size of a 50p piece. Place a large frying pan or casserole over a medium heat, add a drizzle of oil then fry the balls until well coloured – about 10 mins. You’ll need to do this in batches, adding more oil each time. Set aside.');
insert into feedme.recipe_steps values('spiced-chicken-meatballs-with-noodles-basil-and-br',1,1, 'For the broth, put the stock into a large saucepan, bring to the boil, then simmer. Add the sesame oil, fish sauce, ginger, remaining coriander stalks and roots, star anise and peppercorns. Simmer, covered, for 20 mins. Add the spring onions, noodles and chillies, if using. Take 6 large bowls, then divide the noodles between them – tongs are the best tool. Drop in the warm meatballs then ladle in the stock. Scatter with coriander and basil leaves then tuck in.');
insert into feedme.recipes VALUES ('asian-chicken-salad', 'Asian chicken salad', 'This easy-to-prepare salad is filled with zingy flavours - the simplified version is great for kids'' lunchboxes too', 'vietnamese');
insert into feedme.recipe_ingredients values('asian-chicken-salad','chicken','1 boneless, skinless chicken breast',1.0,'unit');
insert into feedme.recipe_ingredients values('asian-chicken-salad','fish sauce','1 tbsp fish sauce',1.0,'tbsp');
insert into feedme.recipe_ingredients values('asian-chicken-salad','lime','zest and juice 0.5 lime(about 1 tbsp)',1.0,'tbsp');
insert into feedme.recipe_ingredients values('asian-chicken-salad','caster sugar','1 tsp caster sugar',1.0,'tsp');
insert into feedme.recipe_ingredients values('asian-chicken-salad','salad','100g bag mixed salad leaves',100.0,'gram');
insert into feedme.recipe_ingredients values('asian-chicken-salad','coriander','large handful coriander, roughly chopped',NULL,'null');
insert into feedme.recipe_ingredients values('asian-chicken-salad','red onion','0.25 red onion, thinly sliced',0.0,'unit');
insert into feedme.recipe_ingredients values('asian-chicken-salad','chilli','0.5 chilli, deseeded and thinly sliced',0.0,'unit');
insert into feedme.recipe_ingredients values('asian-chicken-salad','cucumber','0.25 cucumber, halved lengthways, sliced',0.0,'unit');
insert into feedme.recipe_steps values('asian-chicken-salad',0,0, 'Cover the chicken with cold water, bring to the boil, then cook for 10 mins. Remove from the pan and tear into shreds. Stir together the fish sauce, lime zest, juice and sugar until sugar dissolves.');
insert into feedme.recipe_steps values('asian-chicken-salad',1,1, 'Place the leaves and coriander in a container, then top with the chicken, onion, chilli and cucumber. Place the dressing in a separate container and toss through the salad when ready to eat.');
insert into feedme.recipes VALUES ('raspberry-and-rose-trifles', 'Raspberry & rose trifles', 'A mix of classic Middle Eastern flavours that come together very simply in this pretty trifle', 'turkish');
insert into feedme.recipe_ingredients values('raspberry-and-rose-trifles','turkish delight','2 cubes Turkish delight, optional',2.0,'unit');
insert into feedme.recipe_ingredients values('raspberry-and-rose-trifles','custard','500g pot good-quality vanilla custard',500.0,'gram');
insert into feedme.recipe_ingredients values('raspberry-and-rose-trifles','madeira','0.5 a 350g/12oz bought madeira loaf',350.0,'gram');
insert into feedme.recipe_ingredients values('raspberry-and-rose-trifles','berry','300g pack raspberry',300.0,'gram');
insert into feedme.recipe_ingredients values('raspberry-and-rose-trifles','orange','juice 1 orange',NULL,'null');
insert into feedme.recipe_ingredients values('raspberry-and-rose-trifles','fat','200ml tub crème fraîche (use reduced-fat if you like)',200.0,'ml');
insert into feedme.recipe_ingredients values('raspberry-and-rose-trifles','pistachio','handful pistachios, roughly chopped',NULL,'null');
insert into feedme.recipe_steps values('raspberry-and-rose-trifles',0,0, 'If you don’t want to add the Turkish delight, move on to step 2. Snip the Turkish delight into pieces with kitchen scissors into a large bowl and add a few tablespoons of water. Microwave on High for 1 min, stirring after 30 secs, or until dissolved and smooth. Tip in the custard and stir together.');
insert into feedme.recipe_steps values('raspberry-and-rose-trifles',1,1, 'Break the cake into rough chunks and divide between 6 sundae dishes or serving glasses. Mix half of the raspberries with the orange juice in a bowl and crush gently with a fork to break the fruit up slightly. Spoon over the cake layer. Cover with the custard, then leave to cool in the fridge for at least 30 mins. Can be made up to 1 day ahead. To serve, top the trifles with a dollop of crème fraîche, the remaining raspberries and a sprinkling of pistachios.');
insert into feedme.recipes VALUES ('scampi-with-tartare-sauce', 'Scampi with tartare sauce', 'Crispy beer battered langoustine or prawn tails with a mayo-based dip of gherkins, capers and herbs', 'british');
insert into feedme.recipe_ingredients values('scampi-with-tartare-sauce','prawn','15-20 langoustineor Dublin Bay prawn tails',15.0,'unit');
insert into feedme.recipe_ingredients values('scampi-with-tartare-sauce','sunflower oil','vegetable or sunflower oil, for frying',NULL,'null');
insert into feedme.recipe_ingredients values('scampi-with-tartare-sauce','flour','140g plain flour',140.0,'gram');
insert into feedme.recipe_ingredients values('scampi-with-tartare-sauce','flour','85g cornflour',85.0,'gram');
insert into feedme.recipe_ingredients values('scampi-with-tartare-sauce','beer','150ml beer',150.0,'ml');
insert into feedme.recipe_ingredients values('scampi-with-tartare-sauce','ling','100ml sparkling water',100.0,'ml');
insert into feedme.recipe_ingredients values('scampi-with-tartare-sauce','chips','lemon wedges and chips, to serve',NULL,'null');
insert into feedme.recipe_steps values('scampi-with-tartare-sauce',0,0, 'To prepare the langoustines, pull off the head and pincers. Lay the tail flat on a chopping board and use a sharp pair of scissors to cut a line straight down the back of the shell. Carefully peel the langoustine, score down the back, then remove the grit sac.');
insert into feedme.recipe_steps values('scampi-with-tartare-sauce',1,1, 'Get the oil heating in a large saucepan or wok – you will need enough to come 2-3in up the side of the pan. For the tartare sauce, mix all ingredients in a bowl and season.');
insert into feedme.recipe_steps values('scampi-with-tartare-sauce',2,2, 'Place the flours in a bowl with a good pinch of salt and pepper. Add the beer and sparkling water, and whisk to a smooth batter.');
insert into feedme.recipe_steps values('scampi-with-tartare-sauce',3,3, 'To test if the oil is hot enough, put a drop of batter into the pan – it should crisp and brown within 30 secs. Dip each langoustine or prawn tail into the batter, then carefully drop it into the oil. Drizzle a little extra batter over each one while they are cooking – this will give you a really crispy coating. Cook them in batches, making sure you don’t overcrowd the pan. When golden and floating to the surface, scoop out and drain well on kitchen paper. Sprinkle the scampi with salt and serve with the tartare sauce, lemon wedges and chips.');
insert into feedme.recipes VALUES ('peanut-butter-cheesecake', 'Peanut butter cheesecake', 'This irresistible cheesecake is the perfect indulgence for peanut butter fans - freeze for up to two months and serve as a show-stopping dinner party dessert', 'american');
insert into feedme.recipe_steps values('peanut-butter-cheesecake',0,0, 'Oil and line a 20cm round loose- bottomed cake tin with cling film, making it as smooth as possible. Melt the butter in a pan. Crush the biscuits by bashing them in a bag with a rolling pin, then stir them into the butter until very well coated. Press the mixture firmly into the base of the tin and chill.');
insert into feedme.recipe_steps values('peanut-butter-cheesecake',1,1, 'Soak the gelatine in water while you make the filling. Tip the ricotta into a bowl, then beat in the peanut butter and syrup. Ricotta has a slightly grainy texture so blitz until smooth with a stick blender for a smoother texture if you prefer.');
insert into feedme.recipe_steps values('peanut-butter-cheesecake',2,2, 'Take the soaked gelatine from the water and squeeze dry. Put it into a pan with the milk and heat very gently until the gelatine dissolves. Beat into the peanut mixture, then tip onto the biscuit base. Chill until set.');
insert into feedme.recipe_steps values('peanut-butter-cheesecake',3,3, 'To freeze, leave in the tin and as soon as it is solid, cover the surface with cling film, then wrap the tin with cling film and foil.');
insert into feedme.recipe_steps values('peanut-butter-cheesecake',4,4, 'To defrost, thaw in the fridge overnight.');
insert into feedme.recipe_steps values('peanut-butter-cheesecake',5,5, 'To serve, carefully remove from the tin. Whisk the cream with the sugar until it holds its shape, then spread on top of the cheesecake and scatter with the peanut brittle.');
insert into feedme.recipes VALUES ('spiced-chicken-balti', 'Spiced chicken balti', 'Compliments came rolling in about this almost as soon as the magazine went on sale. Try it and let us know your thoughts', 'indian');
insert into feedme.recipe_ingredients values('spiced-chicken-balti','sunflower oil','1 tbsp sunflower oil',1.0,'tbsp');
insert into feedme.recipe_ingredients values('spiced-chicken-balti','onion','2 large onions, thickly sliced',2.0,'unit');
insert into feedme.recipe_ingredients values('spiced-chicken-balti','chicken','4 skinless chicken breasts',4.0,'unit');
insert into feedme.recipe_ingredients values('spiced-chicken-balti','curry paste','4 tbsp balti curry paste',4.0,'tbsp');
insert into feedme.recipe_ingredients values('spiced-chicken-balti','quinoa','200g quinoa',200.0,'gram');
insert into feedme.recipe_ingredients values('spiced-chicken-balti','tomato','400g can chopped tomato',400.0,'gram');
insert into feedme.recipe_ingredients values('spiced-chicken-balti','stock','1l chicken stock',1.0,'litre');
insert into feedme.recipe_ingredients values('spiced-chicken-balti','cashew','50g roasted salted cashew',50.0,'gram');
insert into feedme.recipe_ingredients values('spiced-chicken-balti','coriander','small bunch coriander, leaves chopped',NULL,'null');
insert into feedme.recipe_steps values('spiced-chicken-balti',0,0, 'Heat the oil in a large pan, fry the onions for 5 mins until golden and softened, then tip onto a plate. Add the chicken breasts, browning for a few mins on each side, then stir in the balti paste, quinoa and onions. Sizzle for a few mins, then pour in the tomatoes and stock and give everything a good mix. Bubble for 25 mins until the quinoa is tender and saucy.');
insert into feedme.recipe_steps values('spiced-chicken-balti',1,1, 'Stir in the cashews and most of the coriander with some seasoning, then pile into bowls and scatter over the rest of the coriander to serve.');
insert into feedme.recipes VALUES ('exotic-avocado-salad', 'Exotic avocado salad', 'Savour sharp, sweet flavours in this exotic avocado salad', 'caribbean');
insert into feedme.recipe_ingredients values('exotic-avocado-salad','pumpkin','2 tbsp pumpkin seeds',2.0,'tbsp');
insert into feedme.recipe_ingredients values('exotic-avocado-salad','papaya','1 ripe papaya',1.0,'unit');
insert into feedme.recipe_ingredients values('exotic-avocado-salad','avocado','1 ripe avocado',1.0,'unit');
insert into feedme.recipe_ingredients values('exotic-avocado-salad','watercress','50g bag trimmed and washed watercress',50.0,'gram');
insert into feedme.recipe_ingredients values('exotic-avocado-salad','mint','half small pack fresh mint',NULL,'null');
insert into feedme.recipe_ingredients values('exotic-avocado-salad','lime','juice of half lime',NULL,'null');
insert into feedme.recipe_ingredients values('exotic-avocado-salad','olive oil','1 tbsp olive oil',1.0,'tbsp');
insert into feedme.recipe_steps values('exotic-avocado-salad',0,0, 'Dry-fry the pumpkin seeds in a frying pan for a few minutes, tossing and turning them until they look toasted. Tip them out of the pan and let them go cold. Peel the papayas, halve them lengthways and scoop out the seeds. Cut the flesh into long, thin slices. Halve the avocados and remove the stones, then peel off the skin and slice the flesh lengthways into thin slices.');
insert into feedme.recipe_steps values('exotic-avocado-salad',1,1, 'Put the papayas, avocados, pumpkin seeds and watercress into a large bowl. Chop about 1 tbsp of the mint leaves and set aside. Pick the remaining leaves from the stalks and tear them into the bowl.');
insert into feedme.recipe_steps values('exotic-avocado-salad',2,2, 'Mix the lime juice and olive oil with the rest of the chopped mint to make a dressing, and season with salt and pepper to taste. Pour over the salad and gently mix all the ingredients together with your hands. Taste and add more salt and pepper if you think it needs it, then transfer to a serving platter.');
insert into feedme.recipes VALUES ('easy-miso', 'Easy miso soup', 'A low fat soup ready in under 5 minutes', 'japanese');
insert into feedme.recipe_ingredients values('easy-miso','miso','sachet of Japanese miso soup',NULL,'null');
insert into feedme.recipe_ingredients values('easy-miso','soy sauce','dash of soy sauce',NULL,'null');
insert into feedme.recipe_ingredients values('easy-miso','tofu','1 tbsp diced silken tofu',1.0,'tbsp');
insert into feedme.recipe_ingredients values('easy-miso','watercress','a good sprig of watercress',NULL,'null');
insert into feedme.recipe_steps values('easy-miso',0,0, 'Dissolve a sachet of Japanese miso soup mix in a bowl with boiling water.');
insert into feedme.recipe_steps values('easy-miso',1,1, 'Add a dash soy sauce, diced silken tofu and a good sprig of watercress.');
insert into feedme.recipes VALUES ('creamy-beetroot-curry', 'Creamy beetroot curry', 'Try an original approach to serving thickly sliced beetroot by combining with Madras spices, mustard seeds and green chilli', 'indian');
insert into feedme.recipe_ingredients values('creamy-beetroot-curry','vegetable oil','1 tbsp vegetable oil',1.0,'tbsp');
insert into feedme.recipe_ingredients values('creamy-beetroot-curry','onion','2 onions, very finely chopped',2.0,'unit');
insert into feedme.recipe_ingredients values('creamy-beetroot-curry','mustard','2 tsp yellow mustard seed',2.0,'tsp');
insert into feedme.recipe_ingredients values('creamy-beetroot-curry','curry paste','3 tbsp Madras curry paste',3.0,'tbsp');
insert into feedme.recipe_ingredients values('creamy-beetroot-curry','beetroot','1kg (peeled weight) raw beetroot, peeled, halved and thickly sliced',1.0,'unit');
insert into feedme.recipe_ingredients values('creamy-beetroot-curry','chilli','1 green chilli, halved lengthways',1.0,'unit');
insert into feedme.recipe_ingredients values('creamy-beetroot-curry','tomato','400g can chopped tomato',400.0,'gram');
insert into feedme.recipe_ingredients values('creamy-beetroot-curry','almond','3 tbsp ground almond',3.0,'tbsp');
insert into feedme.recipe_ingredients values('creamy-beetroot-curry','fat','4 tbsp low-fat natural yogurt, plus extra to serve',4.0,'tbsp');
insert into feedme.recipe_ingredients values('creamy-beetroot-curry','rice','basmati rice, to serve (optional)',NULL,'null');
insert into feedme.recipe_steps values('creamy-beetroot-curry',0,0, 'Heat the oil in a large lidded pan, stir in the onions and cook for 8 mins until soft. Tip in the mustard seeds and cook for 1 min until toasted. Stir through the curry paste and sizzle for 3 mins.');
insert into feedme.recipe_steps values('creamy-beetroot-curry',1,1, 'Mix the beetroot through the spicy onions, then add the chilli, tomatoes and 2 cans of water. Cover and simmer for 30 mins, stirring occasionally, until the beetroot is tender. Remove the lid, turn up the heat and cook until the sauce is thick.');
insert into feedme.recipe_steps values('creamy-beetroot-curry',2,2, 'Take off the heat, then stir through the almonds, yogurt and some seasoning. Top with yogurt and serve with basmati rice, if you like.');
insert into feedme.recipes VALUES ('minty-carrot-pistachio-and-feta-salad', 'Minty carrot, pistachio & feta salad', 'A vegetarian side salad with cumin-roasted carrots, chickpeas, mint, nuts, spinach and salty cheese - great with Moroccan food', 'moroccan');
insert into feedme.recipe_ingredients values('minty-carrot-pistachio-and-feta-salad','olive oil','2 tbsp olive oil, plus a little extra for drizzling',2.0,'tbsp');
insert into feedme.recipe_ingredients values('minty-carrot-pistachio-and-feta-salad','carrot','500g carrot, halved and cut into chunks',500.0,'gram');
insert into feedme.recipe_ingredients values('minty-carrot-pistachio-and-feta-salad','chickpea','400g can chickpea, drained and rinsed',400.0,'gram');
insert into feedme.recipe_ingredients values('minty-carrot-pistachio-and-feta-salad','cumin','2 tsp ground cumin',2.0,'tsp');
insert into feedme.recipe_ingredients values('minty-carrot-pistachio-and-feta-salad','lemon','juice 0.5 lemon',NULL,'null');
insert into feedme.recipe_ingredients values('minty-carrot-pistachio-and-feta-salad','honey','1 tbsp clear honey',1.0,'tbsp');
insert into feedme.recipe_ingredients values('minty-carrot-pistachio-and-feta-salad','mint','small bunch mint, chopped',NULL,'null');
insert into feedme.recipe_ingredients values('minty-carrot-pistachio-and-feta-salad','spinach','2 big handfuls spinach',2.0,'unit');
insert into feedme.recipe_ingredients values('minty-carrot-pistachio-and-feta-salad','pistachio','100g bag shelled pistachio, roughly chopped',100.0,'gram');
insert into feedme.recipe_ingredients values('minty-carrot-pistachio-and-feta-salad','cheese','200g pack feta cheese, crumbled',200.0,'gram');
insert into feedme.recipe_steps values('minty-carrot-pistachio-and-feta-salad',0,0, 'Heat oven to 200C/180C fan/gas 6. Tip 1 tbsp of the oil, the carrots, chickpeas and cumin onto a baking tray, season and toss to coat. Roast for 30 mins or until the carrots are tender.');
insert into feedme.recipe_steps values('minty-carrot-pistachio-and-feta-salad',1,1, 'Mix together the lemon juice, honey and remaining oil, then pour all over the roasted carrots and chickpeas. Leave to cool. You can chill the salad at this stage, for up to 1 day; just bring it out of the fridge 1 hr before serving.');
insert into feedme.recipe_steps values('minty-carrot-pistachio-and-feta-salad',2,2, 'Mix through the mint, spinach leaves and pistachios, and check the seasoning. Scatter the feta over and drizzle with a little extra oil.');
insert into feedme.recipes VALUES ('natalies-trinidadian-fish-stew', 'Natalie''s Trinidadian fish stew', 'This dish of Caribbean sunshine is perfect for sharing with friend - make sure you marinate the fish in lemon and lime to get a really zingy taste', 'caribbean');
insert into feedme.recipe_ingredients values('natalies-trinidadian-fish-stew','lime','2 limes, juice and grated zest of 1, the other halved for serving',2.0,'unit');
insert into feedme.recipe_ingredients values('natalies-trinidadian-fish-stew','cod','1 skinned white fishfillets, such as cod or hoki, about 175-200g/6-8oz each',200.0,'gram');
insert into feedme.recipe_ingredients values('natalies-trinidadian-fish-stew','lemon','2 lemons, juice only',2.0,'unit');
insert into feedme.recipe_ingredients values('natalies-trinidadian-fish-stew','thyme','2 x 15g packets fresh thyme(you need a lot for the aroma)',15.0,'gram');
insert into feedme.recipe_ingredients values('natalies-trinidadian-fish-stew','pepper','0.25 tsp white pepper',25.0,'tsp');
insert into feedme.recipe_ingredients values('natalies-trinidadian-fish-stew','rum','1 tbsp dark rum',1.0,'tbsp');
insert into feedme.recipe_ingredients values('natalies-trinidadian-fish-stew','vegetable oil','3 tbsp vegetable oil',3.0,'tbsp');
insert into feedme.recipe_ingredients values('natalies-trinidadian-fish-stew','onion','1 onion, cut into thin rings',1.0,'unit');
insert into feedme.recipe_ingredients values('natalies-trinidadian-fish-stew','fresh coriander','a handful of fresh coriander, chopped',NULL,'null');
insert into feedme.recipe_ingredients values('natalies-trinidadian-fish-stew','garlic','2 garlic cloves, chopped',2.0,'unit');
insert into feedme.recipe_ingredients values('natalies-trinidadian-fish-stew','steak','1 beefsteak or 3 normal tomatoes, sliced',1.0,'unit');
insert into feedme.recipe_ingredients values('natalies-trinidadian-fish-stew','sugar','2 tsp dark muscovado sugar',2.0,'tsp');
insert into feedme.recipe_ingredients values('natalies-trinidadian-fish-stew','hake','shake of Angostura bitters, optional',NULL,'null');
insert into feedme.recipe_ingredients values('natalies-trinidadian-fish-stew','vegetables','basmati riceor boiled potatoes and steamed vegetables, to serve',NULL,'null');
insert into feedme.recipe_steps values('natalies-trinidadian-fish-stew',0,0, 'Pour the juice and scatter the zest of the first lime into a shallow dish big enough to fit the fish in one layer. Swirl the juice around so it covers the entire surface of the dish. Lay the fish on top and pour over the lemon juice.');
insert into feedme.recipe_steps values('natalies-trinidadian-fish-stew',1,1, 'Strip the thyme leaves off the stalks and crush them with a pinch of salt and the white pepper using a pestle and mortar to make a rough paste. Tip the thyme paste over the fish and gently massage it in, taking care not to break up the flesh at all. Pour the rum over and give it one last rub. Cover and leave to marinate out of the fridge for about 1 hour.');
insert into feedme.recipe_steps values('natalies-trinidadian-fish-stew',2,2, 'Heat the oil in a wide shallow pan with a lid. Throw in the onion rings and cook for 4-5 minutes until they begin to soften. Tip the coriander, garlic, tomato slices and sugar in with the onions, stir and cook for about 3-4 minutes until the tomatoes begin to release their juices and everything smells sweet.');
insert into feedme.recipe_steps values('natalies-trinidadian-fish-stew',3,3, 'Lift the fish from its marinade and nestle it in the pan among the onions and tomatoes. Pour over the marinade, 3 tbsp of water and the Angostura bitters, if you are using it.');
insert into feedme.recipe_steps values('natalies-trinidadian-fish-stew',4,4, 'Cover the pan and gently cook for 6-8 minutes until the fish flakes easily and the onions are softened but still have some bite to them. Season with salt and pepper. The authentic Trinidadian dish is tangy-sweet with lots of thyme flavour and very limey, so taste the sauce and if you want more tang, squeeze over the remaining lime. Serve with basmati rice or boiled potatoes and steamed vegetables.');
insert into feedme.recipes VALUES ('steamed-sea-bass-with-black-bean-sauce', 'Steamed sea bass with black bean sauce', 'Chinese meals don''t need to be eaten out of a takeaway carton - this special fish dish makes a smart dinner for two', 'chinese');
insert into feedme.recipe_ingredients values('steamed-sea-bass-with-black-bean-sauce','sea bass','1 sea bass, head on, gutted',1.0,'unit');
insert into feedme.recipe_ingredients values('steamed-sea-bass-with-black-bean-sauce','ginger','2cm piece ginger, thinly sliced',2.0,'unit');
insert into feedme.recipe_ingredients values('steamed-sea-bass-with-black-bean-sauce','wine','2 tbsp Shaohsing rice wine',2.0,'tbsp');
insert into feedme.recipe_ingredients values('steamed-sea-bass-with-black-bean-sauce','rice','cooked jasmine rice, to serve',NULL,'null');
insert into feedme.recipe_steps values('steamed-sea-bass-with-black-bean-sauce',0,0, 'Wash the fish in cold running water. Pat dry with kitchen paper, then slash 3-4 slits into the skin on both sides. Season all over with salt and ground white pepper. Place ginger slices in the slits of the fish and inside the cavity.');
insert into feedme.recipe_steps values('steamed-sea-bass-with-black-bean-sauce',1,1, 'Place the fish on a plate that fits inside your wok. In your wok place a pudding bowl upside down and pour water to come halfway up. Place the wok on the heat and bring the water to a boil.');
insert into feedme.recipe_steps values('steamed-sea-bass-with-black-bean-sauce',2,2, 'Pour the rice wine over the fish, put the plate onto the upside-down bowl and place the lid of the wok on top. Steam on high heat for 8-9 mins, then leave to rest.');
insert into feedme.recipe_steps values('steamed-sea-bass-with-black-bean-sauce',3,3, 'While the fish is cooking, make the black bean sauce. Add the groundnut oil to a hot wok or frying pan. When it starts to smoke, add the garlic, ginger and beans (or use 100g ready-made black bean sauce) and stir-fry for a few secs. Add the rice wine, sesame oil and light soy sauce, then bring to the bubble.');
insert into feedme.recipe_steps values('steamed-sea-bass-with-black-bean-sauce',4,4, 'Carefully remove the fish (you can keep it on the plate that you cooked it on). Garnish with the spring onions and coriander, then drizzle over the black bean sauce and serve with rice.');
insert into feedme.recipes VALUES ('sag-aloo', 'Sag aloo', 'Nutritious spinach makes the base for this traditional, healthy Indian side dish, combined with potato and spices', 'indian');
insert into feedme.recipe_ingredients values('sag-aloo','sunflower oil','2 tbsp sunflower oil',2.0,'tbsp');
insert into feedme.recipe_ingredients values('sag-aloo','onion','1 onion, finely chopped',1.0,'unit');
insert into feedme.recipe_ingredients values('sag-aloo','garlic','2 garlic cloves, sliced',2.0,'unit');
insert into feedme.recipe_ingredients values('sag-aloo','ginger','1 tbsp chopped ginger',1.0,'tbsp');
insert into feedme.recipe_ingredients values('sag-aloo','potato','500g potato, cut into 2cm, 0.75in chunks',500.0,'gram');
insert into feedme.recipe_ingredients values('sag-aloo','chilli','1 large red chilli, halved, deseeded and finely sliced',1.0,'unit');
insert into feedme.recipe_ingredients values('sag-aloo','cumin','0.5 tsp each black mustard seeds, cumin seeds, turmeric',5.0,'tsp');
insert into feedme.recipe_ingredients values('sag-aloo','spinach','250g spinachleaves',250.0,'gram');
insert into feedme.recipe_steps values('sag-aloo',0,0, 'Heat the oil in a large pan, add the onion, garlic and ginger, and fry for about 3 mins. Stir in the potatoes, chilli, spices and half a tsp salt and continue cooking and stirring for 5 mins more. Add a splash of water, cover, and cook for 8-10 mins.');
insert into feedme.recipe_steps values('sag-aloo',1,1, 'Check the potatoes are ready by spearing with the point of a knife, and if they are, add the spinach and let it wilt into the pan. Take off the heat and serve.');
insert into feedme.recipes VALUES ('slowroast-lamb-with-cinnamon-fennel-and-citrus', 'Slow-roast lamb with cinnamon, fennel & citrus', 'For lamb that will melt in your mouth and surprise your palate, follow Sarah Cook''s easy recipe', 'turkish');
insert into feedme.recipe_ingredients values('slowroast-lamb-with-cinnamon-fennel-and-citrus','lamb','1 leg of lamb',1.0,'unit');
insert into feedme.recipe_ingredients values('slowroast-lamb-with-cinnamon-fennel-and-citrus','orange','zest and juice 1 lemonand 1 orange',NULL,'null');
insert into feedme.recipe_ingredients values('slowroast-lamb-with-cinnamon-fennel-and-citrus','olive oil','4 tbsp olive oil',4.0,'tbsp');
insert into feedme.recipe_ingredients values('slowroast-lamb-with-cinnamon-fennel-and-citrus','honey','2 tbsp clear honey',2.0,'tbsp');
insert into feedme.recipe_ingredients values('slowroast-lamb-with-cinnamon-fennel-and-citrus','cumin','1 tbsp each of cinnamon, fennel seeds, ground cumin',1.0,'tbsp');
insert into feedme.recipe_ingredients values('slowroast-lamb-with-cinnamon-fennel-and-citrus','garlic','3 garlic cloves, crushed',3.0,'unit');
insert into feedme.recipe_steps values('slowroast-lamb-with-cinnamon-fennel-and-citrus',0,0, 'Put the lamb into a large food bag with all the juice and marinate overnight.');
insert into feedme.recipe_steps values('slowroast-lamb-with-cinnamon-fennel-and-citrus',1,1, 'The next day, take the lamb out of the fridge 1 hr before you want to cook it. Heat oven to 220C/200C fan/gas 7. Take the lamb out of the marinade (reserve remaining marinade) and pat dry. Rub with half the oil and roast for 15-20 mins until browned. Remove lamb and reduce oven to 160C/140C fan/gas 3.');
insert into feedme.recipe_steps values('slowroast-lamb-with-cinnamon-fennel-and-citrus',2,2, 'Mix the zests, remaining oil, honey, spices and garlic with plenty of seasoning. Lay a large sheet of baking parchment on a large sheet of foil. Sit the lamb leg on top, rub all over with the paste and pull up the sides of the foil. Drizzle marinade into base, and scrunch foil to seal.');
insert into feedme.recipe_steps values('slowroast-lamb-with-cinnamon-fennel-and-citrus',3,3, 'Roast for 4 hrs, until very tender. Rest, still wrapped, for 30 mins. Unwrap and serve with juices.');
insert into feedme.recipes VALUES ('marinated-feta-in-thyme-and-chilli-oil', 'Marinated feta in thyme & chilli oil', 'Pack salty Greek cheese into jars with olive oil, thyme, tomatoes, artichokes and olives - mop up the goodness with bread', 'greek');
insert into feedme.recipe_ingredients values('marinated-feta-in-thyme-and-chilli-oil','olive oil','100ml olive oil',100.0,'ml');
insert into feedme.recipe_ingredients values('marinated-feta-in-thyme-and-chilli-oil','thyme','2 tbsp fresh thyme leaves, plus a few extra for scattering',2.0,'tbsp');
insert into feedme.recipe_ingredients values('marinated-feta-in-thyme-and-chilli-oil','garlic','4 garlic cloves, sliced',4.0,'unit');
insert into feedme.recipe_ingredients values('marinated-feta-in-thyme-and-chilli-oil','chilli','0.25 tsp dried chilli flakes',25.0,'tsp');
insert into feedme.recipe_ingredients values('marinated-feta-in-thyme-and-chilli-oil','olive oil','140g semi-dried tomatoin olive oil, drained',140.0,'gram');
insert into feedme.recipe_ingredients values('marinated-feta-in-thyme-and-chilli-oil','artichoke','175g pack or jar artichoke heartin oil',175.0,'gram');
insert into feedme.recipe_ingredients values('marinated-feta-in-thyme-and-chilli-oil','cheese','2 x 200g packs feta cheese',200.0,'gram');
insert into feedme.recipe_ingredients values('marinated-feta-in-thyme-and-chilli-oil','olive','85g Kalamata olive',85.0,'gram');
insert into feedme.recipe_steps values('marinated-feta-in-thyme-and-chilli-oil',0,0, 'Tip the olive oil and thyme into a bowl and blitz with a hand blender. Stir in the garlic, chilli and lots of black pepper (but no salt). You could add some of the oil from the tomatoes and artichokes, too.');
insert into feedme.recipe_steps values('marinated-feta-in-thyme-and-chilli-oil',1,1, 'Cut each block of feta into 4 (or 6) and arrange in a dish or a food container with the tomatoes, artichokes and olives. Spoon over the herby oil, scatter with extra thyme and leave to marinate in the fridge overnight.');
insert into feedme.recipes VALUES ('garlicky-prawns-with-sherry', 'Garlicky prawns with sherry', 'Seafood lovers will love this simple tapas-style recipe that''s low fat and quick to cook up', 'spanish');
insert into feedme.recipe_ingredients values('garlicky-prawns-with-sherry','olive oil','3 tbsp olive oil',3.0,'tbsp');
insert into feedme.recipe_ingredients values('garlicky-prawns-with-sherry','garlic','3 garlic cloves, thinly sliced',3.0,'unit');
insert into feedme.recipe_ingredients values('garlicky-prawns-with-sherry','prawn','400g large prawn, uncooked',400.0,'gram');
insert into feedme.recipe_ingredients values('garlicky-prawns-with-sherry','dry sherry','2 tbsp dry sherry (fino)',2.0,'tbsp');
insert into feedme.recipe_ingredients values('garlicky-prawns-with-sherry','parsley','handful parsley, chopped',NULL,'null');
insert into feedme.recipe_steps values('garlicky-prawns-with-sherry',0,0, 'Heat the olive oil in a large frying pan. Tip in the garlic slices and cook for a few secs. Then stir through the prawns and cook for a couple of mins until they start to turn pink.');
insert into feedme.recipe_steps values('garlicky-prawns-with-sherry',1,1, 'Pour over the sherry and cook for a few mins more, just until the prawns are cooked through. Sprinkle with parsley before serving.');
insert into feedme.recipes VALUES ('chilli-ginger-lamb-chops', 'Chilli ginger lamb chops', 'Marinate your chops with ginger, lemon, chilli and cumin then chargrill until juicy in the middle', 'turkish');
insert into feedme.recipe_ingredients values('chilli-ginger-lamb-chops','garlic','4 garlic cloves, crushed',4.0,'unit');
insert into feedme.recipe_ingredients values('chilli-ginger-lamb-chops','ginger','1 tbsp grated ginger',1.0,'tbsp');
insert into feedme.recipe_ingredients values('chilli-ginger-lamb-chops','lemon juice','1 tbsp lemon juice',1.0,'tbsp');
insert into feedme.recipe_ingredients values('chilli-ginger-lamb-chops','olive oil','1 tbsp olive oil',1.0,'tbsp');
insert into feedme.recipe_ingredients values('chilli-ginger-lamb-chops','chilli','0.5 tsp chilli powder',5.0,'tsp');
insert into feedme.recipe_ingredients values('chilli-ginger-lamb-chops','cumin','1 tsp cumin',1.0,'tsp');
insert into feedme.recipe_ingredients values('chilli-ginger-lamb-chops','lamb','8 lamb chops',8.0,'unit');
insert into feedme.recipe_steps values('chilli-ginger-lamb-chops',0,0, 'Put the garlic in a bowl with the ginger, lemon juice, oil, spices and seasoning. Blitz with a hand blender until smooth, then use to coat the lamb chops on both sides. Leave to marinate in the fridge for a couple of hours or overnight.');
insert into feedme.recipe_steps values('chilli-ginger-lamb-chops',1,1, 'Heat a barbecue until hot. Barbecue the chops over the coals for 3 mins on each side until cooked but still pink and juicy in the centre.');
insert into feedme.recipes VALUES ('moroccan-spiced-carrots', 'Moroccan spiced carrots', 'Create a carrot sensation with this simple idea for a dressing and experiment with your favourite veg', 'moroccan');
insert into feedme.recipe_ingredients values('moroccan-spiced-carrots','carrot','500g carrot, halved and cut into thin rounds',500.0,'gram');
insert into feedme.recipe_ingredients values('moroccan-spiced-carrots','olive oil','2 tbsp extra-virgin olive oil',2.0,'tbsp');
insert into feedme.recipe_ingredients values('moroccan-spiced-carrots','harissa','harissa paste',NULL,'null');
insert into feedme.recipe_ingredients values('moroccan-spiced-carrots','lemon juice','1 tbsp lemon juice',1.0,'tbsp');
insert into feedme.recipe_ingredients values('moroccan-spiced-carrots','coriander','handful chopped coriander, plus extra',NULL,'null');
insert into feedme.recipe_steps values('moroccan-spiced-carrots',0,0, 'Place the carrots in a steamer set over a pan of boiling water and cook for 7-10 mins until just soft. Mix together the remaining ingredients, then toss the warm carrots through the dressing.');
insert into feedme.recipe_steps values('moroccan-spiced-carrots',1,1, 'Sprinkle with extra chopped coriander to serve.');
